<body>

    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha">0.0500</span>
            </div>
            <div class="control-row">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta">5.00</span>
            </div>
            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function () {
            // Get DOM elements
            const sliderAlpha = document.getElementById('slider-alpha');
            const sliderBeta = document.getElementById('slider-beta');
            const sliderGamma = document.getElementById('slider-gamma');

            const valueAlpha = document.getElementById('value-alpha');
            const valueBeta = document.getElementById('value-beta');
            const valueGamma = document.getElementById('value-gamma');

            const plotContainer = document.getElementById('plot-container');

            // --- ODE Solver (4th-order Runge-Kutta) ---
            function rk4(dydt, y0, t0, tf, h, params) {
                const n = Math.floor((tf - t0) / h);
                const t_values = new Array(n + 1);
                const y_values = new Array(n + 1).fill(0).map(() => new Array(y0.length));

                t_values[0] = t0;
                y_values[0] = y0.slice();

                let y_curr = y0.slice();

                for (let i = 0; i < n; i++) {
                    const t = t0 + i * h;

                    const k1 = dydt(t, y_curr, params).map(v => v * h);
                    const y_k1 = y_curr.map((yi, j) => yi + 0.5 * k1[j]);

                    const k2 = dydt(t + 0.5 * h, y_k1, params).map(v => v * h);
                    const y_k2 = y_curr.map((yi, j) => yi + 0.5 * k2[j]);

                    const k3 = dydt(t + 0.5 * h, y_k2, params).map(v => v * h);
                    const y_k3 = y_curr.map((yi, j) => yi + k3[j]);

                    const k4 = dydt(t + h, y_k3, params).map(v => v * h);

                    y_curr = y_curr.map((yi, j) => yi + (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]) / 6);

                    t_values[i + 1] = t + h;
                    y_values[i + 1] = y_curr.slice();
                }

                const deltaT = y_values.map(y => y[0]);
                const cX = y_values.map(y => y[1]);

                return { t: t_values, deltaT, cX };
            }

            // --- ODE System Definition ---
            // y[0] = ΔT, y[1] = C_X
            function odeSystem(t, y, params) {
                const { alpha, beta, gamma } = params;
                const deltaT = y[0];
                const cX = y[1];

                const expTerm = Math.exp(deltaT / (1 + deltaT / alpha));

                const dDeltaT_dt = gamma * cX * expTerm - beta * deltaT;
                const dCX_dt = 1 - cX * expTerm;

                return [dDeltaT_dt, dCX_dt];
            }


            // --- Main Update and Plotting Function ---
            function updatePlot() {
                // 1. Get current slider values
                const alpha = parseFloat(sliderAlpha.value);
                const beta = parseFloat(sliderBeta.value);
                const gamma = parseFloat(sliderGamma.value);

                // 2. Update value displays
                valueAlpha.textContent = alpha.toFixed(4);
                valueBeta.textContent = beta.toFixed(2);
                valueGamma.textContent = gamma.toFixed(0);

                // 3. Solve the ODE system
                const params = { alpha, beta, gamma };
                const y0 = [0, 0]; // Initial conditions: ΔT(0) = 0, C_X(0) = 0
                const t0 = 0;
                const tf = 20;
                const h = 0.05;

                const { t, deltaT, cX } = rk4(odeSystem, y0, t0, tf, h, params);

                // 4. Process data for plotting
                const scaledCx = cX.map(val => val * 1000);

                // 5. Define Plotly traces
                const traceDeltaT = {
                    x: t,
                    y: deltaT,
                    mode: 'lines',
                    name: 'ΔT',
                    line: { color: 'orange', width: 2 }
                };

                const traceCx = {
                    x: t,
                    y: scaledCx,
                    mode: 'lines',
                    name: '1000 C_X',
                    line: { color: 'steelblue', width: 2 }
                };

                const data = [traceDeltaT, traceCx];

                // 6. Define Plotly layout
                const layout = {
                    xaxis: { 
                        title: '', 
                        range: [0, 20], 
                        zeroline: true 
                    },
                    yaxis: { 
                        title: '', 
                        range: [0, 100], 
                        zeroline: true 
                    },
                    showlegend: false,
                    margin: { l: 50, r: 20, b: 30, t: 20 },
                    plot_bgcolor: '#f0f0f0',
                    paper_bgcolor: 'white',
                    annotations: [
                        {
                            text: 'ΔT',
                            x: -0.05,
                            y: 100,
                            xref: 'paper',
                            yref: 'y',
                            xanchor: 'right',
                            yanchor: 'top',
                            showarrow: false,
                            font: { color: 'purple', size: 14 }
                        },
                        {
                            text: '1000 C<sub>X</sub>',
                            x: -0.05,
                            y: 92, // Adjusted for spacing
                            xref: 'paper',
                            yref: 'y',
                            xanchor: 'right',
                            yanchor: 'top',
                            showarrow: false,
                            font: { color: 'purple', size: 14 }
                        }
                    ]
                };

                // 7. Render or update the plot
                Plotly.react(plotContainer, data, layout, {responsive: true});
            }

            // --- Event Listeners ---
            sliderAlpha.addEventListener('input', updatePlot);
            sliderBeta.addEventListener('input', updatePlot);
            sliderGamma.addEventListener('input', updatePlot);

            // --- Initial Plot ---
            updatePlot();
        });
    </script>

