<body>

    <h1>Rayleigh Wave Simulation</h1>

    <div id="controls-container">
        <div class="control-group">
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="0" max="25" value="7.0" step="0.01">
            <span id="value-time">7.00</span>
        </div>
        <div class="control-group">
            <label for="slider-wavelength">wavelength</label>
            <input type="range" id="slider-wavelength" min="1" max="8" value="4.0" step="0.01">
            <span id="value-wavelength">4.00</span>
        </div>
        <div class="control-group">
            <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
            <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" value="2.0" step="0.01">
            <span id="value-longitudinal-amplitude">2.00</span>
        </div>
        <div class="control-group">
            <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
            <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" value="1.0" step="0.01">
            <span id="value-latitudinal-amplitude">1.00</span>
        </div>
    </div>

    <div id="canvas-container"></div>

    <script>
        let sketch = function(p) {
            // Slider and value display elements
            let sliderTime, valueTime;
            let sliderWavelength, valueWavelength;
            let sliderLongitudinalAmp, valueLongitudinalAmp;
            let sliderLatitudinalAmp, valueLatitudinalAmp;

            // Simulation parameters
            let time;
            let wavelength;
            let longAmplitude;
            let latAmplitude;

            // Grid properties
            const gridCols = 10;
            const gridRows = 6;
            const particleGrid = [];

            // Animation state
            let isTimeSliderDragged = false;

            p.setup = function() {
                let canvas = p.createCanvas(700, 450);
                canvas.parent('canvas-container');

                // Get DOM elements
                sliderTime = p.select('#slider-time');
                valueTime = p.select('#value-time');
                sliderWavelength = p.select('#slider-wavelength');
                valueWavelength = p.select('#value-wavelength');
                sliderLongitudinalAmp = p.select('#slider-longitudinal-amplitude');
                valueLongitudinalAmp = p.select('#value-longitudinal-amplitude');
                sliderLatitudinalAmp = p.select('#slider-latitudinal-amplitude');
                valueLatitudinalAmp = p.select('#value-latitudinal-amplitude');

                // Initialize time
                time = parseFloat(sliderTime.value());

                // Set up event listeners for sliders
                sliderTime.elt.addEventListener('mousedown', () => isTimeSliderDragged = true);
                sliderTime.elt.addEventListener('mouseup', () => isTimeSliderDragged = false);
                sliderTime.input(() => {
                    if (isTimeSliderDragged) time = sliderTime.value();
                });

                sliderWavelength.input(() => valueWavelength.html(p.nfc(sliderWavelength.value(), 2)));
                sliderLongitudinalAmp.input(() => valueLongitudinalAmp.html(p.nfc(sliderLongitudinalAmp.value(), 2)));
                sliderLatitudinalAmp.input(() => valueLatitudinalAmp.html(p.nfc(sliderLatitudinalAmp.value(), 2)));

                // Initialize particle grid
                const paddingX = 60;
                const paddingY = 60;
                const gridWidth = p.width - 2 * paddingX;
                const gridHeight = p.height - 2 * paddingY;

                for (let i = 0; i < gridRows; i++) {
                    for (let j = 0; j < gridCols; j++) {
                        particleGrid.push({
                            x0: paddingX + (gridWidth / (gridCols - 1)) * j,
                            y0: paddingY + (gridHeight / (gridRows - 1)) * i,
                            row: i,
                        });
                    }
                }
            };

            p.draw = function() {
                // 1. Update State
                if (!isTimeSliderDragged) {
                    time += 0.03;
                    if (time > sliderTime.attribute('max')) {
                        time = parseFloat(sliderTime.attribute('min'));
                    }
                    sliderTime.value(time);
                }
                valueTime.html(p.nfc(time, 2));

                wavelength = sliderWavelength.value();
                longAmplitude = sliderLongitudinalAmp.value();
                latAmplitude = sliderLatitudinalAmp.value();

                // 2. Clear Canvas
                p.background('#E0F0FF');

                // 3. Particle Grid Logic
                const k = p.TWO_PI / wavelength;
                const currentPositions = [];

                for (let i = 0; i < particleGrid.length; i++) {
                    const particle = particleGrid[i];
                    const { x0, y0, row } = particle;

                    // Amplitude decays with depth (exponential decay)
                    const ampDecay = p.exp(-(row / (gridRows - 1)) * 1.5);

                    // Amplitudes for this specific particle
                    const ampX = longAmplitude * ampDecay * 20; // 20 is a scaling factor
                    const ampY = latAmplitude * ampDecay * 20;

                    // Phase calculation
                    const phase = k * x0 - time;

                    // Draw elliptical paths
                    p.noFill();
                    p.stroke('#8F998F');
                    p.strokeWeight(2);
                    p.ellipse(x0, y0, ampX * 2, ampY * 2);

                    // Calculate particle displacement (retrograde motion)
                    const dx = ampX * p.cos(phase);
                    const dy = ampY * p.sin(phase);

                    currentPositions.push({ x: x0 + dx, y: y0 + dy });
                }

                // 4. Draw Connecting Lines and Particles
                // Draw lines connecting particles in the same row
                p.stroke('#0000FF');
                p.strokeWeight(2);
                p.drawingContext.setLineDash([5, 5]); // Dashed lines
                p.noFill();

                for (let i = 0; i < gridRows; i++) {
                    p.beginShape();
                    for (let j = 0; j < gridCols; j++) {
                        const index = i * gridCols + j;
                        p.vertex(currentPositions[index].x, currentPositions[index].y);
                    }
                    p.endShape();
                }

                p.drawingContext.setLineDash([]); // Reset to solid lines

                // Draw the particles themselves
                p.fill('#FF0000');
                p.noStroke();
                for (let i = 0; i < currentPositions.length; i++) {
                    p.circle(currentPositions[i].x, currentPositions[i].y, 8);
                }
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
