<script>
document.addEventListener('DOMContentLoaded', () => {

    // --- 1. GET HTML ELEMENTS ---
    const controls = {
        btnArrhenius: document.getElementById('btn-arrhenius'),
        btnExponential: document.getElementById('btn-exponential'),
        sliders: {
            n: document.getElementById('slider-n'),
            tmin: document.getElementById('slider-tmin'),
            tmax: document.getElementById('slider-tmax'),
            tref: document.getElementById('slider-tref'),
            c: document.getElementById('slider-c'),
            b: document.getElementById('slider-b'),
            a: document.getElementById('slider-a'),
        },
        values: {
            n: document.getElementById('value-n'),
            tmin: document.getElementById('value-tmin'),
            tmax: document.getElementById('value-tmax'),
            tref: document.getElementById('value-tref'),
            c: document.getElementById('value-c'),
            b: document.getElementById('value-b'),
            a: document.getElementById('value-a'),
        }
    };

    // --- 2. DEFINE STATE AND DEFAULTS ---
    const state = {
        model: 'arrhenius',
        n: 6,
        tmin: 25,
        tmax: 75,
        tref: 35,
        c: 0.025,
        b: 273.16,
        a: 4000,
    };

    // --- 3. HELPER FUNCTIONS ---

    /**
     * Performs simple linear regression on a set of [x, y] data points.
     * @param {Array<[number, number]>} data - Array of [x, y] pairs.
     * @returns {{slope: number, intercept: number, rSquared: number}}
     */
    function linearRegression(data) {
        let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0, sumY2 = 0;
        const n = data.length;
        if (n < 2) return { slope: 0, intercept: 0, rSquared: 0 };

        for (const [x, y] of data) {
            sumX += x;
            sumY += y;
            sumXY += x * y;
            sumX2 += x * x;
            sumY2 += y * y;
        }

        const meanX = sumX / n;
        const meanY = sumY / n;

        const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
        const intercept = meanY - slope * meanX;

        let ssTotal = 0;
        let ssResidual = 0;
        for (const [x, y] of data) {
            const predictedY = slope * x + intercept;
            ssTotal += (y - meanY) ** 2;
            ssResidual += (y - predictedY) ** 2;
        }

        let rSquared = 1 - (ssResidual / ssTotal);
        if (isNaN(rSquared) || ssTotal === 0) {
            rSquared = 1;
        }

        return { slope, intercept, rSquared: Math.max(0, rSquared) };
    }

    /**
     * Generates an array of n numbers evenly spaced between start and stop.
     * @param {number} start
     * @param {number} stop
     * @param {number} n
     * @returns {number[]}
     */
    function linspace(start, stop, n) {
        if (n <= 1) return [start];
        const arr = [];
        const step = (stop - start) / (n - 1);
        for (let i = 0; i < n; i++) {
            arr.push(start + (i * step));
        }
        return arr;
    }

    // --- 4. MAIN UPDATE & DRAWING FUNCTION ---

    function updateAndDraw() {
        // --- DATA GENERATION ---
        const T = linspace(state.tmin, state.tmax, state.n);
        let generatedData = [];
        let fittingResult;

        const commonPlotLayout = {
            margin: { t: 50, b: 40, l: 60, r: 20 },
            showlegend: false,
            xaxis: { zeroline: false },
            yaxis: { zeroline: false },
        };
        const commonLineTrace = {
            mode: 'lines',
            line: { width: 2 },
        };
        const commonScatterTrace = {
            mode: 'markers',
            marker: { size: 8 },
        };

        if (state.model === 'arrhenius') {
            // Generate data with Arrhenius model
            const k_tref_arr = Math.exp(-state.a / (state.tref + state.b));
            generatedData = T.map(t => {
                const k_t = Math.exp(-state.a / (t + state.b));
                return { x: t, y: k_t / k_tref_arr };
            });

            // Fit with exponential model: log(y) vs (T - T_ref)
            const dataForFit = generatedData.map(p => [p.x - state.tref, Math.log(p.y)]);
            fittingResult = linearRegression(dataForFit);
            const fitted_c = fittingResult.slope;

            // --- TOP PLOT: Exponential fit on Arrhenius data ---
            const topPlotTraces = [
                { ...commonScatterTrace, x: generatedData.map(p => p.x), y: generatedData.map(p => p.y), name: 'Data', marker: { color: '#d62728' } },
                { ...commonLineTrace, x: T, y: T.map(t => Math.exp(fitted_c * (t - state.tref))), name: 'Fit', line: { color: '#d62728' } }
            ];
            const topPlotLayout = {
                ...commonPlotLayout,
                title: `Exponential model fit to Arrhenius data<br>c = ${fitted_c.toFixed(4)} R² = ${fittingResult.rSquared.toFixed(4)}`,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' }
            };
            Plotly.react('plot-top', topPlotTraces, topPlotLayout);

            // --- BOTTOM PLOT: Linearized view ---
            const bottomPlotTraces = [
                { ...commonScatterTrace, x: dataForFit.map(p => p[0]), y: dataForFit.map(p => p[1]), name: 'Transformed Data', marker: { color: '#2ca02c' } },
                { ...commonLineTrace, x: dataForFit.map(p => p[0]), y: dataForFit.map(p => fitted_c * p[0] + fittingResult.intercept), name: 'Linear Fit', line: { color: '#2ca02c' } }
            ];
            const bottomPlotLayout = {
                ...commonPlotLayout,
                title: `Linear fit of the exponential model<br>R² = ${fittingResult.rSquared.toFixed(4)}`,
                xaxis: { title: 'T - T<sub>ref</sub>' },
                yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                shapes: [
                    { type: 'line', x0: 0, y0: Math.min(...bottomPlotTraces[0].y), x1: 0, y1: Math.max(...bottomPlotTraces[0].y), line: { color: 'grey', width: 1, dash: 'dot' } },
                    { type: 'line', x0: Math.min(...bottomPlotTraces[0].x), y0: 0, x1: Math.max(...bottomPlotTraces[0].x), y1: 0, line: { color: 'grey', width: 1, dash: 'dot' } }
                ]
            };
            Plotly.react('plot-bottom', bottomPlotTraces, bottomPlotLayout);

        } else { // model is 'exponential'
            // Generate data with exponential model
            generatedData = T.map(t => ({
                x: t,
                y: Math.exp(state.c * (t - state.tref))
            }));

            // Fit with Arrhenius model: log(y) vs 1/(T+b)
            const dataForFit = generatedData.map(p => [1 / (p.x + state.b), Math.log(p.y)]);
            fittingResult = linearRegression(dataForFit);
            const fitted_a = -fittingResult.slope;

            // --- TOP PLOT: Arrhenius fit on exponential data ---
            const k_tref_fit = Math.exp(-fitted_a / (state.tref + state.b));
            const topPlotTraces = [
                { ...commonScatterTrace, x: generatedData.map(p => p.x), y: generatedData.map(p => p.y), name: 'Data', marker: { color: '#d62728' } },
                { ...commonLineTrace, x: T, y: T.map(t => Math.exp(-fitted_a / (t + state.b)) / k_tref_fit), name: 'Fit', line: { color: '#d62728' } }
            ];
            const topPlotLayout = {
                ...commonPlotLayout,
                title: `Arrhenius model fit to exponential data<br>a = ${fitted_a.toFixed(1)}, R² = ${fittingResult.rSquared.toFixed(4)}`,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T)/k(T<sub>ref</sub>)' }
            };
            Plotly.react('plot-top', topPlotTraces, topPlotLayout);

            // --- BOTTOM PLOT: Linearized view ---
            const bottomPlotTraces = [
                { ...commonScatterTrace, x: dataForFit.map(p => p[0]), y: dataForFit.map(p => p[1]), name: 'Transformed Data', marker: { color: '#1f77b4' } },
                { ...commonLineTrace, x: dataForFit.map(p => p[0]), y: dataForFit.map(p => fittingResult.slope * p[0] + fittingResult.intercept), name: 'Linear Fit', line: { color: '#1f77b4' } }
            ];
            const bottomPlotLayout = {
                ...commonPlotLayout,
                title: `Linear fit of the Arrhenius model<br>R² = ${fittingResult.rSquared.toFixed(4)}`,
                xaxis: { title: '1/(T + b)' },
                yaxis: { title: 'log(k(T)/k(T<sub>ref</sub>))' },
                shapes: [
                     { type: 'line', x0: Math.min(...bottomPlotTraces[0].x), y0: 0, x1: Math.max(...bottomPlotTraces[0].x), y1: 0, line: { color: 'grey', width: 1, dash: 'dot' } }
                ]
            };
            Plotly.react('plot-bottom', bottomPlotTraces, bottomPlotLayout);
        }
    }


    // --- 5. INITIALIZE CONTROLS AND EVENT LISTENERS ---

    function initialize() {
        // Set initial button state
        controls.btnArrhenius.classList.add('active-button');
        controls.btnExponential.classList.remove('active-button');

        // Set slider values from state and attach listeners
        for (const key in controls.sliders) {
            const slider = controls.sliders[key];
            const valueSpan = controls.values[key];

            slider.value = state[key];
            valueSpan.textContent = slider.value;

            slider.addEventListener('input', (e) => {
                const value = e.target.type === 'range' ? parseFloat(e.target.value) : e.target.value;
                state[key] = value;
                valueSpan.textContent = value.toFixed(key === 'c' || key === 'b' ? 3 : 0);

                // Enforce T_max > T_min constraint
                if (key === 'tmin') {
                    const minTmax = value + 1;
                    controls.sliders.tmax.min = minTmax;
                    if (state.tmax < minTmax) {
                        state.tmax = minTmax;
                        controls.sliders.tmax.value = state.tmax;
                        controls.values.tmax.textContent = state.tmax;
                    }
                }

                updateAndDraw();
            });
        }

        // Model selection button listeners
        controls.btnArrhenius.addEventListener('click', () => {
            if (state.model !== 'arrhenius') {
                state.model = 'arrhenius';
                controls.btnArrhenius.classList.add('active-button');
                controls.btnExponential.classList.remove('active-button');
                updateAndDraw();
            }
        });

        controls.btnExponential.addEventListener('click', () => {
            if (state.model !== 'exponential') {
                state.model = 'exponential';
                controls.btnExponential.classList.add('active-button');
                controls.btnArrhenius.classList.remove('active-button');
                updateAndDraw();
            }
        });

        // Initial draw
        updateAndDraw();
    }

    initialize();
});
</script>

</body>
</html>
