        // --- Helper Functions ---

        /**
         * Calculates letter frequency percentages for a given text.
         * @param {string} text - The input text (assumed to be lowercase a-z).
         * @returns {Array<{letter: string, frequency: number}>} An array of objects sorted by frequency.
         */
        function calculateFrequency(text) {
            const counts = {};
            alphabet.forEach(letter => counts[letter] = 0);

            for (const char of text) {
                if (counts.hasOwnProperty(char)) {
                    counts[char]++;
                }
            }

            const totalLetters = text.length;
            if (totalLetters === 0) {
                 return alphabet.map(letter => ({ letter, frequency: 0 }));
            }

            const frequencies = alphabet.map(letter => {
                const frequency = (counts[letter] / totalLetters) * 100;
                return { letter, frequency };
            });

            return frequencies.sort((a, b) => b.frequency - a.frequency);
