            const processedPlaintext = fullPlaintext.toLowerCase().replace(/[^a-z]/g, '');

            // 4. Display original message and chart
            originalTextP.textContent = fullPlaintext.substring(0, 80) + '...';
            const originalFrequencies = calculateFrequency(processedPlaintext);
            renderChart('original-chart', originalFrequencies);

            // 5. Encrypt the message
            let encryptedText = '';

            if (processedPlaintext.length > 0) {
                switch (cipherType) {
                    case 'Caesar':
                        for (const char of processedPlaintext) {
                            const charCode = char.charCodeAt(0);
                            const shiftedCode = ((charCode - 97 + offset) % 26) + 97;
                            encryptedText += String.fromCharCode(shiftedCode);
                        }
                        break;

                    case 'De Vigenere':
                    case 'Autokey':
                        let sanitizedKeyword = keyword.toLowerCase().replace(/[^a-z]/g, '') || 'a';
                        let keyStream = sanitizedKeyword;
                        if (cipherType === 'Autokey') {
                            keyStream += processedPlaintext;
                        }

                        for (let i = 0; i < processedPlaintext.length; i++) {
                            const plainCharCode = processedPlaintext.charCodeAt(i) - 97;
                            const keyCharCode = keyStream.charCodeAt(i % keyStream.length) - 97;
                            const encryptedCharCode = ((plainCharCode + keyCharCode) % 26) + 97;
                            encryptedText += String.fromCharCode(encryptedCharCode);
                        }
                        break;
                }
            }

            // 6. Display encrypted message and chart
            encryptedTextP.textContent = encryptedText;
            const encryptedFrequencies = calculateFrequency(encryptedText);
            renderChart('encrypted-chart', encryptedFrequencies);
        }

        // --- Event Listeners ---
        function initialize() {
            messageSelect.addEventListener('change', updateVisualization);
            cipherSelect.addEventListener('change', updateVisualization);
            keywordInput.addEventListener('input', updateVisualization);
            offsetSlider.addEventListener('input', updateVisualization);

            // Initial render on page load
            updateVisualization();
        }

        // Run when the DOM is fully loaded
        document.addEventListener('DOMContentLoaded', initialize);

    </script>
</body>
</html>
