<script>
document.addEventListener('DOMContentLoaded', function () {
    // --- DOM Element References ---
    const sliderLambda = document.getElementById('slider-lambda');
    const valueLambda = document.getElementById('value-lambda');
    const sliderOmega = document.getElementById('slider-omega');
    const valueOmega = document.getElementById('value-omega');
    const btnPressureDrop = document.getElementById('btn-pressure-drop');
    const btnDifference = document.getElementById('btn-difference');
    const plotContainer = document.getElementById('plot-container');

    // --- State Management ---
    let state = {
        plotType: 'pressure-drop' // 'pressure-drop' or 'difference'
    };

    // --- Numerical Helper Functions ---

    // Factorial with memoization
    const factorial_memo = [];
    function factorial(n) {
        if (n < 0 || Math.floor(n) !== n) return NaN;
        if (n === 0 || n === 1) return 1;
        if (factorial_memo[n] > 0) return factorial_memo[n];
        return factorial_memo[n] = factorial(n - 1) * n;
    }

    // Bessel function approximations (from Abramowitz & Stegun)
    function besselI0(x) {
        const ax = Math.abs(x);
        if (ax < 3.75) {
            const y = (x / 3.75) * (x / 3.75);
            return 1.0 + y * (3.5156229 + y * (3.0899424 + y * (1.2067492 + y * (0.2659732 + y * (0.0360768 + y * 0.0045813)))));
        }
        const y = 3.75 / ax;
        return (Math.exp(ax) / Math.sqrt(ax)) * (0.39894228 + y * (0.01328592 + y * (0.00225319 - y * (0.00157565 - y * (0.00916281 - y * (0.02057706 - y * (0.02635537 - y * (0.01647633 - y * 0.00392377))))))));
    }

    function besselI1(x) {
        const ax = Math.abs(x);
        if (ax < 3.75) {
            const y = (x / 3.75) * (x / 3.75);
            return x * (0.5 + y * (0.87890594 + y * (0.51498869 + y * (0.15084934 + y * (0.02658733 + y * (0.00301532 + y * 0.00032411))))));
        }
        const y = 3.75 / ax;
        return (Math.exp(ax) / Math.sqrt(ax)) * (0.39894228 - y * (0.03988024 + y * (0.00362018 + y * (0.00163801 + y * (0.01031555 + y * (0.02282967 + y * (0.02895312 + y * (0.01787654 + y * 0.00420059))))))));
    }

    function besselK0(x) {
        if (x <= 0) return Infinity;
        if (x <= 2.0) {
            const y = x * x / 4.0;
            return -Math.log(x / 2.0) * besselI0(x) + (-0.57721566 + y * (0.42278420 + y * (0.23069756 + y * (0.03488590 + y * (0.00262698 + y * (0.00010750 + y * 0.00000740))))));
        }
        const y = 2.0 / x;
        return (Math.exp(-x) / Math.sqrt(x)) * (1.25331414 - y * (0.07832358 - y * (0.02189568 - y * (0.01062446 - y * (0.00587872 - y * (0.00251540 - y * 0.00053208))))));
    }

    function besselK1(x) {
        if (x <= 0) return Infinity;
        if (x <= 2.0) {
            const y = x * x / 4.0;
            return Math.log(x / 2.0) * besselI1(x) + (1.0 / x) * (1.0 + y * (0.15443144 - y * (0.67278579 - y * (0.18156897 - y * (0.01919402 - y * (0.00110404 - y * 0.00004686))))));
        }
        const y = 2.0 / x;
        return (Math.exp(-x) / Math.sqrt(x)) * (1.25331414 + y * (0.23498619 - y * (0.03655620 - y * (0.01504268 - y * (0.00780353 - y * (0.00325614 - y * 0.00068245))))));
    }

    // Stehfest algorithm for numerical inverse Laplace transform
    const N_STEHFEST = 12;
    const V_STEHFEST = new Array(N_STEHFEST + 1);
    (function precomputeStehfestCoeffs() {
        const N2 = N_STEHFEST / 2;
        for (let i = 1; i <= N_STEHFEST; i++) {
            let term = 0;
            for (let k = Math.floor((i + 1) / 2); k <= Math.min(i, N2); k++) {
                term += (Math.pow(k, N2) * factorial(2 * k)) / 
                        (factorial(N2 - k) * factorial(k) * factorial(k - 1) * factorial(i - k) * factorial(2 * k - i));
            }
            V_STEHFEST[i] = term * Math.pow(-1, N2 + i);
        }
    })();

    function stehfest(laplaceFunc, t) {
        if (t === 0) return 0;
        const ln2_t = Math.log(2) / t;
        let sum = 0;
        for (let i = 1; i <= N_STEHFEST; i++) {
            sum += V_STEHFEST[i] * laplaceFunc(i * ln2_t);
        }
        return ln2_t * sum;
    }

    // --- Core Calculation Logic ---
    function logspace(start, stop, num) {
        const arr = [];
        const step = (stop - start) / (num - 1);
        for (let i = 0; i < num; i++) {
            arr.push(Math.pow(10, start + i * step));
        }
        return arr;
    }

    function calculatePlotData(lambda, omega, plotType) {
        const tauValues = logspace(0, 10, 200);
        const yValues = [];

        const laplaceFunc = (s) => {
            if (s === 0) return Infinity; // Should not happen with Stehfest
            const f_s = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
            const arg = Math.sqrt(s * f_s);
            if (arg === 0) return Infinity;

            const K0_arg = besselK0(arg);
            const K1_arg = besselK1(arg);

            if (Math.abs(K1_arg) < 1e-300) return Infinity;

            const P_bar = K0_arg / (arg * K1_arg);
            return P_bar / s;
        };

        for (const tau of tauValues) {
            const pressureDrop = stehfest(laplaceFunc, tau);
            let y;
            if (plotType === 'pressure-drop') {
                y = pressureDrop;
            } else { // difference
                y = pressureDrop - 0.5 * (Math.log(tau) + 0.8091);
            }
            yValues.push(y);
        }

        return { x: tauValues, y: yValues };
    }

    // --- Plotting and UI Update Functions ---
    function getPlotLayout(plotType) {
        const baseLayout = {
            margin: { l: 80, r: 50, b: 60, t: 60 },
            paper_bgcolor: '#fff',
            plot_bgcolor: '#fff',
            xaxis: {
                title: 'dimensionless time, &tau;',
                type: 'log',
                range: [0, 10],
                dtick: 2,
                gridcolor: '#ddd'
            },
        };

        if (plotType === 'pressure-drop') {
            return {
                ...baseLayout,
                title: 'pressure drawdown at the wellbore',
                yaxis: {
                    title: 'dimensionless pressure drop, &Delta;P',
                    range: [0, 12],
                    gridcolor: '#ddd'
                }
            };
        } else { // difference
            return {
                ...baseLayout,
                title: 'deviation from the asymptote',
                yaxis: {
                    title: 'dimensionless difference, &Delta;P &minus; 1/2 (ln(&tau;) + 0.8091)',
                    range: [0, 3.5],
                    gridcolor: '#ddd'
                }
            };
        }
    }

    function updatePlot(isLayoutChange = false) {
        const lambda = Math.pow(10, parseFloat(sliderLambda.value));
        const omega = Math.pow(10, parseFloat(sliderOmega.value));

        // Update value displays
        valueLambda.textContent = lambda < 1e-4 ? lambda.toExponential(1) : lambda.toFixed(Math.abs(parseInt(sliderLambda.value)));
        valueOmega.textContent = omega.toFixed(Math.abs(parseInt(sliderOmega.value)));

        const plotData = calculatePlotData(lambda, omega, state.plotType);

        const trace = {
            x: plotData.x,
            y: plotData.y,
            mode: 'lines',
            line: { color: 'red', width: 2 }
        };

        if (isLayoutChange) {
            const layout = getPlotLayout(state.plotType);
            Plotly.newPlot(plotContainer, [trace], layout);
        } else {
            // react is more efficient for data-only updates
            Plotly.react(plotContainer, [trace], getPlotLayout(state.plotType));
        }
    }

    // --- Event Listeners ---
    sliderLambda.addEventListener('input', () => updatePlot(false));
    sliderOmega.addEventListener('input', () => updatePlot(false));

    btnPressureDrop.addEventListener('click', () => {
        if (state.plotType !== 'pressure-drop') {
            state.plotType = 'pressure-drop';
            btnPressureDrop.classList.add('active');
            btnDifference.classList.remove('active');
            updatePlot(true);
        }
    });

    btnDifference.addEventListener('click', () => {
        if (state.plotType !== 'difference') {
            state.plotType = 'difference';
            btnDifference.classList.add('active');
            btnPressureDrop.classList.remove('active');
            updatePlot(true);
        }
    });

    // --- Initial Plot ---
    updatePlot(true);
});
</script>

