<body>

    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u"></span></p>
            <p>site productivity <i>p</i> = <span id="results-p"></span></p>
        </div>
    </div>

    <div id="right-column">
        <h2>Data Points</h2>
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body">
            </tbody>
        </table>
    </div>

    <script>
        // --- DATA & STATE ---
        let dataPoints = [
            { "age": 30, "volume": 289 },
            { "age": 35, "volume": 445 },
            { "age": 40, "volume": 565 },
            { "age": 45, "volume": 704 },
            { "age": 50, "volume": 833 },
            { "age": 55, "volume": 924 }
        ];

        let p = 58.2729;
        let u = 22.7319;

        let draggingPoint = null;
        let canvas;

        // --- P5.JS SKETCH ---
        function setup() {
            canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');

            // Set noLoop initially; we'll redraw manually when data changes
            noLoop(); 

            // Perform initial calculation and UI update
            recalculateAndRedraw();
        }

        function draw() {
            // Plot styling
            const plot = {
                xMin: 20, xMax: 80,
                yMin: 0, yMax: 1500,
                margin: { top: 60, right: 60, bottom: 60, left: 60 },
                width: width - 80, // Leave space for margins
                height: height - 80,
            };

            // Coordinate mapping functions
            const toPixelX = (age) => map(age, plot.xMin, plot.xMax, plot.margin.left, width - plot.margin.right);
            const toPixelY = (vol) => map(vol, plot.yMin, plot.yMax, height - plot.margin.bottom, plot.margin.top);
            const toDataX = (px) => map(px, plot.margin.left, width - plot.margin.right, plot.xMin, plot.xMax);
            const toDataY = (py) => map(py, height - plot.margin.bottom, plot.margin.top, plot.yMin, plot.yMax);

            // --- Drawing ---
            background('#FFFFE0');

            // Draw grid, axes, and labels
            drawGridAndAxes(plot, toPixelX, toPixelY);

            // Draw fitted curve
            if (!isNaN(p) && !isNaN(u) && dataPoints.length > 1) {
                stroke('#0000FF'); // Blue for curve
                strokeWeight(2);
                noFill();
                beginShape();
                let minAge = Math.min(...dataPoints.map(d => d.age));
                for (let age = Math.max(u + 0.1, plot.xMin); age <= plot.xMax; age += 0.5) {
                    const volume = p * Math.pow(age - u, 4 / 5);
                    if (volume <= plot.yMax) {
                       vertex(toPixelX(age), toPixelY(volume));
                    }
                }
                endShape();
            }

            // Draw data points
            for (const point of dataPoints) {
                const px = toPixelX(point.age);
                const py = toPixelY(point.volume);
                strokeWeight(1);
                stroke(0);
                fill('#0000CD'); // Dark blue for points
                ellipse(px, py, 10, 10);
            }
        }

        function drawGridAndAxes(plot, toPixelX, toPixelY) {
            // Draw grid
            stroke(200); // Light gray
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]); // Dashed lines

            for (let age = plot.xMin; age <= plot.xMax; age += 10) {
                line(toPixelX(age), plot.margin.top, toPixelX(age), height - plot.margin.bottom);
            }
            for (let vol = plot.yMin + 200; vol <= plot.yMax; vol += 200) {
                 line(plot.margin.left, toPixelY(vol), width - plot.margin.right, toPixelY(vol));
            }
            drawingContext.setLineDash([]); // Reset to solid lines

            // Draw axes
            stroke(0);
            strokeWeight(2);
            line(plot.margin.left, height - plot.margin.bottom, width - plot.margin.right, height - plot.margin.bottom); // X-axis
            line(plot.margin.left, plot.margin.top, plot.margin.left, height - plot.margin.bottom); // Y-axis

            // Draw ticks and labels
            noStroke();
            fill(0);
            textAlign(CENTER, TOP);
            for (let age = plot.xMin; age <= plot.xMax; age += 10) {
                text(age, toPixelX(age), height - plot.margin.bottom + 5);
            }
            textAlign(RIGHT, CENTER);
            for (let vol = plot.yMin; vol <= plot.yMax; vol += 200) {
                text(vol, plot.margin.left - 5, toPixelY(vol));
            }

            // Draw titles and labels
            textAlign(CENTER, CENTER);
            textSize(18);
            text("Forest Growth Curve", width / 2, plot.margin.top / 2);
            textSize(14);
            text("Stand age, yr", width / 2, height - plot.margin.bottom / 2 + 10);

            push();
            translate(plot.margin.left / 2 - 10, height / 2);
            rotate(-HALF_PI);
            text("Wood volume, m³ ha⁻¹", 0, 0);
            pop();

            push();
            translate(width - plot.margin.right/2 + 10, height / 2);
            rotate(HALF_PI);
            fill(150);
            text("4/5 law", 0, 0);
            pop();
        }

        // --- INTERACTION LOGIC ---
        function mousePressed() {
            const plot = { xMin: 20, xMax: 80, yMin: 0, yMax: 1500, margin: { top: 60, right: 60, bottom: 60, left: 60 } };
            const toPixelX = (age) => map(age, plot.xMin, plot.xMax, plot.margin.left, width - plot.margin.right);
            const toPixelY = (vol) => map(vol, plot.yMin, plot.yMax, height - plot.margin.bottom, plot.margin.top);

            for (const point of dataPoints) {
                const d = dist(mouseX, mouseY, toPixelX(point.age), toPixelY(point.volume));
                if (d < 10) { // Clicked on a point
                    draggingPoint = point;
                    loop(); // Start draw loop for real-time updates
                    return;
                }
            }
        }

        function mouseDragged() {
            if (draggingPoint) {
                const plot = { xMin: 20, xMax: 80, yMin: 0, yMax: 1500, margin: { top: 60, right: 60, bottom: 60, left: 60 } };
                const toDataX = (px) => map(px, plot.margin.left, width - plot.margin.right, plot.xMin, plot.xMax);
                const toDataY = (py) => map(py, height - plot.margin.bottom, plot.margin.top, plot.yMin, plot.yMax);

                // Constrain mouse position to plot area
                const constrainedMouseX = constrain(mouseX, plot.margin.left, width - plot.margin.right);
                const constrainedMouseY = constrain(mouseY, plot.margin.top, height - plot.margin.bottom);

                draggingPoint.age = toDataX(constrainedMouseX);
                draggingPoint.volume = toDataY(constrainedMouseY);

                recalculateAndRedraw();
            }
        }

        function mouseReleased() {
            if (draggingPoint) {
                draggingPoint = null;
                noLoop(); // Stop draw loop to save resources
            } else {
                 // Add a new point if click is inside plot area
                const plot = { xMin: 20, xMax: 80, yMin: 0, yMax: 1500, margin: { top: 60, right: 60, bottom: 60, left: 60 } };
                if (mouseX > plot.margin.left && mouseX < width - plot.margin.right &&
                    mouseY > plot.margin.top && mouseY < height - plot.margin.bottom) {

                    const toDataX = (px) => map(px, plot.margin.left, width - plot.margin.right, plot.xMin, plot.xMax);
                    const toDataY = (py) => map(py, height - plot.margin.bottom, plot.margin.top, plot.yMin, plot.yMax);

                    const newAge = toDataX(mouseX);
                    const newVolume = toDataY(mouseY);
                    dataPoints.push({ age: newAge, volume: newVolume });
                    recalculateAndRedraw();
                }
            }
        }

        function doubleClicked() {
            const plot = { xMin: 20, xMax: 80, yMin: 0, yMax: 1500, margin: { top: 60, right: 60, bottom: 60, left: 60 } };
            const toPixelX = (age) => map(age, plot.xMin, plot.xMax, plot.margin.left, width - plot.margin.right);
            const toPixelY = (vol) => map(vol, plot.yMin, plot.yMax, height - plot.margin.bottom, plot.margin.top);

            for (let i = dataPoints.length - 1; i >= 0; i--) {
                const point = dataPoints[i];
                const d = dist(mouseX, mouseY, toPixelX(point.age), toPixelY(point.volume));
                if (d < 10) {
                    dataPoints.splice(i, 1);
                    recalculateAndRedraw();
                    return; // Exit after deleting one point
                }
            }
        }

        // --- CALCULATION & UI UPDATE ---
        function recalculateAndRedraw() {
            fitModel();
            updateUI();
            redraw(); // Manually trigger a single redraw
        }

        function updateUI() {
            // Update results display
            document.getElementById('results-u').textContent = isNaN(u) ? 'N/A' : u.toFixed(4);
            document.getElementById('results-p').textContent = isNaN(p) ? 'N/A' : p.toFixed(4);

            // Update data table
            const tableBody = document.getElementById('data-table-body');
            tableBody.innerHTML = ''; // Clear existing rows

            // Sort data points by age for a clean table
            dataPoints.sort((a, b) => a.age - b.age);

            dataPoints.forEach(point => {
                const row = document.createElement('tr');
                const ageCell = document.createElement('td');
                const volumeCell = document.createElement('td');
                ageCell.textContent = point.age.toFixed(2);
                volumeCell.textContent = point.volume.toFixed(2);
                row.appendChild(ageCell);
                row.appendChild(volumeCell);
                tableBody.appendChild(row);
            });
        }

        function fitModel() {
            if (dataPoints.length < 2) {
                p = NaN;
                u = NaN;
                return;
            }

            // Gradient Descent Implementation
            let currentP = 50;
            let currentU = 1;
            const learningRate = 1e-7;
            const iterations = 1000;
            const epsilon = 0.1;

            const minAge = Math.min(...dataPoints.map(d => d.age));

            for (let i = 0; i < iterations; i++) {
                let dEdp = 0;
                let dEdu = 0;

                for (const point of dataPoints) {
                    const age = point.age;
                    const vol = point.volume;

                    const age_minus_u = age - currentU;
                    if (age_minus_u <= 0) continue; // Skip points where model is undefined

                    const predicted = currentP * Math.pow(age_minus_u, 0.8);
                    const error = vol - predicted;

                    // Partial derivative w.r.t p
                    dEdp += -2 * error * Math.pow(age_minus_u, 0.8);

                    // Partial derivative w.r.t u
                    dEdu += -2 * error * (currentP * 0.8 * Math.pow(age_minus_u, -0.2) * (-1));
                }

                currentP -= learningRate * dEdp;
                currentU -= learningRate * dEdu;

                // Enforce constraint: u must be less than the minimum age
                if (currentU >= minAge) {
                    currentU = minAge - epsilon;
                }
            }

            p = currentP;
            u = currentU;
        }

    </script>
</body>
</html>
