<body>

    <div id="demo-container">
        <div id="controls">
            <label for="angle-slider">vertices sorted by angle</label>
            <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
            <span id="angle-value">3</span>
            <button id="reset-button" title="Reset Demo">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const sketch = (p) => {
            const CANVAS_WIDTH = 600;
            const CANVAS_HEIGHT = 600;
            const POLYGON_DEFS = {
                outer: [[50, 50], [550, 50], [550, 550], [50, 550]],
                holes: [
                    [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
                    [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
                    [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
                    [[300, 400], [450, 400], [450, 500], [300, 500]],
                    [[300, 300], [350, 300], [350, 350], [300, 350]]
                ]
            };

            let polygons = [];
            let allVertices = [];
            let allEdges = [];

            let sourcePoint;
            const INITIAL_SOURCE_POS = { x: 275, y: 275 };

            let isDragging = false;
            let sortedVertices = [];

            let slider, valueDisplay, resetButton;

            // --- p5.js setup and draw loop ---
            p.setup = () => {
                p.createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
                setupDOM();
                processGeometry();
                resetDemo();
            };

            p.draw = () => {
                handleDragging();

                p.background(255);

                const sliderVal = parseInt(slider.value());
                const isComplete = sliderVal === parseInt(slider.elt.max);

                // Calculate visibility polygon on the fly
                const { visibilityPolygon, activeRay, activeEdge } = calculateVisibility(sliderVal);

                drawVisibilityPolygon(visibilityPolygon, sliderVal, isComplete);
                drawPolygons();

                if (!isComplete && activeRay) {
                    drawHelperGraphics(activeRay, activeEdge, sliderVal);
                }

                drawPolygonVertices();
                drawSourcePoint();
            };

            // --- Initialization and Reset ---
            function setupDOM() {
                slider = p.select('#angle-slider');
                valueDisplay = p.select('#angle-value');
                resetButton = p.select('#reset-button');

                slider.input(() => valueDisplay.html(slider.value()));
                resetButton.mousePressed(resetDemo);
            }

            function processGeometry() {
                // Convert arrays to p5.Vector objects
                const createVectors = (arr) => arr.map(v => p.createVector(v[0], v[1]));

                polygons.push(createVectors(POLYGON_DEFS.outer));
                POLYGON_DEFS.holes.forEach(h => polygons.push(createVectors(h)));

                polygons.forEach(poly => {
                    allVertices.push(...poly);
                    for (let i = 0; i < poly.length; i++) {
                        allEdges.push([poly[i], poly[(i + 1) % poly.length]]);
                    }
                });
                slider.elt.max = allVertices.length;
            }

            function resetDemo() {
                sourcePoint = p.createVector(INITIAL_SOURCE_POS.x, INITIAL_SOURCE_POS.y);
                slider.value(3);
                valueDisplay.html(slider.value());
                recalculateForNewSource();
            }

            // --- Core Logic ---
            function recalculateForNewSource() {
                // Sort all vertices by angle relative to the source point
                sortedVertices = [...allVertices].sort((a, b) => {
                    const angleA = p.atan2(a.y - sourcePoint.y, a.x - sourcePoint.x);
                    const angleB = p.atan2(b.y - sourcePoint.y, b.x - sourcePoint.x);
                    return angleA - angleB;
                });
            }

            function calculateVisibility(numVerticesToProcess) {
                const visibilityPoints = [];
                let lastActiveRay = null;
                let lastActiveEdge = null;
                const epsilon = 0.0001;

                if (numVerticesToProcess === 0) {
                     return { visibilityPolygon: [], activeRay: null, activeEdge: null };
                }

                // Collect all angles to cast rays to
                const angles = [];
                for(let i = 0; i < numVerticesToProcess; i++) {
                    const v = sortedVertices[i];
                    const angle = p.atan2(v.y - sourcePoint.y, v.x - sourcePoint.x);
                    angles.push(angle - epsilon, angle, angle + epsilon);
                }

                // Add rays to intersect canvas bounds
                const corners = [p.createVector(0,0), p.createVector(CANVAS_WIDTH, 0), p.createVector(CANVAS_WIDTH, CANVAS_HEIGHT), p.createVector(0, CANVAS_HEIGHT)];
                corners.forEach(c => {
                    const angle = p.atan2(c.y - sourcePoint.y, c.x - sourcePoint.x);
                     angles.push(angle - epsilon, angle, angle + epsilon);
                });


                const uniqueAngles = [...new Set(angles)].sort((a,b) => a - b);

                for (const angle of uniqueAngles) {
                    const rayDir = p5.Vector.fromAngle(angle);
                    const { point, edge } = getClosestIntersection(sourcePoint, rayDir, allEdges);
                    if (point) {
                        visibilityPoints.push(point);
                    }
                }

                // Get helper graphics info for the LAST vertex being processed
                if(numVerticesToProcess > 0 && numVerticesToProcess <= sortedVertices.length){
                    const lastVertex = sortedVertices[numVerticesToProcess - 1];
                    const rayDir = p5.Vector.sub(lastVertex, sourcePoint).normalize();
                    const { point, edge } = getClosestIntersection(sourcePoint, rayDir, allEdges);
                    if (point) {
                        lastActiveRay = { start: sourcePoint, end: lastVertex };
                        lastActiveEdge = edge;
                    }
                }

                return { visibilityPolygon: visibilityPoints, activeRay: lastActiveRay, activeEdge: lastActiveEdge };
            }

            // --- Drawing Functions ---
            function drawPolygons() {
                p.stroke(170, 170, 170); // #AAAAAA
                p.strokeWeight(1);
                p.fill(255); // White fill for area
                p.beginShape();
                polygons[0].forEach(v => p.vertex(v.x, v.y)); // Outer polygon
                for (let i = 1; i < polygons.length; i++) {
                    p.beginContour();
                    polygons[i].forEach(v => p.vertex(v.x, v.y));
                    p.endContour();
                }
                p.endShape(p.CLOSE);
            }

            function drawPolygonVertices() {
                p.noStroke();
                p.fill(255, 0, 0); // Red
                allVertices.forEach(v => p.ellipse(v.x, v.y, 4, 4));
            }

            function drawSourcePoint() {
                p.stroke(0, 0, 255, 150);
                p.strokeWeight(2);
                p.fill(0, 0, 255); // Blue
                p.ellipse(sourcePoint.x, sourcePoint.y, 8, 8);
            }

            function drawVisibilityPolygon(points, sliderVal, isComplete) {
                p.stroke(170, 170, 170, 128); // transparent gray
                p.strokeWeight(1);

                if (isComplete) {
                    p.fill(255, 204, 102); // #FFCC66
                    p.beginShape();
                    points.forEach(pt => p.vertex(pt.x, pt.y));
                    p.endShape(p.CLOSE);
                } else if (points.length > 2) {
                    // This is a simplified representation due to the complexity of splitting the polygon
                    // We'll draw the whole calculated shape in light yellow, and an active triangle on top
                    p.fill(255, 255, 153); // #FFFF99
                    p.beginShape();
                    points.forEach(pt => p.vertex(pt.x, pt.y));
                    p.endShape(p.CLOSE);

                    // Draw active triangle
                    const lastVertex = sortedVertices[sliderVal-1];
                    if(lastVertex) {
                        const angle = p.atan2(lastVertex.y - sourcePoint.y, lastVertex.x - sourcePoint.x);
                        const rayDir = p5.Vector.fromAngle(angle);
                        const { point } = getClosestIntersection(sourcePoint, rayDir, allEdges);

                        // Find previous intersection
                        let prevPoint = points[points.length-1]; // A reasonable guess
                        if (point && prevPoint) {
                            p.fill(255, 204, 102); // #FFCC66
                            p.noStroke();
                            p.triangle(sourcePoint.x, sourcePoint.y, prevPoint.x, prevPoint.y, point.x, point.y);
                        }
                    }
                }
            }

            function drawHelperGraphics(ray, edge, sliderVal) {
                // Draw active ray
                p.stroke(0, 0, 255); // Blue
                p.strokeWeight(1);
                p.line(ray.start.x, ray.start.y, ray.end.x, ray.end.y);

                // Draw highlighted edge
                if (edge) {
                    p.stroke(0, 204, 0); // #00CC00 Green
                    p.strokeWeight(3);
                    p.line(edge[0].x, edge[0].y, edge[1].x, edge[1].y);
                }

                // Draw sweep arc
                if (sliderVal > 0) {
                    const startAngle = p.atan2(sortedVertices[0].y - sourcePoint.y, sortedVertices[0].x - sourcePoint.x);
                    const endAngle = p.atan2(sortedVertices[sliderVal - 1].y - sourcePoint.y, sortedVertices[sliderVal - 1].x - sourcePoint.x);
                    p.noFill();
                    p.stroke(0, 0, 255); // Blue
                    p.strokeWeight(1.5);
                    if (endAngle < startAngle) { // Handle wrap-around
                       p.arc(sourcePoint.x, sourcePoint.y, 50, 50, startAngle, p.TWO_PI);
                       p.arc(sourcePoint.x, sourcePoint.y, 50, 50, 0, endAngle);
                    } else {
                       p.arc(sourcePoint.x, sourcePoint.y, 50, 50, startAngle, endAngle);
                    }
                }
            }

            // --- Interaction Handling ---
            p.mousePressed = () => {
                if (p.dist(p.mouseX, p.mouseY, sourcePoint.x, sourcePoint.y) < 10) {
                    isDragging = true;
                }
            };

            p.mouseReleased = () => {
                isDragging = false;
            };

            function handleDragging() {
                if (isDragging) {
                    const newPos = p.createVector(p.mouseX, p.mouseY);
                    if (isPointInsideScene(newPos)) {
                        sourcePoint.set(newPos);
                        recalculateForNewSource();
                    }
                }
            }

            // --- Geometry Utilities ---
            function getClosestIntersection(rayStart, rayDir, edges) {
                let closestPoint = null;
                let closestEdge = null;
                let min_dist = Infinity;

                for (const edge of edges) {
                    const intersection = getLineIntersection(
                        rayStart, 
                        p5.Vector.add(rayStart, rayDir.copy().mult(CANVAS_WIDTH*2)), // A very long ray
                        edge[0], 
                        edge[1]
                    );

                    if (intersection) {
                        const d = p.dist(rayStart.x, rayStart.y, intersection.x, intersection.y);
                        if (d < min_dist && d > 1e-6) {
                            min_dist = d;
                            closestPoint = intersection;
                            closestEdge = edge;
                        }
                    }
                }
                return { point: closestPoint, edge: closestEdge };
            }

            function getLineIntersection(p1, p2, p3, p4) {
                const den = (p1.x - p2.x) * (p3.y - p4.y) - (p1.y - p2.y) * (p3.x - p4.x);
                if (den === 0) return null;

                const t_num = (p1.x - p3.x) * (p3.y - p4.y) - (p1.y - p3.y) * (p3.x - p4.x);
                const u_num = -((p1.x - p2.x) * (p1.y - p3.y) - (p1.y - p2.y) * (p1.x - p3.x));

                const t = t_num / den;
                const u = u_num / den;

                if (t >= 0 && u >= 0 && u <= 1) { // Ray intersects segment
                    return p.createVector(p1.x + t * (p2.x - p1.x), p1.y + t * (p2.y - p1.y));
                }
                return null;
            }

            function isPointInsideScene(pt) {
                // Check if inside outer polygon and outside all holes
                if (!isPointInPolygon(pt, polygons[0])) return false;
                for (let i = 1; i < polygons.length; i++) {
                    if (isPointInPolygon(pt, polygons[i])) return false;
                }
                return true;
            }

            function isPointInPolygon(point, poly) {
                let inside = false;
                for (let i = 0, j = poly.length - 1; i < poly.length; j = i++) {
                    const xi = poly[i].x, yi = poly[i].y;
                    const xj = poly[j].x, yj = poly[j].y;

                    const intersect = ((yi > point.y) !== (yj > point.y))
                        && (point.x < (xj - xi) * (point.y - yi) / (yj - yi) + xi);
                    if (intersect) inside = !inside;
                }
                return inside;
            }

        };

        new p5(sketch, 'canvas-container');
    </script>
</body>
