    <div id="main-container">
        <div id="control-panel">
            <h2>Controls</h2>
            <div>
                <label for="slider-order">Expansion Order</label>
                <div class="slider-container">
                    <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                    <span id="value-order">5</span>
                </div>
            </div>
            <div>
                <label for="slider-a">f(0)</label>
                 <div class="slider-container">
                    <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                    <span id="value-a">2.0</span>
                </div>
            </div>
            <div>
                <label for="slider-b">f'(0)</label>
                <div class="slider-container">
                    <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                    <span id="value-b">0.0</span>
                </div>
            </div>
        </div>

        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Element References ---
            const orderSlider = document.getElementById('slider-order');
            const aSlider = document.getElementById('slider-a');
            const bSlider = document.getElementById('slider-b');
            const orderValue = document.getElementById('value-order');
            const aValue = document.getElementById('value-a');
            const bValue = document.getElementById('value-b');
            const plotContainer = document.getElementById('plot-container');

            // --- Constants ---
            const X_MIN = 0;
            const X_MAX = 1.5;
            const NUM_POINTS = 200;
            const h = (X_MAX - X_MIN) / (NUM_POINTS - 1);

            // --- Helper Functions ---
            const factorial = (n) => {
                if (n === 0 || n === 1) return 1;
                let result = 1;
                for (let i = 2; i <= n; i++) {
                    result *= i;
                }
                return result;
            };

            // --- Core Logic ---

            /**
             * The system of 1st-order ODEs for RK4.
             * y = [f(x), f'(x)]
             * Returns [y', y'']
             */
            const odeSystem = (x, y) => {
                const [y1, y2] = y; // y1 = f(x), y2 = f'(x)
                const dy1_dx = y2;
                const dy2_dx = -(y2 + 1.5 * y1) / y1; // y2' = f''(x)
                return [dy1_dx, dy2_dx];
            };

            /**
             * 4th-order Runge-Kutta solver for a system of ODEs.
             */
            const rk4Solve = (f, y0, t0, t_end, h) => {
                let t = t0;
                let y = y0;
                const times = [t0];
                const solutions = [y0[0]];

                while (t < t_end) {
                    const k1 = f(t, y);
                    const k2 = f(t + h / 2, [y[0] + h / 2 * k1[0], y[1] + h / 2 * k1[1]]);
                    const k3 = f(t + h / 2, [y[0] + h / 2 * k2[0], y[1] + h / 2 * k2[1]]);
                    const k4 = f(t + h, [y[0] + h * k3[0], y[1] + h * k3[1]]);

                    y = [
                        y[0] + (h / 6) * (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]),
                        y[1] + (h / 6) * (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1])
                    ];
                    t += h;

                    times.push(t);
                    solutions.push(y[0]);
                }
                return { x: times, y: solutions };
            };

            /**
             * Formats the polynomial coefficients into a human-readable string for the plot title.
             */
            const formatPolynomial = (coeffs, order) => {
                let parts = [];
                for (let k = 0; k <= order; k++) {
                    const c = coeffs[k];
                    if (Math.abs(c) < 1e-9) continue;

                    const sign = c < 0 ? ' - ' : ' + ';
                    const abs_c = Math.abs(c).toFixed(5);

                    let x_part = '';
                    if (k === 1) {
                        x_part = ' x';
                    } else if (k > 1) {
                        x_part = ` x<sup>${k}</sup>`;
                    }

                    if (parts.length === 0) {
                        parts.push((c < 0 ? '-' : '') + abs_c + x_part);
                    } else {
                        parts.push(sign + abs_c + x_part);
                    }
                }
                if (parts.length === 0) return "f(x) = 0";
                return `f(x) &approx; ${parts.join('')}`;
            };

            /**
             * Main update function triggered by sliders.
             */
            const updatePlot = () => {
                // 1. Read Inputs
                const order = parseInt(orderSlider.value, 10);
                const a = parseFloat(aSlider.value);
                const b = parseFloat(bSlider.value);

                // 2. Update Value Displays
                orderValue.textContent = order;
                aValue.textContent = a.toFixed(1);
                bValue.textContent = b.toFixed(1);

                // 3. Calculate Power Series Coefficients
                const f_derivs = new Array(6);
                f_derivs[0] = a; // f(0)
                f_derivs[1] = b; // f'(0)

                // f''(x)f(x) + f'(x) + 1.5f(x) = 0
                // at x=0: f''(0)f(0) + f'(0) + 1.5f(0) = 0
                f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];

                // f'''(x)f(x) + f''(x)f'(x) + f''(x) + 1.5f'(x) = 0
                // at x=0: f'''(0)f(0) + f''(0)f'(0) + f''(0) + 1.5f'(0) = 0
                f_derivs[3] = -(f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]) / f_derivs[0];

                // f''''(x)f(x) + 2f'''(x)f'(x) + (f''(x))^2 + f'''(x) + 1.5f''(x) = 0
                f_derivs[4] = -(2 * f_derivs[3] * f_derivs[1] + f_derivs[2]**2 + f_derivs[3] + 1.5 * f_derivs[2]) / f_derivs[0];

                // f'''''(x)f(x) + 3f''''(x)f'(x) + 4f'''(x)f''(x) + f''''(x) + 1.5f'''(x) = 0
                f_derivs[5] = -(3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]) / f_derivs[0];

                const coeffs = f_derivs.map((d, i) => d / factorial(i));

                // 4. Generate Power Series Data (Red Curve)
                const x_values = Array.from({ length: NUM_POINTS }, (_, i) => X_MIN + i * h);
                const y_approx = x_values.map(x => {
                    let sum = 0;
                    for (let k = 0; k <= order; k++) {
                        sum += coeffs[k] * Math.pow(x, k);
                    }
                    return sum;
                });

                // 5. Generate Numerical Solution Data (Green Curve)
                const numericalSolution = rk4Solve(odeSystem, [a, b], X_MIN, X_MAX, h);

                // 6. Determine Agreement Region (Blue Shaded Area)
                let x_limit = X_MAX;
                for (let i = 0; i < NUM_POINTS; i++) {
                    const diff = Math.abs(y_approx[i] - numericalSolution.y[i]);
                    if (diff >= 0.01) {
                        x_limit = x_values[i];
                        break;
                    }
                }

                // 7. Update Plot Title
                const plotTitle = formatPolynomial(coeffs, order);

                // 8. Redraw Plot
                const trace_approx = {
                    x: x_values,
                    y: y_approx,
                    type: 'scatter',
                    mode: 'lines',
                    name: `Power Series (order ${order})`,
                    line: { color: 'red', width: 2 }
                };

                const trace_numerical = {
                    x: numericalSolution.x,
                    y: numericalSolution.y,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Numerical (RK4)',
                    line: { color: 'green', width: 3 }
                };

                const layout = {
                    title: plotTitle,
                    xaxis: { title: 'x', range: [X_MIN, X_MAX], zeroline: false },
                    yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                    margin: { l: 50, r: 20, t: 70, b: 40 },
                    legend: { x: 0.65, y: 0.98 },
                    shapes: [{
                        type: 'rect',
                        xref: 'x',
                        yref: 'paper',
                        x0: 0,
                        y0: 0,
                        x1: x_limit,
                        y1: 1,
                        fillcolor: 'lightblue',
                        opacity: 0.4,
                        layer: 'below',
                        line: { width: 0 }
                    }]
                };

                Plotly.react(plotContainer, [trace_approx, trace_numerical], layout);
            };

            // --- Event Listeners ---
            orderSlider.addEventListener('input', updatePlot);
            aSlider.addEventListener('input', updatePlot);
            bSlider.addEventListener('input', updatePlot);

            // --- Initial Plot ---
            updatePlot();
        });
    </script>
</body>
</html>
