                { key: 'P', row: 0, x: -3.3, z: -1.5 }, { key: 'Y', row: 0, x: -2.2, z: -1.5 }, { key: 'F', row: 0, x: -1.1, z: -1.5 }, { key: 'G', row: 0, x: 0.0, z: -1.5 }, { key: 'C', row: 0, x: 1.1, z: -1.5 }, { key: 'R', row: 0, x: 2.2, z: -1.5 }, { key: 'L', row: 0, x: 3.3, z: -1.5 },
                { key: 'A', row: 1, x: -5.0, z: 0 }, { key: 'O', row: 1, x: -3.9, z: 0 }, { key: 'E', row: 1, x: -2.8, z: 0 }, { key: 'U', row: 1, x: -1.7, z: 0 }, { key: 'I', row: 1, x: -0.6, z: 0 }, { key: 'D', row: 1, x: 0.5, z: 0 }, { key: 'H', row: 1, x: 1.6, z: 0 }, { key: 'T', row: 1, x: 2.7, z: 0 }, { key: 'N', row: 1, x: 3.8, z: 0 }, { key: 'S', row: 1, x: 4.9, z: 0 },
                { key: 'Q', row: 2, x: -3.9, z: 1.5 }, { key: 'J', row: 2, x: -2.8, z: 1.5 }, { key: 'K', row: 2, x: -1.7, z: 1.5 }, { key: 'X', row: 2, x: -0.6, z: 1.5 }, { key: 'B', row: 2, x: 0.5, z: 1.5 }, { key: 'M', row: 2, x: 1.6, z: 1.5 }, { key: 'W', row: 2, x: 2.7, z: 1.5 }, { key: 'V', row: 2, x: 3.8, z: 1.5 }, { key: 'Z', row: 2, x: 4.9, z: 1.5 },
            ]
        };
        const ALPHABET = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split('');
        const ROW_COLORS = [0x8fbc8f, 0x87ceeb, 0xdda0dd]; // Olive-ish, Blue-ish, Purple-ish

        // --- DOM ELEMENTS ---
        const selectText = document.getElementById('select-text');
        const btnPlot2D = document.getElementById('btn-plot-2d');
        const btnPlot3D = document.getElementById('btn-plot-3d');
        const btnKeyboardQwerty = document.getElementById('btn-keyboard-qwerty');
        const btnKeyboardDvorak = document.getElementById('btn-keyboard-dvorak');
        const canvasContainer = document.getElementById('canvas-container');
        const canvas = document.getElementById('visualization-canvas');

        // --- APP STATE ---
        let currentState = {
            text: selectText.value,
            view: '2d',
            keyboard: 'qwerty',
            frequencyMap: {},
            maxFrequency: 0
        };

        // --- THREE.JS SETUP ---
        let scene, renderer, camera, visGroup, axesGroup;
        let perspectiveCamera, orthographicCamera;

        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });

            // Cameras
            const aspect = canvasContainer.clientWidth / canvasContainer.clientHeight;
            perspectiveCamera = new THREE.PerspectiveCamera(50, aspect, 0.1, 1000);
            perspectiveCamera.position.set(0, 12, 18);
            perspectiveCamera.lookAt(0, 0, 0);

            const frustumSize = 22;
            orthographicCamera = new THREE.OrthographicCamera(frustumSize * aspect / -2, frustumSize * aspect / 2, frustumSize / 2, frustumSize / -2, 0.1, 100);
            orthographicCamera.position.set(0, 5, 20);
            orthographicCamera.lookAt(0, 0, 0);

            camera = orthographicCamera; // Initial camera

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Groups for easy clearing
            visGroup = new THREE.Group();
            axesGroup = new THREE.Group();
            scene.add(visGroup);
            scene.add(axesGroup);

            // Event Listeners
            setupEventListeners();

            // Initial render
            updateStateAndRender();

            // Handle resize
            window.addEventListener('resize', onWindowResize, false);
            onWindowResize();

            // Start animation loop
            animate();
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;

            renderer.setSize(width, height);

            const aspect = width / height;

            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();

            const frustumSize = 22;
            orthographicCamera.left = frustumSize * aspect / -2;
            orthographicCamera.right = frustumSize * aspect / 2;
            orthographicCamera.top = frustumSize / 2;
            orthographicCamera.bottom = frustumSize / -2;
            orthographicCamera.updateProjectionMatrix();
        }

        // --- EVENT HANDLING & STATE MANAGEMENT ---
        function setupEventListeners() {
            selectText.addEventListener('change', () => {
                currentState.text = selectText.value;
                updateStateAndRender();
            });

            btnPlot2D.addEventListener('click', () => {
                if (currentState.view !== '2d') {
                    currentState.view = '2d';
                    btnPlot2D.classList.add('active');
                    btnPlot3D.classList.remove('active');
                    updateStateAndRender();
                }
            });
            btnPlot3D.addEventListener('click', () => {
                if (currentState.view !== '3d') {
                    currentState.view = '3d';
                    btnPlot3D.classList.add('active');
                    btnPlot2D.classList.remove('active');
                    updateStateAndRender();
                }
            });

            btnKeyboardQwerty.addEventListener('click', () => {
                if (currentState.keyboard !== 'qwerty') {
                    currentState.keyboard = 'qwerty';
                    btnKeyboardQwerty.classList.add('active');
                    btnKeyboardDvorak.classList.remove('active');
                    if (currentState.view === '3d') {
                        updateStateAndRender();
                    }
                }
            });
            btnKeyboardDvorak.addEventListener('click', () => {
                if (currentState.keyboard !== 'dvorak') {
                    currentState.keyboard = 'dvorak';
                    btnKeyboardDvorak.classList.add('active');
                    btnKeyboardQwerty.classList.remove('active');
                    if (currentState.view === '3d') {
                        updateStateAndRender();
                    }
                }
            });
        }

        function updateStateAndRender() {
            calculateFrequencies(currentState.text);
            updateVisualization();
        }

        // --- DATA PROCESSING ---
        function calculateFrequencies(text) {
            const freq = {};
            ALPHABET.forEach(char => freq[char] = 0);

            for (const char of text.toUpperCase()) {
                if (freq.hasOwnProperty(char)) {
                    freq[char]++;
                }
            }
            currentState.frequencyMap = freq;
            const maxVal = Math.max(0, ...Object.values(freq));
            currentState.maxFrequency = Math.ceil(maxVal / 50) * 50 || 50; // Round up to nearest 50, default 50
        }

        // --- VISUALIZATION DRAWING ---
        function updateVisualization() {
            // Clear previous objects
            while(visGroup.children.length > 0) {
                visGroup.remove(visGroup.children[0]);
            }
            while(axesGroup.children.length > 0) {
                axesGroup.remove(axesGroup.children[0]);
            }

            drawAxes(currentState.maxFrequency);

            if (currentState.view === '2d') {
                camera = orthographicCamera;
                draw2DPlot();
            } else { // '3d'
                camera = perspectiveCamera;
                draw3DKeyboard();
            }
        }

        function drawAxes(maxHeight) {
            const gridHelper = new THREE.GridHelper(20, 20, 0xcccccc, 0xcccccc);
            gridHelper.position.y = 0;
            axesGroup.add(gridHelper);

            const material = new THREE.LineBasicMaterial({ color: 0x555555 });

            // Y-Axis Line
            const points = [new THREE.Vector3(0, 0, 0), new THREE.Vector3(0, 15, 0)];
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const yAxis = new THREE.Line(geometry, material);
            yAxis.position.x = -14;
            axesGroup.add(yAxis);

            // Y-Axis Ticks and Labels
            for (let i = 0; i <= maxHeight; i += 50) {
                const yPos = (i / maxHeight) * 12; // Scale to fit visual height
                if (yPos > 15) continue;

                const tickPoints = [new THREE.Vector3(-0.2, yPos, 0), new THREE.Vector3(0.2, yPos, 0)];
                const tickGeom = new THREE.BufferGeometry().setFromPoints(tickPoints);
                const tick = new THREE.Line(tickGeom, material);
                tick.position.x = -14;
                axesGroup.add(tick);

                const label = createTextSprite(i.toString(), { fontsize: 24, fontface: "Arial", textColor: { r: 0, g: 0, b: 0, a: 1.0 }});
                label.position.set(-15, yPos, 0);
                axesGroup.add(label);
            }
        }

        function draw2DPlot() {
            const barWidth = 0.8;
            const barDepth = 0.8;
            const spacing = 1.0;
            const startX = -((ALPHABET.length - 1) * spacing) / 2;
            const maxHeight = 12; // Visual height scale

            ALPHABET.forEach((char, i) => {
                const freq = currentState.frequencyMap[char];
                const normFreq = freq / currentState.maxFrequency;
                const barHeight = Math.max(0.01, normFreq * maxHeight);

                const geometry = new THREE.BoxGeometry(barWidth, barHeight, barDepth);
                const color = getFrequencyColor(freq, currentState.maxFrequency);
                const material = new THREE.MeshStandardMaterial({ color: color, metalness: 0.2, roughness: 0.8 });
                const bar = new THREE.Mesh(geometry, material);

                bar.position.set(startX + i * spacing, barHeight / 2, 0);
                visGroup.add(bar);

                const label = createTextSprite(char, { fontsize: 48, textColor: { r: 50, g: 50, b: 50, a: 1.0 }});
                label.position.set(bar.position.x, barHeight + 0.8, 0);
                visGroup.add(label);
            });
        }

        function draw3DKeyboard() {
            const layout = KEY_LAYOUTS[currentState.keyboard];
            const maxHeight = 12;
            const minHeight = 0.1;
            const keyRadius = 0.5;

            layout.forEach(keyInfo => {
                const char = keyInfo.key;
                const freq = currentState.frequencyMap[char];
                const normFreq = freq / currentState.maxFrequency;
                const keyHeight = minHeight + normFreq * maxHeight;

                // Key body
                const geometry = new THREE.CylinderGeometry(keyRadius, keyRadius, keyHeight, 32);
                const material = new THREE.MeshStandardMaterial({
                    color: ROW_COLORS[keyInfo.row],
                    metalness: 0.4,
                    roughness: 0.5
                });
                const cylinder = new THREE.Mesh(geometry, material);
                cylinder.position.set(keyInfo.x, keyHeight / 2, keyInfo.z);
                visGroup.add(cylinder);

                // Keycap label
                const labelTexture = createTextTexture(char, { fontsize: 64, fontface: 'Arial', textColor: { r:255, g:255, b:255, a:1.0 } });
                const planeGeom = new THREE.CircleGeometry(keyRadius, 32);
                const planeMat = new THREE.MeshBasicMaterial({ map: labelTexture, transparent: true });
                const keyTop = new THREE.Mesh(planeGeom, planeMat);
                keyTop.position.set(keyInfo.x, keyHeight + 0.01, keyInfo.z);
                keyTop.rotation.x = -Math.PI / 2;
                visGroup.add(keyTop);
            });
        }

        // --- UTILITY FUNCTIONS ---
        function getFrequencyColor(frequency, maxFrequency) {
            const value = maxFrequency > 0 ? frequency / maxFrequency : 0;
            const color = new THREE.Color();
            const blue = new THREE.Color(0xadd8e6); // Light Blue
            const purple = new THREE.Color(0xdda0dd); // Plum
            const orange = new THREE.Color(0xffa500); // Orange
            const red = new THREE.Color(0xff6347); // Tomato

            if (value < 0.33) {
                return color.lerpColors(blue, purple, value / 0.33);
            } else if (value < 0.66) {
                return color.lerpColors(purple, orange, (value - 0.33) / 0.33);
            } else {
                return color.lerpColors(orange, red, (value - 0.66) / 0.34);
            }
        }

        function createTextSprite(message, parameters) {
            const fontface = parameters.fontface || 'Arial';
            const fontsize = parameters.fontsize || 18;
            const textColor = parameters.textColor || { r:0, g:0, b:0, a:1.0 };

            const canvas = document.createElement('canvas');
            const context = canvas.getContext('2d');
            context.font = "Bold " + fontsize + "px " + fontface;

            context.fillStyle = "rgba(" + textColor.r + "," + textColor.g + "," + textColor.b + "," + textColor.a + ")";
            context.fillText(message, 0, fontsize);

            const texture = new THREE.Texture(canvas);
            texture.needsUpdate = true;

            const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(spriteMaterial);
            sprite.scale.set(1, 0.5, 1.0);
            return sprite;
        }

        function createTextTexture(message, parameters) {
            const fontface = parameters.fontface || 'Arial';
            const fontsize = parameters.fontsize || 128;
            const textColor = parameters.textColor || { r:0, g:0, b:0, a:1.0 };

            const canvas = document.createElement('canvas');
            canvas.width = 128;
            canvas.height = 128;
            const context = canvas.getContext('2d');
            context.font = `Bold ${fontsize}px ${fontface}`;
            context.fillStyle = `rgba(${textColor.r},${textColor.g},${textColor.b},${textColor.a})`;
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(message, canvas.width / 2, canvas.height / 2);

            const texture = new THREE.CanvasTexture(canvas);
            texture.needsUpdate = true;
            return texture;
        }

        // --- START ---
        init();

    </script>
</body>
</html>
