    <script>
        const sketch = (p) => {
            // --- DOM Elements ---
            let sliderTime, labelTime, btnPlayPause, btnReset;
            let sliderTrail, labelTrail;
            let sliderPoints, labelPoints;
            let sliderPAmp, labelPAmp;
            let sliderPWl, labelPWl;
            let sliderSAmp, labelSAmp;
            let sliderSWl, labelSWl;

            // --- State ---
            let isPlaying = false;
            let particles = [];
            const LOGICAL_WIDTH = 5;
            const LOGICAL_Y_RANGE = 0.5; // from -0.5 to 0.5
            const PADDING = { top: 30, right: 30, bottom: 30, left: 40 };

            // --- Default Values ---
            const DEFAULTS = {
                time: 0,
                trail: 5,
                points: 50,
                p_amp: 0.2,
                p_wl: 3,
                s_amp: 0.1,
                s_wl: 1
            };

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent(canvasContainer);
                canvas.id('p5-canvas');
                p.pixelDensity(1); // Ensure consistent rendering on high-DPI screens

                // --- Get DOM Elements ---
                sliderTime = p.select('#slider-time');
                labelTime = p.select('#label-time');
                btnPlayPause = p.select('#btn-play-pause');
                btnReset = p.select('#btn-reset');
                sliderTrail = p.select('#slider-trail');
                labelTrail = p.select('#label-trail');
                sliderPoints = p.select('#slider-points');
                labelPoints = p.select('#label-points');
                sliderPAmp = p.select('#slider-p-amp');
                labelPAmp = p.select('#label-p-amp');
                sliderPWl = p.select('#slider-p-wl');
                labelPWl = p.select('#label-p-wl');
                sliderSAmp = p.select('#slider-s-amp');
                labelSAmp = p.select('#label-s-amp');
                sliderSWl = p.select('#slider-s-wl');
                labelSWl = p.select('#label-s-wl');

                // --- Setup Event Listeners ---
                btnPlayPause.mousePressed(togglePlayPause);
                btnReset.mousePressed(resetSimulation);

                // General sliders that update labels
                [sliderTime, sliderTrail, sliderPoints, sliderPAmp, sliderPWl, sliderSAmp, sliderSWl]
                .forEach(slider => slider.input(updateLabels));

                // Special slider for recreating particles
                sliderPoints.input(initializeParticles);

                // --- Initial Setup ---
                resetSimulation();
            };

            const resetSimulation = () => {
                // Reset controls to default
                sliderTime.value(DEFAULTS.time);
                sliderTrail.value(DEFAULTS.trail);
                sliderPoints.value(DEFAULTS.points);
                sliderPAmp.value(DEFAULTS.p_amp);
                sliderPWl.value(DEFAULTS.p_wl);
                sliderSAmp.value(DEFAULTS.s_amp);
                sliderSWl.value(DEFAULTS.s_wl);

                // Pause animation
                isPlaying = false;
                btnPlayPause.html('Play');

                // Update all labels
                updateLabels();

                // Re-initialize particles
                initializeParticles();
            };

            const togglePlayPause = () => {
                isPlaying = !isPlaying;
                btnPlayPause.html(isPlaying ? 'Pause' : 'Play');
            };

            const updateLabels = () => {
                labelTime.html(parseFloat(sliderTime.value()).toFixed(2));
                labelTrail.html(sliderTrail.value());
                labelPoints.html(sliderPoints.value());
                labelPAmp.html(parseFloat(sliderPAmp.value()).toFixed(3));
                labelPWl.html(parseFloat(sliderPWl.value()).toFixed(2));
                labelSAmp.html(parseFloat(sliderSAmp.value()).toFixed(3));
                labelSWl.html(parseFloat(sliderSWl.value()).toFixed(2));
            };

            const initializeParticles = () => {
                particles = [];
                const numPoints = sliderPoints.value();
                for (let i = 0; i < numPoints; i++) {
                    const x0 = p.map(i, 0, numPoints - 1, 0, LOGICAL_WIDTH);
                    particles.push({
                        x0: x0,
                        history: []
                    });
                }
                updateLabels(); // update points label
            };

            // --- Coordinate Mapping ---
            const mapX = (x) => p.map(x, 0, LOGICAL_WIDTH, PADDING.left, p.width - PADDING.right);
            const mapY = (y) => p.map(y, -LOGICAL_Y_RANGE, LOGICAL_Y_RANGE, p.height - PADDING.bottom, PADDING.top);

            const drawAxes = () => {
                p.stroke(150);
                p.strokeWeight(1);

                // X-Axis
                p.line(mapX(0), mapY(0), mapX(LOGICAL_WIDTH), mapY(0));
                // Y-Axis
                p.line(mapX(0), mapY(-LOGICAL_Y_RANGE), mapX(0), mapY(LOGICAL_Y_RANGE));

                p.fill(100);
                p.noStroke();
                p.textAlign(p.CENTER, p.TOP);

                // X-Axis Ticks
                for (let i = 0; i <= LOGICAL_WIDTH; i++) {
                    p.text(i, mapX(i), mapY(0) + 5);
                    p.stroke(200);
                    p.line(mapX(i), mapY(0) - 3, mapX(i), mapY(0) + 3);
                    p.noStroke();
                }

                p.textAlign(p.RIGHT, p.CENTER);
                // Y-Axis Ticks
                for (let y = -0.4; y <= 0.4; y += 0.2) {
                    if (p.abs(y) < 0.01) continue; // Skip 0
                    const yPos = mapY(y);
                    p.text(y.toFixed(1), mapX(0) - 5, yPos);
                    p.stroke(200);
                    p.line(mapX(0) - 3, yPos, mapX(0) + 3, yPos);
                    p.noStroke();
                }
            };

            p.draw = () => {
                p.background(255);

                // --- Animation Update ---
                if (isPlaying) {
                    let time = parseFloat(sliderTime.value());
                    time += 0.01;
                    if (time > sliderTime.elt.max) {
                        time = sliderTime.elt.min;
                    }
                    sliderTime.value(time);
                    labelTime.html(time.toFixed(2));
                }

                // --- Get current values ---
                const time = parseFloat(sliderTime.value());
                const trailLength = parseInt(sliderTrail.value());
                const p_amp = parseFloat(sliderPAmp.value());
                const p_wl = parseFloat(sliderPWl.value());
                const s_amp = parseFloat(sliderSAmp.value());
                const s_wl = parseFloat(sliderSWl.value());

                drawAxes();

                // --- Calculate and draw particles ---
                particles.forEach(particle => {
                    const { x0 } = particle;

                    // Wave Calculation
                    const dx = p_amp * p.sin(p.TWO_PI * (x0 / p_wl - time));
                    const dy = s_amp * p.sin(p.TWO_PI * (x0 / s_wl - time));

                    const x_final = x0 + dx;
                    const y_final = dy;

                    // Update history for trail
                    particle.history.push({ x: x_final, y: y_final });
                    if (particle.history.length > trailLength) {
                        particle.history.shift();
                    }

                    // Convert to pixel coordinates
                    const px = mapX(x_final);
                    const py = mapY(y_final);

                    // Draw vertical guide line
                    p.strokeWeight(1);
                    p.stroke('lightsteelblue');
                    p.line(px, PADDING.top, px, p.height - PADDING.bottom);

                    // Draw trail
                    if (trailLength > 0) {
                        p.noStroke();
                        for (let i = 0; i < particle.history.length; i++) {
                            const pos = particle.history[i];
                            const trailPx = mapX(pos.x);
                            const trailPy = mapY(pos.y);
                            // Fade trail based on age
                            const alpha = p.map(i, 0, particle.history.length, 5, 100);
                            p.fill(106, 90, 205, alpha); // slateblue with alpha
                            p.circle(trailPx, trailPy, 3);
                        }
                    }

                    // Draw the particle itself
                    p.noStroke();
                    p.fill('slateblue'); // #6A5ACD
                    p.circle(px, py, 8);
                });
            };

            p.windowResized = () => {
                const canvasContainer = document.getElementById('canvas-container');
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
