<body>

    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-dip">fault dip (°)</label>
                <input type="range" id="slider-dip" min="0" max="90" step="1" value="0">
                <span id="value-dip" class="value-display">0</span>
            </div>
            <div>
                <label for="slider-depth">fault depth (m)</label>
                <input type="range" id="slider-depth" min="100" max="1000" step="10" value="500">
                <span id="value-depth" class="value-display">500</span>
            </div>
            <div>
                <label for="slider-limit">x and y plot limit (m)</label>
                <input type="range" id="slider-limit" min="500" max="2000" step="50" value="1000">
                <span id="value-limit" class="value-display">1000</span>
            </div>
            <div>
                <label>displacement component</label>
                <div id="btn-group-component" class="btn-group">
                    <button id="btn-component-x">X</button>
                    <button id="btn-component-y">Y</button>
                    <button id="btn-component-z">Z</button>
                </div>
            </div>
            <div>
                <label>fault type</label>
                <div id="btn-group-fault-type" class="btn-group">
                    <button id="btn-fault-tensile">tensile</button>
                    <button id="btn-fault-strike-slip">strike-slip</button>
                    <button id="btn-fault-normal">normal</button>
                </div>
            </div>
        </div>
        <div id="plot-container">
            <div id="plot"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE MANAGEMENT ---
            const state = {
                dip: 0,
                depth: 500,
                limit: 1000,
                component: 'Z',
                faultType: 'tensile'
            };

            // --- ELEMENT REFERENCES ---
            const sliderDip = document.getElementById('slider-dip');
            const valueDip = document.getElementById('value-dip');
            const sliderDepth = document.getElementById('slider-depth');
            const valueDepth = document.getElementById('value-depth');
            const sliderLimit = document.getElementById('slider-limit');
            const valueLimit = document.getElementById('value-limit');

            const btnGroupComponent = document.getElementById('btn-group-component');
            const btnGroupFaultType = document.getElementById('btn-group-fault-type');

            const btnComponentX = document.getElementById('btn-component-x');
            const btnComponentY = document.getElementById('btn-component-y');
            const btnComponentZ = document.getElementById('btn-component-z');

            const btnFaultTensile = document.getElementById('btn-fault-tensile');
            const btnFaultStrikeSlip = document.getElementById('btn-fault-strike-slip');
            const btnFaultNormal = document.getElementById('btn-fault-normal');

            // --- GEOPHYSICAL CALCULATION ---
            /**
             * Calculates surface displacement from a point source dislocation in an elastic half-space.
             * The formulas are based on the work of Okada (1992), specifically for a point source.
             * Source: Okada, Y. (1992). Internal deformation due to shear and tensile faults in a half-space. 
             *         Bulletin of the Seismological Society of America, 82(2), 1018-1040.
             * 
             * @param {number} x - Observation point x-coordinate (m).
             * @param {number} y - Observation point y-coordinate (m).
             * @param {number} dip_rad - Fault dip in radians.
             * @param {number} depth - Source depth (positive, m).
             * @param {string} faultType - 'tensile', 'strike-slip', or 'normal'.
             * @returns {{ux: number, uy: number, uz: number}} - Displacement vector in meters.
             */
            function calculateDisplacements(x, y, dip_rad, depth, faultType) {
                const nu = 0.25; // Poisson's ratio
                const b = 1.0; // Dislocation magnitude (m)

                let b1 = 0, b2 = 0, b3 = 0;
                switch (faultType) {
                    case 'strike-slip':
                        b1 = b;
                        break;
                    case 'normal': // Normal fault is a type of dip-slip
                        b2 = -b;
                        break;
                    case 'tensile':
                        b3 = b;
                        break;
                }

                const d = depth;
                const sd = Math.sin(dip_rad);
                const cd = Math.cos(dip_rad);

                // Coordinates in the fault-plane system
                const p = y * cd + d * sd;
                const q = y * sd - d * cd;

                const R2 = x * x + p * p + q * q; // This is equivalent to x*x + y*y + d*d
                const R = Math.sqrt(R2);
                const R3 = R * R2;
                const R5 = R3 * R2;

                let ux = 0, uy = 0, uz = 0;

                // Contribution from strike-slip component (b1)
                if (b1 !== 0) {
                    const term1 = -b1 / (2 * Math.PI);
                    ux += term1 * (x * x * q * 3 / R5 - q / R3);
                    uy += term1 * (x * p * q * 3 / R5 - (q * cd) / R3);
                    uz += term1 * (x * d * q * 3 / R5 - (q * sd) / R3);
                }

                // Contribution from dip-slip component (b2)
                if (b2 !== 0) {
                    const term2 = -b2 / (2 * Math.PI);
                    ux += term2 * (x * p * q * 3 / R5 - (q * cd) / R3);
                    uy += term2 * (p * p * q * 3 / R5 - (p * sd + q * cd) * cd / R3 - (1 - 2 * nu) * (p / R3 - (y * p * 3 * R2 + R2 * cd) / R5));
                    uz += term2 * (p * d * q * 3 / R5 - (d * cd - p * sd) * sd / R3 - (1 - 2 * nu) * (d / R3 - (y * d * 3 * R2 - R2 * sd) / R5));
                }

                // Contribution from tensile component (b3)
                if (b3 !== 0) {
                    const term3 = -b3 / (2 * Math.PI);
                    ux += term3 * (x * d * q * 3 / R5 - (q * sd) / R3);
                    uy += term3 * (p * d * q * 3 / R5 - (d * cd - p * sd) * sd / R3 - (1 - 2 * nu) * (d / R3 - (y * d * 3 * R2 - R2 * sd) / R5));
                    uz += term3 * (d * d * q * 3 / R5 - (d * sd + q * cd) * sd / R3 - (1 - 2 * nu) * (-q / R3 - (y * q * 3 * R2 + R2 * cd) / R5));
                }

                return { ux, uy, uz };
            }


            // --- PLOTTING LOGIC ---
            function updatePlot() {
                const dip_rad = state.dip * Math.PI / 180;
                const n_points = 50;

                const x_coords = Array.from({ length: n_points }, (_, i) => 
                    -state.limit + (2 * state.limit * i) / (n_points - 1)
                );
                const y_coords = Array.from({ length: n_points }, (_, i) => 
                    -state.limit + (2 * state.limit * i) / (n_points - 1)
                );

                const z_values = Array(n_points).fill(0).map(() => Array(n_points).fill(0));

                for (let i = 0; i < n_points; i++) {
                    for (let j = 0; j < n_points; j++) {
                        const x = x_coords[i];
                        const y = y_coords[j];

                        const displacements = calculateDisplacements(x, y, dip_rad, state.depth, state.faultType);

                        let value;
                        switch (state.component) {
                            case 'X': value = displacements.ux; break;
                            case 'Y': value = displacements.uy; break;
                            case 'Z': value = displacements.uz; break;
                        }

                        // Convert meters to millimeters
                        z_values[j][i] = value * 1000;
                    }
                }

                const data = [{
                    type: 'surface',
                    x: x_coords,
                    y: y_coords,
                    z: z_values,
                    colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                    showscale: false,
                    contours: {
                        x: { show: true, color: 'black', width: 0.5 },
                        y: { show: true, color: 'black', width: 0.5 },
                        z: { show: false }
                    },
                    lighting: {
                        ambient: 0.8,
                        diffuse: 0.8,
                        specular: 0.2
                    }
                }];

                const layout = {
                    title: '',
                    autosize: true,
                    scene: {
                        xaxis: { title: 'x (m)', range: [-state.limit, state.limit] },
                        yaxis: { title: 'y (m)', range: [-state.limit, state.limit] },
                        zaxis: { title: `z (${state.component}) (mm)`, autorange: true },
                        camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                    },
                    margin: { l: 20, r: 20, b: 20, t: 20 }
                };

                Plotly.react('plot', data, layout, {responsive: true});
            }

            // --- EVENT HANDLERS ---
            function handleSliderChange(e) {
                const { id, value } = e.target;
                const valNum = Number(value);

                switch (id) {
                    case 'slider-dip':
                        state.dip = valNum;
                        valueDip.textContent = valNum;
                        break;
                    case 'slider-depth':
                        state.depth = valNum;
                        valueDepth.textContent = valNum;
                        break;
                    case 'slider-limit':
                        state.limit = valNum;
                        valueLimit.textContent = valNum;
                        break;
                }
                requestAnimationFrame(updatePlot);
            }

            function handleButtonGroupClick(e, group) {
                if (e.target.tagName !== 'BUTTON') return;

                const groupContainer = e.currentTarget;
                groupContainer.querySelectorAll('button').forEach(btn => btn.classList.remove('active'));
                e.target.classList.add('active');

                if (group === 'component') {
                    state.component = e.target.textContent;
                } else if (group === 'faultType') {
                    state.faultType = e.target.id.replace('btn-fault-', '');
                }

                updatePlot();
            }

            // --- INITIALIZATION ---
            function init() {
                // Set initial slider values and displays
                sliderDip.value = state.dip;
                valueDip.textContent = state.dip;
                sliderDepth.value = state.depth;
                valueDepth.textContent = state.depth;
                sliderLimit.value = state.limit;
                valueLimit.textContent = state.limit;

                // Set initial active buttons
                btnComponentZ.classList.add('active');
                btnFaultTensile.classList.add('active');

                // Attach event listeners
                sliderDip.addEventListener('input', handleSliderChange);
                sliderDepth.addEventListener('input', handleSliderChange);
                sliderLimit.addEventListener('input', handleSliderChange);

                btnGroupComponent.addEventListener('click', (e) => handleButtonGroupClick(e, 'component'));
                btnGroupFaultType.addEventListener('click', (e) => handleButtonGroupClick(e, 'faultType'));

                // Initial plot
                updatePlot();

                // Handle window resize
                window.addEventListener('resize', () => {
                    Plotly.Plots.resize('plot');
                });
            }

            init();
        });
    </script>

</body>
</html>
