<body>

    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div>
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div>
                <label for="checkbox-axes">axes</label>
                <input type="checkbox" id="checkbox-axes" checked>
            </div>
        </div>

        <div id="visualization-area">
            <div id="formula-display"></div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const sketch = (p) => {
            // DOM Elements
            let sliderT, sliderK, checkboxAxes;
            let tValueDisplay, kValueDisplay, formulaDisplay;
            let canvasContainer;

            // State variables
            let t, k, showAxes;

            // Visualization constants
            const besselJ0Zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];
            const amplitude = 0.8;

            p.setup = () => {
                canvasContainer = document.getElementById('canvas-container');
                const canvas = p.createCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
                canvas.parent('canvas-container');

                // Get handles for all control elements
                sliderT = document.getElementById('slider-t');
                sliderK = document.getElementById('slider-k');
                checkboxAxes = document.getElementById('checkbox-axes');
                tValueDisplay = document.getElementById('t-value-display');
                kValueDisplay = document.getElementById('k-value-display');
                formulaDisplay = document.getElementById('formula-display');

                // Attach event listeners
                sliderT.addEventListener('input', updateState);
                sliderK.addEventListener('input', updateState);
                checkboxAxes.addEventListener('change', updateState);

                // Initial state update
                updateState();
            };

            p.draw = () => {
                p.background('#f0f0f0');

                // --- Coordinate System Setup ---
                // Logical coordinate system: x from -2 to 2, y from 0 to -1.2
                // We map this to the canvas.
                // Origin (0,0) of plot is at top-center of the string's attachment point.
                const topMargin = p.height * 0.1;
                p.translate(p.width / 2, topMargin);

                const xScale = p.width / 4.4; // Map x-range of [-2.2, 2.2]
                const yScale = (p.height - topMargin) / 1.2; // Map y-range of [0, -1.2]

                // --- Draw Axes (if enabled) ---
                if (showAxes) {
                    drawAxes(xScale, yScale);
                }

                // --- Calculate Vibration Parameters ---
                const zk = besselJ0Zeros[k - 1];
                const omegaK = zk / 2;
                const timeFactor = Math.cos(omegaK * t);

                // --- Draw the String ---
                p.stroke(0, 123, 255); // A nice blue color
                p.strokeWeight(3);
                p.noFill();
                p.beginShape();
                const steps = 200;
                for (let i = 0; i <= steps; i++) {
                    const y_norm = i / steps; // Normalized y from 0 to 1

                    const besselArg = zk * Math.sqrt(y_norm);
                    const x_pos = amplitude * timeFactor * besselJ0(besselArg);
                    const y_pos = -y_norm; // Y goes down

                    // Apply scaling to map logical coordinates to canvas pixels
                    p.vertex(x_pos * xScale, y_pos * yScale);
                }
                p.endShape();
            };

            function updateState() {
                t = parseFloat(sliderT.value);
                k = parseInt(sliderK.value);
                showAxes = checkboxAxes.checked;

                // Update display values
                tValueDisplay.textContent = t.toFixed(2);
                kValueDisplay.textContent = k;

                updateFormula();
            }

            function updateFormula() {
                const zk = besselJ0Zeros[k - 1];
                const omegaK = zk / 2;

                const formula = `\\(\\omega_{${k}} = ${omegaK.toFixed(2)} \\sqrt{g/l}\\)`;
                formulaDisplay.innerHTML = formula;

                // Ask MathJax to re-typeset the content
                if (window.MathJax && window.MathJax.typesetPromise) {
                    window.MathJax.typesetPromise([formulaDisplay]).catch(err => console.error('MathJax error:', err));
                }
            }

            function drawAxes(xScale, yScale) {
                p.push();

                // Dashed lines for axes
                p.stroke(150);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([4, 4]);

                // Y-axis (vertical)
                p.line(0, 0, 0, -1.1 * yScale);
                // X-axis (horizontal)
                p.line(-2 * xScale, 0, 2 * xScale, 0);

                p.drawingContext.setLineDash([]); // Reset to solid line

                // Draw ticks and labels
                p.noStroke();
                p.fill(100);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                for (let x = -2; x <= 2; x++) {
                    if (x === 0) continue;
                    p.text(x, x * xScale, 10);
                    p.stroke(150);
                    p.line(x * xScale, -5, x * xScale, 5);
                    p.noStroke();
                }

                p.textAlign(p.RIGHT, p.CENTER);
                // Y-axis ticks
                for (let y = 0.2; y <= 1.0; y += 0.2) {
                    const y_pos = -y * yScale;
                    p.text(-y.toFixed(1), -10, y_pos);
                    p.stroke(150);
                    p.line(-5, y_pos, 5, y_pos);
                    p.noStroke();
                }

                p.pop();
            }

            /**
             * Bessel function of the first kind, order 0.
             * Using polynomial approximation from Abramowitz and Stegun.
             * @param {number} x - The input value.
             * @returns {number} J0(x).
             */
            function besselJ0(x) {
                x = Math.abs(x);
                let r;
                if (x <= 3.0) {
                    const t = x * x / 9.0;
                    r = (1.0 + t * (-15.515625 + t * (74.226807 + t * (-117.59025 + t * (89.043346 - t * 29.563534)))));
                    return 1.0 - x * x / 4.0 * r;
                } else {
                    const t = 3.0 / x;
                    r = (1.0 + t * (0.00990753 + t * (0.000342211 + t * (-0.00000853839 - t * (0.000000109593 + t * 0.00000000412331)))));
                    const theta = x - Math.PI / 4.0;
                    return Math.sqrt(2.0 / (Math.PI * x)) * (Math.cos(theta) * r - Math.sin(theta) * t * (0.12499613 + t * (-0.00411349 + t * (-0.000155353 + t * (0.00000216246 + t * 0.0000000171939)))));
                }
            }


            p.windowResized = () => {
                p.resizeCanvas(canvasContainer.offsetWidth, canvasContainer.offsetHeight);
            };
        };

        new p5(sketch);
    </script>

</body>
</html>
