            </div>
            <div class="control-group">
                <label for="slider-opacity4">opacity 4</label>
                <input type="range" id="slider-opacity4" min="0" max="1" step="0.01">
            </div>
            <div id="layering-control" class="control-group">
                <label>layering (bottom to top)</label>
                <span id="layering-display"></span>
                <div class="layer-buttons">
                    <button id="btn-layer-next">▴</button>
                    <button id="btn-layer-prev">▾</button>
                </div>
            </div>
        </div>

        <div id="main-content">
            <div id="color-controls">
                <div class="color-control-group">
                    <label for="color-picker1">color 1</label>
                    <input type="color" id="color-picker1">
                </div>
                <div class="color-control-group">
                    <label for="color-picker2">color 2</label>
                    <input type="color" id="color-picker2">
                </div>
                <div class="color-control-group">
                    <label for="color-picker3">color 3</label>
                    <input type="color" id="color-picker3">
                </div>
                <div class="color-control-group">
                    <label for="color-picker4">color 4</label>
                    <input type="color" id="color-picker4">
                </div>
            </div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE AND DEFAULTS ---
            const defaults = {
                opacities: [0.7, 0.7, 0.7, 0.7],
                colors: ["#00ff00", "#0000ff", "#ff0000", "#000000"],
                layeringIndex: 0
            };

            const layeringPermutations = [
                [1, 2, 3, 4],
                [4, 3, 2, 1],
                [1, 3, 2, 4],
                [2, 4, 1, 3],
                [3, 1, 4, 2]
            ];
            let layeringOrderIndex = defaults.layeringIndex;

            let p5_sketch;

            // --- HTML ELEMENT REFERENCES ---
            const controls = {
                opacities: [
                    document.getElementById('slider-opacity1'),
                    document.getElementById('slider-opacity2'),
                    document.getElementById('slider-opacity3'),
                    document.getElementById('slider-opacity4')
                ],
                colors: [
                    document.getElementById('color-picker1'),
                    document.getElementById('color-picker2'),
                    document.getElementById('color-picker3'),
                    document.getElementById('color-picker4')
                ],
                layeringDisplay: document.getElementById('layering-display'),
                btnLayerPrev: document.getElementById('btn-layer-prev'),
                btnLayerNext: document.getElementById('btn-layer-next'),
                btnReset: document.getElementById('btn-reset'),
                canvasContainer: document.getElementById('canvas-container')
            };

            // --- P5.JS SKETCH DEFINITION ---
            const sketch = (p) => {
                let circles = [];

                p.setup = () => {
                    const canvasWidth = controls.canvasContainer.offsetWidth;
                    const canvasHeight = controls.canvasContainer.offsetHeight;
                    const canvas = p.createCanvas(canvasWidth, canvasHeight);
                    canvas.parent('canvas-container');

                    // Define circle properties based on canvas size
                    circles = [
                        { id: 1, x: canvasWidth * 0.35, y: canvasHeight * 0.4, d: canvasWidth * 0.4 },
                        { id: 2, x: canvasWidth * 0.65, y: canvasHeight * 0.4, d: canvasWidth * 0.4 },
                        { id: 3, x: canvasWidth * 0.50, y: canvasHeight * 0.65, d: canvasWidth * 0.4 },
                        { id: 4, x: canvasWidth * 0.50, y: canvasHeight * 0.45, d: canvasWidth * 0.3 }
                    ];

                    p.noLoop(); // Event-driven drawing
                };

                p.draw = () => {
                    p.background('#f0f0f0');
                    const currentLayerOrder = layeringPermutations[layeringOrderIndex];

                    // Draw circles based on layering order
                    p.noStroke();
                    for (const circleId of currentLayerOrder) {
                        const circleData = circles.find(c => c.id === circleId);
                        const colorHex = controls.colors[circleId - 1].value;
                        const opacity = parseFloat(controls.opacities[circleId - 1].value);

                        const c = p.color(colorHex);
                        c.setAlpha(opacity * 255);

                        p.fill(c);
                        p.ellipse(circleData.x, circleData.y, circleData.d, circleData.d);
                    }

                    // Draw labels on top of all circles
                    for (const circleData of circles) {
                        p.fill(0);
                        p.textSize(32);
                        p.textAlign(p.CENTER, p.CENTER);
                        p.text(circleData.id.toString(), circleData.x, circleData.y);
                    }
                };
            };

            // --- UTILITY FUNCTIONS ---
            function updateLayeringDisplay() {
                controls.layeringDisplay.textContent = layeringPermutations[layeringOrderIndex].join(', ');
            }

            function resetState() {
                // Reset opacities
                defaults.opacities.forEach((val, i) => {
                    controls.opacities[i].value = val;
                });

                // Reset colors
                defaults.colors.forEach((val, i) => {
                    controls.colors[i].value = val;
                });

                // Reset layering
                layeringOrderIndex = defaults.layeringIndex;
                updateLayeringDisplay();

                // Redraw canvas
                if (p5_sketch) {
                    p5_sketch.redraw();
                }
            }

            // --- EVENT LISTENERS ---
            controls.opacities.forEach(slider => {
                slider.addEventListener('input', () => p5_sketch.redraw());
            });

            controls.colors.forEach(picker => {
                picker.addEventListener('input', () => p5_sketch.redraw());
            });

            controls.btnLayerNext.addEventListener('click', () => {
                layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
                updateLayeringDisplay();
                p5_sketch.redraw();
            });

            controls.btnLayerPrev.addEventListener('click', () => {
                layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
                updateLayeringDisplay();
                p5_sketch.redraw();
            });

            controls.btnReset.addEventListener('click', resetState);

            // --- INITIALIZATION ---
            p5_sketch = new p5(sketch, 'canvas-container');
            resetState();
        });
    </script>
</body>
</html>
