<body>

    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <button id="btn-srk">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div class="control-group">
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div class="control-group">
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- DOM Elements ---
            const btnSrk = document.getElementById('btn-srk');
            const btnPr = document.getElementById('btn-pr');
            const checkboxAspen = document.getElementById('checkbox-aspen');
            const sliderPressure = document.getElementById('slider-pressure');
            const displayPressure = document.getElementById('display-pressure');
            const plotContainer = document.getElementById('plot-container');

            // --- State Management ---
            let state = {
                eos: 'srk', // 'srk' or 'pr'
                pressure: 10,
                showAspen: true
            };

            // --- Constants and Data ---
            const constants = {
                Tc: 365.57,    // K
                Pc: 46.65,     // bar
                omega: 0.144,
                R: 0.0831446 // L·bar/(mol·K)
            };

            const aspenData = {
                x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
                y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946]
            };

            // --- Calculation Logic ---

            /**
             * Solves the cubic equation x^3 + ax^2 + bx + c = 0 for its real roots.
             * @param {number} a - Coefficient of x^2
             * @param {number} b - Coefficient of x
             * @param {number} c - Constant term
             * @returns {number[]} An array of real roots.
             */
            function solveCubic(a, b, c) {
                const p = (3 * b - a * a) / 3;
                const q = (2 * a * a * a - 9 * a * b + 27 * c) / 27;

                if (Math.abs(p) < 1e-9) { // Linear case: x^3+q=0
                    return [Math.cbrt(-q) - a / 3];
                }

                const D = (q / 2) ** 2 + (p / 3) ** 3;

                if (D >= 0) { // One real root
                    const sqrtD = Math.sqrt(D);
                    const u = Math.cbrt(-q / 2 + sqrtD);
                    const v = Math.cbrt(-q / 2 - sqrtD);
                    return [u + v - a / 3];
                } else { // Three real roots
                    const phi = Math.acos(-q / (2 * Math.sqrt(-(p ** 3) / 27)));
                    const t = 2 * Math.sqrt(-p / 3);
                    return [
                        t * Math.cos(phi / 3) - a / 3,
                        t * Math.cos((phi + 2 * Math.PI) / 3) - a / 3,
                        t * Math.cos((phi + 4 * Math.PI) / 3) - a / 3
                    ];
                }
            }

            function findMaxRoot(roots) {
                return Math.max(...roots);
            }

            /**
             * Calculates fugacity coefficient based on EoS, pressure, and temperature.
             * @param {string} eos - 'srk' or 'pr'
             * @param {number} P - Pressure in bar
             * @param {number} T - Temperature in Kelvin
             * @returns {number} The fugacity coefficient φ, or NaN if calculation fails.
             */
            function calculateFugacity(eos, P, T) {
                const { Tc, Pc, omega, R } = constants;
                const Tr = T / Tc;

                let a, b, m, alpha, A, B;

                if (eos === 'srk') {
                    a = 0.42748 * R * R * Tc * Tc / Pc;
                    b = 0.08664 * R * Tc / Pc;
                    m = 0.480 + 1.574 * omega - 0.176 * omega * omega;
                    alpha = (1 + m * (1 - Math.sqrt(Tr))) ** 2;
                    const a_eff = a * alpha;
                    A = a_eff * P / (R * T) ** 2;
                    B = b * P / (R * T);

                    // Z^3 - Z^2 + (A - B - B^2)Z - AB = 0
                    const roots = solveCubic(-1, A - B - B * B, -A * B);
                    const Z = findMaxRoot(roots);

                    if (Z <= B) return NaN;

                    const lnPhi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
                    return Math.exp(lnPhi);
                }

                if (eos === 'pr') {
                    a = 0.45724 * R * R * Tc * Tc / Pc;
                    b = 0.07780 * R * Tc / Pc;
                    m = 0.37464 + 1.54226 * omega - 0.26992 * omega * omega;
                    alpha = (1 + m * (1 - Math.sqrt(Tr))) ** 2;
                    const a_eff = a * alpha;
                    A = a_eff * P / (R * T) ** 2;
                    B = b * P / (R * T);

                    // Z^3 - (1-B)Z^2 + (A - 3B^2 - 2B)Z - (AB - B^2 - B^3) = 0
                    const p_coeff = -(1 - B);
                    const q_coeff = A - 3 * B * B - 2 * B;
                    const r_coeff = -(A * B - B * B - B * B * B);
                    const roots = solveCubic(p_coeff, q_coeff, r_coeff);
                    const Z = findMaxRoot(roots);

                    if (Z <= B) return NaN;

                    const lnPhi = Z - 1 - Math.log(Z - B) - (A / (2 * Math.sqrt(2) * B)) * Math.log((Z + (1 + Math.sqrt(2)) * B) / (Z + (1 - Math.sqrt(2)) * B));
                    return Math.exp(lnPhi);
                }

                return NaN;
            }

            // --- Plotting ---
            const plotLayout = {
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505],
                    gridcolor: '#ffffff',
                    showgrid: true,
                    zeroline: false,
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02],
                    gridcolor: '#ffffff',
                    showgrid: true,
                    zeroline: false,
                },
                plot_bgcolor: '#e5e5e5',
                paper_bgcolor: '#ffffff',
                margin: { l: 80, r: 20, b: 50, t: 20 },
                showlegend: false,
                hovermode: 'x unified'
            };

            function updatePlot() {
                // Generate EoS curve data
                const T_values = [];
                const phi_values = [];
                for (let T = 300; T <= 500; T += 2) {
                    const phi = calculateFugacity(state.eos, state.pressure, T);
                    if (!isNaN(phi)) {
                        T_values.push(T);
                        phi_values.push(phi);
                    }
                }

                // Define EoS trace
                const eosTrace = {
                    x: T_values,
                    y: phi_values,
                    type: 'scatter',
                    mode: 'lines',
                    line: {
                        color: state.eos === 'srk' ? 'red' : 'blue',
                        width: 2
                    },
                    name: state.eos.toUpperCase()
                };

                // Define Aspen trace
                const aspenTrace = {
                    x: aspenData.x,
                    y: aspenData.y,
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        symbol: 'square',
                        size: 8
                    },
                    name: 'Aspen Plus',
                    visible: state.showAspen && state.pressure == 10
                };

                Plotly.react(plotContainer, [eosTrace, aspenTrace], plotLayout, {responsive: true});
            }

            // --- Event Handlers ---
            function setupEventListeners() {
                btnSrk.addEventListener('click', () => {
                    if (state.eos === 'pr') {
                        state.eos = 'srk';
                        btnSrk.classList.add('selected');
                        btnPr.classList.remove('selected');
                        updatePlot();
                    }
                });

                btnPr.addEventListener('click', () => {
                    if (state.eos === 'srk') {
                        state.eos = 'pr';
                        btnPr.classList.add('selected');
                        btnSrk.classList.remove('selected');
                        updatePlot();
                    }
                });

                sliderPressure.addEventListener('input', (e) => {
                    const newPressure = parseInt(e.target.value, 10);
                    displayPressure.textContent = newPressure;
                    // Only update plot on change for performance, not input.
                });

                sliderPressure.addEventListener('change', (e) => {
                    const newPressure = parseInt(e.target.value, 10);
                    state.pressure = newPressure;
                    updatePlot();
                });

                checkboxAspen.addEventListener('change', (e) => {
                    state.showAspen = e.target.checked;
                    updatePlot();
                });
            }

            // --- Initialization ---
            function init() {
                // Set initial UI state
                btnSrk.classList.add('selected');
                sliderPressure.value = state.pressure;
                displayPressure.textContent = state.pressure;
                checkboxAspen.checked = state.showAspen;

                setupEventListeners();
                updatePlot(); // Initial plot draw
            }

            init();
        });
    </script>

</body>
