        #control-panel label {
            margin: 0 5px 0 15px;
            cursor: pointer;
        }
        #control-panel input[type="radio"] {
            cursor: pointer;
        }
        #canvas-container {
            border: 1px solid #aaa;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>Step:</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let currentStep = "1";

        let p; // Draggable point P
        let pLast; // Last position of P for Step 3
        let isDragging = false;

        // Main triangle vertices
        let A, B, C;
        let mainTriangleHeight;

        // p5.js sketch
        const sketch = (s) => {
            s.setup = () => {
                const canvas = s.createCanvas(600, 520);
                canvas.parent('canvas-container');

                // Define main equilateral triangle vertices
                const sideLength = 500;
                mainTriangleHeight = (s.sqrt(3) / 2) * sideLength;
                const topMargin = (s.height - mainTriangleHeight) / 2;

                A = s.createVector(s.width / 2, topMargin);
                B = s.createVector(s.width / 2 - sideLength / 2, topMargin + mainTriangleHeight);
                C = s.createVector(s.width / 2 + sideLength / 2, topMargin + mainTriangleHeight);

                // Initial position of point P
                p = s.createVector(s.width * 0.4, s.height * 0.6);
                pLast = p.copy();

                // Set up radio button listeners
                const radios = document.querySelectorAll('input[name="step-selector"]');
                radios.forEach(radio => {
                    radio.addEventListener('change', (event) => {
                        currentStep = event.target.value;
                        if(currentStep !== '3') {
                            pLast = p.copy();
                        }
                    });
                });
            };

            s.draw = () => {
                s.background(255);

                // Update point P if dragging
                if (isDragging) {
                    const nextP = s.createVector(s.mouseX, s.mouseY);
                    if (isInsideTriangle(nextP, A, B, C)) {
                        p.set(nextP);
                    } else {
                        // For a better UX, find the closest point on the boundary
                        p.set(closestPointOnTriangle(nextP, A, B, C));
                    }
                }

                // Draw common elements for all steps
                drawCommonElements();

                // Dispatch to step-specific drawing function
                if (currentStep === "1") {
                    drawStep1();
                } else if (currentStep === "2") {
                    drawStep2();
                } else if (currentStep === "3") {
                    drawStep3();
                }

                // Draw the draggable point P on top
                if (currentStep !== '3') {
                     drawDraggablePoint(p, false);
                }
            };

            // --- GEOMETRIC HELPER FUNCTIONS ---

            // Check if a point is inside a triangle using barycentric coordinates
            const sign = (p1, p2, p3) => (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
            const isInsideTriangle = (pt, v1, v2, v3) => {
                const d1 = sign(pt, v1, v2);
                const d2 = sign(pt, v2, v3);
                const d3 = sign(pt, v3, v1);
                const has_neg = (d1 < 0) || (d2 < 0) || (d3 < 0);
                const has_pos = (d1 > 0) || (d2 > 0) || (d3 > 0);
                return !(has_neg && has_pos);
            };

            // Calculate perpendicular distance from a point to a line segment
            const distToLine = (pt, v1, v2) => {
                const l2 = p5.Vector.dist(v1, v2) ** 2;
                if (l2 === 0) return p5.Vector.dist(pt, v1);
                const t = s.max(0, s.min(1, p5.Vector.dot(p5.Vector.sub(pt, v1), p5.Vector.sub(v2, v1)) / l2));
                const projection = p5.Vector.add(v1, p5.Vector.mult(p5.Vector.sub(v2, v1), t));
                return p5.Vector.dist(pt, projection);
            };

            const getPerpendicularFoot = (pt, v1, v2) => {
                 const l2 = p5.Vector.dist(v1, v2) ** 2;
                 if (l2 === 0) return v1;
                 const t = p5.Vector.dot(p5.Vector.sub(pt, v1), p5.Vector.sub(v2, v1)) / l2;
                 // Note: we don't clamp t, we want projection onto infinite line
                 return p5.Vector.add(v1, p5.Vector.mult(p5.Vector.sub(v2, v1), t));
            };

            const closestPointOnSegment = (p, a, b) => {
                const ab = p5.Vector.sub(b, a);
                const ap = p5.Vector.sub(p, a);
                const t = p5.Vector.dot(ap, ab) / ab.magSq();
                const clampedT = s.constrain(t, 0, 1);
                return p5.Vector.add(a, p5.Vector.mult(ab, clampedT));
            };

            const closestPointOnTriangle = (pt, v1, v2, v3) => {
                const p_ab = closestPointOnSegment(pt, v1, v2);
                const p_bc = closestPointOnSegment(pt, v2, v3);
                const p_ca = closestPointOnSegment(pt, v3, v1);
                const d_ab = p5.Vector.dist(pt, p_ab);
                const d_bc = p5.Vector.dist(pt, p_bc);
                const d_ca = p5.Vector.dist(pt, p_ca);

                let minDist = d_ab;
                let closestPt = p_ab;

                if (d_bc < minDist) {
                    minDist = d_bc;
                    closestPt = p_bc;
                }
                if (d_ca < minDist) {
                    minDist = d_ca;
                    closestPt = p_ca;
                }
                return closestPt;
            };


            // --- DRAWING FUNCTIONS ---

            const drawCommonElements = () => {
                // Draw main equilateral triangle ABC
                s.strokeWeight(2);
                s.stroke(0);
                s.noFill();
                s.triangle(A.x, A.y, B.x, B.y, C.x, C.y);

                // Label vertices
                s.noStroke();
                s.fill(0);
                s.textSize(16);
                s.textAlign(s.CENTER, s.BOTTOM);
                s.text('A', A.x, A.y - 5);
                s.textAlign(s.RIGHT, s.TOP);
                s.text('B', B.x - 5, B.y);
                s.textAlign(s.LEFT, s.TOP);
                s.text('C', C.x + 5, C.y);

                // Draw main altitude
                s.stroke(0, 150, 255, 150);
                s.strokeWeight(2);
                s.drawingContext.setLineDash([5, 5]);
                const midPointBC = p5.Vector.lerp(B, C, 0.5);
                s.line(A.x, A.y, midPointBC.x, midPointBC.y);
                s.drawingContext.setLineDash([]); // Reset line dash
            };

            const drawDraggablePoint = (pos, isFaded) => {
                s.push();
                s.translate(pos.x, pos.y);
                // Circle
                s.strokeWeight(1.5);
                s.stroke(isFaded ? 180 : 0);
                s.fill(isFaded ? 240 : 220);
                s.ellipse(0, 0, 20, 20);
                // Cross
                s.line(-5, -5, 5, 5);
                s.line(-5, 5, 5, -5);
                // Label
                s.noStroke();
                s.fill(isFaded ? 150 : 255, 0, 0);
                s.textSize(14);
                s.textAlign(s.LEFT, s.BOTTOM);
                s.text('P', 12, -12);
                s.pop();
            };

            const drawStep1 = () => {
                // Calculate and draw perpendiculars from P to sides
                const currentPoint = p;

                // Perpendicular to BC (base)
                const foot_BC = getPerpendicularFoot(currentPoint, B, C);
                s.stroke(0);
                s.strokeWeight(1.5);
                s.line(currentPoint.x, currentPoint.y, foot_BC.x, foot_BC.y);
                s.fill(0, 0, 255);
                s.noStroke();
                s.ellipse(foot_BC.x, foot_BC.y, 8, 8);

                // Perpendicular to AC
                const foot_AC = getPerpendicularFoot(currentPoint, A, C);
                s.stroke(0);
                s.strokeWeight(1.5);
                s.line(currentPoint.x, currentPoint.y, foot_AC.x, foot_AC.y);
                s.fill(0, 255, 0);
                s.noStroke();
                s.ellipse(foot_AC.x, foot_AC.y, 8, 8);

                // Perpendicular to AB
                const foot_AB = getPerpendicularFoot(currentPoint, A, B);
                s.stroke(0);
                s.strokeWeight(1.5);
                s.line(currentPoint.x, currentPoint.y, foot_AB.x, foot_AB.y);
                s.fill(255, 0, 0);
                s.noStroke();
                s.ellipse(foot_AB.x, foot_AB.y, 8, 8);

                // Change foot colors to match plan
                s.fill(0, 0, 255); // Blue
                s.ellipse(foot_BC.x, foot_BC.y, 8, 8); // Side BC (will become blue triangle)
                s.ellipse(foot_AC.x, foot_AC.y, 8, 8); // Side AC (green)
                s.ellipse(foot_AB.x, foot_AB.y, 8, 8); // Side AB (red)
            };

            const drawStep2 = () => {
                const currentPoint = p;

                const d_bc = distToLine(currentPoint, B, C); // Red
                const d_ab = distToLine(currentPoint, A, B); // Blue
                const d_ac = distToLine(currentPoint, A, C); // Green

                s.strokeWeight(1);
                s.stroke(0, 100);

                // Red Triangle (altitude d_bc) - based on side BC
                const s_r = d_bc * 2 / s.sqrt(3);
                s.fill(255, 0, 0, 128);
                const r_v1 = currentPoint.copy();
                const r_v2 = s.createVector(currentPoint.x - s_r / 2, currentPoint.y + d_bc);
                const r_v3 = s.createVector(currentPoint.x + s_r / 2, currentPoint.y + d_bc);
                s.triangle(r_v1.x, r_v1.y, r_v2.x, r_v2.y, r_v3.x, r_v3.y);
                s.stroke(255, 0, 0);
                s.line(currentPoint.x, currentPoint.y, currentPoint.x, currentPoint.y + d_bc); // altitude

                // Green Triangle (altitude d_ac) - based on side AC
                const s_g = d_ac * 2 / s.sqrt(3);
                s.fill(0, 255, 0, 128);
                s.stroke(0, 150, 0);
                const u_ac = p5.Vector.sub(C, A).normalize();
                const u_perp_ac = s.createVector(-u_ac.y, u_ac.x);
                const g_v1 = currentPoint.copy();
                const g_base_mid = p5.Vector.add(currentPoint, p5.Vector.mult(u_perp_ac, d_ac));
                const g_v2 = p5.Vector.add(g_base_mid, p5.Vector.mult(u_ac, s_g / 2));
                const g_v3 = p5.Vector.sub(g_base_mid, p5.Vector.mult(u_ac, s_g / 2));
                s.triangle(g_v1.x, g_v1.y, g_v2.x, g_v2.y, g_v3.x, g_v3.y);
                s.line(g_v1.x, g_v1.y, g_base_mid.x, g_base_mid.y); // altitude

                // Blue Triangle (altitude d_ab) - based on side AB
                const s_b = d_ab * 2 / s.sqrt(3);
                s.fill(0, 0, 255, 128);
                s.stroke(0, 0, 150);
                const u_ab = p5.Vector.sub(B, A).normalize();
                const u_perp_ab = s.createVector(-u_ab.y, u_ab.x);
                const b_v1 = currentPoint.copy();
                const b_base_mid = p5.Vector.add(currentPoint, p5.Vector.mult(u_perp_ab, d_ab));
                const b_v2 = p5.Vector.add(b_base_mid, p5.Vector.mult(u_ab, s_b / 2));
                const b_v3 = p5.Vector.sub(b_base_mid, p5.Vector.mult(u_ab, s_b / 2));
                s.triangle(b_v1.x, b_v1.y, b_v2.x, b_v2.y, b_v3.x, b_v3.y);
                s.line(b_v1.x, b_v1.y, b_base_mid.x, b_base_mid.y); // altitude

                // I am omitting the 'x' and 'y' labels as their description in the plan is ambiguous
                // and potentially inconsistent with the specified geometric constructions.
            };

            const drawStep3 = () => {
                // Draw faded marker at original P location
                drawDraggablePoint(pLast, true);

                const d_bc = distToLine(pLast, B, C);
                const d_ab = distToLine(pLast, A, B);
                const d_ac = distToLine(pLast, A, C);

                const mid_x = s.width / 2;
                let current_y = B.y;

                s.strokeWeight(1);
                s.stroke(0, 100);

                // Draw Red Triangle (from d_bc) on the base
                const s_r = d_bc * 2 / s.sqrt(3);
                s.fill(255, 0, 0, 128);
                s.triangle(
                    mid_x, current_y - d_bc,
                    mid_x - s_r / 2, current_y,
                    mid_x + s_r / 2, current_y
                );

                current_y -= d_bc; // Move up for the next triangle

                // Draw Blue Triangle (from d_ab) on top of red
                const s_b = d_ab * 2 / s.sqrt(3);
                s.fill(0, 0, 255, 128);
                s.triangle(
                    mid_x, current_y - d_ab,
                    mid_x - s_b / 2, current_y,
                    mid_x + s_b / 2, current_y
                );
                const blue_top_y = current_y - d_ab;

                current_y -= d_ab; // Move up again

                // Draw Green Triangle (from d_ac) on top of blue
                const s_g = d_ac * 2 / s.sqrt(3);
                s.fill(0, 255, 0, 128);
                s.triangle(
                    mid_x, current_y - d_ac,
                    mid_x - s_g / 2, current_y,
                    mid_x + s_g / 2, current_y
                );

                // Add y label to top of blue triangle
                s.noStroke();
                s.fill(0);
                s.textSize(16);
                s.textAlign(s.LEFT, s.CENTER);
                s.text('y', mid_x + 5, blue_top_y);

                // Add x label to a vertex of the green triangle
                // As the original prompt is ambiguous, I'll place 'x' near the top vertex of the green triangle, which coincides with A.
                s.textAlign(s.RIGHT, s.CENTER);
                s.text('x', mid_x - 5, A.y);
            };

            // --- MOUSE INTERACTION ---

            s.mousePressed = () => {
                const distToP = p5.Vector.dist(s.createVector(s.mouseX, s.mouseY), p);
                if (distToP < 15 && currentStep !== '3') { // 15px is a generous click radius
                    isDragging = true;
                }
            };

            s.mouseReleased = () => {
                if(isDragging) {
                    pLast = p.copy();
                }
                isDragging = false;
            };
        };

        // Initialize p5.js
        new p5(sketch);

    </script>
</body>
</html>
