                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="110000" step="1" value="2922">
                <button id="play-pause-button">▶</button>
            </div>
        </div>
        <div id="visualization-container">
            <div id="date-display"></div>
            <canvas id="3d-canvas"></canvas>
        </div>
    </div>

    <script type="importmap">
        {
            "imports": {
                "three": "https://cdn.jsdelivr.net/npm/three@0.164.1/build/three.module.js",
                "three/addons/": "https://cdn.jsdelivr.net/npm/three@0.164.1/examples/jsm/"
            }
        }
    </script>

    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- DATA ---
        const orbitalData = {
            Mercury:  { a: 0.3871, e: 0.2056, i: 7.005,  Omega: 48.331,  omega: 29.124,  M: 174.796, color: 0x888888, size: 0.05 },
            Venus:    { a: 0.7233, e: 0.0068, i: 3.395,  Omega: 76.680,  omega: 54.884,  M: 50.416,  color: 0xd2b48c, size: 0.08 },
            Earth:    { a: 1.0000, e: 0.0167, i: 0.000,  Omega: -11.260, omega: 114.208, M: 358.617, color: 0x4f86f7, size: 0.08 },
            Mars:     { a: 1.5237, e: 0.0934, i: 1.850,  Omega: 49.562,  omega: 286.502, M: 19.412,   color: 0xff5733, size: 0.06 },
            Ceres:    { a: 2.7691, e: 0.0760, i: 10.593, Omega: 80.393,  omega: 73.598,  M: 77.372,   color: 0x8a2be2, size: 0.15 },
            Pluto:    { a: 39.482, e: 0.2488, i: 17.160, Omega: 110.299, omega: 113.834, M: 14.882,   color: 0x8a2be2, size: 0.15 },
            Haumea:   { a: 43.132, e: 0.1912, i: 28.220, Omega: 122.155, omega: 239.544, M: 204.03,   color: 0x8a2be2, size: 0.15 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Omega: 79.623,  omega: 294.821, M: 95.895,   color: 0x8a2be2, size: 0.15 },
            Eris:     { a: 67.668, e: 0.4418, i: 44.187, Omega: 35.951,  omega: 151.291, M: 206.51,   color: 0x8a2be2, size: 0.15 }
        };

        const J2000_EPOCH = Date.UTC(2000, 0, 1, 12, 0, 0); // Jan 1, 2000, 12:00 UT
        const START_DATE = new Date(Date.UTC(2000, 0, 1, 0, 0, 0)); // Slider start date

        // --- STATE ---
        let scene, camera, renderer, controls;
        let isAnimating = false;
        let animationFrameId;
        let selectedDwarfPlanet = 'Pluto';
        const planetObjects = {};
        let dwarfPlanetMesh, dwarfPlanetOrbit;

        // --- DOM ELEMENTS ---
        const canvas = document.getElementById('3d-canvas');
        const dwarfPlanetSelector = document.getElementById('dwarf-planet-selector');
        const timeSlider = document.getElementById('time-slider');
        const playPauseButton = document.getElementById('play-pause-button');
        const dateDisplay = document.getElementById('date-display');

        // --- HELPER FUNCTIONS ---
        const degToRad = (deg) => deg * (Math.PI / 180);

        function calculatePosition(date, elements) {
            const { a, e, i, Omega, omega, M: M_epoch } = elements;

            const daysSinceEpoch = (date.getTime() - J2000_EPOCH) / (1000 * 60 * 60 * 24);
            const period = 365.25 * Math.pow(a, 1.5);
            const meanMotion = 360 / period; // degrees per day

            let M = (M_epoch + meanMotion * daysSinceEpoch) % 360;
            M = M < 0 ? M + 360 : M;
            const M_rad = degToRad(M);

            // Solve Kepler's equation: M = E - e * sin(E) for E (eccentric anomaly)
            let E = M_rad; // Initial guess
            for (let k = 0; k < 7; k++) { // Newton-Raphson iteration
                E = E - (E - e * Math.sin(E) - M_rad) / (1 - e * Math.cos(E));
            }

            // Calculate true anomaly (nu)
            const cos_nu = (Math.cos(E) - e) / (1 - e * Math.cos(E));
            const sin_nu = Math.sqrt(1 - e * e) * Math.sin(E) / (1 - e * Math.cos(E));
            const nu = Math.atan2(sin_nu, cos_nu);

            // Calculate heliocentric distance (r)
            const r = a * (1 - e * Math.cos(E));

            // Convert to 3D ecliptic coordinates
            const i_rad = degToRad(i);
            const Omega_rad = degToRad(Omega);
            const omega_rad = degToRad(omega);

            const x_orb = r * Math.cos(nu);
            const y_orb = r * Math.sin(nu);

            const x = x_orb * (Math.cos(omega_rad) * Math.cos(Omega_rad) - Math.sin(omega_rad) * Math.sin(Omega_rad) * Math.cos(i_rad)) -
                      y_orb * (Math.sin(omega_rad) * Math.cos(Omega_rad) + Math.cos(omega_rad) * Math.sin(Omega_rad) * Math.cos(i_rad));

            const z = x_orb * (Math.cos(omega_rad) * Math.sin(Omega_rad) + Math.sin(omega_rad) * Math.cos(Omega_rad) * Math.cos(i_rad)) +
                      y_orb * (-Math.sin(omega_rad) * Math.sin(Omega_rad) + Math.cos(omega_rad) * Math.cos(Omega_rad) * Math.cos(i_rad));

            const y = x_orb * (Math.sin(omega_rad) * Math.sin(i_rad)) +
                      y_orb * (Math.cos(omega_rad) * Math.sin(i_rad));

            return new THREE.Vector3(x, y, z);
        }

        function createOrbitPath(elements, color, segments = 360) {
            const points = [];
            const period = 365.25 * Math.pow(elements.a, 1.5);
            for (let i = 0; i <= segments; i++) {
                const days = (i / segments) * period;
                const date = new Date(J2000_EPOCH + days * 24 * 60 * 60 * 1000);
                points.push(calculatePosition(date, elements));
            }
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color });
            return new THREE.Line(geometry, material);
        }

        function updateDateDisplay(days) {
            const currentDate = new Date(START_DATE.getTime() + days * 24 * 60 * 60 * 1000);
            const dayOfWeek = currentDate.toUTCString().slice(0, 3);
            const day = currentDate.getUTCDate();
            const month = currentDate.toLocaleString('en-US', { month: 'short', timeZone: 'UTC' });
            const year = currentDate.getUTCFullYear();
            dateDisplay.textContent = `${dayOfWeek} ${day} ${month} ${year} 00:00:00`;
        }

        // --- UPDATE & RENDER LOGIC ---
        function updatePositions() {
            const days = parseFloat(timeSlider.value);
            const currentDate = new Date(START_DATE.getTime() + days * 24 * 60 * 60 * 1000);

            updateDateDisplay(days);

            // Update planets
            for (const name in planetObjects) {
                if (orbitalData[name]) {
                    const pos = calculatePosition(currentDate, orbitalData[name]);
                    planetObjects[name].mesh.position.copy(pos);
                }
            }

            // Update dwarf planet
            if (dwarfPlanetMesh) {
                const pos = calculatePosition(currentDate, orbitalData[selectedDwarfPlanet]);
                dwarfPlanetMesh.position.copy(pos);
            }
        }

        function updateDwarfPlanet() {
            if (dwarfPlanetMesh) scene.remove(dwarfPlanetMesh);
            if (dwarfPlanetOrbit) scene.remove(dwarfPlanetOrbit);

            const data = orbitalData[selectedDwarfPlanet];

            // Create body
            const geometry = new THREE.SphereGeometry(data.size, 16, 16);
            const material = new THREE.MeshLambertMaterial({ color: data.color });
            dwarfPlanetMesh = new THREE.Mesh(geometry, material);
            scene.add(dwarfPlanetMesh);

            // Create orbit
            dwarfPlanetOrbit = createOrbitPath(data, 0xff0000); // Red orbit
            scene.add(dwarfPlanetOrbit);

            updatePositions();
        }

        function animate() {
            animationFrameId = requestAnimationFrame(animate);

            if (isAnimating) {
                let currentValue = parseFloat(timeSlider.value);
                currentValue += 2; // Animation speed
                if (currentValue > timeSlider.max) {
                    currentValue = timeSlider.min;
                }
                timeSlider.value = currentValue;
                updatePositions();
            }

            controls.update();
            renderer.render(scene, camera);
        }

        function startAnimation() {
            if (!isAnimating) return;
            animate();
        }

        function stopAnimation() {
            cancelAnimationFrame(animationFrameId);
        }

        // --- EVENT HANDLERS ---
        dwarfPlanetSelector.addEventListener('click', (e) => {
            if (e.target.tagName === 'BUTTON') {
                dwarfPlanetSelector.querySelector('.active').classList.remove('active');
                e.target.classList.add('active');
                selectedDwarfPlanet = e.target.dataset.planet;
                updateDwarfPlanet();
            }
        });

        timeSlider.addEventListener('input', () => {
            updatePositions();
        });

        playPauseButton.addEventListener('click', () => {
            isAnimating = !isAnimating;
            playPauseButton.textContent = isAnimating ? '⏸' : '▶';
            if (!isAnimating) {
                stopAnimation();
            }
            // The main `animate` loop handles starting/stopping based on the flag
        });

        window.addEventListener('resize', () => {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        });

        // --- INITIALIZATION ---
        function init() {
            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0x1a1a2a); // Dark space blue

            // Camera
            camera = new THREE.PerspectiveCamera(45, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
            camera.position.set(0, 15, 30);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Controls
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;
            controls.maxDistance = 200;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const pointLight = new THREE.PointLight(0xffffff, 2.5, 300);
            scene.add(pointLight);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            const sun = new THREE.Mesh(sunGeometry, sunMaterial);
            scene.add(sun);

            // Inner Planets
            const innerPlanets = ['Mercury', 'Venus', 'Earth', 'Mars'];
            innerPlanets.forEach(name => {
                const data = orbitalData[name];

                // Planet body
                const geometry = new THREE.SphereGeometry(data.size, 16, 16);
                const material = new THREE.MeshLambertMaterial({ color: data.color });
                const mesh = new THREE.Mesh(geometry, material);

                // Orbit path
                const orbit = createOrbitPath(data, 0x0000ff, 200); // Blue orbit

                planetObjects[name] = { mesh, orbit };
                scene.add(mesh);
                scene.add(orbit);
            });

            // Initial Dwarf Planet setup
            updateDwarfPlanet();

            // Initial position and date update
            updatePositions();

            // Start render loop
            animate();
        }

        init();

    </script>
</body>
