            <div id="scene-container"></div>
        </div>
    </div>

<script>
document.addEventListener('DOMContentLoaded', function() {

    // --- 1. DATA AND CONSTANTS ---
    const cfcData = {
      'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
      'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
      'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
      'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
    };

    // --- 2. DOM ELEMENT REFERENCES ---
    const selectCfc = document.getElementById('select-cfc');
    const checkboxShowAll = document.getElementById('checkbox-show-all');
    const sliderYear = document.getElementById('slider-year');
    const labelYear = document.getElementById('label-year');
    const sliderTime = document.getElementById('slider-time');
    const labelTime = document.getElementById('label-time');

    const plotOzoneDiv = document.getElementById('plot-ozone');
    const plotCfcDiv = document.getElementById('plot-cfc');
    const sceneContainer = document.getElementById('scene-container');

    // --- 3. THREE.JS SCENE SETUP ---
    let scene, camera, renderer, earth, ozoneLayer;

    function initThree() {
        scene = new THREE.Scene();
        const width = sceneContainer.clientWidth;
        const height = sceneContainer.clientHeight;

        camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
        camera.position.set(0, 2.5, 5);
        camera.lookAt(0, 0, 0);

        renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(width, height);
        sceneContainer.appendChild(renderer.domElement);

        // Earth
        const earthGeometry = new THREE.SphereGeometry(1.5, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        earth = new THREE.Mesh(earthGeometry, earthMaterial);
        scene.add(earth);

        // Ozone Layer
        const ozoneGeometry = new THREE.SphereGeometry(1.7, 32, 32);
        const ozoneMaterial = new THREE.MeshBasicMaterial({
            color: 0x00ff00,
            transparent: true,
            opacity: 0.8
        });
        ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
        scene.add(ozoneLayer);

        // Atmosphere
        const atmosphereGeometry = new THREE.SphereGeometry(1.9, 32, 32);
        const atmosphereMaterial = new THREE.MeshBasicMaterial({
            color: 0xffaa88,
            transparent: true,
            opacity: 0.1
        });
        const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
        scene.add(atmosphere);

        // Sun
        const sunGeometry = new THREE.SphereGeometry(0.3, 16, 16);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        sun.position.set(-5, 1, 0);
        scene.add(sun);

        // Radiation Arrows
        const radiationGroup = new THREE.Group();
        const arrowColor = 0xffa500; // Orange
        const arrowLength = 2.5;

        for (let i = 0; i < 5; i++) {
            const angle = (i - 2) * 0.2;
            const dir = new THREE.Vector3(Math.cos(angle), Math.sin(angle), 0).normalize();
            const origin = sun.position.clone().add(dir.clone().multiplyScalar(0.5));
            const arrow = new THREE.ArrowHelper(dir, origin, arrowLength, arrowColor, 0.3, 0.2);
            radiationGroup.add(arrow);
        }
        scene.add(radiationGroup);

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }
        animate();

        window.addEventListener('resize', () => {
            const w = sceneContainer.clientWidth;
            const h = sceneContainer.clientHeight;
            renderer.setSize(w, h);
            camera.aspect = w / h;
            camera.updateProjectionMatrix();
        });
    }

    // --- 4. PLOTLY SETUP ---
    function initPlots() {
        // CFC Concentration Plot
        const cfcLayout = {
            title: 'CFC Concentration',
            xaxis: { title: 'Year', range: [1935, 2025] },
            yaxis: { range: [0, 600], title: { text: 'Concentration (ppt)' } },
            margin: { t: 40, r: 20, b: 40, l: 60 },
            showlegend: true,
            legend: { x: 0.05, y: 0.95 }
        };
        Plotly.newPlot(plotCfcDiv, [], cfcLayout);

        // Ozone Concentration Plot
        const ozoneLayout = {
            title: 'Kinetics of Ozone Destruction',
            xaxis: { title: 'Time (s)', range: [0, 30] },
            yaxis: { title: { text: '$$ \\frac{1}{[O_3]} $$' }, range: [0, 110] },
            margin: { t: 40, r: 20, b: 40, l: 60 },
            showlegend: false
        };
        Plotly.newPlot(plotOzoneDiv, [], ozoneLayout);
    }

    // --- 5. HELPER FUNCTIONS ---
    function interpolate(year, data) {
        if (!data || data.length === 0) return 0;

        for (let i = 0; i < data.length - 1; i++) {
            const [x1, y1] = data[i];
            const [x2, y2] = data[i + 1];
            if (year >= x1 && year <= x2) {
                const slope = (y2 - y1) / (x2 - x1);
                return y1 + slope * (year - x1);
            }
        }
        // Handle edge cases
        if (year < data[0][0]) return data[0][1];
        if (year > data[data.length - 1][0]) return data[data.length - 1][1];
        return 0;
    }

    // --- 6. CORE UPDATE LOGIC ---
    function update() {
        // Get current control values
        const selectedCfc = selectCfc.value;
        const showAll = checkboxShowAll.checked;
        const year = parseInt(sliderYear.value, 10);
        const time = parseInt(sliderTime.value, 10);

        // Update labels
        labelYear.textContent = year;
        labelTime.textContent = time;

        // --- Update CFC Plot ---
        const cfcTraces = [];
        if (showAll) {
            for (const cfc in cfcData) {
                const trace = {
                    x: cfcData[cfc].data.map(p => p[0]),
                    y: cfcData[cfc].data.map(p => p[1]),
                    mode: 'lines',
                    name: cfc,
                    line: {
                        color: cfcData[cfc].color,
                        width: cfc === selectedCfc ? 4 : 2 // Highlight selected
                    }
                };
                cfcTraces.push(trace);
            }
        } else {
            const trace = {
                x: cfcData[selectedCfc].data.map(p => p[0]),
                y: cfcData[selectedCfc].data.map(p => p[1]),
                mode: 'lines',
                name: selectedCfc,
                line: { color: cfcData[selectedCfc].color, width: 2 }
            };
            cfcTraces.push(trace);
        }

        // Calculate interpolated concentration for the marker and ozone plot
        const currentCfcData = cfcData[selectedCfc].data;
        const cfcConcentration = interpolate(year, currentCfcData);

        // Add marker trace
        const markerTrace = {
            x: [year],
            y: [cfcConcentration],
            mode: 'markers',
            type: 'scatter',
            name: 'Current Year',
            marker: { color: 'red', size: 10, symbol: 'circle' },
            showlegend: false
        };
        cfcTraces.push(markerTrace);

        Plotly.react(plotCfcDiv, cfcTraces, plotCfcDiv.layout);

        // --- Update Ozone Plot ---
        const k = 0.005; // Pseudo-first-order rate constant factor
        const initialOzoneInv = 10; // 1/[O3](0)
        const slope = k * cfcConcentration;

        const ozoneX = [];
        const ozoneY = [];
        for (let t = 0; t <= time; t++) {
            ozoneX.push(t);
            ozoneY.push(initialOzoneInv + slope * t);
        }

        const ozoneTrace = {
            x: ozoneX,
            y: ozoneY,
            mode: 'lines',
            line: { color: 'blue', width: 3 }
        };

        Plotly.react(plotOzoneDiv, [ozoneTrace], plotOzoneDiv.layout);

        // --- Update 3D Scene ---
        // Map concentration (0-600) to opacity (1.0-0.1)
        const maxConcentration = 600;
        const minOpacity = 0.1;
        const maxOpacity = 1.0;
        let opacity = maxOpacity - (cfcConcentration / maxConcentration) * (maxOpacity - minOpacity);
        opacity = Math.max(minOpacity, Math.min(maxOpacity, opacity)); // Clamp value

        if (ozoneLayer) {
            ozoneLayer.material.opacity = opacity;
        }
    }

    // --- 7. INITIALIZATION AND EVENT LISTENERS ---
    function init() {
        // Populate dropdown
        Object.keys(cfcData).forEach(cfc => {
            const option = document.createElement('option');
            option.value = cfc;
            option.textContent = cfc;
            selectCfc.appendChild(option);
        });
        selectCfc.value = "CFC-12"; // Set default

        // Initialize visualizations
        initPlots();
        initThree();

        // Add event listeners
        selectCfc.addEventListener('change', update);
        checkboxShowAll.addEventListener('change', update);
        sliderYear.addEventListener('input', update);
        sliderTime.addEventListener('input', update);

        // Initial render
        update();
    }

    init();
});
</script>

</body>
</html>
