    </style>
</head>
<body>
    <div class="container">
        <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
        <p>
            This visualization demonstrates the method used by Eratosthenes to estimate the Earth's circumference. By measuring the angle of a shadow cast by a post in Alexandria at the same time a well in Syene was fully sunlit, he could calculate the central angle between the two cities. Adjust the angle and toggle the zoom to see the geometry.
        </p>

        <div id="control-panel">
            <label for="slider-post-angle">post angle</label>
            <input type="range" id="slider-post-angle" min="1" max="60" value="7" step="0.1">
            <span id="display-post-angle">7.0°</span>
            <label for="checkbox-zoom">zoom</label>
            <input type="checkbox" id="checkbox-zoom">
        </div>

        <div id="visualization-container">
            <!-- p5.js canvas will be created here and given the id 'demo-canvas' -->
        </div>
    </div>

    <script>
        // --- Global State Variables ---
        let angle;
        let isZoomed;

        // --- HTML Element References ---
        let sliderPostAngle;
        let displayPostAngle;
        let checkboxZoom;

        function setup() {
            let canvas = createCanvas(600, 450);
            canvas.parent('visualization-container');
            canvas.id('demo-canvas'); // Assign the ID to the canvas element
            angleMode(DEGREES);

            // --- Initialize Controls and State ---
            sliderPostAngle = select('#slider-post-angle');
            displayPostAngle = select('#display-post-angle');
            checkboxZoom = select('#checkbox-zoom');

            angle = parseFloat(sliderPostAngle.value());
            isZoomed = checkboxZoom.checked();
            displayPostAngle.html(angle.toFixed(1) + '°');

            // --- Event Listeners ---
            sliderPostAngle.input(() => {
                angle = parseFloat(sliderPostAngle.value());
                displayPostAngle.html(angle.toFixed(1) + '°');
            });

            checkboxZoom.changed(() => {
                isZoomed = checkboxZoom.checked();
            });
        }

        function draw() {
            background('#F0F0F0');
            drawSunRays();

            if (isZoomed) {
                drawZoomView();
            } else {
                drawFullView();
            }
        }

        function drawSunRays() {
            stroke('#DAA520'); // Goldenrod
            strokeWeight(1.5);
            for (let y = 40; y < height; y += 60) {
                line(width, y, width - 200, y);
                // Arrowhead
                line(width - 200, y, width - 210, y - 5);
                line(width - 200, y, width - 210, y + 5);
            }
        }

        function drawFullView() {
            const earthRadius = 150;
            const earthCenterX = 220;
            const earthCenterY = height / 2;

            // Draw Earth
            push();
            noStroke();
            fill('#468296'); // SteelBlue
            ellipse(earthCenterX, earthCenterY, earthRadius * 2);
            fill(255);
            textAlign(CENTER, CENTER);
            textSize(20);
            text("Earth", earthCenterX, earthCenterY);
            pop();

            // --- Syene ---
            push();
            let syeneX = earthCenterX + earthRadius;
            let syeneY = earthCenterY;
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(earthCenterX, earthCenterY, syeneX, syeneY);
            drawingContext.setLineDash([]);
            noStroke();
            fill(0);
            textAlign(LEFT, CENTER);
            textSize(14);
            text("Syene", syeneX + 10, syeneY);
            pop();

            // --- Alexandria ---
            push();
            let alexandriaX = earthCenterX + earthRadius * cos(-angle);
            let alexandriaY = earthCenterY + earthRadius * sin(-angle);

            // Alexandria Radius Line
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            line(earthCenterX, earthCenterY, alexandriaX, alexandriaY);

            // Horizontal Sun Ray line at Alexandria
            line(alexandriaX - 60, alexandriaY, alexandriaX + 120, alexandriaY);
            drawingContext.setLineDash([]);

            // Alexandria Label
            fill(0);
            textAlign(LEFT, CENTER);
            textSize(14);
            text("Alexandria", alexandriaX + 10, alexandriaY - 20);

            // Small black triangle at the observation point
            push();
            translate(alexandriaX, alexandriaY);
            rotate(-angle);
            fill(0);
            noStroke();
            triangle(0, 0, 10, 5, 10, -5);
            pop();

            // Angle Visualization
            stroke(255, 0, 0);
            strokeWeight(1.5);
            noFill();
            // Arc between horizontal line (180 deg) and radial line's direction.
            arc(alexandriaX, alexandriaY, 60, 60, 180 - angle, 180);

            fill(255, 0, 0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(12);
            // Position text in the middle of the arc
            let textAngle = 180 - angle / 2;
            let angleTextX = alexandriaX + 45 * cos(textAngle);
            let angleTextY = alexandriaY + 45 * sin(textAngle);
            text(angle.toFixed(1) + "°", angleTextX, angleTextY);
            pop();
        }

        function drawZoomView() {
            const sectorCenterX = 50;
            const sectorCenterY = height + 350;
            const sectorRadius = 400;

            // Draw Earth Sector
            push();
            noStroke();
            fill('#468296');
            arc(sectorCenterX, sectorCenterY, sectorRadius * 2, sectorRadius * 2, -angle, 0, PIE);
            pop();

            // --- Radii and Labels ---
            push();
            drawingContext.setLineDash([5, 5]);
            stroke(0);
            strokeWeight(1);

            // Syene Radius
            let syeneEndX = sectorCenterX + sectorRadius * cos(0);
            let syeneEndY = sectorCenterY + sectorRadius * sin(0);
            line(sectorCenterX, sectorCenterY, syeneEndX, syeneEndY);

            // Alexandria Radius
            let alexandriaX = sectorCenterX + sectorRadius * cos(-angle);
            let alexandriaY = sectorCenterY + sectorRadius * sin(-angle);
            line(sectorCenterX, sectorCenterY, alexandriaX, alexandriaY);

            drawingContext.setLineDash([]);

            noStroke();
            fill(0);
            textSize(14);
            textAlign(CENTER, BOTTOM);
            text("Syene", syeneEndX, syeneEndY - 10);
            text("Alexandria", alexandriaX, alexandriaY - 10);
            pop();

            // --- Central Angle Visualization ---
            push();
            noFill();
            stroke(255, 0, 0);
            strokeWeight(1.5);
            arc(sectorCenterX, sectorCenterY, 150, 150, -angle, 0);

            fill(255, 0, 0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(16);
            let angleTextX = sectorCenterX + 100 * cos(-angle / 2);
            let angleTextY = sectorCenterY + 100 * sin(-angle / 2);
            text(angle.toFixed(1) + "°", angleTextX, angleTextY);
            pop();

            // --- Post and Shadow Triangle ---
            // This triangle shows the alternate interior angle at Alexandria, which equals the central angle.
            // One leg is radial (the post), the other is horizontal (the sun's direction).
            // This is a schematic representation.
            push();
            let alexandriaX_tri = sectorCenterX + sectorRadius * cos(-angle);
            let alexandriaY_tri = sectorCenterY + sectorRadius * sin(-angle);

            const legLength = 40;
            // Point 1: Vertex at Alexandria on the arc
            const V1 = createVector(alexandriaX_tri, alexandriaY_tri);
            // Point 2: Along the inward radius (post)
            const V2 = p5.Vector.fromAngle(radians(180 - angle), legLength).add(V1);
            // Point 3: Along the horizontal (sun ray)
            const V3 = createVector(V1.x - legLength, V1.y);

            fill('rgba(210, 180, 140, 0.8)'); // Tan
            stroke('#8B4513'); // SaddleBrown
            strokeWeight(1);

            beginShape();
            vertex(V1.x, V1.y);
            vertex(V2.x, V2.y);
            vertex(V3.x, V3.y);
            endShape(CLOSE);
            pop();
        }

    </script>
</body>
</html>
