            let moonPull = sliderMoonPull.value();
            let sunAngle = sliderSunPos.value();
            let sunPull = sliderSunPull.value();

            // Convert slider angles (in degrees) to radians for trigonometric functions
            let moonAngle_rad = radians(moonAngle);
            let sunAngle_rad = radians(sunAngle);

            // Center the coordinate system for easier drawing
            translate(width / 2, height / 2);

            // --- Draw Water Layer ---
            fill('#aed6f1'); // Light blue for water
            stroke('#000000');
            strokeWeight(1);

            beginShape();
            // Iterate through a full circle, calculating the radius at each point
            for (let theta_deg = 0; theta_deg < 360; theta_deg += 1) {
                let theta_rad = radians(theta_deg);

                // Calculate tidal effects. cos^2 creates two bulges (high tides)
                // on opposite sides of the Earth for each celestial body.
                let moonEffect = moonPull * pow(cos(theta_rad - moonAngle_rad), 2);
                let sunEffect = sunPull * pow(cos(theta_rad - sunAngle_rad), 2);

                // Calculate the final radius of the water layer at this angle
                let totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

                // Convert polar coordinates (radius, angle) to Cartesian (x, y) for the vertex
                let x = totalRadius * cos(theta_rad);
                let y = totalRadius * sin(theta_rad);

                vertex(x, y);
            }
            endShape(CLOSE);

            // --- Draw Earth ---
            noStroke();
            fill('#966F33'); // Solid brown for Earth
            ellipse(0, 0, earthRadius * 2, earthRadius * 2);
        }

        function resetControls() {
            // Set sliders back to their specified default values
            sliderMoonPos.value(144);
            sliderMoonPull.value(40);
            sliderSunPos.value(144);
            sliderSunPull.value(20);

            // Trigger a redraw to update the canvas
            redraw();
        }
    </script>
</body>
</html>
