            </div>
        </div>
    </div>

    <script type="importmap">
        {
            "imports": {
                "three": "https://unpkg.com/three@0.160.0/build/three.module.js"
            }
        }
    </script>
    <script type="module">
        import * as THREE from 'three';

        // --- DOM Elements ---
        const sliders = {
            earthPosition: document.getElementById('slider-earth-position'),
            cometPosition: document.getElementById('slider-comet-position'),
            coma: document.getElementById('slider-coma'),
            tail: document.getElementById('slider-tail'),
            tailSpread: document.getElementById('slider-tail-spread'),
        };
        const telescopicCanvas = document.getElementById('telescopic-canvas');
        const orbitCanvas = document.getElementById('orbit-canvas');
        const telescopicWrapper = document.getElementById('telescopic-view-wrapper');
        const orbitWrapper = document.getElementById('orbit-view-wrapper');

        // --- Global 3D objects ---
        let orbitScene, orbitCamera, orbitRenderer;
        let telescopicScene, telescopicCamera, telescopicRenderer;
        let sun, earth, cometGroup, cometNucleus, cometComa, cometTail;

        // --- Orbital Parameters ---
        const orbits = {
            earth: { a: 4, e: 0.0167, color: 0x0077ff },
            mars: { a: 6.1, e: 0.0934, color: 0xff4500 },
            jupiter: { a: 20.8, e: 0.0489, color: 0xffa500 },
            holmes: { a: 14.4, e: 0.432, color: 0xffff00 },
        };

        // --- Initialization ---
        function init() {
            // --- Orbit View Setup ---
            orbitScene = new THREE.Scene();
            orbitScene.background = new THREE.Color(0xffffff);

            const orbitAspect = orbitWrapper.clientWidth / orbitWrapper.clientHeight;
            orbitCamera = new THREE.PerspectiveCamera(45, orbitAspect, 0.1, 1000);
            orbitCamera.position.set(0, 35, 35);
            orbitCamera.lookAt(0, 0, 0);

            orbitRenderer = new THREE.WebGLRenderer({ canvas: orbitCanvas, antialias: true });

            const ambientLightOrbit = new THREE.AmbientLight(0xffffff, 0.5);
            orbitScene.add(ambientLightOrbit);
            const pointLight = new THREE.PointLight(0xffffff, 1.5, 200);
            orbitScene.add(pointLight);

            // Sun
            sun = new THREE.Mesh(
                new THREE.SphereGeometry(0.5, 32, 16),
                new THREE.MeshBasicMaterial({ color: 0xffff00 })
            );
            orbitScene.add(sun);

            // Orbits
            orbitScene.add(createOrbitLine(orbits.earth));
            orbitScene.add(createOrbitLine(orbits.mars));
            orbitScene.add(createOrbitLine(orbits.jupiter));
            orbitScene.add(createOrbitLine(orbits.holmes));

            // Earth
            earth = new THREE.Mesh(
                new THREE.SphereGeometry(0.1, 16, 8),
                new THREE.MeshBasicMaterial({ color: orbits.earth.color })
            );
            orbitScene.add(earth);

            // Comet Group
            cometGroup = new THREE.Group();

            cometNucleus = new THREE.Mesh(
                new THREE.SphereGeometry(0.08, 16, 8),
                new THREE.MeshBasicMaterial({ color: 0xffffff })
            );

            cometComa = new THREE.Mesh(
                new THREE.SphereGeometry(0.5, 32, 16),
                new THREE.MeshBasicMaterial({
                    color: 0x88ff88,
                    transparent: true,
                    opacity: 0.5,
                    blending: THREE.AdditiveBlending,
                })
            );

            const tailHeight = 5;
            const tailGeometry = new THREE.ConeGeometry(1, tailHeight, 32);
            tailGeometry.translate(0, tailHeight / 2, 0); // Tip at origin
            tailGeometry.rotateX(Math.PI / 2); // Align with Z-axis
            cometTail = new THREE.Mesh(
                tailGeometry,
                new THREE.MeshBasicMaterial({
                    color: 0x88ddff,
                    transparent: true,
                    opacity: 0.4,
                    blending: THREE.AdditiveBlending,
                    side: THREE.DoubleSide
                })
            );

            cometGroup.add(cometNucleus, cometComa, cometTail);
            orbitScene.add(cometGroup);

            // --- Telescopic View Setup ---
            telescopicScene = new THREE.Scene();
            telescopicScene.background = new THREE.Color(0x000000);

            const teleAspect = telescopicWrapper.clientWidth / telescopicWrapper.clientHeight;
            telescopicCamera = new THREE.PerspectiveCamera(15, teleAspect, 0.1, 1000);

            telescopicRenderer = new THREE.WebGLRenderer({ canvas: telescopicCanvas, antialias: true });

            const ambientLightTele = new THREE.AmbientLight(0xffffff, 1.0);
            telescopicScene.add(ambientLightTele);

            // Add the same comet group instance to the telescopic scene
            telescopicScene.add(cometGroup);

            // --- Event Listeners and Initial State ---
            Object.values(sliders).forEach(slider => slider.addEventListener('input', update));
            window.addEventListener('resize', onWindowResize);

            onWindowResize(); // Set initial renderer sizes
            update(); // Set initial positions and scales
            animate(); // Start the animation loop
        }

        // --- Helper Functions ---
        function createOrbitLine({ a, e, color }) {
            const focusOffset = a * e;
            const b = a * Math.sqrt(1 - e * e); // Semi-minor axis
            const curve = new THREE.EllipseCurve(
                focusOffset, 0, // ax, aY (center of ellipse)
                a, b,            // xRadius, yRadius
                0, 2 * Math.PI,  // aStartAngle, aEndAngle
                false,           // aClockwise
                0                // aRotation
            );

            const points = curve.getPoints(128);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color });

            const ellipse = new THREE.Line(geometry, material);
            ellipse.rotation.x = Math.PI / 2; // Rotate to XZ plane
            return ellipse;
        }

        function getPointOnOrbit(a, e, angleRad) {
            const r = (a * (1 - e * e)) / (1 + e * Math.cos(angleRad));
            const x = r * Math.cos(angleRad);
            const z = r * Math.sin(angleRad);
            return new THREE.Vector3(x, 0, z);
        }

        // --- Update Logic ---
        function update() {
            // Get slider values
            const earthAngle = parseFloat(sliders.earthPosition.value) * (Math.PI / 180);
            const cometAngle = parseFloat(sliders.cometPosition.value) * (Math.PI / 180);
            const comaSize = parseFloat(sliders.coma.value);
            const tailLength = parseFloat(sliders.tail.value);
            const tailSpread = parseFloat(sliders.tailSpread.value);

            // Update Earth position
            const earthPos = getPointOnOrbit(orbits.earth.a, orbits.earth.e, earthAngle);
            earth.position.copy(earthPos);

            // Update Comet position
            const cometPos = getPointOnOrbit(orbits.holmes.a, orbits.holmes.e, cometAngle);
            cometGroup.position.copy(cometPos);

            // Update Comet appearance
            const comaScale = 0.1 + (comaSize / 100) * 5;
            cometComa.scale.set(comaScale, comaScale, comaScale);

            const tailLenScale = (tailLength / 100) * 20;
            const tailSpreadScale = 0.1 + (tailSpread / 100) * 4;
            cometTail.scale.set(tailSpreadScale, tailSpreadScale, tailLenScale);

            // Orient tail to point away from the sun (origin)
            // The tail mesh is child of the group, which is at cometPos.
            // We want the tail to point from cometPos to a point further out, e.g., 2 * cometPos
            // lookAt works in world space, so this is the simplest method.
            cometTail.lookAt(cometPos.clone().multiplyScalar(2));

            // Update Telescopic Camera
            telescopicCamera.position.copy(earthPos);
            telescopicCamera.lookAt(cometPos);
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }

        // --- Resize Handler ---
        function onWindowResize() {
            const teleRect = telescopicWrapper.getBoundingClientRect();
            telescopicCamera.aspect = teleRect.width / teleRect.height;
            telescopicCamera.updateProjectionMatrix();
            telescopicRenderer.setSize(teleRect.width, teleRect.height);

            const orbitRect = orbitWrapper.getBoundingClientRect();
            orbitCamera.aspect = orbitRect.width / orbitRect.height;
            orbitCamera.updateProjectionMatrix();
            orbitRenderer.setSize(orbitRect.width, orbitRect.height);
        }

        // --- Start ---
        init();

    </script>
</body>
