</head>
<body>

    <div class="main-wrapper">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" value="2.845" step="0.001">
                <span id="label-m">2.845</span>
            </div>
            <div class="control-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" value="4.1" step="0.1">
                <span id="label-x">4.1</span>
            </div>
            <div class="control-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" value="1.498" step="0.001">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="visualization-area">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // Global variables for sliders and labels
        let sliderM, sliderX, sliderY;
        let labelM, labelX, labelY;

        function setup() {
            // Create canvas and parent it to the visualization container
            const canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');

            // Get references to HTML elements
            sliderM = select('#slider-m');
            sliderX = select('#slider-x');
            sliderY = select('#slider-y');

            labelM = select('#label-m');
            labelX = select('#label-x');
            labelY = select('#label-y');

            // Add input event listeners to update labels
            sliderM.input(() => labelM.html(sliderM.value()));
            sliderX.input(() => labelX.html(sliderX.value()));
            sliderY.input(() => labelY.html(sliderY.value()));
        }

        function draw() {
            // 1. Read values from sliders
            const m = parseFloat(sliderM.value());
            const x = parseFloat(sliderX.value());
            const y = parseFloat(sliderY.value());

            // 2. Define physical constants
            const L = 10; // Distance between posts
            const g = 9.81; // Acceleration due to gravity

            // 3. Calculate angles
            // Use a small epsilon to avoid division by zero if x is 0 or L
            const epsilon = 0.0001;
            const theta1 = atan(y / (x + epsilon));
            const theta2 = atan(y / (L - x + epsilon));

            // 4. Calculate gravitational force
            const Fg = m * g;

            // 5. Calculate tensions
            // Avoid division by zero if sin(theta1 + theta2) is zero
            let T1 = 0, T2 = 0;
            const sinSum = sin(theta1 + theta2);
            if (abs(sinSum) > epsilon) {
                T1 = (Fg * cos(theta2)) / sinSum;
                T2 = (Fg * cos(theta1)) / sinSum;
            }

            // 6. Redraw the canvas
            background(255); // White background for canvas

            // --- Visualization Rendering ---

            // Coordinate mapping
            const anchor1 = createVector(100, 150);
            const anchor2 = createVector(700, 150);
            const pixelWidth = anchor2.x - anchor1.x;
            const y_scale = 50;

            const massPos = createVector(
                anchor1.x + (x / L) * pixelWidth,
                anchor1.y + y * y_scale
            );

            // Draw Posts
            strokeWeight(10);
            stroke(0);
            line(anchor1.x, anchor1.y - 50, anchor1.x, anchor1.y + 150);
            line(anchor2.x, anchor2.y - 50, anchor2.x, anchor2.y + 150);

            // Draw horizontal dashed line
            drawDashedLine(anchor1.x, anchor1.y, anchor2.x, anchor2.y, 10);

            // Draw Ropes
            strokeWeight(3);
            stroke(0);
            line(anchor1.x, anchor1.y, massPos.x, massPos.y);
            line(anchor2.x, anchor2.y, massPos.x, massPos.y);

            // Draw Mass
            noStroke();
            fill(0);
            ellipse(massPos.x, massPos.y, 40, 40);

            // Draw Angles
            noFill();
            stroke(0, 0, 255); // Blue for angles
            strokeWeight(1.5);
            arc(anchor1.x, anchor1.y, 80, 80, 0, theta1);
            arc(anchor2.x, anchor2.y, 80, 80, PI - theta2, PI);

            // --- Labels and Values ---
            noStroke();
            fill(0);
            textAlign(CENTER, CENTER);

            // Tension values at the top
            textSize(18);
            const tensionText = `T\u2081 = ${T1.toFixed(4)} N  |  T\u2082 = ${T2.toFixed(4)} N`;
            text(tensionText, width / 2, 30);

            // Angle labels
            textSize(16);
            fill(0, 0, 255);
            text('θ\u2081', anchor1.x + 50, anchor1.y + 15);
            text('θ\u2082', anchor2.x - 50, anchor2.y + 15);

            // Tension labels
            fill(255, 0, 0); // Red for tension labels
            push();
            translate((anchor1.x + massPos.x) / 2, (anchor1.y + massPos.y) / 2);
            rotate(-theta1);
            text('T\u2081', -20, -15);
            pop();

            push();
            translate((anchor2.x + massPos.x) / 2, (anchor2.y + massPos.y) / 2);
            rotate(theta2);
            text('T\u2082', 20, -15);
            pop();
        }

        // Helper function to draw a dashed line
        function drawDashedLine(x1, y1, x2, y2, dashLength) {
            push();
            stroke(150);
            strokeWeight(1);
            const d = dist(x1, y1, x2, y2);
            const dashCount = d / dashLength;
            const dashX = (x2 - x1) / dashCount;
            const dashY = (y2 - y1) / dashCount;
            for (let i = 0; i < dashCount; i += 2) {
                line(x1 + i * dashX, y1 + i * dashY, x1 + (i + 1) * dashX, y1 + (i + 1) * dashY);
            }
            pop();
        }
    </script>

</body>
</html>
