    <script type="module">
        import * as THREE from 'three';

        // --- STATE VARIABLES ---
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;
        let lastTimestamp = 0;
        const SIMULATION_DURATION = 10; // seconds

        let analyticalSolution = { x: t => 0, v: t => 0 };

        // --- DOM ELEMENTS ---
        const controls = {
            displacement: document.getElementById('slider-displacement'),
            velocity: document.getElementById('slider-velocity'),
            stiffness: document.getElementById('slider-stiffness'),
            mass: document.getElementById('slider-mass'),
            damping: document.getElementById('slider-damping'),
            proportionalGain: document.getElementById('slider-proportional-gain'),
            derivativeGain: document.getElementById('slider-derivative-gain'),
        };

        const values = {
            displacement: document.getElementById('value-displacement'),
            velocity: document.getElementById('value-velocity'),
            stiffness: document.getElementById('value-stiffness'),
            mass: document.getElementById('value-mass'),
            damping: document.getElementById('value-damping'),
            proportionalGain: document.getElementById('value-proportional-gain'),
            derivativeGain: document.getElementById('value-derivative-gain'),
        };

        const buttons = {
            play: document.getElementById('btn-play'),
            pause: document.getElementById('btn-pause'),
            reset: document.getElementById('btn-reset'),
        };

        const visuals = {
            sceneContainer: document.getElementById('scene-container'),
            plotDisplacement: document.getElementById('plot-displacement'),
            plotVelocity: document.getElementById('plot-velocity'),
        };

        const allSliders = Object.values(controls);

        // --- THREE.JS SETUP ---
        let scene, camera, renderer, mass, spring, damperPiston, damperHousing, clock;
        const SPRING_NATURAL_LENGTH = 3;
        const SPRING_ANCHOR_X = -4;

        function initThree() {
            scene = new THREE.Scene();
            clock = new THREE.Clock();

            const aspect = visuals.sceneContainer.clientWidth / visuals.sceneContainer.clientHeight;
            camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 1000);
            camera.position.set(0, 4, 12);
            camera.lookAt(0, 0, 0);

            renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
            renderer.setSize(visuals.sceneContainer.clientWidth, visuals.sceneContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            visuals.sceneContainer.appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 7.5);
            scene.add(directionalLight);

            // Geometries
            const base = new THREE.Mesh(
                new THREE.BoxGeometry(10, 0.2, 4),
                new THREE.MeshStandardMaterial({ color: 0x228B22 }) // green
            );
            base.position.y = -0.6;
            scene.add(base);

            const wall = new THREE.Mesh(
                new THREE.BoxGeometry(0.5, 3, 2),
                new THREE.MeshStandardMaterial({ color: 0x888888 })
            );
            wall.position.set(SPRING_ANCHOR_X - 0.25, 0.8, 0);
            scene.add(wall);

            mass = new THREE.Mesh(
                new THREE.BoxGeometry(1, 1, 1),
                new THREE.MeshStandardMaterial({ color: 0xffff00 }) // yellow
            );
            mass.position.y = 0;
            scene.add(mass);

            // Spring
            class CustomSinCurve extends THREE.Curve {
                constructor(scale) { super(); this.scale = scale; }
                getPoint(t) {
                    const tx = t * 3 - 1.5;
                    const ty = Math.sin(12 * Math.PI * t) * 0.25;
                    const tz = 0;
                    return new THREE.Vector3(tx, ty, tz).multiplyScalar(this.scale);
                }
            }
            const springPath = new CustomSinCurve(SPRING_NATURAL_LENGTH);
            const springGeometry = new THREE.TubeGeometry(springPath, 64, 0.1, 8, false);
            spring = new THREE.Mesh(springGeometry, new THREE.MeshStandardMaterial({ color: 0xff0000 })); // red
            scene.add(spring);

            // Damper
            const damperMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff }); // blue
            damperHousing = new THREE.Mesh(new THREE.CylinderGeometry(0.2, 0.2, 2, 16), damperMaterial);
            damperHousing.rotation.z = Math.PI / 2;
            damperHousing.position.set(SPRING_ANCHOR_X + 1, -0.2, 0);
            scene.add(damperHousing);

            damperPiston = new THREE.Mesh(new THREE.CylinderGeometry(0.05, 0.05, 2.5, 16), damperMaterial);
            damperPiston.rotation.z = Math.PI / 2;
            scene.add(damperPiston);

            // Enclosure
            const wireframe = new THREE.LineSegments(
                new THREE.EdgesGeometry(new THREE.BoxGeometry(10, 4, 4)),
                new THREE.LineBasicMaterial({ color: 0x555555 })
            );
            wireframe.position.y = 1.4;
            scene.add(wireframe);

            // Resize handler
            new ResizeObserver(() => {
                camera.aspect = visuals.sceneContainer.clientWidth / visuals.sceneContainer.clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(visuals.sceneContainer.clientWidth, visuals.sceneContainer.clientHeight);
            }).observe(visuals.sceneContainer);
        }

        // --- PLOTLY.JS SETUP ---
        function initPlots() {
            const commonLayout = {
                margin: { t: 40, b: 40, l: 50, r: 20 },
                xaxis: { range: [0, SIMULATION_DURATION], title: 'Time (s)', color: '#fff', gridcolor: '#444' },
                yaxis: { color: '#fff', gridcolor: '#444' },
                plot_bgcolor: 'var(--control-bg)',
                paper_bgcolor: 'var(--control-bg)',
                font: { color: '#fff' }
            };

            Plotly.newPlot(visuals.plotDisplacement, [
                { x: [], y: [], mode: 'lines', line: { color: '#007bff' } }, // Solution curve
                { x: [], y: [], mode: 'markers', marker: { color: '#17a2b8', size: 10 } } // Marker
            ], {
                ...commonLayout,
                title: 'Displacement vs. Time',
                yaxis: { ...commonLayout.yaxis, range: [-3.5, 3.5], title: 'Displacement (m)' },
            }, { responsive: true, displayModeBar: false });

            Plotly.newPlot(visuals.plotVelocity, [
                { x: [], y: [], mode: 'lines', line: { color: '#28a745' } }, // Solution curve
                { x: [], y: [], mode: 'markers', marker: { color: '#fd7e14', size: 10 } } // Marker
            ], {
                ...commonLayout,
                title: 'Velocity vs. Time',
                yaxis: { ...commonLayout.yaxis, range: [-5.5, 5.5], title: 'Velocity (m/s)' },
            }, { responsive: true, displayModeBar: false });
        }


        // --- SIMULATION LOGIC ---
        function solveODE(params) {
            const { m, c, k, Kp, Kd, x0, v0 } = params;

            const c_eff = c + Kd;
            const k_eff = k + Kp;

            // Handle edge case of m or k_eff being zero
            if (m <= 0 || k_eff <= 0) {
                 return { x: t => x0 + v0*t, v: t => v0 };
            }

            const zeta = c_eff / (2 * Math.sqrt(k_eff * m)); // Damping ratio
            const omega_n = Math.sqrt(k_eff / m); // Natural frequency

            let x_func, v_func;

            if (Math.abs(zeta - 1) < 1e-9) { // Critically Damped
                const r = -omega_n;
                const c1 = x0;
                const c2 = v0 - r * x0;
                x_func = t => (c1 + c2 * t) * Math.exp(r * t);
                v_func = t => (c2 + r * (c1 + c2 * t)) * Math.exp(r * t);
            } else if (zeta > 1) { // Overdamped
                const r1 = -zeta * omega_n + omega_n * Math.sqrt(zeta * zeta - 1);
                const r2 = -zeta * omega_n - omega_n * Math.sqrt(zeta * zeta - 1);
                const c1 = (v0 - r2 * x0) / (r1 - r2);
                const c2 = x0 - c1;
                x_func = t => c1 * Math.exp(r1 * t) + c2 * Math.exp(r2 * t);
                v_func = t => c1 * r1 * Math.exp(r1 * t) + c2 * r2 * Math.exp(r2 * t);
            } else { // Underdamped
                const omega_d = omega_n * Math.sqrt(1 - zeta * zeta); // Damped frequency
                const a = -zeta * omega_n;
                const c1 = x0;
                const c2 = (v0 - a * x0) / omega_d;
                x_func = t => Math.exp(a * t) * (c1 * Math.cos(omega_d * t) + c2 * Math.sin(omega_d * t));
                v_func = t => a * Math.exp(a * t) * (c1 * Math.cos(omega_d * t) + c2 * Math.sin(omega_d * t)) +
                               Math.exp(a * t) * (-c1 * omega_d * Math.sin(omega_d * t) + c2 * omega_d * Math.cos(omega_d * t));
            }
            return { x: x_func, v: v_func };
        }

        // --- UPDATE & RENDER FUNCTIONS ---
        function handleSliderChange() {
            pauseAnimation();
            resetAnimation();
            updateSimulationAndPlots();
        }

        function getParameters() {
            return {
                x0: parseFloat(controls.displacement.value),
                v0: parseFloat(controls.velocity.value),
                k: parseFloat(controls.stiffness.value),
                m: parseFloat(controls.mass.value),
                c: parseFloat(controls.damping.value),
                Kp: parseFloat(controls.proportionalGain.value),
                Kd: parseFloat(controls.derivativeGain.value)
            };
        }

        function updateValueSpans() {
             values.displacement.textContent = parseFloat(controls.displacement.value).toFixed(1);
             values.velocity.textContent = parseFloat(controls.velocity.value).toFixed(1);
             values.stiffness.textContent = parseFloat(controls.stiffness.value).toFixed(2);
             values.mass.textContent = parseFloat(controls.mass.value).toFixed(2);
             values.damping.textContent = parseFloat(controls.damping.value).toFixed(1);
             values.proportionalGain.textContent = parseFloat(controls.proportionalGain.value).toFixed(1);
             values.derivativeGain.textContent = parseFloat(controls.derivativeGain.value).toFixed(1);
        }

        function updateSimulationAndPlots() {
            updateValueSpans();
            const currentParams = getParameters();
            analyticalSolution = solveODE(currentParams);

            const t_data = Array.from({ length: 501 }, (_, i) => i * SIMULATION_DURATION / 500);
            const x_data = t_data.map(analyticalSolution.x);
            const v_data = t_data.map(analyticalSolution.v);

            Plotly.react(visuals.plotDisplacement, [{ x: t_data, y: x_data }], {
                'xaxis.autorange': false, 'yaxis.autorange': false
            });
            Plotly.react(visuals.plotVelocity, [{ x: t_data, y: v_data }], {
                'xaxis.autorange': false, 'yaxis.autorange': false
            });

            updateVisuals(0);
        }

        function updateVisuals(currentTime) {
            const currentX = analyticalSolution.x(currentTime);
            const currentV = analyticalSolution.v(currentTime);

            // Update 3D scene
            if(mass && spring && damperPiston) {
                mass.position.x = currentX;

                const springCurrentLength = SPRING_NATURAL_LENGTH + currentX;
                spring.scale.x = springCurrentLength / SPRING_NATURAL_LENGTH;
                spring.position.x = SPRING_ANCHOR_X + springCurrentLength / 2;

                damperPiston.position.x = SPRING_ANCHOR_X - 0.25 + currentX;
            }

            // Update Plotly markers
            Plotly.restyle(visuals.plotDisplacement, { x: [[currentTime]], y: [[currentX]] }, [1]);
            Plotly.restyle(visuals.plotVelocity, { x: [[currentTime]], y: [[currentV]] }, [1]);
        }

        // --- ANIMATION CONTROLS ---
        function playAnimation() {
            if (isRunning) return;
            isRunning = true;
            lastTimestamp = performance.now();
            allSliders.forEach(slider => slider.disabled = true);
            buttons.play.disabled = true;
            buttons.pause.disabled = false;

            animationFrameId = requestAnimationFrame(animate);
        }

        function pauseAnimation() {
            if (!isRunning) return;
            isRunning = false;
            cancelAnimationFrame(animationFrameId);
            allSliders.forEach(slider => slider.disabled = false);
            buttons.play.disabled = false;
            buttons.pause.disabled = true;
        }

        function resetAnimation() {
            if (isRunning) pauseAnimation();
            time = 0;
            updateVisuals(0);
        }

        function animate(timestamp) {
            if (!isRunning) return;

            const deltaTime = (timestamp - lastTimestamp) / 1000;
            lastTimestamp = timestamp;
            time += deltaTime;

            if (time >= SIMULATION_DURATION) {
                time = SIMULATION_DURATION;
                updateVisuals(time);
                pauseAnimation();
                return;
            }

            updateVisuals(time);
            animationFrameId = requestAnimationFrame(animate);
        }

        function renderThree() {
            if (renderer) renderer.render(scene, camera);
            requestAnimationFrame(renderThree);
        }

        // --- INITIALIZATION ---
        function init() {
            initThree();
            initPlots();

            allSliders.forEach(slider => {
                slider.addEventListener('input', handleSliderChange);
            });

            buttons.play.addEventListener('click', playAnimation);
            buttons.pause.addEventListener('click', pauseAnimation);
            buttons.reset.addEventListener('click', resetAnimation);

            buttons.pause.disabled = true;

            // Initial calculation and render
            handleSliderChange();

            // Start the separate render loop for Three.js
            renderThree();
        }

        window.addEventListener('load', init);

    </script>
</body>
</html>
