    }

    function handleSliderChange() {
        // Debounce reset to avoid excessive calculations while dragging
        clearTimeout(window.sliderChangeTimeout);
        window.sliderChangeTimeout = setTimeout(resetSimulation, 50);
    }

    function initPlots() {
        const commonLayout = {
            margin: { l: 50, r: 20, b: 40, t: 40, pad: 4 },
            xaxis: { title: 'time t', range: [0, 10] },
            yaxis: { automargin: true },
            showlegend: true,
            legend: { x: 1, xanchor: 'right', y: 1 },
        };

        Plotly.newPlot(plotContainers.displacement, [], {
            ...commonLayout,
            title: 'Displacement',
            yaxis: { ...commonLayout.yaxis, title: 'y(t)' }
        });

        Plotly.newPlot(plotContainers.velocity, [], {
            ...commonLayout,
            title: 'Velocity',
            yaxis: { ...commonLayout.yaxis, title: 'v(t)' }
        });
    }

    // --- SIMULATION LOGIC ---
    function resetSimulation() {
        if (isAnimating) {
            toggleAnimation();
        }
        time = 0;
        lastFrameTime = performance.now();
        readSliderValues();
        calculatePhysics();
        updatePlots();
        updateAnimation(true); // Force redraw
        updateTimeMarker();
    }

    function readSliderValues() {
        T = parseFloat(controls.tension.value);
        m1 = parseFloat(controls.m1.value);
        m2 = parseFloat(controls.m2.value);
        m3 = parseFloat(controls.m3.value);
        x1_0 = parseFloat(controls.x1.value);
        x2_0 = parseFloat(controls.x2.value);
        x3_0 = parseFloat(controls.x3.value);

        valueDisplays.tension.textContent = T.toFixed(0);
        valueDisplays.m1.textContent = m1.toFixed(2);
        valueDisplays.m2.textContent = m2.toFixed(2);
        valueDisplays.m3.textContent = m3.toFixed(2);
        valueDisplays.x1.textContent = x1_0.toFixed(2);
        valueDisplays.x2.textContent = x2_0.toFixed(2);
        valueDisplays.x3.textContent = x3_0.toFixed(2);

        y = [x1_0, x2_0, x3_0];
    }

    function calculatePhysics() {
        const M = [[m1, 0, 0], [0, m2, 0], [0, 0, m3]];
        const K = numeric.mul(T, [[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);
        const y0 = [x1_0, x2_0, x3_0];

        // Solve generalized eigenvalue problem K*A = w^2*M*A
        // by converting to standard form C*z = lambda*z
        const M_inv_sqrt = [
            [1/Math.sqrt(m1), 0, 0], 
            [0, 1/Math.sqrt(m2), 0], 
            [0, 0, 1/Math.sqrt(m3)]
        ];

        const C = numeric.dot(numeric.dot(M_inv_sqrt, K), M_inv_sqrt);
        const eig = numeric.eig(C);

        const lambdas = eig.lambda.x; // eigenvalues (w^2)
        const z = eig.E.x; // eigenvectors of C

        omegas = lambdas.map(l => Math.sqrt(Math.max(0, l))); // handle potential small negative float errors

        // Transform eigenvectors z back to original system's eigenvectors A
        // A = M_inv_sqrt * z
        eigenvectors = numeric.dot(M_inv_sqrt, z);

        // Calculate modal participation factors c_i
        // y(0) = sum(c_i * A_i) => c = A_inv * y(0)
        // Since eigenvectors form columns of a matrix, we need to transpose to solve
        const A_matrix = numeric.transpose(eigenvectors);
        try {
            const A_inv = numeric.inv(A_matrix);
            modalCoefficients = numeric.dot(A_inv, y0);
        } catch (e) {
            console.error("Matrix is singular, cannot calculate modal coefficients.", e);
            modalCoefficients = [0, 0, 0];
        }
    }

    // --- ANIMATION & VISUALIZATION ---
    function toggleAnimation() {
        isAnimating = !isAnimating;
        controls.playPauseBtn.textContent = isAnimating ? '❚❚' : '▶';
        if (isAnimating) {
            lastFrameTime = performance.now();
            requestAnimationFrame(animationLoop);
        }
    }

    function animationLoop(currentTime) {
        if (!isAnimating) return;

        const deltaTime = (currentTime - lastFrameTime) / 1000; // in seconds
        lastFrameTime = currentTime;

        time += deltaTime;
        if (time > 10) { // Loop animation
            time = 0;
        }

        updateAnimation();
        updateTimeMarker();

        requestAnimationFrame(animationLoop);
    }

    function updateAnimation(isReset = false) {
        // Calculate current displacements
        for (let i = 0; i < 3; i++) {
            let displacement = 0;
            for (let j = 0; j < 3; j++) {
                displacement += modalCoefficients[j] * eigenvectors[i][j] * Math.cos(omegas[j] * time);
            }
            y[i] = displacement;
        }

        if (p5Sketch && (isAnimating || isReset)) {
             p5Sketch.redraw();
        }
    }

    function updatePlots() {
        const timePoints = 500;
        const maxTime = 10;
        const t_vals = Array.from({length: timePoints}, (_, i) => i * maxTime / (timePoints - 1));

        const y_data = [[], [], []];
        const v_data = [[], [], []];

        for (const t of t_vals) {
            let current_y = [0, 0, 0];
            let current_v = [0, 0, 0];

            for (let i = 0; i < 3; i++) { // mass index
                for (let j = 0; j < 3; j++) { // mode index
                    const A_ij = eigenvectors[i][j];
                    const c_j = modalCoefficients[j];
                    const w_j = omegas[j];
                    current_y[i] += c_j * A_ij * Math.cos(w_j * t);
                    current_v[i] -= c_j * w_j * A_ij * Math.sin(w_j * t);
                }
            }
            y_data[0].push(current_y[0]);
            y_data[1].push(current_y[1]);
            y_data[2].push(current_y[2]);
            v_data[0].push(current_v[0]);
            v_data[1].push(current_v[1]);
            v_data[2].push(current_v[2]);
        }

        const traces = (data, prefix) => [
            { x: t_vals, y: data[0], mode: 'lines', name: `${prefix}₁`, line: {color: 'blue'} },
            { x: t_vals, y: data[1], mode: 'lines', name: `${prefix}₂`, line: {color: 'orange'} },
            { x: t_vals, y: data[2], mode: 'lines', name: `${prefix}₃`, line: {color: 'green'} },
        ];

        Plotly.react(plotContainers.displacement, traces(y_data, 'y'));
        Plotly.react(plotContainers.velocity, traces(v_data, 'v'));
    }

    function updateTimeMarker() {
        const shape = {
            type: 'line',
            x0: time,
            x1: time,
            y0: 0,
            y1: 1,
            yref: 'paper',
            line: {
                color: 'red',
                width: 1.5,
                dash: 'dash'
            }
        };
        Plotly.relayout(plotContainers.displacement, { shapes: [shape] });
        Plotly.relayout(plotContainers.velocity, { shapes: [shape] });
    }

    // --- P5.JS SKETCH ---
    const sketch = (p) => {
        p.setup = () => {
            const container = document.getElementById('main-animation-container');
            const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
            canvas.parent('main-animation-container');
            p.noLoop(); // We will manually call redraw
            window.addEventListener('resize', () => {
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                p.redraw();
            });
        };

        p.draw = () => {
            p.background(255);

            const w = p.width;
            const h = p.height;
            const horizontalScale = w / 4.0;
            const verticalScale = h / 3.0; // scale vertical displacements
            const y_center = h / 2;

            // Draw grid and axes
            p.stroke(220);
            p.strokeWeight(1);
            p.line(0, y_center, w, y_center); // x-axis
            for(let i = 1; i <= 3; i++) {
                p.line(i * horizontalScale, 0, i * horizontalScale, h); // vertical grid
            }

            // Get current positions
            const p0 = { x: 0, y: y_center };
            const p1 = { x: 1 * horizontalScale, y: y_center - y[0] * verticalScale };
            const p2 = { x: 2 * horizontalScale, y: y_center - y[1] * verticalScale };
            const p3 = { x: 3 * horizontalScale, y: y_center - y[2] * verticalScale };
            const p4 = { x: 4 * horizontalScale, y: y_center };

            // Draw string
            p.stroke(50);
            p.strokeWeight(2);
            p.line(p0.x, p0.y, p1.x, p1.y);
            p.line(p1.x, p1.y, p2.x, p2.y);
            p.line(p2.x, p2.y, p3.x, p3.y);
            p.line(p3.x, p3.y, p4.x, p4.y);

            // Draw masses
            p.noStroke();
            p.fill(255, 0, 0);
            const massRadiusScale = 12;
            p.circle(p1.x, p1.y, massRadiusScale * Math.sqrt(m1));
            p.circle(p2.x, p2.y, massRadiusScale * Math.sqrt(m2));
            p.circle(p3.x, p3.y, massRadiusScale * Math.sqrt(m3));
        };
    };

    // --- START THE APPLICATION ---
    init();

});
</script>

</body>
</html>
