<body>

    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>
        <div id="step-controls">
            <div>
                <button class="label" disabled>step 1</button>
                <button id="btn-step1-explain">explain</button>
                <button id="btn-step1-do">do it</button>
            </div>
            <div>
                <button class="label" disabled>step 2</button>
                <button id="btn-step2-explain">explain</button>
                <button id="btn-step2-do">do it</button>
            </div>
            <div id="step3-controls-container">
                <button class="label" disabled>step 3</button>
                <button id="btn-step3-explain">explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        // --- STATE VARIABLES ---
        let mode = 'move';
        let points = [];
        let draggedPoint = null;
        let algorithmState = {};

        const canvasWidth = 700;
        const canvasHeight = 500;

        // --- P5.JS SKETCH ---
        const sketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.pixelDensity(1); // Ensure consistent sizing
                initializePoints();
                resetAlgorithmState();
                setupUI();
            };

            p.draw = () => {
                p.background(255);

                // Draw hull edges first so they are behind points
                drawHullEdges(p);
                drawCandidateLines(p);

                // Draw points on top
                drawPoints(p);

                // Draw canvas border
                p.stroke(220);
                p.noFill();
                p.strokeWeight(1);
                p.rect(0, 0, p.width - 1, p.height - 1);
            };

            p.mousePressed = () => {
                if (mode === 'move') {
                    for (let i = 0; i < points.length; i++) {
                        if (p.dist(p.mouseX, p.mouseY, points[i].x, points[i].y) < 12) {
                            draggedPoint = i;
                            break;
                        }
                    }
                }
            };

            p.mouseDragged = () => {
                if (draggedPoint !== null) {
                    // Constrain points within the canvas
                    points[draggedPoint].x = p.constrain(p.mouseX, 10, p.width - 10);
                    points[draggedPoint].y = p.constrain(p.mouseY, 10, p.height - 10);
                }
            };

            p.mouseReleased = () => {
                draggedPoint = null;
            };
        };

        // --- DRAWING HELPERS ---
        function drawPoints(p) {
            for (let i = 0; i < points.length; i++) {
                const pt = points[i];
                p.stroke(0);
                p.strokeWeight(1);

                if (algorithmState.hullPointsIndices.includes(i)) {
                    p.fill(45, 60, 110); // Dark blue for hull points
                } else {
                    p.fill(200, 220, 100); // Light green for other points
                }

                p.ellipse(pt.x, pt.y, 12, 12);

                // Crosshair
                p.line(pt.x - 4, pt.y, pt.x + 4, pt.y);
                p.line(pt.x, pt.y - 4, pt.x, pt.y + 4);
            }
        }

        function drawCandidateLines(p) {
            if (algorithmState.candidateLines.length > 0) {
                p.stroke(60, 150, 140); // Teal
                p.strokeWeight(2);
                for (const line of algorithmState.candidateLines) {
                    const from = points[line[0]];
                    const to = points[line[1]];
                    p.line(from.x, from.y, to.x, to.y);
                }
            }
        }

        function drawHullEdges(p) {
            if (algorithmState.hullPointsIndices.length > 1) {
                p.stroke(45, 60, 110); // Dark blue
                p.strokeWeight(3);
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    const p1 = points[algorithmState.hullPointsIndices[i]];
                    const p2 = points[algorithmState.hullPointsIndices[i+1]];
                    p.line(p1.x, p1.y, p2.x, p2.y);
                }
                // Draw closing edge if algorithm is complete
                if (algorithmState.hullPointsIndices.length === algorithmState.fullHull.length && algorithmState.fullHull.length > 0) {
                     const p_last = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 1]];
                     const p_first = points[algorithmState.hullPointsIndices[0]];
                     p.line(p_last.x, p_last.y, p_first.x, p_first.y);
                }
            }
        }

        // --- ALGORITHM LOGIC ---
        function orientation(p, q, r) {
            const val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
            if (val === 0) return 0; // Collinear
            return (val > 0) ? 1 : 2; // Clockwise or Counter-clockwise
        }

        function runJarvisMarch() {
            if (points.length < 3) return [];

            let hull = [];
            let l = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[l].x) {
                    l = i;
                }
            }

            let p = l, q;
            do {
                hull.push(p);
                q = (p + 1) % points.length;
                for (let i = 0; i < points.length; i++) {
                    if (orientation(points[p], points[i], points[q]) === 2) {
                        q = i;
                    }
                }
                p = q;
            } while (p !== l);

            return hull;
        }

        // --- STATE & UI MANAGEMENT ---
        function initializePoints() {
            const initialPositions = [
                {x: 150, y: 350}, {x: 250, y: 150}, {x: 350, y: 250}, 
                {x: 550, y: 180}, {x: 480, y: 320}, {x: 420, y: 400}
            ];
            points = initialPositions.map(pos => new p5.Vector(pos.x, pos.y));
        }

        function resetAlgorithmState() {
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: [],
            };
            document.getElementById('explanation-text').innerHTML = '';
        }

        function updateUI() {
            const btnMove = document.getElementById('btn-move');
            const btnStartAlgo = document.getElementById('btn-start-algo');
            const stepControls = document.getElementById('step-controls');

            const step1Btns = [document.getElementById('btn-step1-explain'), document.getElementById('btn-step1-do')];
            const step2Btns = [document.getElementById('btn-step2-explain'), document.getElementById('btn-step2-do')];
            const step3Btns = [document.getElementById('btn-step3-explain'), ...document.getElementById('step3-actions').children];

            if (mode === 'move') {
                btnMove.classList.add('active');
                btnStartAlgo.classList.remove('active');
                stepControls.style.display = 'none';
            } else { // 'algorithm' mode
                btnMove.classList.remove('active');
                btnStartAlgo.classList.add('active');
                stepControls.style.display = 'block';

                [...step1Btns, ...step2Btns, ...step3Btns].forEach(b => b.disabled = true);

                if (algorithmState.step === 0) { // Start of algo
                    step1Btns.forEach(b => b.disabled = false);
                } else if (algorithmState.step === 1) { // After step 1
                    step2Btns.forEach(b => b.disabled = false);
                } else if (algorithmState.step === 2) { // After step 2, start of step 3
                    step3Btns.forEach(b => b.disabled = false);
                    // Disable all but the first action button of step 3
                    for(let i=1; i < document.getElementById('step3-actions').children.length; i++) {
                        document.getElementById('step3-actions').children[i].disabled = true;
                    }
                }
            }
        }

        function appendExplanation(text) {
             if (!algorithmState.explanationSteps.includes(text)) {
                algorithmState.explanationSteps.push(text);
                document.getElementById('explanation-text').innerHTML = algorithmState.explanationSteps.join('<br>');
            }
        }

        // --- EVENT HANDLERS ---
        function setupUI() {
            document.getElementById('btn-move').addEventListener('click', () => {
                mode = 'move';
                resetAlgorithmState();
                updateUI();
            });

            document.getElementById('btn-start-algo').addEventListener('click', () => {
                mode = 'algorithm';
                resetAlgorithmState();

                // Pre-calculate hull and generate dynamic buttons
                algorithmState.fullHull = runJarvisMarch();
                const step3Actions = document.getElementById('step3-actions');
                step3Actions.innerHTML = '';

                if (algorithmState.fullHull.length > 2) {
                    for (let i = 2; i < algorithmState.fullHull.length; i++) {
                         const btn = document.createElement('button');
                         btn.id = `btn-step3-repeat-${i-2}`;
                         btn.textContent = (i === 2) ? "do it" : "repeat";
                         btn.addEventListener('click', () => handleStep3Repeat(i));
                         step3Actions.appendChild(btn);
                    }
                }
                updateUI();
            });

            // Step 1
            document.getElementById('btn-step1-explain').addEventListener('click', () => {
                appendExplanation("Step 1. Find the leftmost point. This point is guaranteed to be part of the convex hull and serves as our starting point.");
            });
            document.getElementById('btn-step1-do').addEventListener('click', () => {
                let l = 0;
                for (let i = 1; i < points.length; i++) {
                    if (points[i].x < points[l].x) l = i;
                }
                algorithmState.leftmostPointIndex = l;
                algorithmState.currentPointIndex = l;
                algorithmState.hullPointsIndices.push(l);

                algorithmState.step = 1;
                updateUI();
                document.getElementById('btn-step1-explain').disabled = true;
                document.getElementById('btn-step1-do').disabled = true;
            });

            // Step 2
            document.getElementById('btn-step2-explain').addEventListener('click', () => {
                appendExplanation("Step 2. From the current hull point, find the next point by picking the one that creates the most counter-clockwise turn. All other points are considered candidates.");
            });
            document.getElementById('btn-step2-do').addEventListener('click', () => {
                const currentIdx = algorithmState.currentPointIndex;
                const nextHullIdx = algorithmState.fullHull[1];

                // Show candidate lines
                algorithmState.candidateLines = points
                    .map((_, i) => i)
                    .filter(i => i !== currentIdx)
                    .map(i => [currentIdx, i]);

                setTimeout(() => {
                    algorithmState.candidateLines = [];
                    algorithmState.hullPointsIndices.push(nextHullIdx);
                    algorithmState.currentPointIndex = nextHullIdx;

                    algorithmState.step = 2;
                    updateUI();
                    document.getElementById('btn-step2-explain').disabled = true;
                    document.getElementById('btn-step2-do').disabled = true;
                }, 700);
            });

            // Step 3
            document.getElementById('btn-step3-explain').addEventListener('click', () => {
                appendExplanation("Step 3. Repeat the process: from the last point added to the hull, find the next most counter-clockwise point until we wrap around and reach the starting (leftmost) point again.");
            });
        }

        function handleStep3Repeat(hullIndex) {
            const currentIdx = algorithmState.currentPointIndex;
            const nextHullIdx = algorithmState.fullHull[hullIndex];

            this.event.target.disabled = true;

            // Show candidate lines from the current point
            const existingHull = new Set(algorithmState.hullPointsIndices);
            algorithmState.candidateLines = points
                .map((_, i) => i)
                .filter(i => !existingHull.has(i))
                .map(i => [currentIdx, i]);

            setTimeout(() => {
                algorithmState.candidateLines = [];
                algorithmState.hullPointsIndices.push(nextHullIdx);
                algorithmState.currentPointIndex = nextHullIdx;

                // Enable the next button if it exists
                const nextBtn = document.getElementById(`btn-step3-repeat-${hullIndex - 1}`);
                if (nextBtn) {
                    nextBtn.disabled = false;
                }

                // If this was the last point, disable all remaining step 3 buttons
                if (hullIndex === algorithmState.fullHull.length - 1) {
                    const step3Btns = [...document.getElementById('step3-actions').children];
                    step3Btns.forEach(b => b.disabled = true);
                }

            }, 700);
        }

        // --- INITIALIZATION ---
        new p5(sketch);

    </script>
