    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Element References ---
            const sliders = {
                alphaMax: document.getElementById('slider-alpha-max'),
                tMin: document.getElementById('slider-t-min'),
                tMax: document.getElementById('slider-t-max'),
                enzymeControl: document.getElementById('slider-enzyme-control'),
                feedRate: document.getElementById('slider-feed-rate'),
                substrateFeedConc: document.getElementById('slider-substrate-feed-conc'),
                feedTime: document.getElementById('slider-feed-time'),
            };

            const values = {
                alphaMax: document.getElementById('value-alpha-max'),
                tMin: document.getElementById('value-t-min'),
                tMax: document.getElementById('value-t-max'),
                enzymeControl: document.getElementById('value-enzyme-control'),
                feedRate: document.getElementById('value-feed-rate'),
                substrateFeedConc: document.getElementById('value-substrate-feed-conc'),
                feedTime: document.getElementById('value-feed-time'),
            };

            const plotSelect = document.getElementById('select-plot');

            let simulationData = {};

            // --- Model Constants ---
            const modelConsts = {
                Y_xg_f: 0.1,    // Yield of biomass from glucose (fermentative)
                Y_eg_f: 0.45,   // Yield of ethanol from glucose (fermentative)
                Y_xe_r: 0.6,    // Yield of biomass from ethanol (respirative)
                mu_max_g_f: 2.5, // Max specific rate for fermentation
                mu_max_e_r: 0.6, // Max specific rate for ethanol respiration
                Ks_g: 1.0,      // Saturation constant for glucose
                Ks_e: 1.0,      // Saturation constant for ethanol
                k_syn_e1_base: 0.1,  // Base synthesis rate for enzyme E1
                k_syn_e2_base: 0.2, // Base synthesis rate for enzyme E2
                k_deg_e: 0.02,    // Degradation rate for enzymes
                K_ind_e1: 1.0,    // Induction constant for E1 synthesis
                n_hill: 4,      // Hill coefficient for E2 repression
                E_total: 1.0    // Total enzyme pool for E0 calculation
            };

            // --- ODE System Definition: dy/dt = f(t, y, params) ---
            function dydt(t, y, params) {
                // Unpack state variables (ensure they are non-negative)
                let [X, Glu, Eta, E1, E2, V] = y.map(val => Math.max(0, val));

                // Determine feed rate and concentration based on time
                const F = (t <= params.feedTime) ? params.feedRate : 0;
                const G_in = (t <= params.feedTime) ? params.substrateFeedConc : 0;
                const D = F / V; // Dilution rate

                // Specific reaction rates
                const rate_ferm = modelConsts.mu_max_g_f * E1 * (Glu / (modelConsts.Ks_g + Glu));
                const rate_resp = modelConsts.mu_max_e_r * E2 * (Eta / (modelConsts.Ks_e + Eta));

                // ODEs for state variables
                const dX_dt = (rate_ferm * modelConsts.Y_xg_f + rate_resp * modelConsts.Y_xe_r) * X - D * X;
                const dGlu_dt = -rate_ferm * X + D * (G_in - Glu);
                const dEta_dt = (rate_ferm * modelConsts.Y_eg_f - rate_resp) * X - D * Eta;

                const E1_synthesis = modelConsts.k_syn_e1_base * (Glu / (modelConsts.K_ind_e1 + Glu));
                const E2_synthesis = (params.alphaMax * modelConsts.k_syn_e2_base) *
                    (Math.pow(params.enzymeControl, modelConsts.n_hill) /
                    (Math.pow(params.enzymeControl, modelConsts.n_hill) + Math.pow(Glu, modelConsts.n_hill)));

                const dE1_dt = E1_synthesis - modelConsts.k_deg_e * E1 - D * E1;
                const dE2_dt = E2_synthesis - modelConsts.k_deg_e * E2 - D * E2;
                const dV_dt = F;

                return [dX_dt, dGlu_dt, dEta_dt, dE1_dt, dE2_dt, dV_dt];
            }

            // --- 4th Order Runge-Kutta (RK4) Solver ---
            function rk4(f, y0, t_span, h, params) {
                const [t_start, t_end] = t_span;
                const steps = Math.floor((t_end - t_start) / h);
                let t = t_start;
                let y = [...y0];

                const results = {
                    time: [t],
                    X: [y[0]], Glu: [y[1]], Eta: [y[2]],
                    E1: [y[3]], E2: [y[4]], V: [y[5]],
                };

                function add(v1, v2, scalar) {
                    return v1.map((val, i) => val + v2[i] * scalar);
                }

                for (let i = 0; i < steps; i++) {
                    const k1 = f(t, y, params);
                    const k2 = f(t + h / 2, add(y, k1, h / 2), params);
                    const k3 = f(t + h / 2, add(y, k2, h / 2), params);
                    const k4 = f(t + h, add(y, k3, h), params);

                    for (let j = 0; j < y.length; j++) {
                        y[j] += (h / 6) * (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]);
                    }
                    t += h;

                    results.time.push(t);
                    results.X.push(y[0]);
                    results.Glu.push(y[1]);
                    results.Eta.push(y[2]);
                    results.E1.push(y[3]);
                    results.E2.push(y[4]);
                    results.V.push(y[5]);
                }
                return results;
            }

            // --- Main Simulation Runner ---
            function runSimulation() {
                const params = {
                    alphaMax: parseFloat(sliders.alphaMax.value),
                    tMax: parseInt(sliders.tMax.value),
                    enzymeControl: parseFloat(sliders.enzymeControl.value),
                    feedRate: parseFloat(sliders.feedRate.value),
                    substrateFeedConc: parseFloat(sliders.substrateFeedConc.value),
                    feedTime: parseInt(sliders.feedTime.value),
                };

                const y0 = [0.1, 60.0, 0.0, 0.0, 0.0, 1.0]; // X, Glu, Eta, E1, E2, V
                const t_span = [0, params.tMax];
                const h = 0.1;

                const results = rk4(dydt, y0, t_span, h, params);

                // Calculate derived quantities
                results.E0 = results.E1.map((e1, i) => {
                    const e2 = results.E2[i];
                    return Math.max(0, modelConsts.E_total - e1 - e2);
                });
                results.alpha = results.E1.map((e1, i) => {
                    const e2 = results.E2[i];
                    return e2 / (e1 + e2 + 1e-9); // Add epsilon to avoid 0/0
                });

                simulationData = results;
                drawPlots();
            }

            // --- Plotting Logic ---
            function drawPlots() {
                const plotType = plotSelect.value;
                const { time, X, Glu, Eta, E0, E1, E2, alpha } = simulationData;

                const commonLayout = {
                    margin: { l: 60, r: 20, b: 50, t: 50, pad: 4 },
                    xaxis: { title: 'time (h)', gridcolor: '#eee' },
                    yaxis: { gridcolor: '#eee' },
                    legend: { x: 1, xanchor: 'right', y: 1 },
                    plot_bgcolor: '#fff',
                    paper_bgcolor: '#fff',
                };

                let topData, topLayout, bottomData, bottomLayout;

                if (plotType === 'fed batch') {
                    topData = [
                        { x: time, y: X, name: 'X(t)', type: 'scatter', line: { color: 'blue' } },
                        { x: time, y: Glu, name: 'Glu(t)', type: 'scatter', line: { color: 'red' } },
                        { x: time, y: Eta, name: 'Eta(t)', type: 'scatter', line: { color: 'green' } },
                    ];
                    topLayout = {
                        ...commonLayout,
                        title: 'diauxic behavior',
                        yaxis: { ...commonLayout.yaxis, title: 'concentration [mol/m³]' }
                    };

                    bottomData = [
                        { x: time, y: E2, name: 'E₂(t)', type: 'scatter', line: { color: 'black' } },
                        { x: time, y: E1, name: 'E₁(t)', type: 'scatter', line: { color: 'magenta' } },
                        { x: time, y: E0, name: 'E₀(t)', type: 'scatter', line: { color: 'orange' } },
                    ];
                    bottomLayout = {
                        ...commonLayout,
                        title: 'enzyme concentration',
                        yaxis: { ...commonLayout.yaxis, title: 'enzyme concentration [mol/m³]' }
                    };
                } else if (plotType === 'α') {
                    topData = [
                        { x: time, y: Glu, name: 'Glu(t)', type: 'scatter', line: { color: 'red' } },
                    ];
                    topLayout = {
                        ...commonLayout,
                        title: 'glucose concentration',
                        yaxis: { ...commonLayout.yaxis, title: 'glucose concentration [mol/m³]' }
                    };

                    bottomData = [
                        { x: time, y: alpha, name: 'α', type: 'scatter', line: { color: 'blue' } },
                    ];
                    bottomLayout = {
                        ...commonLayout,
                        title: 'fraction of respiring biomass',
                        yaxis: { ...commonLayout.yaxis, title: 'α', range: [0, 1] }
                    };
                }

                Plotly.react('plot-top', topData, topLayout, {responsive: true});
                Plotly.react('plot-bottom', bottomData, bottomLayout, {responsive: true});
            }

            // --- Event Handlers ---
            function setupEventListeners() {
                for (const key in sliders) {
                    const slider = sliders[key];
                    const valueSpan = values[key];

                    slider.addEventListener('input', () => {
                        valueSpan.textContent = slider.value;

                        if (key === 'tMax') {
                            // Dynamically update feed time max value
                            sliders.feedTime.max = slider.value;
                            if (parseFloat(sliders.feedTime.value) > parseFloat(slider.value)) {
                                sliders.feedTime.value = slider.value;
                                values.feedTime.textContent = slider.value;
                            }
                        }
                        runSimulation();
                    });
                }

                plotSelect.addEventListener('change', drawPlots);
            }

            // --- Initialization ---
            setupEventListeners();
            runSimulation(); // Initial run on page load
        });
    </script>

</body>
</html>
