            sunLight = new THREE.DirectionalLight(0xffffff, 3.0);
            sun.add(sunLight);
            scene.add(sun); // Sun is shared but position is handled differently per view

            // Celestial Sphere & Constellations
            celestialSphere = new THREE.Mesh(
                new THREE.SphereGeometry(500, 64, 64),
                new THREE.MeshBasicMaterial({ color: 0x050515, side: THREE.BackSide, transparent: true, opacity: 1.0 })
            );
            scene.add(celestialSphere);
            constellationsGroup = new THREE.Group();
            const starMaterial = new THREE.LineBasicMaterial({ color: 0xaaaaee });
            for(let i=0; i<100; i++) { // Generate some random constellations
                const points = [];
                const start = new THREE.Vector3().setFromSphericalCoords(490, Math.random() * Math.PI, Math.random() * 2 * Math.PI);
                const end = new THREE.Vector3().setFromSphericalCoords(490, start.y + (Math.random()-0.5)*0.1, start.z + (Math.random()-0.5)*0.1);
                points.push(start, end);
                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const line = new THREE.Line(geometry, starMaterial);
                constellationsGroup.add(line);
            }
            celestialSphere.add(constellationsGroup);

            // -- GROUND VIEW OBJECTS --
            ground = new THREE.Mesh(
                new THREE.CylinderGeometry(20, 20, 0.2, 64),
                new THREE.MeshPhongMaterial({ color: 0x228b22 })
            );
            ground.position.y = -0.1;
            groundViewObjects.add(ground);

            cardinalPointsGroup = new THREE.Group();
            const n = createTextSprite('N'); n.position.set(0, 1, -20);
            const e = createTextSprite('E'); e.position.set(20, 1, 0);
            const s = createTextSprite('S'); s.position.set(0, 1, 20);
            const w = createTextSprite('W'); w.position.set(-20, 1, 0);
            cardinalPointsGroup.add(n, e, s, w);
            groundViewObjects.add(cardinalPointsGroup);

            celestialDome = new THREE.Mesh(
                new THREE.SphereGeometry(30, 64, 32, 0, Math.PI * 2, 0, Math.PI / 2),
                new THREE.MeshPhongMaterial({ color: 0x87ceeb, side: THREE.BackSide, transparent: true })
            );
            groundViewObjects.add(celestialDome);

            sunPathLine = new THREE.Line(
                new THREE.BufferGeometry(),
                new THREE.LineDashedMaterial({ color: 0xffa500, dashSize: 0.5, gapSize: 0.25 })
            );
            groundViewObjects.add(sunPathLine);

            // -- SPACE VIEW OBJECTS --
            const earthTilt = degToRad(23.45);
            earth = new THREE.Group();
            const earthSphere = new THREE.Mesh(
                new THREE.SphereGeometry(5, 64, 64),
                new THREE.MeshPhongMaterial({ color: 0x4682b4 })
            );
            earth.add(earthSphere);
            earthAxis = new THREE.Mesh(
                new THREE.CylinderGeometry(0.1, 0.1, 14, 8),
                new THREE.MeshBasicMaterial({ color: 0xffffff })
            );
            earth.add(earthAxis);
            earth.rotation.z = earthTilt;
            spaceViewObjects.add(earth);

            terminator = new THREE.Mesh(
                new THREE.PlaneGeometry(30, 30),
                new THREE.MeshBasicMaterial({ color: 0x000000, transparent: true, opacity: 0.2, side: THREE.DoubleSide })
            );
            spaceViewObjects.add(terminator);

            latitudeParallelLine = new THREE.LineLoop(
                new THREE.BufferGeometry(),
                new THREE.LineBasicMaterial({ color: 0xffff00 })
            );
            earth.add(latitudeParallelLine);

            dayNightOnParallelLine = new THREE.Line(
                new THREE.BufferGeometry(),
                new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 3 })
            );
            earth.add(dayNightOnParallelLine);

            // --- EVENT LISTENERS ---
            window.addEventListener('resize', onWindowResize);
            sliderDate.addEventListener('input', () => { labelDate.textContent = dayOfYearToDateString(sliderDate.value); updateAll(); });
            sliderLatitude.addEventListener('input', () => { labelLatitude.textContent = `${sliderLatitude.value}°`; updateAll(); });
            sliderTime.addEventListener('input', () => { labelTime.textContent = hoursToTimeString(sliderTime.value); updateTime(); });
            selectView.addEventListener('change', updateViewAndAll);
            [checkSunPath, checkConstellationsDay, checkStationaryEarth, checkConstellations, checkDayNightDivision, checkDayNightParallel].forEach(chk => {
                chk.addEventListener('change', updateCheckboxVisibility);
            });

            // Initial setup
            updateAll();
            animate();
        }

        // --- UPDATE & ANIMATION ---
        function onWindowResize() {
            camera.aspect = vizContainer.clientWidth / vizContainer.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
            needsRender = true;
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update(); // only sets needsRender if there was a change

            if (needsRender) {
                renderer.render(scene, camera);
                needsRender = false;
            }
        }

        function updateAll() {
            const day = parseFloat(sliderDate.value);
            const lat = degToRad(parseFloat(sliderLatitude.value));

            // --- ASTRONOMICAL CALCULATIONS ---
            // Simplified formula for solar declination
            const declination = -degToRad(23.45) * Math.cos(2 * Math.PI * (day + 10) / 365);

            // Sunrise/sunset hour angle
            const cosHourAngle = -Math.tan(lat) * Math.tan(declination);

            let sunriseTime, sunsetTime, dayLength, sunriseAzimuth, sunsetAzimuth;
            if (cosHourAngle > 1) { // Polar night
                sunriseTime = sunsetTime = dayLength = sunriseAzimuth = sunsetAzimuth = NaN;
            } else if (cosHourAngle < -1) { // Polar day
                sunriseTime = 0; sunsetTime = 24; dayLength = 24;
                sunriseAzimuth = sunsetAzimuth = NaN;
            } else {
                const hourAngle = Math.acos(cosHourAngle);
                dayLength = 2 * radToDeg(hourAngle) / 15;
                sunriseTime = 12 - dayLength / 2;
                sunsetTime = 12 + dayLength / 2;
                // Azimuth at sunrise/sunset (altitude = 0)
                const sinDec = Math.sin(declination);
                const cosLat = Math.cos(lat);
                sunriseAzimuth = radToDeg(Math.acos(sinDec / cosLat));
                sunsetAzimuth = 360 - sunriseAzimuth;
            }

            // Update readouts
            readoutDayLength.textContent = isNaN(dayLength) ? "N/A" : `${hoursToTimeString(dayLength)}`;
            readoutNightLength.textContent = isNaN(dayLength) ? "N/A" : `${hoursToTimeString(24 - dayLength)}`;
            readoutSunriseTime.textContent = hoursToTimeString(sunriseTime);
            readoutSunsetTime.textContent = hoursToTimeString(sunsetTime);
            if(isNaN(sunriseAzimuth)){
                readoutSunriseDir.textContent = readoutSunsetDir.textContent = "N/A";
            } else {
                readoutSunriseDir.textContent = `${sunriseAzimuth.toFixed(1)}° (from N)`;
                readoutSunsetDir.textContent = `${sunsetAzimuth.toFixed(1)}° (from N)`;
            }

            // --- UPDATE 3D OBJECTS ---
            // Update Sun Path (Ground View)
            const pathPoints = [];
            const pathRadius = 25;
            for (let t = 0; t <= 24; t += 0.5) {
                const h = degToRad(15 * (t - 12));
                const alt = Math.asin(Math.sin(lat) * Math.sin(declination) + Math.cos(lat) * Math.cos(declination) * Math.cos(h));
                if (alt > 0) {
                    const az = Math.acos((Math.sin(declination) - Math.sin(alt) * Math.sin(lat)) / (Math.cos(alt) * Math.cos(lat)));
                    const x = -pathRadius * Math.cos(alt) * Math.sin(h > 0 ? az : -az);
                    const y = pathRadius * Math.sin(alt);
                    const z = -pathRadius * Math.cos(alt) * Math.cos(h > 0 ? az : -az);
                    pathPoints.push(new THREE.Vector3(x, y, z));
                }
            }
            if(pathPoints.length > 1) {
                sunPathLine.geometry.setFromPoints(pathPoints);
                sunPathLine.computeLineDistances();
                sunPathLine.geometry.attributes.position.needsUpdate = true;
            } else {
                sunPathLine.geometry.setFromPoints([]); // Clear path
            }

            // Update sun direction (for Space View lighting)
            const sunAngle = - (day / 365) * 2 * Math.PI;
            sunLight.position.set(100 * Math.cos(sunAngle), 0, 100 * Math.sin(sunAngle));

            // Update Latitude Parallel (Space View)
            const latRadius = 5 * Math.cos(lat);
            const latY = 5 * Math.sin(lat);
            const parallelPoints = [];
            for (let i = 0; i <= 64; i++) {
                const angle = (i / 64) * 2 * Math.PI;
                parallelPoints.push(new THREE.Vector3(latRadius * Math.cos(angle), latY, latRadius * Math.sin(angle)));
            }
            latitudeParallelLine.geometry.setFromPoints(parallelPoints);
            latitudeParallelLine.geometry.attributes.position.needsUpdate = true;

            // Update view-specific elements and time-dependent elements
            updateView();
            updateTime(); // Recalculates time-based elements
        }

        function updateTime() {
            const time = parseFloat(sliderTime.value);
            const lat = degToRad(parseFloat(sliderLatitude.value));
            const day = parseFloat(sliderDate.value);
            const declination = -degToRad(23.45) * Math.cos(2 * Math.PI * (day + 10) / 365);

            // --- GROUND VIEW ---
            // Sun position along its path
            const hourAngle = degToRad(15 * (time - 12));
            const altitude = Math.asin(Math.sin(lat) * Math.sin(declination) + Math.cos(lat) * Math.cos(declination) * Math.cos(hourAngle));
            const azimuth = Math.acos((Math.sin(declination) - Math.sin(altitude) * Math.sin(lat)) / (Math.cos(altitude) * Math.cos(lat)));

            const sunRadius = 25;
            sun.position.setFromSphericalCoords(sunRadius, Math.PI / 2 - altitude, hourAngle > 0 ? -azimuth + Math.PI : azimuth + Math.PI);

            // Dome color based on sun altitude
            const nightFactor = Math.max(0, Math.min(1, (-radToDeg(altitude) + 5) / 15));
            celestialDome.material.color.set(new THREE.Color(0x87ceeb).lerp(new THREE.Color(0x000033), nightFactor));
            celestialDome.material.opacity = 0.4 + 0.6 * nightFactor;
            celestialSphere.material.opacity = nightFactor;

            // --- SPACE VIEW ---
            const earthRotation = degToRad(15 * time); // Solar time corresponds to rotation
            const isStationary = checkStationaryEarth.checked;

            earth.rotation.y = isStationary ? 0 : -earthRotation;

            const sunAngle = - (day / 365) * 2 * Math.PI;
            const sunDist = 50;
            const sunX = sunDist * Math.cos(sunAngle);
            const sunZ = sunDist * Math.sin(sunAngle);

            if (isStationary) {
                // If earth is stationary, sun appears to rotate
                const stationarySunAngle = sunAngle + earthRotation;
                sun.position.set(sunDist * Math.cos(stationarySunAngle), 0, sunDist * Math.sin(stationarySunAngle));
            } else {
                sun.position.set(sunX, 0, sunZ);
            }
            sunLight.position.copy(sun.position); // ensure light comes from sun
            terminator.position.set(0, 0, 0);
            terminator.lookAt(sun.position);

            // Day/Night on parallel
            const cosHourAngle = -Math.tan(lat) * Math.tan(declination);
            if (Math.abs(cosHourAngle) <= 1) {
                const hourAngle = Math.acos(cosHourAngle);
                const dayNightPoints = [];
                const latRadius = 5 * Math.cos(lat);
                const latY = 5 * Math.sin(lat);

                // Angle on the parallel where sun rises/sets.
                // We need to account for earth's tilt and position in orbit.
                const worldSunDir = new THREE.Vector3().copy(sun.position).normalize();
                const localSunDir = earth.worldToLocal(worldSunDir.clone()).normalize();
                const terminatorAngle = Math.atan2(localSunDir.z, localSunDir.x);

                const startAngle = terminatorAngle - hourAngle;
                const endAngle = terminatorAngle + hourAngle;

                for (let angle = startAngle; angle <= endAngle; angle += 0.1) {
                     dayNightPoints.push(new THREE.Vector3(latRadius * Math.cos(angle), latY, latRadius * Math.sin(angle)));
                }
                if(dayNightPoints.length > 1){
                    dayNightOnParallelLine.geometry.setFromPoints(dayNightPoints);
                    dayNightOnParallelLine.geometry.attributes.position.needsUpdate = true;
                } else {
                    dayNightOnParallelLine.geometry.setFromPoints([]);
                }
            } else {
                dayNightOnParallelLine.geometry.setFromPoints([]);
            }

            needsRender = true;
        }

        function updateViewAndAll() {
            updateAll(); // recalculate everything before switching view
        }

        function updateView() {
            const view = selectView.value;

            // Toggle control panels
            optionsGround.style.display = (view === 'ground') ? 'block' : 'none';
            optionsSpace.style.display = (view === 'space') ? 'block' : 'none';

            // Toggle visibility of major object groups
            groundViewObjects.visible = (view === 'ground');
            spaceViewObjects.visible = (view === 'space');

            if (view === 'ground') {
                camera.position.set(0, 1.6, 5);
                controls.target.set(0, 1, 0);
                controls.maxPolarAngle = Math.PI / 2;
                controls.minDistance = 2;
                controls.maxDistance = 20;
            } else { // 'space'
                camera.position.set(0, 15, 25);
                controls.target.set(0, 0, 0);
                controls.maxPolarAngle = Math.PI;
                controls.minDistance = 10;
                controls.maxDistance = 100;
            }
            updateCheckboxVisibility(); // Apply checkbox settings to the new view
        }

        function updateCheckboxVisibility() {
            const view = selectView.value;
            const time = parseFloat(sliderTime.value);
            const lat = degToRad(parseFloat(sliderLatitude.value));
            const day = parseFloat(sliderDate.value);
            const declination = -degToRad(23.45) * Math.cos(2 * Math.PI * (day + 10) / 365);
            const altitude = Math.asin(Math.sin(lat) * Math.sin(declination) + Math.cos(lat) * Math.cos(declination) * Math.cos(degToRad(15 * (time - 12))));

            if (view === 'ground') {
                sunPathLine.visible = checkSunPath.checked;
                // Show constellations only at night unless 'day' box is checked
                const isNight = altitude < 0;
                constellationsGroup.visible = isNight || checkConstellationsDay.checked;
            } else { // 'space'
                constellationsGroup.visible = checkConstellations.checked;
                terminator.visible = checkDayNightDivision.checked;
                latitudeParallelLine.visible = checkDayNightParallel.checked;
                dayNightOnParallelLine.visible = checkDayNightParallel.checked;
                // If stationary earth is toggled, need to re-calc positions
                updateTime();
            }
            needsRender = true;
        }

        // --- START ---
        init();
    </script>
</body>
</html>
