        #diffusivity-value {
            font-family: "Courier New", Courier, monospace;
            font-weight: bold;
            color: #005A9C;
            white-space: nowrap;
            text-align: left;
        }

        #plot-div {
            width: 100%;
            height: 450px;
            border: 1px solid #ddd;
            border-radius: 4px;
        }
    </style>
</head>
<body>

    <div id="main-container">
        <h1>Unsteady-State Diffusion in a Slab</h1>
        <p>
            This demonstration visualizes the one-dimensional, unsteady-state diffusion of a solute into a slab of finite thickness. The concentration profile evolves over time according to Fick's second law. The initial condition is a linear concentration profile, and the boundary conditions maintain a constant surface concentration on both sides. Use the sliders below to adjust the elapsed time and the material's diffusivity to observe their effect on the concentration profile.
        </p>

        <div id="control-panel">
            <div class="control-row">
                <label for="time-slider">time (s)</label>
                <input type="range" id="time-slider" min="0" max="2500" step="1" value="100">
                <span id="time-value">100.0</span>
            </div>
            <div class="control-row">
                <label for="diffusivity-slider">diffusivity in m<sup>2</sup>/s</label>
                <input type="range" id="diffusivity-slider" min="1e-10" max="1e-8" step="1e-10" value="4e-9">
                <span id="diffusivity-value">4.0 &times; 10<sup>-9</sup></span>
            </div>
        </div>

        <div id="plot-div"></div>
    </div>

    <script>
        document.addEventListener('DOMContentLoaded', function() {
            // --- DOM Element References ---
            const timeSlider = document.getElementById('time-slider');
            const timeValue = document.getElementById('time-value');
            const diffusivitySlider = document.getElementById('diffusivity-slider');
            const diffusivityValue = document.getElementById('diffusivity-value');
            const plotDiv = document.getElementById('plot-div');

            // --- Physical and Simulation Constants ---
            const L = 0.004;      // Slab thickness in meters
            const c_s = 4.0e-3;   // Surface concentration
            const N_TERMS = 100;  // Number of terms in the series solution
            const N_POINTS = 201; // Number of points for plotting along x-axis

            // --- Plotly State ---
            let plotRevision = 0;

            // --- Helper Function for Scientific Notation ---
            function formatScientific(valueStr) {
                const value = parseFloat(valueStr);
                if (value === 0) return "0.0 &times; 10<sup>0</sup>";
                const [mantissa, exponent] = value.toExponential(1).split('e');
                return `${mantissa} &times; 10<sup>${exponent}</sup>`;
            }

            // --- Main Plotting Function ---
            function updatePlot() {
                const t = parseFloat(timeSlider.value);
                const D = parseFloat(diffusivitySlider.value);

                const x_values = Array.from({length: N_POINTS}, (_, i) => i * L / (N_POINTS - 1));
                let c_values;

                if (t === 0) {
                    // Case 1: Initial condition (t=0)
                    // c(x, 0) = (1.0 + 250 * x) * 1e-3
                    c_values = x_values.map(x => (1.0 + 250 * x) * 1e-3);
                } else {
                    // Case 2: Analytical solution for t > 0
                    c_values = x_values.map(x => {
                        let u_xt = 0;
                        for (let n = 0; n < N_TERMS; n++) {
                            const kn = ((n + 0.5) * Math.PI) / L;
                            const Bn_term1 = (250 * Math.pow(-1, n)) / (kn * kn);
                            const Bn_term2 = 3 / kn;
                            const Bn = (2 / L) * 1e-3 * (Bn_term1 - Bn_term2);

                            const term = Bn * Math.sin(kn * x) * Math.exp(-kn * kn * D * t);
                            u_xt += term;
                        }
                        // c(x, t) = u(x, t) + c_s
                        return u_xt + c_s;
                    });
                }

                const trace = {
                    x: x_values,
                    y: c_values,
                    mode: 'lines',
                    line: {
                        color: '#005A9C',
                        width: 2.5
                    }
                };

                const layout = {
                    xaxis: {
                        title: 'position',
                        range: [0, 0.004],
                        tickformat: '.3f',
                        dtick: 0.001,
                        showgrid: true,
                        minor: { showgrid: true }
                    },
                    yaxis: {
                        title: 'concentration',
                        range: [0.001, 0.0042],
                        tickformat: '.4f',
                        dtick: 0.0005,
                        showgrid: true,
                        minor: { showgrid: true }
                    },
                    margin: { l: 80, r: 20, t: 20, b: 50 },
                    datarevision: plotRevision++
                };

                Plotly.react(plotDiv, [trace], layout, {responsive: true});
            }

            // --- Event Listeners ---
            timeSlider.addEventListener('input', () => {
                timeValue.textContent = parseFloat(timeSlider.value).toFixed(1);
                updatePlot();
            });

            diffusivitySlider.addEventListener('input', () => {
                diffusivityValue.innerHTML = formatScientific(diffusivitySlider.value);
                updatePlot();
            });

            // --- Initializer ---
            function initialize() {
                // Set initial text values from slider defaults
                timeValue.textContent = parseFloat(timeSlider.value).toFixed(1);
                diffusivityValue.innerHTML = formatScientific(diffusivitySlider.value);

                // Generate the initial plot
                updatePlot();
            }

            initialize();
        });
    </script>

</body>
</html>
