            </div>

            <div id="slider-a-controls" class="control-group">
                <label for="slider-a">upper limit a</label>
                <input type="range" id="slider-a" min="0.1" max="8" step="0.01" value="5">
                <span id="value-a">5.00</span>
            </div>

            <div id="slider-n-controls" class="control-group">
                <label for="slider-n">number of quadrilaterals</label>
                <input type="range" id="slider-n" min="1" max="50" step="1" value="3">
                <span id="value-n">3</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        // --- CENTRAL STATE & FUNCTION DEFINITIONS ---
        let state = {
            funcKey: 'x',
            type: 'right',
            a: 5,
            n: 3
        };

        const functions = {
            'x': {
                f: x => x,
                F: x => 0.5 * x * x
            },
            'x-sq': {
                f: x => (x - 3) ** 2,
                F: x => ((x - 3) ** 3) / 3
            },
            'x-cub': {
                f: x => (x - 3) ** 3 + 27,
                F: x => ((x - 3) ** 4) / 4 + 27 * x
            },
            'sqrt-x': {
                f: x => Math.sqrt(Math.max(0, x)),
                F: x => (2 / 3) * Math.pow(Math.max(0, x), 1.5)
            },
            'sin-x': {
                f: x => Math.sin(x) + 1,
                F: x => -Math.cos(x) + x
            },
            'e-x': {
                f: x => Math.exp(x / 3),
                F: x => 3 * Math.exp(x / 3)
            }
        };

        // --- p5.js Sketch ---
        const sketch = (p) => {
            const canvasWidth = 880;
            const canvasHeight = 500;
            const padding = 50;

            // Logical coordinate system
            const xMinLogical = -1;
            const xMaxLogical = 8.5;
            let yMinLogical, yMaxLogical;

            // Mapping functions
            const mapX = x => p.map(x, xMinLogical, xMaxLogical, padding, canvasWidth - padding);
            const mapY = y => p.map(y, yMinLogical, yMaxLogical, canvasHeight - padding, padding);

            p.setup = () => {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.noLoop();
                setupEventListeners();
                updateActiveButtons();
                p.redraw();
            };

            p.draw = () => {
                const currentFunc = functions[state.funcKey].f;
                const currentIntegral = functions[state.funcKey].F;

                // 1. Clear and set background
                p.background(248, 248, 248);

                // 2. Establish scale (dynamic Y-axis)
                calculateYRange(currentFunc);

                // 3. Draw Axes
                drawAxes();

                // 4. Draw Function Curve
                drawFunctionCurve(currentFunc);

                // 5. Calculate and Draw Estimation Shapes
                let estimatedArea = drawEstimationShapes(currentFunc, currentIntegral);

                // 6. Draw Upper Limit Marker
                drawUpperLimitMarker();

                // 7. Display Calculations
                const trueIntegral = currentIntegral(state.a) - currentIntegral(0);
                displayCalculations(estimatedArea, trueIntegral);
            };

            function calculateYRange(func) {
                let minVal = func(xMinLogical);
                let maxVal = func(xMinLogical);
                for (let x = xMinLogical; x <= xMaxLogical; x += 0.01) {
                    const y = func(x);
                    if (y < minVal) minVal = y;
                    if (y > maxVal) maxVal = y;
                }
                const range = maxVal - minVal;
                yMinLogical = minVal - range * 0.1;
                yMaxLogical = maxVal + range * 0.1;
            }

            function drawAxes() {
                p.stroke(204, 204, 204); // #cccccc
                p.strokeWeight(1);
                p.fill(120);
                p.textSize(10);
                p.textAlign(p.CENTER, p.CENTER);

                // X-Axis
                p.line(mapX(xMinLogical), mapY(0), mapX(xMaxLogical), mapY(0));
                for (let i = Math.floor(xMinLogical); i <= Math.ceil(xMaxLogical); i++) {
                    p.line(mapX(i), mapY(-0.1), mapX(i), mapY(0.1));
                    if (i !== 0) p.text(i, mapX(i), mapY(0) + 15);
                }

                // Y-Axis
                p.line(mapX(0), mapY(yMinLogical), mapX(0), mapY(yMaxLogical));
                const yStep = (yMaxLogical - yMinLogical) / 10;
                for (let y = yMinLogical; y <= yMaxLogical; y+= yStep) {
                    const roundedY = Math.round(y);
                    p.line(mapX(-0.1), mapY(roundedY), mapX(0.1), mapY(roundedY));
                     if (roundedY !== 0) p.text(roundedY, mapX(0) - 20, mapY(roundedY));
                }
            }

            function drawFunctionCurve(func) {
                p.noFill();
                p.stroke(0);
                p.strokeWeight(2);
                p.beginShape();
                for (let x = xMinLogical; x <= xMaxLogical; x += 0.01) {
                    p.vertex(mapX(x), mapY(func(x)));
                }
                p.endShape();
            }

            function drawEstimationShapes(func, integralFunc) {
                let totalEstimatedArea = 0;
                const deltaX = state.a / state.n;

                p.strokeWeight(1);
                p.stroke(80, 80, 80, 200);

                for (let i = 0; i < state.n; i++) {
                    const x_i = i * deltaX;
                    const x_i1 = (i + 1) * deltaX;
                    let shapeArea = 0;

                    const trueSubIntegral = integralFunc(x_i1) - integralFunc(x_i);

                    // Determine shape vertices based on type
                    if (state.type === 'trapezoidal') {
                        const y_i = func(x_i);
                        const y_i1 = func(x_i1);
                        shapeArea = (y_i + y_i1) / 2 * deltaX;

                        p.fill(shapeArea > trueSubIntegral ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)');
                        p.quad(mapX(x_i), mapY(0), mapX(x_i), mapY(y_i), mapX(x_i1), mapY(y_i1), mapX(x_i1), mapY(0));
                    } else {
                        let h = 0;
                        if (state.type === 'left') {
                            h = func(x_i);
                        } else if (state.type === 'right') {
                            h = func(x_i1);
                        } else if (state.type === 'midpoint') {
                            h = func((x_i + x_i1) / 2);
                        } else if (state.type === 'overestimate' || state.type === 'underestimate') {
                            let extremeY = func(x_i);
                            // Sample within the interval to find min/max
                            for (let x_sub = x_i; x_sub <= x_i1; x_sub += deltaX / 20) {
                                const y_sub = func(x_sub);
                                if (state.type === 'overestimate' && y_sub > extremeY) {
                                    extremeY = y_sub;
                                } else if (state.type === 'underestimate' && y_sub < extremeY) {
                                    extremeY = y_sub;
                                }
                            }
                            h = extremeY;
                        }
                        shapeArea = h * deltaX;
                        p.fill(shapeArea > trueSubIntegral ? 'rgba(173, 216, 230, 0.7)' : 'rgba(144, 238, 144, 0.7)');
                        p.rect(mapX(x_i), mapY(h), mapX(x_i1) - mapX(x_i), mapY(0) - mapY(h));
                    }
                    totalEstimatedArea += shapeArea;
                }
                return totalEstimatedArea;
            }

            function drawUpperLimitMarker() {
                p.stroke(255, 0, 0, 180);
                p.strokeWeight(2);
                p.line(mapX(state.a), mapY(yMinLogical), mapX(state.a), mapY(yMaxLogical));
            }

            function displayCalculations(estimated, trueVal) {
                p.noStroke();
                p.fill(50);
                p.textSize(14);
                p.textAlign(p.CENTER, p.TOP);
                p.text(`estimated area: ${estimated.toFixed(5)}`, canvasWidth / 2, 10);
                p.text(`integral: ${trueVal.toFixed(5)}`, canvasWidth / 2, 30);
            }
        };

        // --- Event Listener Setup ---
        function setupEventListeners() {
            // Function Buttons
            document.querySelectorAll('.function-btn').forEach(button => {
                button.addEventListener('click', (e) => {
                    state.funcKey = e.target.dataset.function;
                    updateActiveButtons();
                    myP5.redraw();
                });
            });

            // Type Buttons
            document.querySelectorAll('.type-btn').forEach(button => {
                button.addEventListener('click', (e) => {
                    state.type = e.target.dataset.type;
                    updateActiveButtons();
                    myP5.redraw();
                });
            });

            // Slider 'a'
            const sliderA = document.getElementById('slider-a');
            const valueA = document.getElementById('value-a');
            sliderA.addEventListener('input', (e) => {
                state.a = parseFloat(e.target.value);
                valueA.textContent = state.a.toFixed(2);
                myP5.redraw();
            });

            // Slider 'n'
            const sliderN = document.getElementById('slider-n');
            const valueN = document.getElementById('value-n');
            sliderN.addEventListener('input', (e) => {
                state.n = parseInt(e.target.value, 10);
                valueN.textContent = state.n;
                myP5.redraw();
            });
        }

        function updateActiveButtons() {
            // Update function buttons
            document.querySelectorAll('.function-btn').forEach(btn => {
                btn.classList.toggle('active', btn.dataset.function === state.funcKey);
            });
            // Update type buttons
            document.querySelectorAll('.type-btn').forEach(btn => {
                btn.classList.toggle('active', btn.dataset.type === state.type);
            });
        }

        // Instantiate p5
        let myP5 = new p5(sketch);
    </script>
</body>
</html>
