<script>
    // --- SKETCH PARAMETERS ---
    let params = {
        current: 0.431,
        earthField: 0.0000194,
        radius: 0.5,
        turns: 25
    };

    const initialParams = { ...params };
    const mu0 = 4 * Math.PI * 1e-7; // Permeability of free space

    // --- DOM ELEMENTS ---
    let sliderCurrent, sliderEarthField, sliderRadius, sliderTurns;
    let valueCurrent, valueEarthField, valueRadius, valueTurns;
    let btnReset;

    // --- P5.JS SKETCH ---
    function sketch(p) {
        // --- VISUALIZATION CONSTANTS ---
        const worldBounds = { yMin: -0.5, yMax: 0.5, zMin: -0.5, zMax: 0.5 };
        const margin = 50;
        let canvasWidth, canvasHeight;

        // --- SETUP ---
        p.setup = function() {
            canvasWidth = document.getElementById('viz-container').offsetWidth;
            canvasHeight = canvasWidth * ((worldBounds.zMax - worldBounds.zMin) / (worldBounds.yMax - worldBounds.yMin));
            const canvas = p.createCanvas(canvasWidth, canvasHeight);
            canvas.parent('p5-canvas');
            p.colorMode(p.HSB, 360, 100, 100, 100);

            // Get DOM elements
            sliderCurrent = document.getElementById('slider-current');
            sliderEarthField = document.getElementById('slider-earth-field');
            sliderRadius = document.getElementById('slider-radius');
            sliderTurns = document.getElementById('slider-turns');

            valueCurrent = document.getElementById('value-current');
            valueEarthField = document.getElementById('value-earth-field');
            valueRadius = document.getElementById('value-radius');
            valueTurns = document.getElementById('value-turns');

            btnReset = document.getElementById('btn-reset');

            // Setup event listeners
            sliderCurrent.addEventListener('input', updateParamsAndRedraw);
            sliderEarthField.addEventListener('input', updateParamsAndRedraw);
            sliderRadius.addEventListener('input', updateParamsAndRedraw);
            sliderTurns.addEventListener('input', updateParamsAndRedraw);
            btnReset.addEventListener('click', resetParamsAndRedraw);

            updateUI();
            p.noLoop(); // We will redraw on demand
            p.redraw();
        };

        // --- DRAW ---
        p.draw = function() {
            p.background(255); // White background

            const R = params.radius;

            // --- 1. Calculate Field Magnitude Range for Color Mapping ---
            let minMag = Infinity, maxMag = -Infinity;
            const gridPoints = 30;
            for (let i = 0; i <= gridPoints; i++) {
                for (let j = 0; j <= gridPoints; j++) {
                    const y = p.map(i, 0, gridPoints, worldBounds.yMin, worldBounds.yMax);
                    const z = p.map(j, 0, gridPoints, worldBounds.zMin, worldBounds.zMax);
                    const B = calculateBTotal(y, z, params);
                    const mag = B.mag();
                    if (mag < minMag) minMag = mag;
                    if (mag > maxMag) maxMag = mag;
                }
            }
            if (maxMag === minMag) maxMag += 1e-12; // Avoid division by zero

            // --- 2. Draw Streamlines ---
            const streamGrid = 25;
            for (let i = 0; i <= streamGrid; i++) {
                for (let j = 0; j <= streamGrid; j++) {
                    const startY = p.map(i, 0, streamGrid, worldBounds.yMin, worldBounds.yMax);
                    const startZ = p.map(j, 0, streamGrid, worldBounds.zMin, worldBounds.zMax);
                    drawStreamline(p.createVector(startY, startZ), minMag, maxMag, 1); // Forward
                    drawStreamline(p.createVector(startY, startZ), minMag, maxMag, -1); // Backward
                }
            }

            // --- 3. Draw Axes ---
            drawAxes();
        };

        // --- INTERACTION HANDLERS ---
        function updateParamsAndRedraw() {
            params.current = parseFloat(sliderCurrent.value);
            params.earthField = parseFloat(sliderEarthField.value);
            params.radius = parseFloat(sliderRadius.value);
            params.turns = parseInt(sliderTurns.value, 10);
            updateUI();
            p.redraw();
        }

        function resetParamsAndRedraw() {
            sliderCurrent.value = initialParams.current;
            sliderEarthField.value = initialParams.earthField;
            sliderRadius.value = initialParams.radius;
            sliderTurns.value = initialParams.turns;
            updateParamsAndRedraw();
        }

        function updateUI() {
            valueCurrent.textContent = `${params.current.toFixed(3)} A`;
            valueEarthField.textContent = `${params.earthField.toExponential(1)} T`;
            valueRadius.textContent = `${params.radius.toFixed(2)} m`;
            valueTurns.textContent = `${params.turns}`;
        }

        // --- VISUALIZATION HELPERS ---

        function drawAxes() {
            p.push();
            p.stroke(255, 100, 100); // Red color for axes
            p.fill(255, 100, 100);
            p.strokeWeight(1);
            p.textSize(12);

            // Y-axis (horizontal)
            const yAxisZ = worldToScreen(0, 0).y;
            p.line(margin, yAxisZ, canvasWidth - margin, yAxisZ);
            p.text('Y (m)', canvasWidth - margin + 5, yAxisZ + 4);

            // Z-axis (vertical)
            const zAxisY = worldToScreen(0, 0).x;
            p.line(zAxisY, margin, zAxisY, canvasHeight - margin);
            p.text('Z (m)', zAxisY - 4, margin - 8);

            // Ticks
            const numTicks = 5;
            for (let i = -numTicks; i <= numTicks; i++) {
                const tickVal = i * 0.2;
                if (tickVal === 0) continue;

                // Y-ticks
                let screenPos = worldToScreen(tickVal, 0);
                p.line(screenPos.x, yAxisZ - 5, screenPos.x, yAxisZ + 5);
                p.textAlign(p.CENTER, p.TOP);
                p.text(tickVal.toFixed(1), screenPos.x, yAxisZ + 8);

                // Z-ticks
                screenPos = worldToScreen(0, tickVal);
                p.line(zAxisY - 5, screenPos.y, zAxisY + 5, screenPos.y);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(tickVal.toFixed(1), zAxisY - 8, screenPos.y);
            }
            p.pop();
        }

        function drawStreamline(startPos, minMag, maxMag, direction) {
            const numSteps = 100;
            const stepSize = 0.005;
            const arrowInterval = 20;

            let currentPos = startPos.copy();

            for (let i = 0; i < numSteps; i++) {
                if (currentPos.y < worldBounds.yMin || currentPos.y > worldBounds.yMax ||
                    currentPos.z < worldBounds.zMin || currentPos.z > worldBounds.zMax) {
                    break;
                }

                const B = calculateBTotal(currentPos.y, currentPos.z, params);
                const mag = B.mag();
                const fieldDir = B.normalize();

                const nextPos = p5.Vector.add(currentPos, p5.Vector.mult(fieldDir, direction * stepSize));

                const hue = p.map(mag, minMag, maxMag, 240, 0); // Blue (high) to Red (low), via purple
                p.stroke(hue, 90, 90, 80);
                p.strokeWeight(1.5);

                const screenStart = worldToScreen(currentPos.x, currentPos.y);
                const screenEnd = worldToScreen(nextPos.x, nextPos.y);
                p.line(screenStart.x, screenStart.y, screenEnd.x, screenEnd.y);

                // Draw arrow
                if (i % arrowInterval === 0 && i > 0) {
                    p.push();
                    p.stroke(hue, 90, 90, 100);
                    p.translate(screenEnd.x, screenEnd.y);
                    p.rotate(fieldDir.heading() + (direction < 0 ? p.PI : 0));
                    p.line(0, 0, -5, -3);
                    p.line(0, 0, -5, 3);
                    p.pop();
                }

                currentPos = nextPos;
            }
        }

        function worldToScreen(y, z) {
            const x_s = p.map(y, worldBounds.yMin, worldBounds.yMax, margin, canvasWidth - margin);
            const y_s = p.map(z, worldBounds.zMax, worldBounds.zMin, margin, canvasHeight - margin); // Z is inverted on screen
            return { x: x_s, y: y_s };
        }

        // --- MAGNETIC FIELD CALCULATION ---

        /**
         * Calculates B-field for a single loop of wire in the YZ plane, centered at z_offset.
         * @param {number} y - y-coordinate in meters
         * @param {number} z - z-coordinate in meters
         * @param {number} R - Radius of the loop
         * @param {number} I - Current
         * @param {number} N - Number of turns
         * @param {number} z_offset - z-position of the loop center
         * @returns {p5.Vector} Magnetic field vector (By, Bz)
         */
        function calculateBLoop(y, z, R, I, N, z_offset) {
            const zp = z - z_offset;

            // Handle y=0 case where formulas are singular but field is well-defined.
            if (Math.abs(y) < 1e-9) {
                const B_y = 0;
                const B_z = (mu0 * N * I * R * R) / (2 * Math.pow(R * R + zp * zp, 1.5));
                return p.createVector(B_y, B_z);
            }

            const y2 = y * y;
            const R2 = R * R;
            const zp2 = zp * zp;

            const m_num = 4 * R * y;
            const m_den = (R + y)**2 + zp2;
            const m = m_num / m_den;

            const K_m = ellipticK(m);
            const E_m = ellipticE(m);

            const common_factor_1 = (mu0 * I * N) / (2 * Math.PI);
            const common_factor_2 = 1 / Math.sqrt(m_den);

            const By_term1 = (R2 + y2 + zp2) / ((R - y)**2 + zp2);
            const B_y = common_factor_1 * (zp / y) * common_factor_2 * (By_term1 * E_m - K_m);

            const Bz_term1 = (R2 - y2 - zp2) / ((R - y)**2 + zp2);
            const B_z = common_factor_1 * common_factor_2 * (K_m - Bz_term1 * E_m);

            return p.createVector(B_y, B_z);
        }

        /**
         * Calculates the total magnetic field from the Helmholtz coils plus the Earth's field.
         */
        function calculateBTotal(y, z, currentParams) {
            const { current, earthField, radius, turns } = currentParams;

            const R = radius;
            const z_offset1 = -R / 2;
            const z_offset2 = R / 2;

            const B_coil1 = calculateBLoop(y, z, R, current, turns, z_offset1);
            const B_coil2 = calculateBLoop(y, z, R, current, turns, z_offset2);

            const B_coils = p5.Vector.add(B_coil1, B_coil2);

            const B_earth = p.createVector(0, earthField);

            return p5.Vector.add(B_coils, B_earth);
        }

        // --- ELLIPTIC INTEGRAL APPROXIMATIONS ---
        // Using algorithms from Abramowitz & Stegun

        function ellipticK(m) {
            if (m >= 1.0) return Infinity;
            if (m < 0) return NaN;
            let a = 1.0;
            let g = Math.sqrt(1.0 - m);
            for (let i = 0; i < 15; i++) {
                let a_next = (a + g) / 2.0;
                let g_next = Math.sqrt(a * g);
                a = a_next;
                g = g_next;
                if (Math.abs(a - g) < 1e-15) break;
            }
            return Math.PI / (2.0 * a);
        }

        function ellipticE(m) {
            if (m > 1.0 || m < 0) return NaN;
            if (m === 1.0) return 1.0;

            let a_vals = [1.0];
            let g_vals = [Math.sqrt(1.0 - m)];
            let i = 0;
            for(i = 0; i < 15; i++) {
                a_vals.push((a_vals[i] + g_vals[i]) / 2.0);
                g_vals.push(Math.sqrt(a_vals[i] * g_vals[i]));
                if (Math.abs(a_vals[i+1] - g_vals[i+1]) < 1e-15) break;
            }
            const M = a_vals[i+1];
            let sum_c2 = 0;
            let p2 = 0.5;
            for (let j = 1; j <= i + 1; j++) {
                const c = (a_vals[j-1] - g_vals[j-1]) / 2.0;
                sum_c2 += p2 * c * c;
                p2 *= 2.0;
            }
            return (Math.PI / (2 * M)) * (1.0 - sum_c2);
        }
    }

    // Instantiate p5.js in instance mode
    new p5(sketch);

</script>

</body>
</html>
