    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- STATE ---
            let currentMessage = "";
            let currentCiphertext = "";
            const MAX_LENGTH = 32;

            const WORD_LIST_UNSORTED = ['THE', 'IDES', 'OF', 'MARCH', 'ARE', 'COME', 'EVEN', 'YOU', 'BRUTUS', 'CAESAR', 'CIPHER', 'HELLO', 'WORLD', 'SECRET', 'MESSAGE', 'ATTACK', 'DAWN', 'I', 'A'];
            const WORD_LIST = WORD_LIST_UNSORTED.sort((a, b) => b.length - a.length);

            // --- DOM ELEMENTS ---
            const btnToggleEncoding = document.getElementById('btn-toggle-encoding');
            const btnToggleDecoding = document.getElementById('btn-toggle-decoding');
            const panelEncoding = document.getElementById('panel-encoding');
            const panelDecoding = document.getElementById('panel-decoding');

            // Encoding Panel Elements
            const selectMessage = document.getElementById('select-message');
            const btnClearEncoding = document.getElementById('btn-clear-encoding');
            const sliderEncodingOffset = document.getElementById('slider-encoding-offset');
            const spanEncodingOffsetValue = document.getElementById('span-encoding-offset-value');
            const keyboardEncoding = document.getElementById('keyboard-encoding');
            const displayMessage = document.getElementById('display-message');
            const displayPlaintext = document.getElementById('display-plaintext');
            const displayEncodingAlphabetFrom = document.getElementById('display-encoding-alphabet-from');
            const displayEncodingAlphabetTo = document.getElementById('display-encoding-alphabet-to');
            const displayCiphertext = document.getElementById('display-ciphertext');

            // Decoding Panel Elements
            const btnIntercept = document.getElementById('btn-intercept');
            const btnClearDecoding = document.getElementById('btn-clear-decoding');
            const sliderDecodingOffset = document.getElementById('slider-decoding-offset');
            const spanDecodingOffsetValue = document.getElementById('span-decoding-offset-value');
            const keyboardDecoding = document.getElementById('keyboard-decoding');
            const displayIntercept = document.getElementById('display-intercept');
            const displayDecodingAlphabetFrom = document.getElementById('display-decoding-alphabet-from');
            const displayDecodingAlphabetTo = document.getElementById('display-decoding-alphabet-to');
            const tableDecodingOutputBody = document.querySelector('#table-decoding-output tbody');

            // --- HELPER FUNCTIONS ---
            const ALPHABET = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

            const shiftChar = (char, offset) => {
                const charCode = char.charCodeAt(0);
                if (charCode < 65 || charCode > 90) return char; // Not an uppercase letter
                const newCode = (charCode - 65 + offset + 26) % 26 + 65;
                return String.fromCharCode(newCode);
            };

            const generateKeyboard = (container, includeSpace) => {
                ALPHABET.split('').forEach(char => {
                    const key = document.createElement('div');
                    key.className = 'key';
                    key.textContent = char;
                    key.dataset.key = char;
                    container.appendChild(key);
                });

                if (includeSpace) {
                    const spaceKey = document.createElement('div');
                    spaceKey.className = 'key space';
                    spaceKey.textContent = 'SPACE';
                    spaceKey.dataset.key = ' ';
                    container.appendChild(spaceKey);
                }

                const backspaceKey = document.createElement('div');
                backspaceKey.className = 'key backspace';
                backspaceKey.textContent = '⌫';
                backspaceKey.dataset.key = 'BACKSPACE';
                container.appendChild(backspaceKey);
            };

            // --- CORE LOGIC ---

            // ENCODING
            const updateEncoding = () => {
                const offset = parseInt(sliderEncodingOffset.value, 10);

                // 1. Update offset display
                spanEncodingOffsetValue.textContent = offset;

                // 2. Generate Plaintext
                const plaintext = currentMessage.toUpperCase().replace(/ /g, 'X');
                displayPlaintext.textContent = plaintext;

                // 3. Update Alphabet Map
                const shiftedAlphabet = ALPHABET.split('').map(char => shiftChar(char, offset)).join('');
                displayEncodingAlphabetFrom.textContent = ALPHABET.split('').join(' ');
                displayEncodingAlphabetTo.textContent = shiftedAlphabet.split('').join(' ');

                // 4. Generate Ciphertext
                currentCiphertext = plaintext.split('').map(char => shiftChar(char, offset)).join('');
                displayCiphertext.textContent = currentCiphertext;
            };

            // DECODING
            const updateDecoding = () => {
                const ciphertext = displayIntercept.textContent;
                const centerOffset = parseInt(sliderDecodingOffset.value, 10);

                // 1. Update offset display
                spanDecodingOffsetValue.textContent = centerOffset;

                // 2. Update Alphabet Map
                const fromAlphabet = ALPHABET.split('').map(char => shiftChar(char, -centerOffset)).join('');
                displayDecodingAlphabetFrom.textContent = fromAlphabet.split('').join(' ');
                displayDecodingAlphabetTo.textContent = ALPHABET.split('').join(' ');

                // 3. Generate Decoding Table
                tableDecodingOutputBody.innerHTML = ''; // Clear table
                const offsetsToShow = [centerOffset - 2, centerOffset - 1, centerOffset, centerOffset + 1, centerOffset + 2];

                offsetsToShow.forEach(d => {
                    const decryptedText = ciphertext.split('').map(char => shiftChar(char, -d)).join('');

                    // Word Highlighting
                    let highlightedHTML = '';
                    let i = 0;
                    while (i < decryptedText.length) {
                        let matchFound = false;
                        for (const word of WORD_LIST) {
                            if (decryptedText.substring(i).startsWith(word)) {
                                highlightedHTML += `<span class="highlighted">${word}</span>`;
                                i += word.length;
                                matchFound = true;
                                break;
                            }
                        }
                        if (!matchFound) {
                            highlightedHTML += decryptedText[i];
                            i++;
                        }
                    }

                    const row = document.createElement('tr');
                    row.innerHTML = `<td>${d}</td><td>${highlightedHTML}</td>`;
                    tableDecodingOutputBody.appendChild(row);
                });
            };

            // --- EVENT LISTENERS ---

            // Tab Controls
            btnToggleEncoding.addEventListener('click', () => {
                panelEncoding.style.display = 'block';
                panelDecoding.style.display = 'none';
                btnToggleEncoding.classList.add('active');
                btnToggleDecoding.classList.remove('active');
            });

            btnToggleDecoding.addEventListener('click', () => {
                panelEncoding.style.display = 'none';
                panelDecoding.style.display = 'block';
                btnToggleDecoding.classList.add('active');
                btnToggleEncoding.classList.remove('active');
            });

            // Encoding Panel Listeners
            selectMessage.addEventListener('change', (e) => {
                currentMessage = e.target.value;
                displayMessage.textContent = currentMessage;
                updateEncoding();
            });

            sliderEncodingOffset.addEventListener('input', updateEncoding);

            keyboardEncoding.addEventListener('click', (e) => {
                if (!e.target.classList.contains('key')) return;
                const key = e.target.dataset.key;
                if (key === 'BACKSPACE') {
                    currentMessage = currentMessage.slice(0, -1);
                } else if (currentMessage.length < MAX_LENGTH) {
                    currentMessage += key;
                }
                displayMessage.textContent = currentMessage;
                selectMessage.value = ""; // deselect dropdown
                updateEncoding();
            });

            btnClearEncoding.addEventListener('click', () => {
                currentMessage = "";
                currentCiphertext = "";
                sliderEncodingOffset.value = 0;
                selectMessage.value = "";
                displayMessage.textContent = "";
                displayPlaintext.textContent = "";
                displayCiphertext.textContent = "";
                updateEncoding();
            });

            // Decoding Panel Listeners
            btnIntercept.addEventListener('click', () => {
                displayIntercept.textContent = currentCiphertext;
                updateDecoding();
            });

            sliderDecodingOffset.addEventListener('input', updateDecoding);

            keyboardDecoding.addEventListener('click', (e) => {
                 if (!e.target.classList.contains('key')) return;
                const key = e.target.dataset.key;
                let currentText = displayIntercept.textContent;
                if (key === 'BACKSPACE') {
                    currentText = currentText.slice(0, -1);
                } else if (currentText.length < MAX_LENGTH) {
                    currentText += key;
                }
                displayIntercept.textContent = currentText;
                updateDecoding();
            });

            btnClearDecoding.addEventListener('click', () => {
                displayIntercept.textContent = "";
                sliderDecodingOffset.value = 0;
                tableDecodingOutputBody.innerHTML = '';
                updateDecoding();
            });

            // --- INITIALIZATION ---
            const init = () => {
                generateKeyboard(keyboardEncoding, true);
                generateKeyboard(keyboardDecoding, false);
                updateEncoding();
                updateDecoding();
            };

            init();
        });
    </script>
</body>
</html>
