    <script>
        // p5.js sketch logic
        const sketch = (s) => {
            // Control elements
            let sliderConvolver, sliderImageSize, inputLetter, radioShadow, radioBlur;

            // Off-screen graphics buffer for drawing the letter
            let pg; 
            const canvasSize = 200; // Size of one square area (before/after)
            const totalCanvasWidth = canvasSize * 2;

            s.setup = () => {
                s.createCanvas(totalCanvasWidth, canvasSize).parent('canvas-container');
                s.pixelDensity(1); // Ensure pixel array matches canvas dimensions
                s.noLoop(); // We only redraw on input changes

                // Get DOM elements
                sliderConvolver = document.getElementById('slider-convolver');
                sliderImageSize = document.getElementById('slider-image-size');
                inputLetter = document.getElementById('input-letter');
                radioShadow = document.getElementById('radio-shadow');
                radioBlur = document.getElementById('radio-blur');

                // --- Event Listeners ---
                const controls = [sliderConvolver, sliderImageSize, inputLetter, radioShadow, radioBlur];
                controls.forEach(control => control.addEventListener('input', () => s.redraw()));

                // Slider +/- buttons
                document.getElementById('convolver-plus-btn').addEventListener('click', () => {
                    sliderConvolver.value = Math.min(parseInt(sliderConvolver.max), parseInt(sliderConvolver.value) + 1);
                    sliderConvolver.dispatchEvent(new Event('input'));
                });
                document.getElementById('convolver-minus-btn').addEventListener('click', () => {
                    sliderConvolver.value = Math.max(parseInt(sliderConvolver.min), parseInt(sliderConvolver.value) - 1);
                    sliderConvolver.dispatchEvent(new Event('input'));
                });
                document.getElementById('image-size-plus-btn').addEventListener('click', () => {
                    sliderImageSize.value = Math.min(parseInt(sliderImageSize.max), parseInt(sliderImageSize.value) + 1);
                    sliderImageSize.dispatchEvent(new Event('input'));
                });
                document.getElementById('image-size-minus-btn').addEventListener('click', () => {
                    sliderImageSize.value = Math.max(parseInt(sliderImageSize.min), parseInt(sliderImageSize.value) - 1);
                    sliderImageSize.dispatchEvent(new Event('input'));
                });

                // Letter input validation and arrow keys
                inputLetter.addEventListener('input', () => {
                    if (inputLetter.value.length > 1) {
                        inputLetter.value = inputLetter.value.slice(0, 1);
                    }
                    if (inputLetter.value.length === 0) {
                        inputLetter.value = 'a'; // a default to prevent blank
                    }
                });

                inputLetter.addEventListener('keydown', (e) => {
                    if (e.key === 'ArrowUp' || e.key === 'ArrowDown') {
                        e.preventDefault();
                        let charCode = (inputLetter.value.length > 0) ? inputLetter.value.charCodeAt(0) : 'a'.charCodeAt(0);
                        if (e.key === 'ArrowUp') charCode++;
                        if (e.key === 'ArrowDown') charCode--;
                        inputLetter.value = String.fromCharCode(charCode);
                        inputLetter.dispatchEvent(new Event('input'));
                    }
                });

                // Initial draw
                s.redraw();
            };

            s.draw = function updateVisualization() {
                s.background(255);

                // 1. Read current values from controls
                const convolverK = parseInt(sliderConvolver.value);
                const imageN = parseInt(sliderImageSize.value);
                const letter = inputLetter.value;
                const filterType = document.querySelector('input[name="filter"]:checked').value;

                // --- 2. "Before" Image Data Generation ---
                if (!pg || pg.width !== imageN) {
                    pg = s.createGraphics(imageN, imageN);
                }
                pg.background(255); // White background
                pg.fill(0); // Black text
                pg.textAlign(s.CENTER, s.CENTER);
                pg.textSize(imageN * 0.85);
                pg.textFont('sans-serif');
                pg.text(letter, imageN / 2, imageN / 2 + imageN * 0.05); // Small offset for better centering

                const sourceImage = Array.from({ length: imageN }, () => Array(imageN));
                pg.loadPixels();
                for (let i = 0; i < imageN; i++) {
                    for (let j = 0; j < imageN; j++) {
                        const index = (i * imageN + j) * 4;
                        const r = pg.pixels[index];
                        sourceImage[i][j] = (r < 128) ? 1 : 0; // 1 for black, 0 for white
                    }
                }

                // --- Render "Before" Image ---
                s.noStroke();
                const pixelSize = canvasSize / imageN;
                for (let i = 0; i < imageN; i++) {
                    for (let j = 0; j < imageN; j++) {
                        s.fill(sourceImage[i][j] === 1 ? 0 : 255);
                        s.rect(j * pixelSize, i * pixelSize, pixelSize, pixelSize);
                    }
                }

                // --- 4. Convolution Kernel Generation ---
                const k = convolverK;
                const kernelDim = 2 * k + 1;
                const kernel = Array.from({ length: kernelDim }, () => Array(kernelDim).fill(0));

                if (filterType === 'blur') {
                    const norm = kernelDim * kernelDim;
                    for (let i = 0; i < kernelDim; i++) {
                        for (let j = 0; j < kernelDim; j++) {
                            kernel[i][j] = 1.0 / norm;
                        }
                    }
                } else if (filterType === 'shadow') {
                    if (k > 0) {
                        const norm = k * k;
                        for (let i = 0; i < k; i++) {
                            for (let j = 0; j < k; j++) {
                                const row = k + 1 + i;
                                const col = k + 1 + j;
                                if (row < kernelDim && col < kernelDim){
                                   kernel[row][col] = 1.0 / norm;
                                }
                            }
                        }
                    } else { // Handle k=1 (min value) case for shadow (becomes identity)
                         kernel[k][k] = 1.0;
                    }
                }

                // --- 5. Convolution Calculation ---
                const outputImage = Array.from({ length: imageN }, () => Array(imageN).fill(0));

                for (let y = 0; y < imageN; y++) {
                    for (let x = 0; x < imageN; x++) {
                        let sum = 0;
                        for (let ky = -k; ky <= k; ky++) {
                            for (let kx = -k; kx <= k; kx++) {
                                const srcY = y - ky;
                                const srcX = x - kx;

                                if (srcY >= 0 && srcY < imageN && srcX >= 0 && srcX < imageN) {
                                    const sourceVal = sourceImage[srcY][srcX];
                                    const kernelVal = kernel[ky + k][kx + k];
                                    sum += sourceVal * kernelVal;
                                }
                            }
                        }
                        outputImage[y][x] = sum;
                    }
                }

                // --- 6. "After" Image Rendering ---
                s.noStroke();
                for (let i = 0; i < imageN; i++) {
                    for (let j = 0; j < imageN; j++) {
                        const val = outputImage[i][j];
                        s.fill(s.lerp(255, 0, val)); // Map value (0-1) to grayscale (255-0)
                        s.rect(canvasSize + (j * pixelSize), i * pixelSize, pixelSize, pixelSize);
                    }
                }
            };
        };

        // Initialize the p5 sketch
        new p5(sketch);
    </script>
</body>
</html>
