    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        document.addEventListener('DOMContentLoaded', () => {
            // --- Global State & Constants ---
            const AMINO_ACIDS = {
                'A': 'ALA', 'C': 'CYS', 'D': 'ASP', 'E': 'GLU', 'F': 'PHE', 
                'G': 'GLY', 'H': 'HIS', 'I': 'ILE', 'K': 'LYS', 'L': 'LEU', 
                'M': 'MET', 'N': 'ASN', 'P': 'PRO', 'Q': 'GLN', 'R': 'ARG', 
                'S': 'SER', 'T': 'THR', 'V': 'VAL', 'W': 'TRP', 'Y': 'TYR'
            };
            const AMINO_ACID_KEYS = Object.keys(AMINO_ACIDS);
            const NUM_RESIDUES = 400;

            let state = {
                view: 'crystallography', // 'crystallography' or 'contact-map'
                proteinData: [],
                distanceMatrix: [],
                plotSize: 400,
                showLateralChains: true,
                grayLevel: 0.5,
                browseSequence: 1,
                residueX: 50,
                residueY: 52,
                rmsd: 4.9,
            };

            // --- Visualization Instances ---
            let three = { scene: null, camera: null, renderer: null, controls: null, container: null, atomMeshes: [] };
            let p5Instance = null;

            // --- DOM Elements ---
            const dom = {
                btnCrystallography: document.getElementById('btn-crystallography'),
                btnContactMap: document.getElementById('btn-contact-map'),
                checkboxLateralChains: document.getElementById('checkbox-lateral-chains'),
                sliderGrayLevel: document.getElementById('slider-gray-level'),
                grayLevelValue: document.getElementById('gray-level-value'),
                sliderBrowseSequence: document.getElementById('slider-browse-sequence'),
                browseSequenceValue: document.getElementById('browse-sequence-value'),
                selectResidue: document.getElementById('select-residue'),
                btnPrevResidueType: document.getElementById('btn-prev-residue-type'),
                btnNextResidueType: document.getElementById('btn-next-residue-type'),
                sliderResidueX: document.getElementById('slider-residue-x'),
                residueXValue: document.getElementById('residue-x-value'),
                sliderResidueY: document.getElementById('slider-residue-y'),
                residueYValue: document.getElementById('residue-y-value'),
                sliderRmsd: document.getElementById('slider-rmsd'),
                rmsdValue: document.getElementById('rmsd-value'),
                sliderPlotSize: document.getElementById('slider-plot-size'),
                plotSizeValue: document.getElementById('plot-size-value'),
                infoDisplay: document.getElementById('info-display'),
                btnReset: document.getElementById('btn-reset'),
                canvasContainer: document.getElementById('canvas-container'),
            };

            // --- Data Generation ---
            function generateProteinData(numResidues) {
                const residues = [];
                let currentPos = new THREE.Vector3(0, 0, 0);
                const stepSize = 3.8; // Approx distance between C-alpha atoms

                for (let i = 1; i <= numResidues; i++) {
                    // Random walk for C-alpha backbone
                    const randomVec = new THREE.Vector3(
                        Math.random() - 0.5,
                        Math.random() - 0.5,
                        Math.random() - 0.5
                    ).normalize().multiplyScalar(stepSize);
                    currentPos.add(randomVec);

                    const resTypeKey = AMINO_ACID_KEYS[Math.floor(Math.random() * AMINO_ACID_KEYS.length)];
                    const residue = {
                        resName: AMINO_ACIDS[resTypeKey],
                        resSeq: i,
                        atoms: []
                    };

                    // Add C-alpha
                    residue.atoms.push({ atomName: 'CA', x: currentPos.x, y: currentPos.y, z: currentPos.z });

                    // Add other atoms at fixed offsets from C-alpha for simplicity
                    residue.atoms.push({ atomName: 'N', x: currentPos.x + 1, y: currentPos.y + 0.5, z: currentPos.z - 0.5 });
                    residue.atoms.push({ atomName: 'C', x: currentPos.x - 1, y: currentPos.y - 0.5, z: currentPos.z + 0.5 });
                    residue.atoms.push({ atomName: 'O', x: currentPos.x - 2, y: currentPos.y - 0.2, z: currentPos.z + 0.2 });
                    residue.atoms.push({ atomName: 'CB', x: currentPos.x + 0.5, y: currentPos.y - 1.5, z: currentPos.z - 0.5 });

                    residues.push(residue);
                }
                return residues;
            }

            function precomputeDistanceMatrix() {
                const matrix = Array(NUM_RESIDUES).fill(0).map(() => Array(NUM_RESIDUES).fill(0));
                for (let i = 0; i < NUM_RESIDUES; i++) {
                    for (let j = i; j < NUM_RESIDUES; j++) {
                        const ca1 = state.proteinData[i].atoms.find(a => a.atomName === 'CA');
                        const ca2 = state.proteinData[j].atoms.find(a => a.atomName === 'CA');
                        if (ca1 && ca2) {
                            const dist = Math.sqrt(
                                Math.pow(ca1.x - ca2.x, 2) +
                                Math.pow(ca1.y - ca2.y, 2) +
                                Math.pow(ca1.z - ca2.z, 2)
                            );
                            matrix[i][j] = dist;
                            matrix[j][i] = dist;
                        }
                    }
                }
                state.distanceMatrix = matrix;
            }

            // --- 3D Visualization (three.js) ---
            function initThree() {
                three.container = dom.canvasContainer;
                const size = state.plotSize;

                three.scene = new THREE.Scene();
                three.scene.background = new THREE.Color(0xffffff);

                three.camera = new THREE.PerspectiveCamera(75, size / size, 0.1, 1000);
                three.camera.position.z = 150;

                three.renderer = new THREE.WebGLRenderer({ antialias: true });
                three.renderer.setSize(size, size);

                three.controls = new THREE.OrbitControls(three.camera, three.renderer.domElement);
                three.controls.enableDamping = true;
                three.controls.dampingFactor = 0.05;

                const ambientLight = new THREE.AmbientLight(0x404040, 2.0);
                three.scene.add(ambientLight);
                const directionalLight = new THREE.DirectionalLight(0xffffff, 1.5);
                directionalLight.position.set(1, 1, 1);
                three.scene.add(directionalLight);

                function animate() {
                    if (state.view !== 'crystallography') return;
                    requestAnimationFrame(animate);
                    three.controls.update();
                    three.renderer.render(three.scene, three.camera);
                }
                animate();
            }

            function drawCrystallography() {
                if (!three.scene) return;

                // Clear previous meshes
                three.atomMeshes.forEach(mesh => three.scene.remove(mesh));
                three.atomMeshes = [];

                const geometry = new THREE.SphereGeometry(0.5, 16, 16);
                const browseIdx = state.browseSequence - 1;
                const xIdx = state.residueX - 1;
                const yIdx = state.residueY - 1;

                const mainChainColor = new THREE.Color(0x0000ff).lerp(new THREE.Color(0x000000), 1 - state.grayLevel);

                state.proteinData.forEach((residue, resIndex) => {
                    residue.atoms.forEach(atom => {
                        if (!state.showLateralChains && atom.atomName !== 'CA') return;

                        let color = mainChainColor;
                        if (resIndex === browseIdx) {
                            color = 0xffff00; // Yellow for browse sequence
                        } else if (atom.atomName === 'CA' && (resIndex === xIdx || resIndex === yIdx)) {
                            color = 0xff0000; // Red for X and Y
                        }

                        const material = new THREE.MeshLambertMaterial({ color });
                        const sphere = new THREE.Mesh(geometry, material);
                        sphere.position.set(atom.x, atom.y, atom.z);
                        three.scene.add(sphere);
                        three.atomMeshes.push(sphere);
                    });
                });

                // Add a bounding box
                const box = new THREE.Box3().setFromObject(three.scene);
                const boxHelper = new THREE.BoxHelper(three.scene, 0x888888);
                three.scene.add(boxHelper);
                three.atomMeshes.push(boxHelper);
            }

            // --- 2D Visualization (p5.js) ---
            function contactMapSketch(p) {
                p.setup = function() {
                    p.createCanvas(state.plotSize, state.plotSize);
                    p.noLoop(); // Only draw when explicitly told to
                };

                p.draw = function() {
                    p.background(255);
                    const size = state.plotSize;
                    const margin = 40;
                    const plotArea = size - margin;

                    // Draw axes and labels
                    p.stroke(200);
                    p.strokeWeight(1);
                    p.line(margin, 0, margin, size);
                    p.line(0, size - margin, size, size - margin);
                    p.line(size, 0, size, size - margin); // Right axis
                    p.line(margin, 0, size, 0); // Top axis

                    p.fill(100);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    for (let i = 0; i <= NUM_RESIDUES; i += 100) {
                        const tick = i === 0 ? 1 : i;
                        const x = p.map(tick, 1, NUM_RESIDUES, margin, size);
                        const y = p.map(tick, 1, NUM_RESIDUES, 0, size - margin);

                        // Bottom labels
                        p.text(tick, x, size - margin / 2);
                        // Top labels
                        p.text(tick, x, margin / 2);
                        // Left labels
                        p.text(tick, margin / 2, y);
                         // Right labels
                        p.text(tick, size - margin / 2, y);
                    }

                    // Draw contact points
                    p.stroke(0);
                    p.strokeWeight(2);
                    for (let i = 0; i < NUM_RESIDUES; i++) {
                        for (let j = 0; j < NUM_RESIDUES; j++) {
                            if (state.distanceMatrix[i][j] <= state.rmsd) {
                                const x = p.map(i + 1, 1, NUM_RESIDUES, margin, size);
                                const y = p.map(j + 1, 1, NUM_RESIDUES, 0, size - margin);
                                p.point(x, y);
                            }
                        }
                    }

                    // Draw selection lines
                    p.stroke(255, 0, 0);
                    p.strokeWeight(1.5);
                    const xLine = p.map(state.residueX, 1, NUM_RESIDUES, margin, size);
                    const yLine = p.map(state.residueY, 1, NUM_RESIDUES, 0, size-margin);
                    p.line(xLine, 0, xLine, size - margin); // Vertical line for X
                    p.line(margin, yLine, size, yLine); // Horizontal line for Y
                };
            }

            function drawContactMap() {
                if(p5Instance) {
                    p5Instance.redraw();
                }
            }

            // --- View Management ---
            function switchView(view) {
                state.view = view;
                dom.canvasContainer.innerHTML = ''; // Clear container

                if (view === 'crystallography') {
                    dom.btnCrystallography.classList.add('active');
                    dom.btnContactMap.classList.remove('active');
                    if(p5Instance) {
                        p5Instance.remove();
                        p5Instance = null;
                    }
                    dom.canvasContainer.appendChild(three.renderer.domElement);
                    three.controls.enabled = true;
                    resizePlots(state.plotSize);
                    drawCrystallography();
                    // Restart animation loop
                    function animate() {
                        if (state.view !== 'crystallography') return;
                        requestAnimationFrame(animate);
                        three.controls.update();
                        three.renderer.render(three.scene, three.camera);
                    }
                    animate();

                } else if (view === 'contact-map') {
                    dom.btnContactMap.classList.add('active');
                    dom.btnCrystallography.classList.remove('active');
                    three.controls.enabled = false;
                    p5Instance = new p5(contactMapSketch, dom.canvasContainer);
                    resizePlots(state.plotSize);
                }
            }

            // --- UI Update & Event Handlers ---
            function updateSliderValue(slider, span) {
                span.textContent = slider.value;
            }

            function updateInfoDisplay() {
                const resA = state.proteinData[state.browseSequence - 1];
                const resX = state.proteinData[state.residueX - 1];
                const resY = state.proteinData[state.residueY - 1];
                dom.infoDisplay.textContent = `Res A: ${resA.resName}${resA.resSeq} | Res X: ${resX.resName}${resX.resSeq} | Res Y: ${resY.resName}${resY.resSeq}`;
            }

            function resizePlots(size) {
                state.plotSize = size;
                dom.canvasContainer.style.width = `${size}px`;
                dom.canvasContainer.style.height = `${size}px`;

                if (state.view === 'crystallography' && three.renderer) {
                    three.camera.aspect = size / size;
                    three.camera.updateProjectionMatrix();
                    three.renderer.setSize(size, size);
                    drawCrystallography();
                } else if (state.view === 'contact-map' && p5Instance) {
                    p5Instance.resizeCanvas(size, size);
                    drawContactMap();
                }
            }

            function setupEventListeners() {
                // View Switcher
                dom.btnCrystallography.addEventListener('click', () => switchView('crystallography'));
                dom.btnContactMap.addEventListener('click', () => switchView('contact-map'));

                // Controls
                dom.checkboxLateralChains.addEventListener('change', (e) => {
                    state.showLateralChains = e.target.checked;
                    if (state.view === 'crystallography') drawCrystallography();
                });

                dom.sliderGrayLevel.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.grayLevelValue);
                    state.grayLevel = parseFloat(e.target.value);
                    if (state.view === 'crystallography') drawCrystallography();
                });

                dom.sliderBrowseSequence.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.browseSequenceValue);
                    state.browseSequence = parseInt(e.target.value);
                    updateInfoDisplay();
                    if (state.view === 'crystallography') drawCrystallography();
                });

                dom.sliderResidueX.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.residueXValue);
                    state.residueX = parseInt(e.target.value);
                    updateInfoDisplay();
                    if (state.view === 'crystallography') drawCrystallography();
                    else if (state.view === 'contact-map') drawContactMap();
                });

                dom.sliderResidueY.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.residueYValue);
                    state.residueY = parseInt(e.target.value);
                    updateInfoDisplay();
                    if (state.view === 'crystallography') drawCrystallography();
                    else if (state.view === 'contact-map') drawContactMap();
                });

                dom.sliderRmsd.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.rmsdValue);
                    state.rmsd = parseFloat(e.target.value);
                    if (state.view === 'contact-map') drawContactMap();
                });

                dom.sliderPlotSize.addEventListener('input', (e) => {
                    updateSliderValue(e.target, dom.plotSizeValue);
                    resizePlots(parseInt(e.target.value));
                });

                dom.btnReset.addEventListener('click', () => {
                    if (three.controls) three.controls.reset();
                });

                function findResidueType(direction) {
                    const selectedType = AMINO_ACIDS[dom.selectResidue.value];
                    let currentIndex = state.browseSequence - 1;
                    const initialIndex = currentIndex;

                    for(let i=0; i<NUM_RESIDUES; i++) {
                        currentIndex = (currentIndex + direction + NUM_RESIDUES) % NUM_RESIDUES;
                        if(state.proteinData[currentIndex].resName === selectedType) {
                            dom.sliderBrowseSequence.value = currentIndex + 1;
                            // Manually trigger input event
                            dom.sliderBrowseSequence.dispatchEvent(new Event('input'));
                            return;
                        }
                    }
                    // If not found (e.g., protein has none of this type), do nothing.
                }

                dom.btnNextResidueType.addEventListener('click', () => findResidueType(1));
                dom.btnPrevResidueType.addEventListener('click', () => findResidueType(-1));
            }

            // --- Initialization ---
            function init() {
                // Generate Data
                state.proteinData = generateProteinData(NUM_RESIDUES);
                precomputeDistanceMatrix();

                // Populate UI
                AMINO_ACID_KEYS.forEach(key => {
                    const option = document.createElement('option');
                    option.value = key;
                    option.textContent = `${AMINO_ACIDS[key]} (${key})`;
                    dom.selectResidue.appendChild(option);
                });

                // Set initial slider values and text
                const sliders = [
                    { slider: dom.sliderGrayLevel, span: dom.grayLevelValue },
                    { slider: dom.sliderBrowseSequence, span: dom.browseSequenceValue },
                    { slider: dom.sliderResidueX, span: dom.residueXValue },
                    { slider: dom.sliderResidueY, span: dom.residueYValue },
                    { slider: dom.sliderRmsd, span: dom.rmsdValue },
                    { slider: dom.sliderPlotSize, span: dom.plotSizeValue },
                ];
                sliders.forEach(item => updateSliderValue(item.slider, item.span));

                // Set initial state from default HTML values
                state.plotSize = parseInt(dom.sliderPlotSize.value);
                state.showLateralChains = dom.checkboxLateralChains.checked;
                state.grayLevel = parseFloat(dom.sliderGrayLevel.value);
                state.browseSequence = parseInt(dom.sliderBrowseSequence.value);
                state.residueX = parseInt(dom.sliderResidueX.value);
                state.residueY = parseInt(dom.sliderResidueY.value);
                state.rmsd = parseFloat(dom.sliderRmsd.value);

                // Initialize visualizations
                initThree();

                // Set up interactions
                setupEventListeners();

                // Final initial setup
                updateInfoDisplay();
                switchView(state.view);
            }

            init();
        });
    </script>
</body>
</html>
