    <script>
        // DOM Elements
        const infoToggle = document.getElementById('info-toggle');
        const infoContent = document.getElementById('info-content');
        const checkboxMode = document.getElementById('checkbox-mode');
        const checkboxDisplay = document.getElementById('checkbox-display');
        const inputA = document.getElementById('input-a');
        const inputB = document.getElementById('input-b');
        const sliderC = document.getElementById('slider-c');
        const displayC = document.getElementById('display-c');
        const equationText = document.getElementById('equation-text');

        // State variables
        let a, b, c;
        let isDotsMode, isHighestMode;

        // GCD helper function
        const gcd = (x, y) => {
            while(y) {
                let t = y;
                y = x % y;
                x = t;
            }
            return x;
        };

        // p5.js sketch
        let sketch = function(p) {
            const canvasWidth = 550;
            const canvasHeight = 550;
            const margin = 50;
            let solvableSet = new Set();

            p.setup = function() {
                let canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                p.noLoop(); // Redraw only on interaction
                attachEventListeners();
                updateStateAndRedraw();
            };

            function updateStateAndRedraw() {
                a = parseInt(inputA.value);
                b = parseInt(inputB.value);
                c = parseInt(sliderC.value);
                isDotsMode = checkboxMode.checked;
                isHighestMode = checkboxDisplay.checked;

                displayC.textContent = c;

                sliderC.disabled = isHighestMode;
                displayC.style.opacity = isHighestMode ? 0.5 : 1;

                // Update slider max value
                const newMax = a * b * 2;
                if (parseInt(sliderC.max) !== newMax) {
                    sliderC.max = newMax;
                }

                p.redraw();
            }

            function attachEventListeners() {
                infoToggle.addEventListener('click', () => {
                    const isHidden = infoContent.style.display === 'none' || infoContent.style.display === '';
                    infoContent.style.display = isHidden ? 'block' : 'none';
                    infoToggle.textContent = isHidden ? '-' : '+';
                });

                [inputA, inputB, sliderC, checkboxMode, checkboxDisplay].forEach(el => {
                    el.addEventListener('input', updateStateAndRedraw);
                });
            }

            p.draw = function() {
                p.background(255);

                if (gcd(a, b) !== 1) {
                    equationText.textContent = "Numbers a and b should be coprime!";
                    equationText.style.color = "#D0021B";
                    return;
                }
                equationText.style.color = "#4A90E2"; // Reset color

                // Set up coordinate system (origin at bottom-left, y-axis up)
                p.translate(margin, canvasHeight - margin);
                p.scale(1, -1);

                const drawWidth = canvasWidth - 2 * margin;
                const drawHeight = canvasHeight - 2 * margin;

                drawAxes(drawWidth, drawHeight);

                // Determine effective C value
                const frobeniusNumber = a * b - a - b;
                const currentC = isHighestMode ? frobeniusNumber : c;

                // Pre-calculate solvable numbers for 'numbers' mode with highest nonsolvable display
                if (!isDotsMode && isHighestMode) {
                    solvableSet.clear();
                    for (let x = 0; x <= b; x++) {
                        for (let y = 0; y <= a; y++) {
                            solvableSet.add(a * x + b * y);
                        }
                    }
                }

                if (isDotsMode) {
                    drawDotsMode(drawWidth, drawHeight, currentC);
                } else {
                    drawNumbersMode(drawWidth, drawHeight, currentC);
                }

                // Update text
                if (isHighestMode) {
                    equationText.textContent = `Highest nonsolvable: a*b - a - b = ${frobeniusNumber}`;
                } else {
                    equationText.textContent = `Current equation: ${a}x + ${b}y = ${currentC}`;
                }
            };

            function drawAxes(w, h) {
                p.stroke(0);
                p.strokeWeight(1);
                // X and Y axes
                p.line(0, 0, w, 0);
                p.line(0, 0, 0, h);

                p.push();
                p.scale(1, -1); // Flip back for text
                p.textAlign(p.CENTER, p.TOP);
                p.fill(0, 0, 200); // Blue for labels
                p.noStroke();

                // X-axis labels
                for (let i = 0; i <= b; i++) {
                    let xPos = p.map(i, 0, b, 0, w);
                    p.text(i, xPos, 5);
                }

                // Y-axis labels
                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= a; i++) {
                    let yPos = p.map(i, 0, a, 0, h);
                    p.text(i, -5, -yPos);
                }
                p.pop();
            }

            function drawDotsMode(w, h, currentC) {
                // Draw all integer points
                p.stroke(0);
                p.strokeWeight(1);
                p.fill(0);
                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        let px = p.map(x, 0, b, 0, w);
                        let py = p.map(y, 0, a, 0, h);
                        p.ellipse(px, py, 4, 4);
                    }
                }

                // Draw line ax + by = c
                p.stroke(255, 0, 0); // Red line
                p.strokeWeight(2);
                const x_intercept = currentC / a;
                const y_intercept = currentC / b;
                const px1 = p.map(x_intercept, 0, b, 0, w);
                const py2 = p.map(y_intercept, 0, a, 0, h);
                p.line(px1, 0, 0, py2);

                // Draw solution points
                p.noStroke();
                p.fill(0, 200, 0); // Green solutions
                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        if (a * x + b * y === currentC) {
                            let px = p.map(x, 0, b, 0, w);
                            let py = p.map(y, 0, a, 0, h);
                            p.ellipse(px, py, 10, 10);
                        }
                    }
                }
            }

            function drawNumbersMode(w, h, currentC) {
                p.push();
                p.scale(1, -1); // Flip for text
                p.textAlign(p.CENTER, p.CENTER);
                p.noStroke();

                for (let x = 0; x <= b; x++) {
                    for (let y = 0; y <= a; y++) {
                        const val = a * x + b * y;
                        const px = p.map(x, 0, b, 0, w);
                        const py = p.map(y, 0, a, 0, h);

                        let isHighlighted = false;
                        if (isHighestMode) {
                            if (!solvableSet.has(val) && val <= currentC) {
                                isHighlighted = true;
                                p.fill(255, 0, 0); // Red for unsolvable
                            }
                        } else {
                            if (val === currentC) {
                                isHighlighted = true;
                                p.fill(0, 180, 0); // Green for current C
                            }
                        }

                        if (!isHighlighted) {
                            p.fill(150); // Default color for numbers
                        }

                        p.text(val, px, -py);
                    }
                }
                p.pop();
            }
        };

        new p5(sketch);
    </script>

</body>
</html>
