                    currentQuizData.forEach(item => {
                        if (!item.found) {
                            const { x, y } = mapToPixels(item.lon, item.lat);
                            p.circle(x, y, 10);
                        }
                    });
                }

                // Draw hints
                if (showHintsCheck.checked) {
                    p.fill(255); // White text
                    p.stroke(0); // Black outline
                    p.strokeWeight(2);
                    p.textSize(14);
                    p.textAlign(p.LEFT, p.CENTER);
                    hintedItems.forEach(item => {
                        if (!item.found) {
                           const { x, y } = mapToPixels(item.lon, item.lat);
                           p.text(item.name, x + 10, y);
                        }
                    });
                }

                // Draw cursor indicator
                if (p.mouseX > 0 && p.mouseX < p.width && p.mouseY > 0 && p.mouseY < p.height) {
                    p.noStroke();
                    p.fill(173, 216, 230, 150); // Semi-transparent light blue
                    p.circle(p.mouseX, p.mouseY, findRadius * 2);
                }
            };

            p.mouseMoved = () => {
                if (p.mouseX > 0 && p.mouseX < p.width && p.mouseY > 0 && p.mouseY < p.height) {
                    const { lon, lat } = mapToGeo(p.mouseX, p.mouseY);
                    posDisplay.textContent = `Current position {${lon.toFixed(0)}, ${lat.toFixed(0)}}`;
                }
            };

            p.mouseClicked = () => {
                if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) {
                    return; // Click was outside canvas
                }

                for (let i = 0; i < currentQuizData.length; i++) {
                    const item = currentQuizData[i];
                    if (!item.found) {
                        const { x, y } = mapToPixels(item.lon, item.lat);
                        const distance = p.dist(p.mouseX, p.mouseY, x, y);
                        if (distance < findRadius) {
                            item.found = true;
                            updateTableOnFound(i);
                            // Only find one item per click
                            break; 
                        }
                    }
                }
            };
        };

        // --- Quiz Control Logic ---

        function populateTable() {
            const table = document.getElementById('table-quiz');
            table.innerHTML = ''; // Clear previous table

            const thead = table.createTHead();
            const tbody = table.createTBody();
            const nameRow = thead.insertRow();
            const statusRow = tbody.insertRow();

            currentQuizData.forEach((item, index) => {
                const th = document.createElement('th');
                th.textContent = item.name;
                nameRow.appendChild(th);

                const td = statusRow.insertCell();
                td.textContent = 'False';
                td.dataset.index = index; // To easily find this cell later
            });
        }

        function resetQuiz() {
            currentQuizData.forEach(item => item.found = false);

            const cells = document.querySelectorAll('#table-quiz td');
            cells.forEach(cell => {
                cell.textContent = 'False';
                cell.classList.remove('found');
            });

            generateHints();
        }

        function switchQuizType(type) {
            const data = (type === 'rivers') ? RIVERS_DATA : LAKES_DATA;
            // Create a deep copy to not modify original data arrays
            currentQuizData = JSON.parse(JSON.stringify(data));
            populateTable();
            resetQuiz();
        }

        function updateTableOnFound(index) {
            const cell = document.querySelector(`#table-quiz td[data-index='${index}']`);
            if (cell) {
                cell.textContent = 'True';
                cell.classList.add('found');
            }
        }

        function generateHints() {
            const unfound = currentQuizData.filter(item => !item.found);
            // Shuffle the unfound items array
            for (let i = unfound.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [unfound[i], unfound[j]] = [unfound[j], unfound[i]];
            }
            hintedItems = unfound.slice(0, 3);
        }

        // --- Event Listeners Initialization ---

        document.addEventListener('DOMContentLoaded', () => {
            const radioRivers = document.getElementById('radio-rivers');
            const radioLakes = document.getElementById('radio-lakes');
            const btnReset = document.getElementById('btn-reset');
            const showHintsCheck = document.getElementById('check-show-some');

            radioRivers.addEventListener('change', () => switchQuizType('rivers'));
            radioLakes.addEventListener('change', () => switchQuizType('lakes'));
            btnReset.addEventListener('click', resetQuiz);

            // Re-generate hints when checkbox is toggled
            showHintsCheck.addEventListener('change', () => {
                if(showHintsCheck.checked){
                    generateHints();
                }
            });

            // Initial setup
            switchQuizType('rivers'); 
            new p5(quizSketch, 'canvas-container');
        });
    </script>
</body>
</html>
