    <script>
        // --- State Variables ---
        let currentTier = 5;
        let showBinary = false;
        let showNodeChars = true;

        // --- Data ---
        const nodes = [
            { id: 'I',  label: 'I', freq: 5, tier: 1, pos: {x: 100, y: 450}, children: [] },
            { id: 'S',  label: 'S', freq: 4, tier: 1, pos: {x: 180, y: 450}, children: [] },
            { id: 'P',  label: 'P', freq: 2, tier: 1, pos: {x: 260, y: 450}, children: [] },
            { id: 'R',  label: 'R', freq: 2, tier: 1, pos: {x: 340, y: 450}, children: [] },
            { id: 'M',  label: 'M', freq: 1, tier: 1, pos: {x: 420, y: 450}, children: [] },
            { id: 'V',  label: 'V', freq: 1, tier: 1, pos: {x: 500, y: 450}, children: [] },
            { id: 'E',  label: 'E', freq: 1, tier: 1, pos: {x: 580, y: 450}, children: [] },
            { id: '_',  label: '_', freq: 1, tier: 1, pos: {x: 660, y: 450}, children: [] },
            { id: 'IS', label: 'IS', freq: 9, tier: 2, pos: {x: 140, y: 350}, children: ['I', 'S'] },
            { id: 'PR', label: 'PR', freq: 4, tier: 2, pos: {x: 300, y: 350}, children: ['P', 'R'] },
            { id: 'MV', label: 'MV', freq: 2, tier: 2, pos: {x: 460, y: 350}, children: ['M', 'V'] },
            { id: 'E_', label: 'E_', freq: 2, tier: 2, pos: {x: 620, y: 350}, children: ['E', '_'] },
            { id: 'MVE_', label: 'MVE_', freq: 4, tier: 3, pos: {x: 540, y: 250}, children: ['MV', 'E_'] },
            { id: 'PRMVE_', label: 'PRMVE_', freq: 8, tier: 4, pos: {x: 420, y: 150}, children: ['PR', 'MVE_'] },
            { id: 'ROOT', label: 'ISPRMVE_', freq: 17, tier: 5, pos: {x: 280, y: 50}, children: ['IS', 'PRMVE_'] }
        ];
        const nodeMap = nodes.reduce((acc, node) => ({...acc, [node.id]: node}), {});

        const explanations = {
            1: "After determining the frequency of each letter, we draw the tree bottom up, one tier at a time.",
            2: "We continue grouping until we get to the highest tier.",
            3: "We continue grouping until we get to the highest tier.",
            4: "We continue grouping until we get to the highest tier.",
            5: "Once at the top, the total frequency becomes 17, which is the total number of characters."
        };

        // --- DOM Elements ---
        const explanationTextEl = document.getElementById('explanation-text');
        const encodingTableEl = document.getElementById('encoding-table');
        const encodedTextEl = document.getElementById('encoded-text');
        const tierButtons = [
            document.getElementById('btn-tier-1'),
            document.getElementById('btn-tier-2'),
            document.getElementById('btn-tier-3'),
            document.getElementById('btn-tier-4'),
            document.getElementById('btn-tier-5')
        ];
        const binaryCheckbox = document.getElementById('checkbox-binary');
        const radioCharsYes = document.getElementById('radio-chars-yes');
        const radioCharsNo = document.getElementById('radio-chars-no');

        // --- UI Update Logic ---
        function updateUI() {
            // Update explanation text
            explanationTextEl.textContent = explanations[currentTier];

            // Update tier button active state
            tierButtons.forEach((btn, index) => {
                if (index + 1 === currentTier) {
                    btn.classList.add('active');
                } else {
                    btn.classList.remove('active');
                }
            });

            // Update visibility of table and encoded string
            encodingTableEl.classList.toggle('hidden', currentTier < 2);
            encodedTextEl.classList.toggle('hidden', currentTier < 5);
        }

        // --- p5.js Sketch ---
        let sketch = function(p) {

            p.setup = function() {
                const canvasContainer = document.getElementById('canvas-area');
                p.createCanvas(800, 500).parent(canvasContainer);
                p.noLoop(); // We only redraw on state change

                // --- Initial Setup and Event Listeners ---

                // Tier buttons
                tierButtons.forEach((btn, index) => {
                    btn.addEventListener('click', () => {
                        currentTier = index + 1;
                        updateUI();
                        p.redraw();
                    });
                });

                // Binary checkbox
                binaryCheckbox.addEventListener('change', (e) => {
                    showBinary = e.target.checked;
                    p.redraw();
                });

                // Characters radio buttons
                radioCharsYes.addEventListener('change', () => {
                    if (radioCharsYes.checked) {
                        showNodeChars = true;
                        p.redraw();
                    }
                });
                radioCharsNo.addEventListener('change', () => {
                    if (radioCharsNo.checked) {
                        showNodeChars = false;
                        p.redraw();
                    }
                });

                // Populate static content
                encodedTextEl.textContent = '1100000101000101001001000011111010011011110101';
                encodingTableEl.innerHTML = `
                    <h3>Encoding Table</h3>
                    <p>I = 00</p>
                    <p>S = 01</p>
                    <p>P = 100</p>
                    <p>R = 101</p>
                    <p>M = 1100</p>
                    <p>V = 1101</p>
                    <p>E = 1110</p>
                    <p>_ = 1111</p>
                `;

                // Set initial UI state and draw
                updateUI();
                p.redraw();
            };

            p.draw = function() {
                p.background(255);

                // Draw Edges
                nodes.forEach(parentNode => {
                    if (parentNode.children.length > 0) {
                        const leftChild = nodeMap[parentNode.children[0]];
                        const rightChild = nodeMap[parentNode.children[1]];

                        if (parentNode.tier <= currentTier) {
                            // Draw left branch
                            if (leftChild && leftChild.tier <= currentTier) {
                                p.stroke('#C70000');
                                p.strokeWeight(2);
                                p.line(parentNode.pos.x, parentNode.pos.y, leftChild.pos.x, leftChild.pos.y);
                                if (showBinary) {
                                    p.noStroke();
                                    p.fill(0);
                                    p.textAlign(p.CENTER, p.CENTER);
                                    p.text('0', (parentNode.pos.x + leftChild.pos.x) / 2 - 10, (parentNode.pos.y + leftChild.pos.y) / 2);
                                }
                            }
                            // Draw right branch
                            if (rightChild && rightChild.tier <= currentTier) {
                                p.stroke('#C70000');
                                p.strokeWeight(2);
                                p.line(parentNode.pos.x, parentNode.pos.y, rightChild.pos.x, rightChild.pos.y);
                                if (showBinary) {
                                    p.noStroke();
                                    p.fill(0);
                                    p.textAlign(p.CENTER, p.CENTER);
                                    p.text('1', (parentNode.pos.x + rightChild.pos.x) / 2 + 10, (parentNode.pos.y + rightChild.pos.y) / 2);
                                }
                            }
                        }
                    }
                });

                // Draw Nodes
                nodes.forEach(node => {
                    if (node.tier <= currentTier) {
                        let label = '';
                        const isLeaf = node.children.length === 0;

                        if (isLeaf) {
                            label = `${node.label}(${node.freq})`;
                        } else {
                            if (showNodeChars) {
                                label = `${node.label}(${node.freq})`;
                            } else {
                                label = `(${node.freq})`;
                            }
                        }

                        p.push();
                        p.translate(node.pos.x, node.pos.y);
                        p.textAlign(p.CENTER, p.CENTER);
                        p.textSize(14);

                        const textPadding = 15;
                        const rectWidth = p.textWidth(label) + textPadding;
                        const rectHeight = 30;

                        p.stroke('#C70000');
                        p.strokeWeight(1.5);
                        p.fill('#FEFDD5');
                        p.rectMode(p.CENTER);
                        p.rect(0, 0, rectWidth, rectHeight, 5);

                        p.noStroke();
                        p.fill(0);
                        p.text(label, 0, 0);
                        p.pop();
                    }
                });
            };
        };

        new p5(sketch);

    </script>
