<body>

    <div id="control-panel">
        <label for="slider-n">n:</label>
        <input type="range" id="slider-n" min="1" max="20" step="1" value="1">
        <input type="number" id="input-n" min="1" max="20" step="1" value="1">
    </div>

    <div id="visualization-area">
        <div id="function-plot-container">
            <div id="canvas-function"></div>
            <div id="formula-label">
                $$y = \frac{2}{x}$$
            </div>
        </div>
        <div id="sum-plot-container">
            <div id="canvas-sum"></div>
        </div>
    </div>

    <script>
        // Global state and shared resources
        let n = 1;
        const colors = [
            '#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2', 
            '#c71585', '#ff1493', '#ff69b4', '#ffb6c1', '#ffa07a', '#ff7f50', '#ff4500', '#ffd700', 
            '#f0e68c', '#dda0dd', '#da70d6', '#ba55d3'
        ];

        // p5.js sketches
        let p5Func, p5Sum;

        // p5 sketch for the function plot (left)
        const functionPlotSketch = (p) => {
            const margin = { top: 20, right: 20, bottom: 40, left: 50 };
            const xRange = { min: 0, max: 10.5 };
            const yRange = { min: 0, max: 2.2 };
            let canvasWidth, canvasHeight;

            p.setup = () => {
                const container = document.getElementById('canvas-function');
                canvasWidth = container.clientWidth;
                canvasHeight = container.clientHeight;
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop(); // Redraw only on demand
            };

            p.draw = () => {
                p.background(255);

                // Helper functions for coordinate mapping
                const mapX = (val) => p.map(val, xRange.min, xRange.max, margin.left, canvasWidth - margin.right);
                const mapY = (val) => p.map(val, yRange.min, yRange.max, canvasHeight - margin.bottom, margin.top);

                drawAxes();
                drawRectangles();
                drawFunctionCurve();
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1);
                // X Axis
                p.line(mapX(xRange.min), mapY(0), mapX(xRange.max), mapY(0));
                // Y Axis
                p.line(mapX(0), mapY(yRange.min), mapX(0), mapY(yRange.max));

                // Ticks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.noStroke();
                p.fill(0);
                for (let i = 1; i <= 10; i++) {
                    p.text(i, mapX(i), mapY(0) + 5);
                    p.stroke(200);
                    p.line(mapX(i), mapY(0), mapX(i), mapY(0) - 5);
                    p.noStroke();
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 1; i <= 2; i++) {
                    p.text(i, mapX(0) - 5, mapY(i));
                     p.stroke(200);
                    p.line(mapX(0), mapY(i), mapX(0) + 5, mapY(i));
                    p.noStroke();
                }
            }

            function drawFunctionCurve() {
                p.noFill();
                p.stroke(0, 0, 255); // Blue
                p.strokeWeight(2);
                p.beginShape();
                for (let x = 0.5; x <= xRange.max; x += 0.1) {
                    p.vertex(mapX(x), mapY(2 / x));
                }
                p.endShape();
            }

            function drawRectangles() {
                for (let k = 1; k <= n; k++) {
                    const x1 = k;
                    const x2 = k + 1;
                    const y1 = 2 / (k + 1);
                    const y2 = 2 / k;

                    const rectX = mapX(x1);
                    const rectY = mapY(y2);
                    const rectW = mapX(x2) - mapX(x1);
                    const rectH = mapY(y1) - mapY(y2);

                    p.fill(colors[(k - 1) % colors.length]);
                    p.stroke(100);
                    p.strokeWeight(0.5);
                    p.rect(rectX, rectY, rectW, rectH);

                    if (k <= 3) {
                        drawDashedLine(mapX(k), mapY(0), mapY(2 / k));
                    }
                }
            }

            function drawDashedLine(x, y1, y2) {
                p.stroke(150); // Gray
                p.strokeWeight(1);
                const dashLen = 5;
                const gap = 3;
                for (let y = y1; y > y2; y -= (dashLen + gap)) {
                    p.line(x, y, x, p.max(y - dashLen, y2));
                }
            }

            p.windowResized = () => {
                const container = document.getElementById('canvas-function');
                canvasWidth = container.clientWidth;
                canvasHeight = container.clientHeight;
                p.resizeCanvas(canvasWidth, canvasHeight);
            };
        };

        // p5 sketch for the summation plot (right)
        const sumPlotSketch = (p) => {
            const margin = { top: 20, right: 20, bottom: 40, left: 50 };
            const xRange = { min: 0, max: 1.5 };
            const yRange = { min: 0, max: 2.2 };
            let canvasWidth, canvasHeight;

            p.setup = () => {
                const container = document.getElementById('canvas-sum');
                canvasWidth = container.clientWidth;
                canvasHeight = container.clientHeight;
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                const mapX = (val) => p.map(val, xRange.min, xRange.max, margin.left, canvasWidth - margin.right);
                const mapY = (val) => p.map(val, yRange.min, yRange.max, canvasHeight - margin.bottom, margin.top);

                drawAxes();
                drawStackedBar(mapX, mapY);
            };

            function drawAxes() {
                p.stroke(0);
                p.strokeWeight(1);
                // X Axis
                p.line(mapX(xRange.min), mapY(0), mapX(xRange.max), mapY(0));
                // Y Axis
                p.line(mapX(0), mapY(yRange.min), mapX(0), mapY(yRange.max));

                // Ticks and labels
                p.textAlign(p.CENTER, p.TOP);
                p.noStroke();
                p.fill(0);
                p.text("0", mapX(0), mapY(0) + 5);
                p.text("1", mapX(1), mapY(0) + 5);

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= 2; i++) {
                    p.text(i, mapX(0) - 5, mapY(i));
                    p.stroke(200);
                    p.line(mapX(0), mapY(i), mapX(0) + 5, mapY(i));
                    p.noStroke();
                }
            }

            function drawStackedBar(mapX, mapY) {
                let yStack = 0;

                // Loop backwards to stack from bottom to top (k=1 on top)
                for (let k = n; k >= 1; k--) {
                    const h = (2 / k) - (2 / (k + 1));

                    const rectX = mapX(0);
                    const rectY = mapY(yStack + h);
                    const rectW = mapX(1) - mapX(0);
                    const rectH = mapY(yStack) - mapY(yStack + h);

                    p.fill(colors[(k - 1) % colors.length]);
                    p.stroke(100);
                    p.strokeWeight(0.5);
                    p.rect(rectX, rectY, rectW, rectH);

                    yStack += h;
                }
            }

            p.windowResized = () => {
                const container = document.getElementById('canvas-sum');
                canvasWidth = container.clientWidth;
                canvasHeight = container.clientHeight;
                p.resizeCanvas(canvasWidth, canvasHeight);
            };
        };

        // --- Event Listeners and Initialization ---
        document.addEventListener('DOMContentLoaded', () => {
            const slider = document.getElementById('slider-n');
            const input = document.getElementById('input-n');

            function updateValue(newValue) {
                n = parseInt(newValue, 10);
                slider.value = n;
                input.value = n;

                if (p5Func && p5Sum) {
                    p5Func.redraw();
                    p5Sum.redraw();
                }
            }

            slider.addEventListener('input', (e) => {
                updateValue(e.target.value);
            });

            input.addEventListener('input', (e) => {
                let val = parseInt(e.target.value, 10);
                if (isNaN(val) || val < 1) val = 1;
                if (val > 20) val = 20;
                updateValue(val);
            });

            // Instantiate p5 sketches
            p5Func = new p5(functionPlotSketch, 'canvas-function');
            p5Sum = new p5(sumPlotSketch, 'canvas-sum');
        });

        window.addEventListener('resize', () => {
            if(p5Func) p5Func.windowResized();
            if(p5Sum) p5Sum.windowResized();
        });

    </script>
</body>
</html>
