                    <label for="slider-umax">&mu;<sub>max</sub> <span id="value-umax" class="value-display"></span></label>
                    <input type="range" id="slider-umax" min="0.1" max="2" step="0.01" value="0.6">
                </div>
                <div class="slider-container">
                    <label for="slider-km">k<sub>m</sub> <span id="value-km" class="value-display"></span></label>
                    <input type="range" id="slider-km" min="0.01" max="1" step="0.01" value="0.12">
                </div>
                <div class="slider-container hidden" id="k1-container">
                    <label for="slider-k1">k<sub>1</sub> <span id="value-k1" class="value-display"></span></label>
                    <input type="range" id="slider-k1" min="0.1" max="2" step="0.0001" value="0.4545">
