                    <label for="slider-ds">D<sub>s</sub> <span id="value-ds" class="value-display"></span></label>
                    <input type="range" id="slider-ds" min="0.01" max="1.0" step="0.01" value="0.6">
                </div>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

<script>
document.addEventListener('DOMContentLoaded', function() {

    // --- STATE AND PARAMETERS ---
    let currentModel = 'monod';
    let currentView = 'bifurcation';
    let params = {
        x2f: 4.0,
        umax: 0.6,
        km: 0.12,
        k1: 0.4545,
        ds: 0.6
    };

    // --- DOM ELEMENT REFERENCES ---
    const controls = {
        btnMonod: document.getElementById('btn-monod'),
        btnSi: document.getElementById('btn-si'),
        btnBifurcation: document.getElementById('btn-bifurcation'),
        btnNontrivial: document.getElementById('btn-nontrivial'),

        sliderX2f: document.getElementById('slider-x2f'),
        sliderUmax: document.getElementById('slider-umax'),
        sliderKm: document.getElementById('slider-km'),
        sliderK1: document.getElementById('slider-k1'),
        sliderDs: document.getElementById('slider-ds'),

        valueX2f: document.getElementById('value-x2f'),
        valueUmax: document.getElementById('value-umax'),
        valueKm: document.getElementById('value-km'),
        valueK1: document.getElementById('value-k1'),
        valueDs: document.getElementById('value-ds'),

        k1Container: document.getElementById('k1-container'),
        plotContainer: document.getElementById('plot-container')
    };

    // --- MATH HELPER FUNCTIONS ---
    const D_monod = (x2) => params.umax * x2 / (params.km + x2);
    const D_si = (x2) => params.umax * x2 / (params.km + x2 + params.k1 * x2 * x2);
    const mu_si = D_si; // Same formula

    // --- INITIALIZATION ---
    function init() {
        // Set initial slider values and displays
        controls.sliderX2f.value = params.x2f;
        controls.valueX2f.textContent = params.x2f.toFixed(1);

        controls.sliderUmax.value = params.umax;
        controls.valueUmax.textContent = params.umax.toFixed(2);

        controls.sliderKm.value = params.km;
        controls.valueKm.textContent = params.km.toFixed(2);

        controls.sliderK1.value = params.k1;
        controls.valueK1.textContent = params.k1.toFixed(4);

        controls.sliderDs.value = params.ds;
        controls.valueDs.textContent = params.ds.toFixed(2);

        // Add event listeners
        controls.btnMonod.addEventListener('click', () => setModel('monod'));
        controls.btnSi.addEventListener('click', () => setModel('si'));

        controls.btnBifurcation.addEventListener('click', () => setView('bifurcation'));
        controls.btnNontrivial.addEventListener('click', () => setView('nontrivial'));

        controls.sliderX2f.addEventListener('input', (e) => updateParam('x2f', e.target.value, 1));
        controls.sliderUmax.addEventListener('input', (e) => updateParam('umax', e.target.value, 2));
        controls.sliderKm.addEventListener('input', (e) => updateParam('km', e.target.value, 2));
        controls.sliderK1.addEventListener('input', (e) => updateParam('k1', e.target.value, 4));
        controls.sliderDs.addEventListener('input', (e) => updateParam('ds', e.target.value, 2));

        updatePlot();
    }

    // --- STATE UPDATE FUNCTIONS ---
    function setModel(model) {
        if (currentModel === model) return;
        currentModel = model;

        controls.btnMonod.classList.toggle('active', model === 'monod');
        controls.btnSi.classList.toggle('active', model === 'si');
        controls.k1Container.classList.toggle('hidden', model === 'monod');

        updatePlot();
    }

    function setView(view) {
        if (currentView === view) return;
        currentView = view;

        controls.btnBifurcation.classList.toggle('active', view === 'bifurcation');
        controls.btnNontrivial.classList.toggle('active', view === 'nontrivial');

        updatePlot();
    }

    function updateParam(name, value, precision) {
        params[name] = parseFloat(value);
        controls['value' + name.charAt(0).toUpperCase() + name.slice(1)].textContent = params[name].toFixed(precision);
        updatePlot();
    }

    // --- PLOTTING FUNCTIONS ---
    function updatePlot() {
        if (currentView === 'bifurcation') {
            drawBifurcationDiagram();
        } else {
            drawNontrivialStatePlot();
        }
    }

    function drawBifurcationDiagram() {
        const traces = [];
        const n_points = 500;
        const x2_max_plot = Math.max(params.x2f, 6);
        const D_max_plot = 1.0;
        let x2_vals = Array.from({length: n_points}, (_, i) => (i / (n_points - 1)) * x2_max_plot);

        // 1. Nontrivial steady state curve(s)
        if (currentModel === 'monod') {
            let d_vals = x2_vals.map(x2 => D_monod(x2));
            traces.push({
                x: d_vals.filter(d => d <= D_max_plot),
                y: x2_vals.filter((_, i) => d_vals[i] <= D_max_plot),
                mode: 'lines',
                name: 'Nontrivial SS (Stable)',
                line: { color: 'blue', width: 2.5 }
            });
        } else { // Substrate Inhibition
            const x2_crit = Math.sqrt(params.km / params.k1);
            let stable_x = [], stable_y = [], unstable_x = [], unstable_y = [];
            x2_vals.forEach(x2 => {
                const d = D_si(x2);
                if (d <= D_max_plot) {
                    if (x2 < x2_crit) {
                        stable_x.push(d);
                        stable_y.push(x2);
                    } else {
                        unstable_x.push(d);
                        unstable_y.push(x2);
                    }
                }
            });
            traces.push({ x: stable_x, y: stable_y, mode: 'lines', name: 'Nontrivial SS (Stable)', line: { color: 'blue', width: 2.5 }});
            traces.push({ x: unstable_x, y: unstable_y, mode: 'lines', name: 'Nontrivial SS (Unstable)', line: { color: 'red', dash: 'dash', width: 2.5 }});
        }

        // 2. Trivial steady state line
        const d_bif = currentModel === 'monod' ? D_monod(params.x2f) : D_si(params.x2f);
        traces.push({ x: [0, d_bif], y: [params.x2f, params.x2f], mode: 'lines', name: 'Trivial SS (Unstable)', line: { color: 'red', dash: 'dash', width: 2.5 } });
        traces.push({ x: [d_bif, D_max_plot], y: [params.x2f, params.x2f], mode: 'lines', name: 'Trivial SS (Stable)', line: { color: 'blue', width: 2.5 } });

        // 3. Intersection points
        const intersection_points = { x: [], y: [], marker: { color: [], size: 12, symbol: 'circle' }, mode: 'markers', type: 'scatter', name: 'Operating Points' };

        // Add trivial intersection point
        intersection_points.x.push(params.ds);
        intersection_points.y.push(params.x2f);
        intersection_points.marker.color.push('green');

        // Calculate and add nontrivial intersection points
        let roots = [];
        if (params.ds < params.umax) {
            if (currentModel === 'monod') {
                const root = params.ds * params.km / (params.umax - params.ds);
                if (root > 0) roots.push(root);
            } else { // Substrate Inhibition
                const a = params.ds * params.k1;
                const b = params.ds - params.umax;
                const c = params.ds * params.km;
                const delta = b * b - 4 * a * c;
                if (delta >= 0) {
                    const r1 = (-b + Math.sqrt(delta)) / (2 * a);
                    const r2 = (-b - Math.sqrt(delta)) / (2 * a);
                    if (r1 > 0) roots.push(r1);
                    if (r2 > 0) roots.push(r2);
                }
            }
        }

        const x2_crit = currentModel === 'si' ? Math.sqrt(params.km / params.k1) : Infinity;
        roots.forEach(root => {
            if (root < params.x2f) {
                intersection_points.x.push(params.ds);
                intersection_points.y.push(root);
                // Monod is always stable, SI stability depends on x2_crit
                const is_stable = (currentModel === 'monod' || root < x2_crit);
                intersection_points.marker.color.push(is_stable ? 'magenta' : 'cyan');
            }
        });
        traces.push(intersection_points);

        const layout = {
            title: 'Bifurcation Diagram',
            xaxis: { title: 'Dilution Rate, D', range: [0, D_max_plot] },
            yaxis: { title: 'Substrate Concentration, x<sub>2</sub>', range: [0, x2_max_plot] },
            showlegend: false,
            margin: { l: 60, r: 20, b: 50, t: 50 },
            shapes: [{
                type: 'line',
                x0: params.ds, x1: params.ds,
                y0: 0, y1: x2_max_plot,
                line: { color: 'grey', width: 1.5, dash: 'dot' }
            }]
        };

        Plotly.newPlot(controls.plotContainer, traces, layout);
    }

    function drawNontrivialStatePlot() {
        const traces = [];
        const x2_max_plot = 7.0;
        const mu_max_plot = Math.max(params.umax * 1.1, 1.0);
        const n_points = 500;
        let x2_vals = Array.from({length: n_points}, (_, i) => (i / (n_points - 1)) * x2_max_plot);

        // This view is primarily for the SI model
        const mu_vals = currentModel === 'monod' ? x2_vals.map(x2 => D_monod(x2)) : x2_vals.map(x2 => mu_si(x2));

        traces.push({
            x: x2_vals,
            y: mu_vals,
            mode: 'lines',
            name: '&mu;(x<sub>2</sub>)',
            line: { color: 'blue', width: 2.5 }
        });

        const shapes = [];
        // Horizontal line for Ds
        shapes.push({
            type: 'line',
            x0: 0, x1: x2_max_plot,
            y0: params.ds, y1: params.ds,
            line: { color: 'grey', width: 2 }
        });

        // Bistability region for SI model
        if (currentModel === 'si') {
            const x2_peak = Math.sqrt(params.km / params.k1);
            const d_upper = mu_si(x2_peak);
            const d_lower = mu_si(params.x2f);

            // Shaded region
            if (d_lower < d_upper) {
                shapes.push({
                    type: 'rect',
                    x0: 0, x1: x2_max_plot,
                    y0: d_lower, y1: d_upper,
                    fillcolor: 'rgba(255, 165, 0, 0.2)',
                    line: { width: 0 }
                });
            }

            // Boundary lines
            shapes.push({ type: 'line', x0: 0, x1: x2_max_plot, y0: d_upper, y1: d_upper, line: { color: 'red', dash: 'dash' } });
            shapes.push({ type: 'line', x0: 0, x1: x2_max_plot, y0: d_lower, y1: d_lower, line: { color: 'red', dash: 'dash' } });
        }

        // Intersection points
        // (Calculations are identical to the bifurcation diagram)
        const intersection_points = { x: [], y: [], marker: { color: [], size: 12, symbol: 'circle' }, mode: 'markers', type: 'scatter', name: 'Operating Points' };
        let roots = [];
        if (params.ds < params.umax) {
            if (currentModel === 'monod') {
                const root = params.ds * params.km / (params.umax - params.ds);
                if (root > 0) roots.push(root);
            } else { // SI
                const a = params.ds * params.k1;
                const b = params.ds - params.umax;
                const c = params.ds * params.km;
                const delta = b * b - 4 * a * c;
                if (delta >= 0) {
                    const r1 = (-b + Math.sqrt(delta)) / (2 * a);
                    const r2 = (-b - Math.sqrt(delta)) / (2 * a);
                    if (r1 > 0) roots.push(r1);
                    if (r2 > 0) roots.push(r2);
                }
            }
        }

        const x2_crit = currentModel === 'si' ? Math.sqrt(params.km / params.k1) : Infinity;
        roots.forEach(root => {
            if (root < x2_max_plot) {
                 intersection_points.x.push(root);
                 intersection_points.y.push(params.ds);
                 const is_stable = (currentModel === 'monod' || root < x2_crit);
                 intersection_points.marker.color.push(is_stable ? 'magenta' : 'cyan');
            }
        });
        traces.push(intersection_points);

        const layout = {
            title: 'Nontrivial Steady State Analysis',
            xaxis: { title: 'Substrate Concentration, x<sub>2</sub>', range: [0, x2_max_plot] },
            yaxis: { title: 'Specific Growth Rate Coefficient, &mu;', range: [0, mu_max_plot] },
            showlegend: false,
            margin: { l: 80, r: 20, b: 50, t: 50 },
            shapes: shapes
        };

        Plotly.newPlot(controls.plotContainer, traces, layout);
    }

    // --- START THE APP ---
    init();
});
</script>

</body>
</html>
