    <script src="https://cdn.jsdelivr.net/npm/three@0.149.0/examples/js/controls/OrbitControls.js"></script>
</head>
<body>

    <div id="app-container">
        <h1>Solar Energy Incident on Earth's Surface</h1>

        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-season">season</label>
                <input type="range" id="slider-season" min="0" max="1" step="0.001" value="0">
                <span id="value-season"></span>
            </div>
            <div class="slider-group">
                <label for="slider-latitude">latitude</label>
                <input type="range" id="slider-latitude" min="0" max="90" step="0.01" value="51.4789">
                <span id="value-latitude"></span>
            </div>
            <div class="slider-group">
                <label for="slider-time">time of day</label>
                <input type="range" id="slider-time" min="0" max="24" step="0.01" value="12">
                <span id="value-time"></span>
            </div>
        </div>

        <div id="main-content">
            <div id="globe-container"></div>
            <div id="plots-container">
                <div id="plot-instant"></div>
                <div id="plot-legend">
                    <div class="legend-item">
                        <div class="legend-line" style="background-color: #e41a1c;"></div>
                        <span>Equator</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line" style="background-color: #377eb8;"></div>
                        <span>Selected Latitude</span>
                    </div>
                    <div class="legend-item">
                        <div class="legend-line" style="background-color: #4daf4a;"></div>
                        <span>North Pole</span>
                    </div>
                </div>
                <div id="plot-cumulative"></div>
            </div>
        </div>
    </div>

    <script>
        // --- UTILITY FUNCTIONS ---
        const degToRad = (deg) => deg * Math.PI / 180;
        const radToDeg = (rad) => rad * 180 / Math.PI;

        // --- GLOBAL VARIABLES ---
        // Sliders and value displays
        const sliderSeason = document.getElementById('slider-season');
        const valueSeason = document.getElementById('value-season');
        const sliderLatitude = document.getElementById('slider-latitude');
        const valueLatitude = document.getElementById('value-latitude');
        const sliderTime = document.getElementById('slider-time');
        const valueTime = document.getElementById('value-time');

        // Three.js components
        let scene, camera, renderer, controls;
        let earthMesh, sunLight, polarAxis, surfaceNormal, sunVector, latLine;
        const EARTH_RADIUS = 5;

        // --- INITIALIZATION ---
        function init() {
            init3D();
            initPlots();
            setupEventListeners();
            update();
            animate();
        }

        function createEarthTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 1024;
            canvas.height = 512;
            const context = canvas.getContext('2d');

            // Oceans
            context.fillStyle = '#1b2f4f'; // Darker blue
            context.fillRect(0, 0, canvas.width, canvas.height);

            // Continents (simplified shapes)
            context.fillStyle = '#4a6d2f'; // Muted green

            // Americas
            context.fillRect(100, 100, 150, 300);
            context.fillRect(200, 220, 100, 100);

            // Africa & Europe
            context.fillRect(450, 150, 150, 200);
            context.fillRect(480, 100, 100, 50);

            // Asia & Australia
            context.fillRect(650, 100, 250, 200);
            context.fillRect(750, 350, 100, 100);

            return new THREE.CanvasTexture(canvas);
        }

        function init3D() {
            const container = document.getElementById('globe-container');

            // Scene
            scene = new THREE.Scene();

            // Camera
            camera = new THREE.PerspectiveCamera(75, container.clientWidth / container.clientHeight, 0.1, 1000);
            camera.position.z = 12;

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);
            container.appendChild(renderer.domElement);

            // Lights
            scene.add(new THREE.AmbientLight(0x404040, 0.5)); // Soft ambient light
            sunLight = new THREE.DirectionalLight(0xffffff, 1.5);
            scene.add(sunLight);
            scene.add(sunLight.target);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(EARTH_RADIUS, 64, 32);
            const earthMaterial = new THREE.MeshPhongMaterial({ 
                map: createEarthTexture(),
                shininess: 5
            });
            earthMesh = new THREE.Mesh(earthGeometry, earthMaterial);
            scene.add(earthMesh);

            // Graticule (wireframe)
            const wireframe = new THREE.WireframeGeometry(earthGeometry);
            const line = new THREE.LineSegments(wireframe);
            line.material.color.set(0xffffff);
            line.material.opacity = 0.1;
            line.material.transparent = true;
            earthMesh.add(line);

            // Polar Axis
            polarAxis = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0), 
                new THREE.Vector3(0, -EARTH_RADIUS * 1.2, 0),
                EARTH_RADIUS * 2.4, 0x000000, 0.5, 0.2
            );
            scene.add(polarAxis);

            // Equator Line
            const equatorGeom = new THREE.BufferGeometry().setFromPoints(
                new THREE.Path().absarc(0, 0, EARTH_RADIUS * 1.005, 0, Math.PI * 2, false).getSpacedPoints(128)
            );
            const equatorMat = new THREE.LineBasicMaterial({ color: 0xffffff });
            const equatorLine = new THREE.Line(equatorGeom, equatorMat);
            equatorLine.rotation.x = Math.PI / 2;
            earthMesh.add(equatorLine);


            // Latitude Line (will be updated)
            const latLineMaterial = new THREE.LineDashedMaterial({ 
                color: 0x377eb8, // Blue to match plot
                linewidth: 2, 
                dashSize: 0.1, 
                gapSize: 0.1 
            });
            const latLineGeom = new THREE.BufferGeometry().setFromPoints(new Array(129).fill(new THREE.Vector3()));
            latLine = new THREE.Line(latLineGeom, latLineMaterial);
            earthMesh.add(latLine);

            // Surface Normal Vector
            surfaceNormal = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0), 
                new THREE.Vector3(0, 0, 0),
                EARTH_RADIUS * 0.3, 0x000000, 0.2, 0.1
            );
            earthMesh.add(surfaceNormal);

            // Sun Vector
            sunVector = new THREE.ArrowHelper(
                new THREE.Vector3(1, 0, 0),
                new THREE.Vector3(0, 0, 0),
                EARTH_RADIUS * 0.5, 0xe41a1c, 0.2, 0.1 // Red to match plot
            );
            earthMesh.add(sunVector);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Handle resize
            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            const container = document.getElementById('globe-container');
            if (!container) return;
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        function initPlots() {
            const plotLayoutInstant = {
                title: 'Instantaneous Solar Energy',
                xaxis: { title: 'Time of Day (hr)', range: [0, 24] },
                yaxis: { title: 'Energy (watts/m²)', range: [0, 1400] },
                margin: { l: 60, r: 30, b: 50, t: 50 },
                showlegend: false
            };

            const plotLayoutCumulative = {
                title: 'Cumulative Daily Energy',
                xaxis: { title: 'Time of Day (hr)', range: [0, 24] },
                yaxis: { title: 'Total Energy (Wh/m²)', range: [0, 14000] },
                margin: { l: 60, r: 30, b: 50, t: 50 },
                showlegend: false
            };

            const initialTraces = [
                { x: [], y: [], mode: 'lines', line: { color: '#e41a1c' } }, // Equator
                { x: [], y: [], mode: 'lines', line: { color: '#377eb8' } }, // Selected Lat
                { x: [], y: [], mode: 'lines', line: { color: '#4daf4a' } }, // North Pole
                { x: [], y: [], mode: 'markers', marker: { color: 'black', size: 10 } } // Time marker
            ];

            Plotly.newPlot('plot-instant', initialTraces, plotLayoutInstant, {responsive: true});
            Plotly.newPlot('plot-cumulative', initialTraces.slice(0, 3), plotLayoutCumulative, {responsive: true});
        }

        function setupEventListeners() {
            sliderSeason.addEventListener('input', update);
            sliderLatitude.addEventListener('input', update);
            sliderTime.addEventListener('input', update);
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // --- CORE UPDATE LOGIC ---
        function update() {
            // 1. Read values from sliders
            const season = parseFloat(sliderSeason.value);
            const latitude = parseFloat(sliderLatitude.value);
            const timeOfDay = parseFloat(sliderTime.value);

            // 2. Update UI display
            valueSeason.textContent = season.toFixed(3);
            valueLatitude.textContent = latitude.toFixed(2) + '°';
            valueTime.textContent = timeOfDay.toFixed(2) + 'h';

            // 3. Physics Calculations
            // Declination (δ): Angle of the sun relative to the equator.
            // season=0 -> NH Summer Solstice (+23.5), season=0.5 -> NH Winter Solstice (-23.5)
            const declinationDeg = 23.5 * Math.cos(2 * Math.PI * season);
            const declinationRad = degToRad(declinationDeg);

            // 4. Update 3D Globe
            // Earth rotation based on time of day. 0h is midnight, 12h is noon.
            // We want 0 longitude to be at the front at noon (time = 12).
            earthMesh.rotation.y = (timeOfDay / 24) * 2 * Math.PI;

            // Sun direction. We point it from a distance towards the origin.
            // Z-axis points towards observer, Y is up. Sun is in YZ plane.
            const sunDir = new THREE.Vector3(0, Math.sin(declinationRad), -Math.cos(declinationRad)).normalize();
            sunLight.position.copy(sunDir.clone().multiplyScalar(20)); // Position light far away
            sunLight.target.position.set(0, 0, 0);

            // Update latitude line
            const latRad = degToRad(latitude);
            const latLineRadius = EARTH_RADIUS * Math.cos(latRad) * 1.005;
            const latLineY = EARTH_RADIUS * Math.sin(latRad) * 1.005;
            const points = [];
            for (let i = 0; i <= 128; i++) {
                const theta = (i / 128) * Math.PI * 2;
                points.push(new THREE.Vector3(
                    latLineRadius * Math.cos(theta),
                    latLineY,
                    -latLineRadius * Math.sin(theta) // Negative Z to match screen coordinates
                ));
            }
            latLine.geometry.setFromPoints(points);
            latLine.computeLineDistances(); // Crucial for dashed lines

            // Update vectors. They are at 0 longitude on the sphere.
            // The point on the sphere surface (in local coords)
            const vectorOrigin = new THREE.Vector3(
                EARTH_RADIUS * Math.cos(latRad),
                EARTH_RADIUS * Math.sin(latRad),
                0
            );
            surfaceNormal.position.copy(vectorOrigin);
            sunVector.position.copy(vectorOrigin);

            // Normal vector direction (radially outward)
            surfaceNormal.setDirection(vectorOrigin.clone().normalize());

            // Sun vector direction (parallel to sunLight's direction)
            // It needs to be transformed into the Earth's local coordinate system, which is rotated.
            const inverseEarthRotation = new THREE.Quaternion().setFromEuler(earthMesh.rotation).invert();
            const localSunDir = sunDir.clone().applyQuaternion(inverseEarthRotation);
            sunVector.setDirection(localSunDir);

            // 5. Update Plots
            const latsToPlot = [0, latitude, 90]; // Equator, selected, North Pole
            const timePoints = [];
            const instantaneousData = [[], [], []];
            const cumulativeData = [[], [], []];
            const SOLAR_CONSTANT = 1361;

            for (let t = 0; t <= 24; t += 0.25) { // Calculate every 15 minutes for smooth curves
                timePoints.push(t);
                const hourAngleRad = degToRad(15 * (t - 12));

                for (let i = 0; i < latsToPlot.length; i++) {
                    const latRad = degToRad(latsToPlot[i]);

                    const cosZenith = Math.sin(latRad) * Math.sin(declinationRad) + 
                                      Math.cos(latRad) * Math.cos(declinationRad) * Math.cos(hourAngleRad);

                    const instantaneousEnergy = SOLAR_CONSTANT * Math.max(0, cosZenith);
                    instantaneousData[i].push(instantaneousEnergy);

                    const lastCumulative = cumulativeData[i].length > 0 ? cumulativeData[i][cumulativeData[i].length - 1] : 0;
                    // Integrate: energy * time_step_in_hours
                    const cumulativeEnergy = lastCumulative + (instantaneousEnergy * 0.25); 
                    cumulativeData[i].push(cumulativeEnergy);
                }
            }

            // Update Plotly data
            Plotly.react('plot-instant', [
                { x: timePoints, y: instantaneousData[0], mode: 'lines', line: { color: '#e41a1c' } },
                { x: timePoints, y: instantaneousData[1], mode: 'lines', line: { color: '#377eb8' } },
                { x: timePoints, y: instantaneousData[2], mode: 'lines', line: { color: '#4daf4a' } },
                { // The marker for current time
                    x: [timeOfDay],
                    y: [SOLAR_CONSTANT * Math.max(0, 
                        Math.sin(degToRad(latitude)) * Math.sin(declinationRad) + 
                        Math.cos(degToRad(latitude)) * Math.cos(declinationRad) * Math.cos(degToRad(15 * (timeOfDay - 12))))
                       ],
                    mode: 'markers',
                    marker: { color: 'black', size: 10, symbol: 'diamond' }
                }
            ]);

            Plotly.react('plot-cumulative', [
                { x: timePoints, y: cumulativeData[0], mode: 'lines', line: { color: '#e41a1c' } },
                { x: timePoints, y: cumulativeData[1], mode: 'lines', line: { color: '#377eb8' } },
                { x: timePoints, y: cumulativeData[2], mode: 'lines', line: { color: '#4daf4a' } },
            ]);
        }

        // --- RUN ---
        document.addEventListener('DOMContentLoaded', init);
    </script>

</body>
</html>
