            94: { a: {n: 4136, d: 105}, b: {n: 1995, d: 44}, c: {n: 219041, d: 4620} },
            95: { a: {n: 19, d: 2}, b: {n: 20, d: 1}, c: {n: 57, d: 2, s:2} },
            96: { a: {n: 12, d: 1}, b: {n: 16, d: 1}, c: {n: 20, d: 1} },
            101: { a: {n: 201, d: 2, s:2}, b: {n: 202, d: 2, s:2}, c: {n: 203, d: 2, s:2} },
            101: { a: {n: 20200, d: 99}, b: {n: 99, d: 2}, c: {n: 40801, d: 198} }
        };


        const sketch = p => {
            let sliderArea, inputArea;
            const canvasPadding = 60;
            const labelColor = '#8B5A2B';
            const triangleFill = '#FFF2E5';

            p.setup = function() {
                const container = document.getElementById('canvas-container');
                const canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('canvas-container');

                sliderArea = p.select('#slider-area');
                inputArea = p.select('#input-area');

                sliderArea.input(() => {
                    inputArea.value(sliderArea.value());
                    redrawCanvas();
                });

                inputArea.input(() => {
                    // Clamp value to min/max
                    let val = parseInt(inputArea.value(), 10);
                    if (isNaN(val)) return;
                    val = p.constrain(val, sliderArea.elt.min, sliderArea.elt.max);
                    inputArea.value(val);
                    sliderArea.value(val);
                    redrawCanvas();
                });

                // Initial draw
                redrawCanvas();
            };

            p.windowResized = function() {
                const container = document.getElementById('canvas-container');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
                redrawCanvas();
            };

            function redrawCanvas() {
                const n = parseInt(sliderArea.value(), 10);
                p.background(255);

                if (congruentData[n]) {
                    drawTriangle(n);
                } else {
                    drawNotCongruentMessage(n);
                }
            }

            function drawTriangle(n) {
                const sides = congruentData[n];
                const valA = sides.a.n / sides.a.d;
                const valB = sides.b.n / sides.b.d;

                const scale = p.min(
                    (p.width - 2 * canvasPadding) / valA,
                    (p.height - 2 * canvasPadding) / valB
                );

                const scaledA = valA * scale;
                const scaledB = valB * scale;

                const cx = p.width / 2;
                const cy = p.height / 2;

                const v1 = { x: cx - scaledA / 2, y: cy + scaledB / 2 }; // Bottom-left (right angle)
                const v2 = { x: cx + scaledA / 2, y: cy + scaledB / 2 }; // Bottom-right
                const v3 = { x: cx - scaledA / 2, y: cy - scaledB / 2 }; // Top-left

                // Draw the triangle
                p.fill(triangleFill);
                p.stroke(0);
                p.strokeWeight(2);
                p.beginShape();
                p.vertex(v1.x, v1.y);
                p.vertex(v2.x, v2.y);
                p.vertex(v3.x, v3.y);
                p.endShape(p.CLOSE);

                const fontSize = p.constrain(p.width / 50, 12, 18);

                // Draw labels
                // Label for side 'a' (base)
                drawFraction(sides.a, (v1.x + v2.x) / 2, v1.y + 30, 0, fontSize);

                // Label for side 'b' (height)
                drawFraction(sides.b, v1.x - 45, (v1.y + v3.y) / 2, 0, fontSize);

                // Label for side 'c' (hypotenuse)
                const midHypX = (v2.x + v3.x) / 2;
                const midHypY = (v2.y + v3.y) / 2;
                const angle = p.atan2(v3.y - v2.y, v3.x - v2.x);
                const offset = 35;
                const labelX = midHypX + offset * p.cos(angle - p.PI / 2);
                const labelY = midHypY + offset * p.sin(angle - p.PI / 2);

                drawFraction(sides.c, labelX, labelY, angle, fontSize);
            }

            function drawNotCongruentMessage(n) {
                p.fill('#FF0000');
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(p.constrain(p.width / 25, 16, 24));
                p.text(`${n} is not a congruent number`, p.width / 2, p.height / 2);
            }

            function drawFraction(fraction, x, y, rotation, fontSize) {
                p.push();
                p.translate(x, y);
                p.rotate(rotation);

                p.fill(labelColor);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(fontSize);

                // Use space as thousands separator for readability
                const formatNumber = (num) => num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ");

                const numStr = formatNumber(fraction.n);
                const denStr = formatNumber(fraction.d);

                const numY = -fontSize * 0.6;
                const denY = fontSize * 0.6;

                p.text(numStr, 0, numY);
                p.text(denStr, 0, denY);

                const lineWidth = p.max(p.textWidth(numStr), p.textWidth(denStr)) * 1.1;
                p.stroke(labelColor);
                p.strokeWeight(1.5);
                p.line(-lineWidth / 2, 0, lineWidth / 2, 0);

                p.pop();
            }
        };

        new p5(sketch, 'canvas-container-placeholder'); // Pass a dummy ID, parent is set in setup

    </script>
</body>
</html>
