    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // --- DOM Elements ---
        const sliders = {
            alpha: document.getElementById('slider-alpha'), beta: document.getElementById('slider-beta'),
            w: document.getElementById('slider-w'), r: document.getElementById('slider-r'),
            Q: document.getElementById('slider-Q'), C: document.getElementById('slider-C'),
        };
        const labels = {
            alpha: document.getElementById('label-alpha'), beta: document.getElementById('label-beta'),
            w: document.getElementById('label-w'), r: document.getElementById('label-r'),
            Q: document.getElementById('label-Q'), C: document.getElementById('label-C'),
        };
        const buttons = {
            reset: document.getElementById('btn-reset'),
            qFromDual: document.getElementById('btn-Q-from-dual'),
            cFromPrimal: document.getElementById('btn-C-from-primal'),
        };
        const outputs = {
            primal: document.getElementById('output-primal'), dual: document.getElementById('output-dual'),
        };
        const plotContainers = {
            primal: document.getElementById('plot-primal'), dual: document.getElementById('plot-dual'),
        };

        // --- State and Config ---
        const defaults = {
            alpha: { val: 0.5, min: 0.1, max: 1.0, step: 0.01 },
            beta:  { val: 0.5, min: 0.1, max: 1.0, step: 0.01 },
            w:     { val: 1.0, min: 0.1, max: 2.0, step: 0.01 },
            r:     { val: 1.0, min: 0.1, max: 2.0, step: 0.01 },
            Q:     { val: 3.0, min: 1,   max: 10,  step: 0.1 },
            C:     { val: 4.0, min: 1,   max: 10,  step: 0.1 },
        };
        const state = {
            params: {},
            results: { primal: {}, dual: {} }
        };

        const viz = {}; // To store three.js objects

        // --- Main Functions ---
        function init() {
            setupSliders();
            setupThreeJS();
            setupEventListeners();
            resetToDefaults();
        }

        function update() {
            readInputs();
            solveProblems();
            updateOutputs();
            updateVisualizations();
        }

        // --- Setup ---
        function setupSliders() {
            for (const key in defaults) {
                sliders[key].min = defaults[key].min;
                sliders[key].max = defaults[key].max;
                sliders[key].step = defaults[key].step;
                sliders[key].value = defaults[key].val;
            }
        }

        function setupThreeJS() {
            const createScene = (container, axisLabels) => {
                const scene = new THREE.Scene();
                const camera = new THREE.PerspectiveCamera(50, container.clientWidth / container.clientHeight, 0.1, 100);
                camera.position.set(8, 8, 12);

                const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
                renderer.setSize(container.clientWidth, container.clientHeight);
                renderer.setClearColor(0xffffff, 1);
                container.appendChild(renderer.domElement);

                const controls = new OrbitControls(camera, renderer.domElement);
                controls.target.set(3, 3, 3);
                controls.update();

                scene.add(new THREE.AmbientLight(0xffffff, 0.8));
                const dirLight = new THREE.DirectionalLight(0xffffff, 0.6);
                dirLight.position.set(5, 10, 7.5);
                scene.add(dirLight);

                const boxSize = 10;
                const boxHelper = new THREE.BoxHelper(new THREE.Mesh(new THREE.BoxGeometry(boxSize, boxSize, boxSize)), 0xcccccc);
                boxHelper.position.set(boxSize/2, boxSize/2, boxSize/2);
                scene.add(boxHelper);

                scene.add(new THREE.AxesHelper(boxSize + 1));

                const dynamicGroup = new THREE.Group();
                scene.add(dynamicGroup);

                return { scene, camera, renderer, controls, dynamicGroup, boxSize };
            };

            viz.primal = createScene(plotContainers.primal);
            viz.dual = createScene(plotContainers.dual);

            window.addEventListener('resize', () => {
                [viz.primal, viz.dual].forEach((v, i) => {
                    const container = i === 0 ? plotContainers.primal : plotContainers.dual;
                    v.camera.aspect = container.clientWidth / container.clientHeight;
                    v.camera.updateProjectionMatrix();
                    v.renderer.setSize(container.clientWidth, container.clientHeight);
                });
            }, false);

            animate();
        }

        function setupEventListeners() {
            Object.values(sliders).forEach(slider => slider.addEventListener('input', update));
            buttons.reset.addEventListener('click', resetToDefaults);

            buttons.cFromPrimal.addEventListener('click', () => {
                const newC = state.results.primal.C;
                sliders.C.value = Math.max(defaults.C.min, Math.min(defaults.C.max, newC));
                update();
            });
            buttons.qFromDual.addEventListener('click', () => {
                const newQ = state.results.dual.Q;
                sliders.Q.value = Math.max(defaults.Q.min, Math.min(defaults.Q.max, newQ));
                update();
            });
        }

        function resetToDefaults() {
            for (const key in defaults) {
                sliders[key].value = defaults[key].val;
            }
            update();
        }

        // --- Logic and Calculations ---
        function readInputs() {
            for (const key in sliders) {
                state.params[key] = parseFloat(sliders[key].value);
                const isInt = key === 'Q' || key === 'C';
                labels[key].textContent = state.params[key].toFixed(isInt ? 1 : 2);
            }
        }

        function solveProblems() {
            const { alpha, beta, w, r, Q, C } = state.params;

            // Primal: min C = wx + ry, s.t. Q_in = x^a * y^b
            const k = (w * beta) / (r * alpha);
            const x_p = Math.pow(Q / Math.pow(k, beta), 1 / (alpha + beta));
            const y_p = x_p * k;
            state.results.primal = { x: x_p, y: y_p, C: w * x_p + r * y_p };

            // Dual: max Q = x^a * y^b, s.t. C_in = wx + ry
            const x_d = (C * alpha) / (w * (alpha + beta));
            const y_d = (C * beta) / (r * (alpha + beta));
            state.results.dual = { x: x_d, y: y_d, Q: Math.pow(x_d, alpha) * Math.pow(y_d, beta) };
        }

        function updateOutputs() {
            const p = state.results.primal;
            outputs.primal.innerHTML = `x = ${p.x.toFixed(2)}, y = ${p.y.toFixed(2)}<br><strong>min C = ${p.C.toFixed(2)}</strong>`;
            const d = state.results.dual;
            outputs.dual.innerHTML = `x = ${d.x.toFixed(2)}, y = ${d.y.toFixed(2)}<br><strong>max Q = ${d.Q.toFixed(2)}</strong>`;
        }

        // --- Visualization Update ---
        function updateVisualizations() {
            updatePrimalPlot();
            updateDualPlot();
        }

        function clearGroup(group){
            while(group.children.length > 0){ group.remove(group.children[0]); }
        }

        function updatePrimalPlot() {
            const { dynamicGroup, boxSize } = viz.primal;
            clearGroup(dynamicGroup);
            const { alpha, beta, w, r, Q } = state.params;
            const { x: x_p, y: y_p, C: C_out } = state.results.primal;

            // 1. Isoquant "Curtain"
            const points = [];
            for (let i = 1; i <= 50; i++) {
                const x = (i / 50) * boxSize * 1.5;
                const y = Math.pow(Q / Math.pow(x, alpha), 1 / beta);
                if (y < boxSize * 1.5) points.push(new THREE.Vector2(x, y));
            }
            if(points.length > 1) {
                const vertices = [];
                for(let i=0; i<points.length-1; i++){
                    const p1 = points[i], p2 = points[i+1];
                    vertices.push(p1.x, p1.y, 0,  p2.x, p2.y, 0,  p1.x, p1.y, boxSize);
                    vertices.push(p2.x, p2.y, 0,  p2.x, p2.y, boxSize, p1.x, p1.y, boxSize);
                }
                const geom = new THREE.BufferGeometry();
                geom.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                geom.computeVertexNormals();
                dynamicGroup.add(new THREE.Mesh(geom, new THREE.MeshBasicMaterial({ color: 0xffa500, side: THREE.DoubleSide, transparent: true, opacity: 0.6 })));
            }

            // 2. Isocost Plane C_out = w*x + r*y
            if (isFinite(x_p) && isFinite(y_p) && isFinite(C_out)){
                const plane = new THREE.Mesh(new THREE.PlaneGeometry(boxSize * 2.5, boxSize * 2.5), new THREE.MeshBasicMaterial({ color: 0x808080, transparent: true, opacity: 0.5, side: THREE.DoubleSide }));
                plane.position.set(x_p, y_p, C_out);
                plane.lookAt(x_p + w, y_p + r, C_out + 1); // Normal is (w, r, 1), we point "up" along Z
                dynamicGroup.add(plane);

                // 3. Solution Vector
                const lineGeom = new THREE.BufferGeometry().setFromPoints([ new THREE.Vector3(0, 0, 0), new THREE.Vector3(x_p, y_p, C_out) ]);
                const line = new THREE.Line(lineGeom, new THREE.LineBasicMaterial({ color: 0x0000ff, linewidth: 5 })); // linewidth > 1 requires Line2 from examples
                dynamicGroup.add(line);
            }
        }

        function updateDualPlot() {
            const { dynamicGroup, boxSize } = viz.dual;
            clearGroup(dynamicGroup);
            const { alpha, beta, w, r, C } = state.params;
            const { x: x_d, y: y_d, Q: Q_out } = state.results.dual;

            // 1. Production Surface Q = x^a * y^b
            const prodFunc = (u, v, target) => {
                const x = u * boxSize, y = v * boxSize;
                const z = Math.pow(x, alpha) * Math.pow(y, beta);
                target.set(x, y, z > boxSize ? boxSize : z);
            };
            const surf = new THREE.Mesh(new THREE.ParametricGeometry(prodFunc, 40, 40), new THREE.MeshLambertMaterial({ color: 0xffa500, side: THREE.DoubleSide }));
            dynamicGroup.add(surf);

            // 2. Budget "Curtain"
            const p1 = new THREE.Vector3(C/w, 0, 0);
            const p2 = new THREE.Vector3(0, C/r, 0);
            const vertices = [p1.x, p1.y, 0,  p2.x, p2.y, 0,  p1.x, p1.y, boxSize, p2.x, p2.y, 0, p2.x, p2.y, boxSize, p1.x, p1.y, boxSize];
            const geom = new THREE.BufferGeometry();
            geom.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            dynamicGroup.add(new THREE.Mesh(geom, new THREE.MeshBasicMaterial({ color: 0x808080, side: THREE.DoubleSide, transparent: true, opacity: 0.6 })));

            // 3. Intersection Curve
            const points = [];
            for (let i = 0; i <= 50; i++) {
                const x = i / 50 * p1.x;
                const y = (C - w * x) / r;
                if (x >= 0 && y >= 0) {
                    const z = Math.pow(x, alpha) * Math.pow(y, beta);
                    if (z < boxSize) points.push(new THREE.Vector3(x, y, z));
                }
            }
            if(points.length > 1) {
                dynamicGroup.add(new THREE.Line(new THREE.BufferGeometry().setFromPoints(points), new THREE.LineBasicMaterial({ color: 0xff0000, linewidth: 5 })));
            }

            // 4. Optimal Point Marker
            if (isFinite(x_d) && isFinite(y_d) && isFinite(Q_out) && Q_out < boxSize) {
                const marker = new THREE.Mesh(new THREE.SphereGeometry(0.15, 16, 16), new THREE.MeshBasicMaterial({ color: 0x333333 }));
                marker.position.set(x_d, y_d, Q_out);
                dynamicGroup.add(marker);
            }
        }

        // --- Animation Loop ---
        function animate() {
            requestAnimationFrame(animate);
            viz.primal.controls.update();
            viz.dual.controls.update();
            viz.primal.renderer.render(viz.primal.scene, viz.primal.camera);
            viz.dual.renderer.render(viz.dual.scene, viz.dual.camera);
        }

        // --- Start ---
        document.addEventListener('DOMContentLoaded', init);

    </script>
</body>
</html>
