    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // --- Thermodynamic & Physical Constants ---
        const CONSTANTS = {
            F: 10, // Feed Flow, kmol/hr
            T_F: 323.2, // Feed Temperature, K
            P_F: 1000, // Feed Pressure, kPa
            z: [0.25, 0.25, 0.25, 0.25], // Feed Mole Fractions
            components: ['n-butane', 'n-pentane', 'n-heptane', 'n-octane'],
            antoine: [ // For P_sat in kPa, T in K
                { A: 6.80165, B: 1005.157, C: -27.159 }, // n-butane
                { A: 6.86177, B: 1184.249, C: -35.223 }, // n-pentane
                { A: 6.90237, B: 1358.59,  C: -51.303 }, // n-heptane
                { A: 6.92383, B: 1431.149, C: -59.501 }  // n-octane
            ],
            Cp_L: [132, 161, 225, 255], // Liquid Heat Capacity, kJ/kmol·K
            deltaH_vap: [22400, 25800, 31800, 34400] // Heat of Vaporization, kJ/kmol
        };

        // --- Global State for Visualization ---
        let flashResults = {};
        let sliderPressure, sliderHeat;
        let pressureValueSpan, heatValueSpan;

        // --- Root Finding Solver (Bisection Method) ---
        function bisection(func, low, high, tolerance = 1e-6, maxIter = 100) {
            let fLow = func(low);
            let fHigh = func(high);

            if (fLow * fHigh > 0) {
                // Return a boundary if the function value is smaller there
                return Math.abs(fLow) < Math.abs(fHigh) ? low : high;
            }

            let mid;
            for (let i = 0; i < maxIter; i++) {
                mid = (low + high) / 2;
                let fMid = func(mid);
                if (Math.abs(fMid) < tolerance) {
                    return mid;
                }
                if (fLow * fMid < 0) {
                    high = mid;
                } else {
                    low = mid;
                    fLow = fMid;
                }
            }
            return mid;
        }

        // --- Core Thermodynamic Calculation ---
        function calculateFlash(P_flash, Q_hr) {
            const { z, antoine, F, Cp_L, T_F, deltaH_vap } = CONSTANTS;
            const n_comp = z.length;

            const getK = (T) => antoine.map(p => Math.pow(10, p.A - p.B / (T + p.C)) / P_flash);

            const solveRachfordRice = (T) => {
                const K = getK(T);

                const rr_objective = (V) => z.reduce((sum, zi, i) => sum + zi * (K[i] - 1) / (1 + V * (K[i] - 1)), 0);

                // Check for boundary conditions (all liquid or all vapor)
                if (rr_objective(0) <= 0) return 0; // All liquid
                if (rr_objective(1) >= 0) return 1; // All vapor

                // Find V in (0, 1)
                return bisection(rr_objective, 0, 1);
            };

            const energyBalanceObjective = (T) => {
                const V = solveRachfordRice(T);
                const K = getK(T);

                if (V <= 0) { // All liquid
                    const h_L = Cp_L.reduce((sum, cpi, i) => sum + z[i] * cpi * (T_F - T), 0);
                    const Q_calc = -h_L * F;
                    return Q_hr - Q_calc;
                }
                if (V >= 1) { // All vapor
                     const y = z.slice();
                     const h_V = y.reduce((sum, yi, i) => sum + yi * deltaH_vap[i], 0);
                     const h_L = Cp_L.reduce((sum, cpi, i) => sum + z[i] * cpi * (T_F - T), 0);
                     const Q_calc = F * (h_V - h_L);
                     return Q_hr - Q_calc;
                }

                const x = z.map((zi, i) => zi / (1 + V * (K[i] - 1)));
                const y = x.map((xi, i) => xi * K[i]);

                const vaporEnthalpy = y.reduce((sum, yi, i) => sum + yi * deltaH_vap[i], 0);
                const sensibleHeat = Cp_L.reduce((sum, cpi, i) => sum + z[i] * cpi * (T_F - T), 0);

                const Q_calc = F * (V * vaporEnthalpy - sensibleHeat);

                return Q_hr - Q_calc;
            };

            // Find temperature that satisfies the energy balance
            const T_flash = bisection(energyBalanceObjective, 200, 500, 1e-3);

            // Recalculate final state with the found temperature
            const V_frac = solveRachfordRice(T_flash);
            const K = getK(T_flash);

            let x, y;
            if (V_frac <= 0) { // All liquid
                x = z.slice();
                y = x.map((xi, i) => xi * K[i]);
            } else if (V_frac >= 1) { // All vapor
                y = z.slice();
                x = y.map((yi, i) => yi / K[i]);
            } else { // Two-phase
                x = z.map((zi, i) => zi / (1 + V_frac * (K[i] - 1)));
                y = x.map((xi, i) => xi * K[i]);
            }

            return { T_flash, V_frac, x, y, Q_hr, P_flash };
        }

        // --- p5.js Visualization Code ---

        function setup() {
            const canvas = createCanvas(800, 500);
            canvas.parent('visualization-area');

            // Get DOM elements
            sliderPressure = select('#slider-pressure');
            sliderHeat = select('#slider-heat');
            pressureValueSpan = select('#pressure-value');
            heatValueSpan = select('#heat-value');

            // Attach event listeners
            sliderPressure.input(updateSimulation);
            sliderHeat.input(updateSimulation);

            // Initial calculation and draw
            updateSimulation();
            noLoop(); // We only redraw when inputs change
        }

        function updateSimulation() {
            const P = parseFloat(sliderPressure.value());
            const Q = parseFloat(sliderHeat.value());

            pressureValueSpan.html(`${P.toFixed(0)} kPa`);
            heatValueSpan.html(`${Q.toFixed(0)} kJ/hr`);

            flashResults = calculateFlash(P, Q);
            redraw();
        }

        function draw() {
            background('#F0F0F0');

            if (!flashResults || !flashResults.x) return; // Don't draw if results are not ready

            push(); // Start a new drawing state

            // --- Helper Functions for Drawing ---
            const drawArrow = (x1, y1, x2, y2) => {
                line(x1, y1, x2, y2);
                const angle = atan2(y2 - y1, x2 - x1);
                push();
                translate(x2, y2);
                rotate(angle);
                triangle(0, 0, -10, -5, -10, 5);
                pop();
            };

            const drawDashedLine = (x1, y1, x2, y2, dashLength = 10, gapLength = 5) => {
                const totalLength = dist(x1, y1, x2, y2);
                const numDashes = Math.floor(totalLength / (dashLength + gapLength));
                const dx = (x2 - x1) / totalLength;
                const dy = (y2 - y1) / totalLength;

                for (let i = 0; i < numDashes; i++) {
                    const startX = x1 + i * (dashLength + gapLength) * dx;
                    const startY = y1 + i * (dashLength + gapLength) * dy;
                    const endX = startX + dashLength * dx;
                    const endY = startY + dashLength * dy;
                    line(startX, startY, endX, endY);
                }
            };

            // --- Draw Diagram Elements ---
            const vesselX = 350, vesselY = 200, vesselW = 100, vesselH = 100;

            // Flash Vessel
            stroke(0);
            strokeWeight(2);
            fill('#E6E6FA');
            rect(vesselX, vesselY, vesselW, vesselH);
            noStroke();
            fill(0);
            textAlign(CENTER, CENTER);
            textSize(16);
            textStyle(BOLD);
            text('Flash', vesselX + vesselW / 2, vesselY + vesselH / 2);

            // Feed Stream
            strokeWeight(4);
            stroke('#9ACD32');
            drawArrow(100, 250, vesselX, 250);

            // Vapor Stream
            stroke('#9370DB');
            line(vesselX + vesselW / 2, vesselY, vesselX + vesselW / 2, 100);
            drawArrow(vesselX + vesselW / 2, 100, 550, 100);

            // Liquid Stream
            stroke('#FFA500');
            line(vesselX + vesselW / 2, vesselY + vesselH, vesselX + vesselW / 2, 400);
            drawArrow(vesselX + vesselW / 2, 400, 550, 400);

            // Heat Input
            strokeWeight(2);
            stroke('#FF0000');
            drawDashedLine(250, 320, vesselX, 260);
            drawArrow(250, 320, vesselX, 260);

            // --- Draw Text Labels ---
            noStroke();
            fill(0);
            textStyle(NORMAL);
            textSize(12);

            // Feed Info
            textAlign(LEFT, TOP);
            textStyle(BOLD);
            text('FEED', 50, 200);
            textStyle(NORMAL);
            text(`F = ${CONSTANTS.F} kmol/hr`, 50, 220);
            text(`T = ${CONSTANTS.T_F} K`, 50, 235);
            text(`P = ${CONSTANTS.P_F} kPa`, 50, 250);
            text(`z = [${CONSTANTS.z.join(', ')}]`, 50, 265);

            // Heat Info
            textAlign(CENTER, CENTER);
            text(`Q = ${flashResults.Q_hr.toFixed(0)} kJ/hr`, 300, 310);

            // Process Info (Center Right)
            textAlign(LEFT, CENTER);
            textStyle(BOLD);
            text('PROCESS CONDITIONS', 570, 220);
            textStyle(NORMAL);
            text(`Vapor Fraction (V/F): ${flashResults.V_frac.toFixed(3)}`, 570, 240);
            text(`Flash Temperature: ${flashResults.T_flash.toFixed(2)} K`, 570, 260);
            text(`Flash Pressure: ${flashResults.P_flash.toFixed(0)} kPa`, 570, 280);

            // Vapor Info (Top Right)
            textStyle(BOLD);
            text('VAPOR OUT (V)', 570, 50);
            textStyle(NORMAL);
            text(`V = ${(CONSTANTS.F * flashResults.V_frac).toFixed(2)} kmol/hr`, 570, 70);
            text('mole fractions (y):', 570, 90);
            flashResults.y.forEach((yi, i) => {
                text(`${CONSTANTS.components[i]}: ${yi.toFixed(3)}`, 570, 110 + i * 15);
            });

            // Liquid Info (Bottom Right)
            textStyle(BOLD);
            text('LIQUID OUT (L)', 570, 350);
            textStyle(NORMAL);
            text(`L = ${(CONSTANTS.F * (1 - flashResults.V_frac)).toFixed(2)} kmol/hr`, 570, 370);
            text('mole fractions (x):', 570, 390);
            flashResults.x.forEach((xi, i) => {
                text(`${CONSTANTS.components[i]}: ${xi.toFixed(3)}`, 570, 410 + i * 15);
            });

            pop(); // Restore original drawing state
        }
    </script>
</body>
</html>
