            const atmoGeometry = new THREE.SphereGeometry(1.05, 32, 32);
            const atmoMaterial = new THREE.MeshBasicMaterial({ 
                color: 0x87ceeb, 
                transparent: true, 
                opacity: 0.3, 
                blending: THREE.AdditiveBlending 
            });
            const atmosphere = new THREE.Mesh(atmoGeometry, atmoMaterial);
            group.add(atmosphere);

            return group;
        }

        function createConstellations() {
            const group = new THREE.Group();
            const starVertices = [];
            const lineVertices = [];
            const constellationData = [ // Dummy constellations
                [[0,0,0], [1,1,0], [2,0,0], [1,-1,0], [0,0,0]],
                [[-3,3,0], [-2,4,0], [-1,3,0]],
                [[3,-2,0], [4,-3,0], [5,-2,0], [4,-1,0], [3,-2,0]]
            ];

            const radius = 100;
            for (let i = 0; i < 2000; i++) {
                const u = Math.random();
                const v = Math.random();
                const theta = 2 * Math.PI * u;
                const phi = Math.acos(2 * v - 1);
                starVertices.push(
                    radius * Math.sin(phi) * Math.cos(theta),
                    radius * Math.sin(phi) * Math.sin(theta),
                    radius * Math.cos(phi)
                );
            }

            const starGeometry = new THREE.BufferGeometry();
            starGeometry.setAttribute('position', new THREE.Float32BufferAttribute(starVertices, 3));
            const starMaterial = new THREE.PointsMaterial({ color: 0xffffff, size: 0.3 });
            const stars = new THREE.Points(starGeometry, starMaterial);
            group.add(stars);
            return group;
        }

        // --- UPDATE LOGIC ---
        function updateAll() {
            // View
            const isLandView = params.view === 'land';
            moonSurface.visible = isLandView;
            moonSphere.visible = !isLandView;

            if (isLandView) {
                camera.position.set(0, 1, 5); // on moon surface
                camera.lookAt(0, 5, -EARTH_ORBIT_RADIUS);
            } else {
                camera.position.set(0, 15, 50); // space overview
                camera.lookAt(0, 0, 0);
            }

            // Options
            constellations.visible = params.showConstellations;
            constellations.rotation.y = params.zodiac;

            const earthScale = params.realisticSize ? 0.27 : 1.0; // Earth is ~0.27 size of moon
            earth.scale.set(earthScale, earthScale, earthScale);

            // Orbital mechanics
            const earthAngle = (params.time * 2 * Math.PI) / 27.3; // 27.3 days for sidereal month
            const earthX = EARTH_ORBIT_RADIUS * Math.cos(earthAngle);
            const earthZ = -EARTH_ORBIT_RADIUS * Math.sin(earthAngle);

            earth.position.set(earthX, 0, earthZ);
            earth.rotation.y = -earthAngle; // Keep one face towards the moon

            // Apply nodal angle tilt to the orbital plane
            const orbitalPlane = new THREE.Object3D();
            orbitalPlane.add(earth);
            orbitalPlane.rotation.x = params.nodalAngle;
            scene.add(orbitalPlane); // Temporarily add to get world pos
            earth.getWorldPosition(earth.position); // Update earth position to world coords
            scene.remove(orbitalPlane); // Clean up

            // Sun position (light direction)
            const sunAngle = earthAngle + params.sunEarthAngle;
            sunLight.position.set(
                EARTH_ORBIT_RADIUS * 2 * Math.cos(sunAngle),
                10, // Slightly elevated to get better shadows
                -EARTH_ORBIT_RADIUS * 2 * Math.sin(sunAngle)
            );
            sunLight.target = earth; // Target earth for shadows

            // Eclipse Check
            const sunDir = sunLight.position.clone().normalize();
            const earthDir = earth.position.clone().normalize();
            const dot = sunDir.dot(earthDir);
            const earthCore = earth.getObjectByName("earth_core");

            if (dot < -0.995) { // Sun is behind Earth
                earthCore.material = earthMaterialEclipse;
            } else {
                earthCore.material = earthMaterialBlue;
            }
        }

        // --- ANIMATION LOOP ---
        function animate() {
            requestAnimationFrame(animate);

            const delta = clock.getDelta();
            let needsUpdate = false;

            if (isAnimating.time) {
                let val = parseFloat(sliderTime.value) + delta * 5; // 5 days per second
                if (val > sliderTime.max) val = 0;
                sliderTime.value = val;
                params.time = val;
                sliderTimeValue.textContent = val.toFixed(2);
                needsUpdate = true;
            }
            if (isAnimating.nodalAngle) {
                let val = parseFloat(sliderNodalAngle.value) + delta * 0.5;
                if (val > sliderNodalAngle.max) val = 0;
                sliderNodalAngle.value = val;
                params.nodalAngle = val;
                sliderNodalAngleValue.textContent = val.toFixed(2);
                needsUpdate = true;
            }
            if (isAnimating.sunEarthAngle) {
                let val = parseFloat(sliderSunEarthAngle.value) + delta * 0.5;
                if (val > sliderSunEarthAngle.max) val = 0;
                sliderSunEarthAngle.value = val;
                params.sunEarthAngle = val;
                sliderSunEarthAngleValue.textContent = val.toFixed(2);
                needsUpdate = true;
            }
            if (isAnimating.zodiac) {
                let val = parseFloat(sliderZodiac.value) + delta * 0.2;
                if (val > sliderZodiac.max) val = 0;
                sliderZodiac.value = val;
                params.zodiac = val;
                sliderZodiacValue.textContent = val.toFixed(2);
                needsUpdate = true;
            }

            if (needsUpdate) {
                updateAll();
            }

            renderer.render(scene, camera);
        }

        // --- EVENT LISTENERS ---
        function setupEventListeners() {
            // View buttons
            btnLand.addEventListener('click', () => {
                params.view = 'land';
                btnLand.classList.add('active');
                btnSpace.classList.remove('active');
                updateAll();
            });
            btnSpace.addEventListener('click', () => {
                params.view = 'space';
                btnSpace.classList.add('active');
                btnLand.classList.remove('active');
                updateAll();
            });

            // Sliders
            sliderTime.addEventListener('input', (e) => {
                params.time = parseFloat(e.target.value);
                sliderTimeValue.textContent = params.time.toFixed(2);
                updateAll();
            });
            sliderNodalAngle.addEventListener('input', (e) => {
                params.nodalAngle = parseFloat(e.target.value);
                sliderNodalAngleValue.textContent = params.nodalAngle.toFixed(2);
                updateAll();
            });
            sliderSunEarthAngle.addEventListener('input', (e) => {
                params.sunEarthAngle = parseFloat(e.target.value);
                sliderSunEarthAngleValue.textContent = params.sunEarthAngle.toFixed(2);
                updateAll();
            });
            sliderZodiac.addEventListener('input', (e) => {
                params.zodiac = parseFloat(e.target.value);
                sliderZodiacValue.textContent = params.zodiac.toFixed(2);
                updateAll();
            });

            // Play buttons
            btnTimePlay.addEventListener('click', () => isAnimating.time = !isAnimating.time);
            btnNodalAnglePlay.addEventListener('click', () => isAnimating.nodalAngle = !isAnimating.nodalAngle);
            btnSunEarthAnglePlay.addEventListener('click', () => isAnimating.sunEarthAngle = !isAnimating.sunEarthAngle);
            btnZodiacPlay.addEventListener('click', () => isAnimating.zodiac = !isAnimating.zodiac);

            // Checkboxes
            checkboxConstellations.addEventListener('change', (e) => {
                params.showConstellations = e.target.checked;
                updateAll();
            });
            checkboxRealisticSize.addEventListener('change', (e) => {
                params.realisticSize = e.target.checked;
                updateAll();
            });

            // Window Resize
            window.addEventListener('resize', () => {
                camera.aspect = vizContainer.clientWidth / vizContainer.clientHeight;
                camera.updateProjectionMatrix();
                renderer.setSize(vizContainer.clientWidth, vizContainer.clientHeight);
            });
        }

        // Initializer
        document.addEventListener('DOMContentLoaded', () => {
            // Set initial param values from HTML
            params.time = parseFloat(sliderTime.value);
            params.nodalAngle = parseFloat(sliderNodalAngle.value);
            params.sunEarthAngle = parseFloat(sliderSunEarthAngle.value);
            params.zodiac = parseFloat(sliderZodiac.value);
            params.showConstellations = checkboxConstellations.checked;
            params.realisticSize = checkboxRealisticSize.checked;

            // Set initial text values
            sliderTimeValue.textContent = params.time.toFixed(2);
            sliderNodalAngleValue.textContent = params.nodalAngle.toFixed(2);
            sliderSunEarthAngleValue.textContent = params.sunEarthAngle.toFixed(2);
            sliderZodiacValue.textContent = params.zodiac.toFixed(2);

            init();
            setupEventListeners();
        });
    </script>
</body>
</html>
