<body>

    <h1>Best Effort Global Warming Trajectories</h1>
    <p>This interactive demo visualizes simplified global warming trajectories. Use the sliders to adjust parameters of a basic climate model and observe the impact on atmospheric carbon concentration and annual emissions over the next 300 years.</p>

    <div id="controls-container">
        <div>
            <label for="slider-residence-time">Atmospheric CO2 residence time (years)</label>
            <input type="range" id="slider-residence-time" min="50" max="500" value="200" step="1">
            <span id="value-residence-time">200</span>
        </div>
        <div>
            <label for="slider-transition-pace">Transition to constant reduction pace (years)</label>
            <input type="range" id="slider-transition-pace" min="0" max="100" value="25" step="1">
            <span id="value-transition-pace">25</span>
        </div>
        <div>
            <label for="slider-wedges">Number of constant pace wedges (above emission stabilization at t=0)</label>
            <input type="range" id="slider-wedges" min="0" max="10" value="4" step="0.01">
            <span id="value-wedges">4.00</span>
        </div>
    </div>

    <div id="plot-div"></div>

    <script>
        window.onload = function() {
            // --- DOM Element References ---
            const sliderResidenceTime = document.getElementById('slider-residence-time');
            const valueResidenceTime = document.getElementById('value-residence-time');

            const sliderTransitionPace = document.getElementById('slider-transition-pace');
            const valueTransitionPace = document.getElementById('value-transition-pace');

            const sliderWedges = document.getElementById('slider-wedges');
            const valueWedges = document.getElementById('value-wedges');

            const plotDiv = document.getElementById('plot-div');

            // --- Mathematical Model Constants ---
            const C0 = 850;        // Initial atmospheric carbon in GtC at t=0
            const E0 = 8.5;        // Initial annual emission rate in GtC/yr at t=0
            const C_eq = 586;      // Pre-industrial equilibrium atmospheric carbon in GtC
            const E_floor = 1.5;   // Floor for emission rate in GtC/yr
            const rise_rate = 0.02;// GtC/yr^2, rate of emission increase during transition
            const time_step = 0.5; // years, for numerical integration
            const max_time = 300;  // years

            // --- Core Calculation Function ---
            function runSimulation(t_res, t_trans, N_wedges) {
                const time_values = [];
                const emissions_values = [];
                const carbon_values = [];

                const E_peak = E0 + rise_rate * t_trans;
                const reduction_rate = N_wedges / 50.0;

                let C = C0; // Current carbon starts at the initial value

                for (let t = 0; t <= max_time; t += time_step) {
                    time_values.push(t);

                    // a. Calculate Emission Rate E(t)
                    let E_t;
                    if (t < t_trans) {
                        E_t = E0 + rise_rate * t;
                    } else {
                        const E_linear = E_peak - reduction_rate * (t - t_trans);
                        E_t = Math.max(E_floor, E_linear);
                    }
                    emissions_values.push(100 * E_t); // Scaled for plotting

                    // b. Store current atmospheric carbon C(t) for this time step
                    carbon_values.push(C);

                    // c. Calculate next carbon value C(t+dt) using Euler's method
                    const dC = (E_t - (C - C_eq) / t_res) * time_step;
                    C = C + dC;
                }
                return { time_values, emissions_values, carbon_values };
            }

            // --- Plotting Function ---
            function updatePlot() {
                // 1. Read current values from sliders
                const t_res = parseFloat(sliderResidenceTime.value);
                const t_trans = parseFloat(sliderTransitionPace.value);
                const N_wedges = parseFloat(sliderWedges.value);

                // 2. Perform simulation to get data arrays
                const data = runSimulation(t_res, t_trans, N_wedges);

                // 3. Define Plotly traces
                const carbonTrace = {
                    x: data.time_values,
                    y: data.carbon_values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 }
                };

                const emissionsTrace = {
                    x: data.time_values,
                    y: data.emissions_values,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'purple', width: 2 }
                };

                const targetTrace = {
                    x: [0, max_time],
                    y: [1200, 1200],
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'black', width: 2, dash: 'dash' }
                };

                // 4. Define Plotly layout
                const layout = {
                    title: 'Atmospheric carbon (GtC; blue) and annual emissions (GtC/yr × 100; purple) versus time',
                    xaxis: { title: 'years', range: [0, 300], zeroline: true },
                    yaxis: { title: 'GtC or GtC/yr × 100', range: [0, 1600], zeroline: true, gridcolor: '#ddd' },
                    showlegend: false,
                    margin: { l: 60, r: 20, b: 40, t: 80 },
                    paper_bgcolor: '#f9f9f9',
                    plot_bgcolor: 'white'
                };

                // 5. Update the plot using Plotly.react for efficient redraws
                Plotly.react(plotDiv, [carbonTrace, emissionsTrace, targetTrace], layout);
            }

            // --- Event Listeners for Sliders ---
            sliderResidenceTime.addEventListener('input', (event) => {
                valueResidenceTime.textContent = event.target.value;
                updatePlot();
            });

            sliderTransitionPace.addEventListener('input', (event) => {
                valueTransitionPace.textContent = event.target.value;
                updatePlot();
            });

            sliderWedges.addEventListener('input', (event) => {
                valueWedges.textContent = parseFloat(event.target.value).toFixed(2);
                updatePlot();
            });

            // --- Initial Plot Render on Page Load ---
            updatePlot();
        };
    </script>
</body>
