<body>

    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-theta0">Initial Angle (θ<sub>0</sub>)</label>
                <div class="slider-container">
                    <input type="range" id="slider-theta0" min="-3.14159" max="3.14159" value="0.5236" step="0.01">
                    <span id="value-theta0" class="value-display">π/6</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-v0">Initial Velocity (v<sub>0</sub>)</label>
                <div class="slider-container">
                    <input type="range" id="slider-v0" min="-5" max="5" value="0" step="0.1">
                    <span id="value-v0" class="value-display">0</span>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-length">Connector Length (l)</label>
                <div class="slider-container">
                    <input type="range" id="slider-length" min="0.5" max="4" value="2" step="0.1">
                    <span id="value-length" class="value-display">2</span>
                </div>
            </div>

            <div class="control-group">
                <label>Animate</label>
                <div class="button-group">
                    <button id="btn-play-pause">▶</button>
                    <button id="btn-reset">|&lt;</button>
                </div>
            </div>
        </div>

        <div id="visualization-section">
            <!-- p5.js canvas will be inserted here -->
        </div>
    </div>

    <script>
        // --- DOM Element References ---
        const sliderTheta0 = document.getElementById('slider-theta0');
        const valueTheta0 = document.getElementById('value-theta0');

        const sliderV0 = document.getElementById('slider-v0');
        const valueV0 = document.getElementById('value-v0');

        const sliderLength = document.getElementById('slider-length');
        const valueLength = document.getElementById('value-length');

        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnReset = document.getElementById('btn-reset');

        // --- Simulation State ---
        let isPlaying = false;

        // --- Physics Parameters & State Variables ---
        // Initial conditions (from sliders)
        let theta0, v0, l;

        // State variables
        let x, x_dot, theta, theta_dot, theta_ddot;

        // Constants
        const g = 9.8;  // Gravity
        const m = 1;    // Mass of bob
        const M = 1;    // Mass of support
        const dt = 1 / 60; // Timestep for integration

        // Visualization
        const scale = 50; // pixels per meter

        // --- p5.js Sketch ---
        const sketch = (p) => {
            p.setup = () => {
                const canvas = p.createCanvas(500, 500);
                canvas.parent('visualization-section');

                // Initialize simulation with default slider values
                resetSimulation();

                // Attach event listeners
                sliderTheta0.addEventListener('input', handleSliderChange);
                sliderV0.addEventListener('input', handleSliderChange);
                sliderLength.addEventListener('input', handleSliderChange);

                btnPlayPause.addEventListener('click', togglePlayPause);
                btnReset.addEventListener('click', resetSimulation);
            };

            p.draw = () => {
                // Update physics if playing
                if (isPlaying) {
                    updatePhysics();
                }

                // Draw the system regardless of play state
                drawSystem(p);
            };
        };

        // Initialize p5.js
        new p5(sketch);

        // --- Interaction Logic ---
        function handleSliderChange(event) {
            updateValueDisplays();
            // Stop and reset simulation on any slider change
            if (isPlaying) {
                isPlaying = false;
                btnPlayPause.textContent = "▶";
            }
            resetSimulation();
        }

        function updateValueDisplays() {
            valueTheta0.textContent = formatThetaLabel(parseFloat(sliderTheta0.value));
            valueV0.textContent = parseFloat(sliderV0.value).toFixed(1);
            valueLength.textContent = parseFloat(sliderLength.value).toFixed(1);
        }

        function formatThetaLabel(value) {
            const pi = Math.PI;
            const epsilon = 0.01; // Tolerance for float comparison

            const fractions = {
                [pi]: "π", [-pi]: "-π", [pi / 2]: "π/2", [-pi / 2]: "-π/2",
                [pi / 3]: "π/3", [-pi / 3]: "-π/3", [pi / 4]: "π/4", [-pi / 4]: "-π/4",
                [pi / 6]: "π/6", [-pi / 6]: "-π/6", [0]: "0"
            };

            for (const key in fractions) {
                if (Math.abs(value - parseFloat(key)) < epsilon) {
                    return fractions[key];
                }
            }
            return value.toFixed(2);
        }

        function togglePlayPause() {
            isPlaying = !isPlaying;
            btnPlayPause.textContent = isPlaying ? "||" : "▶";
        }

        function resetSimulation() {
            // Stop animation
            isPlaying = false;
            btnPlayPause.textContent = "▶";

            // Read initial conditions from sliders
            theta0 = parseFloat(sliderTheta0.value);
            v0 = parseFloat(sliderV0.value);
            l = parseFloat(sliderLength.value);

            // Reset physics state variables to initial conditions
            theta = theta0;
            theta_dot = 0;
            x = 0;
            x_dot = v0;

            // Update display values if they are out of sync (e.g., initial load)
            updateValueDisplays();
        }

        // --- Physics & Drawing ---
        function updatePhysics() {
            // Calculate angular acceleration using the provided equation
            const sin_t = Math.sin(theta);
            const cos_t = Math.cos(theta);

            theta_ddot = (-m * l * sin_t * cos_t * theta_dot * theta_dot - g * (M + m) * sin_t) / (l * (M + m * sin_t * sin_t));

            // Numerical integration (Euler method)
            theta_dot += theta_ddot * dt;
            theta += theta_dot * dt;

            // Update pivot velocity based on conservation of momentum (as specified in plan)
            x_dot = v0 - (m * l * cos_t * theta_dot) / (M + m);

            // Update pivot position
            x += x_dot * dt;
        }

        function drawSystem(p) {
            p.background('#F0F0F0');

            // Pivot screen position
            const px = p.width / 2 + x * scale;
            const py = p.height / 2;

            // Bob screen position
            const bx = px + (l * scale) * Math.sin(theta);
            const by = py + (l * scale) * Math.cos(theta);

            // Draw connector rod
            p.stroke('black');
            p.strokeWeight(2);
            p.line(px, py, bx, by);

            // Draw pivot
            p.noStroke();
            p.fill('red');
            p.rectMode(p.CENTER);
            p.rect(px, py, 60, 20);

            // Draw bob
            p.stroke('black');
            p.strokeWeight(2);
            p.fill('orange');
            p.circle(bx, by, 30);
        }

    </script>

</body>
