    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128/examples/js/controls/OrbitControls.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128/examples/js/loaders/FontLoader.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128/examples/js/geometries/TextGeometry.js"></script>

    <script>
        // --- DOM Element References ---
        const controls = {
            strike: document.getElementById('slider-strike'),
            dip: document.getElementById('slider-dip'),
            slipAngle: document.getElementById('slider-slip-angle'),
            slipAmount: document.getElementById('slider-slip-amount'),
            box: document.getElementById('checkbox-box'),
            reset: document.getElementById('btn-reset'),
        };

        const values = {
            strike: document.getElementById('value-strike'),
            dip: document.getElementById('value-dip'),
            slipAngle: document.getElementById('value-slip-angle'),
            slipAmount: document.getElementById('value-slip-amount'),
        };

        // --- Default State ---
        const defaultState = {
            strike: 180,
            dip: 60,
            slipAngle: 90,
            slipAmount: 0.5,
            box: false,
        };

        // --- Three.js Scene Variables ---
        let scene, camera, renderer, controls_orbit, font;
        let sphereMesh, boundingBoxHelper;
        let originalVertices = [];
        let pLabel, tLabel, uLabel;

        const sphereRadius = 1;
        const sphereSegments = 64;
        const sphereRings = 32;
        const deformationScale = 0.2; // Visual scaling for deformation

        // --- Main Initialization ---
        function init() {
            // Load font first, as text geometry depends on it
            const fontLoader = new THREE.FontLoader();
            fontLoader.load('https://cdn.jsdelivr.net/npm/three@0.128/examples/fonts/helvetiker_regular.typeface.json', (loadedFont) => {
                font = loadedFont;
                setupScene();
                setupEventListeners();
                resetControls(); // Set initial state and trigger first render
                animate();
            });
        }

        // --- Scene and Object Setup ---
        function setupScene() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvas = document.getElementById('webgl-canvas');

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xeeeeee);

            // Camera
            camera = new THREE.PerspectiveCamera(50, canvasContainer.clientWidth / canvasContainer.clientHeight, 0.1, 100);
            camera.position.set(0, 1.5, 3.5);
            camera.lookAt(0, 0, 0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvasContainer.clientWidth, canvasContainer.clientHeight);
            renderer.setPixelRatio(window.devicePixelRatio);

            // Orbit Controls
            controls_orbit = new THREE.OrbitControls(camera, renderer.domElement);
            controls_orbit.enableDamping = true;

            // Lighting
            scene.add(new THREE.AmbientLight(0xffffff, 0.6));
            const dirLight = new THREE.DirectionalLight(0xffffff, 0.8);
            dirLight.position.set(5, 10, 7.5);
            scene.add(dirLight);

            // Create Visual Elements
            createDeformedSphere();
            createCompass();
            createAxesLabels();

            // Handle window resizing
            window.addEventListener('resize', onWindowResize, false);
        }

        function createDeformedSphere() {
            const geometry = new THREE.SphereGeometry(sphereRadius, sphereSegments, sphereRings);
            geometry.setAttribute('color', new THREE.BufferAttribute(new Float32Array(geometry.attributes.position.count * 3), 3));

            // Store original vertices for deformation calculation
            originalVertices = Array.from(geometry.attributes.position.array);

            const material = new THREE.MeshStandardMaterial({
                vertexColors: true,
                metalness: 0.2,
                roughness: 0.6,
            });
            sphereMesh = new THREE.Mesh(geometry, material);
            scene.add(sphereMesh);

            boundingBoxHelper = new THREE.BoxHelper(sphereMesh, 0x000000);
            boundingBoxHelper.visible = false;
            scene.add(boundingBoxHelper);
        }

        function createTextLabel(text, color) {
            const geometry = new THREE.TextGeometry(text, {
                font: font,
                size: 0.1,
                height: 0.01,
            });
            geometry.center(); // Center the text geometry
            const material = new THREE.MeshBasicMaterial({ color: color });
            return new THREE.Mesh(geometry, material);
        }

        function createAxesLabels() {
            pLabel = createTextLabel('P', 0x0000ff);
            tLabel = createTextLabel('T', 0xff0000);
            uLabel = createTextLabel('U', 0x008000);
            scene.add(pLabel, tLabel, uLabel);
        }

        function createCompass() {
            const compassRadius = sphereRadius * 1.5;
            const group = new THREE.Group();

            // Circle
            const circleGeom = new THREE.RingGeometry(compassRadius, compassRadius, 64);
            const circleMat = new THREE.MeshBasicMaterial({ color: 0x888888, side: THREE.DoubleSide });
            const circle = new THREE.Mesh(circleGeom, circleMat);
            circle.rotation.x = -Math.PI / 2;
            group.add(circle);

            // Labels
            const directions = { 'N': [0, -1], 'E': [1, 0], 'S': [0, 1], 'W': [-1, 0] };
            for (const [dir, pos] of Object.entries(directions)) {
                const label = createTextLabel(dir, 0x333333);
                label.position.set(pos[0] * compassRadius, 0, pos[1] * compassRadius);
                label.rotation.x = -Math.PI / 2;
                group.add(label);
            }
            scene.add(group);
        }


        // --- Interaction Logic & Event Handlers ---
        function setupEventListeners() {
            Object.values(controls).forEach(control => {
                if (control.type === 'range') {
                    control.addEventListener('input', updateVisualization);
                }
            });
            controls.box.addEventListener('change', updateVisualization);
            controls.reset.addEventListener('click', resetControls);
        }

        function resetControls() {
            controls.strike.value = defaultState.strike;
            controls.dip.value = defaultState.dip;
            controls.slipAngle.value = defaultState.slipAngle;
            controls.slipAmount.value = defaultState.slipAmount;
            controls.box.checked = defaultState.box;
            updateVisualization();
        }

        function updateVisualization() {
            // 1. Read values from controls
            const strike = parseFloat(controls.strike.value);
            const dip = parseFloat(controls.dip.value);
            const slipAngle = parseFloat(controls.slipAngle.value);
            const slipAmount = parseFloat(controls.slipAmount.value);
            const showBox = controls.box.checked;

            // 2. Update text displays
            values.strike.value = strike.toFixed(0);
            values.dip.value = dip.toFixed(0);
            values.slipAngle.value = slipAngle.toFixed(0);
            values.slipAmount.value = slipAmount.toFixed(2);

            // 3. Perform calculations
            const vectors = calculateSeismicVectors(strike, dip, slipAngle);

            // 4. Update 3D objects
            updateSphere(vectors.P, vectors.T, slipAmount);
            updateAxesLabels(vectors.P, vectors.T, vectors.u);
            boundingBoxHelper.visible = showBox;
            boundingBoxHelper.update();
        }

        // --- Core Calculation & Update Functions ---

        function calculateSeismicVectors(strike, dip, slipAngle) {
            // Convert degrees to radians
            const phi = strike * Math.PI / 180;
            const delta = dip * Math.PI / 180;
            const lambda = slipAngle * Math.PI / 180;

            // Calculate vectors in Seismological coordinate system (x=N, y=E, z=Down)
            const n_seis = new THREE.Vector3(
                -Math.sin(delta) * Math.sin(phi),
                 Math.sin(delta) * Math.cos(phi),
                -Math.cos(delta)
            );
            const u_seis = new THREE.Vector3(
                 Math.cos(lambda) * Math.cos(phi) + Math.cos(delta) * Math.sin(lambda) * Math.sin(phi),
                 Math.cos(lambda) * Math.sin(phi) - Math.cos(delta) * Math.sin(lambda) * Math.cos(phi),
                -Math.sin(delta) * Math.sin(lambda)
            );

            // Calculate P and T axes
            const p_seis = new THREE.Vector3().addVectors(u_seis, n_seis).normalize();
            const t_seis = new THREE.Vector3().subVectors(u_seis, n_seis).normalize();

            // Convert to Three.js coordinate system (x=E, y=Up, z=-N)
            const toThreeJS = (v) => new THREE.Vector3(v.y, -v.z, -v.x);

            return {
                P: toThreeJS(p_seis),
                T: toThreeJS(t_seis),
                u: toThreeJS(u_seis),
            };
        }

        function updateSphere(P, T, slipAmount) {
            const positionAttribute = sphereMesh.geometry.attributes.position;
            const colorAttribute = sphereMesh.geometry.attributes.color;

            const colorWhite = new THREE.Color(0xffffff);
            const colorOrange = new THREE.Color(0xffa500);

            const v = new THREE.Vector3();
            for (let i = 0; i < positionAttribute.count; i++) {
                // Get original vertex position
                v.fromArray(originalVertices, i * 3).normalize();

                // --- Deformation ---
                const dotP = v.dot(P);
                const dotT = v.dot(T);
                const deformationFactor = (dotT * dotT) - (dotP * dotP);
                const displacement = v.clone().multiplyScalar(deformationFactor * slipAmount * deformationScale);

                const newPos = new THREE.Vector3().fromArray(originalVertices, i * 3).add(displacement);
                positionAttribute.setXYZ(i, newPos.x, newPos.y, newPos.z);

                // --- Coloring ---
                const color = deformationFactor > 0 ? colorOrange : colorWhite;
                colorAttribute.setXYZ(i, color.r, color.g, color.b);
            }

            positionAttribute.needsUpdate = true;
            colorAttribute.needsUpdate = true;
            sphereMesh.geometry.computeVertexNormals(); // Recalculate normals for correct lighting
        }

        function updateAxesLabels(P, T, u) {
            const labelDist = sphereRadius * 1.15;
            pLabel.position.copy(P).multiplyScalar(labelDist);
            tLabel.position.copy(T).multiplyScalar(labelDist);
            uLabel.position.copy(u).multiplyScalar(labelDist);

            // Make labels always face the camera
            [pLabel, tLabel, uLabel].forEach(label => label.quaternion.copy(camera.quaternion));
        }

        // --- Animation Loop & Resize Handler ---
        function animate() {
            requestAnimationFrame(animate);
            controls_orbit.update();
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const canvasContainer = document.getElementById('canvas-container');
            const width = canvasContainer.clientWidth;
            const height = canvasContainer.clientHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // --- Start the application ---
        init();
    </script>
</body>
</html>
