            border-radius: 4px;
        }
        #probability-display {
            text-align: center;
            margin-top: 20px;
            font-size: 1.2em;
            font-weight: bold;
            color: #2d6a4f;
        }
    </style>
</head>
<body>

    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" value="10" step="1">
                <div class="value-display"><span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" value="2" step="1">
                <div class="value-display"><span id="display-iterations">2</span></div>
            </div>
        </div>

        <div id="canvas-container"></div>

        <div id="probability-display">99.9% success probability</div>
    </div>

    <script>
        // p5.js sketch for Grover's Algorithm Visualization

        let sliderDbSize, sliderIterations;
        let displayDbSize, displayIterations;
        let probDisplay;

        const targetIndex = 6; // Fixed target state (0-indexed)
        const barColor = '#808080'; // Grey for non-target states
        const targetBarColor = '#FFA500'; // Orange for the target state

        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            const canvasWidth = canvasContainer.offsetWidth;
            const canvasHeight = 600;

            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');

            // Get references to HTML elements
            sliderDbSize = select('#slider-db-size');
            sliderIterations = select('#slider-iterations');
            displayDbSize = select('#display-db-size');
            displayIterations = select('#display-iterations');
            probDisplay = select('#probability-display');

            // The draw loop will handle updates, no explicit event listeners needed.
        }

        function draw() {
            background(255);

            // 1. Read current values from sliders
            const N = parseInt(sliderDbSize.value());
            const k = parseInt(sliderIterations.value());

            // Update display spans
            displayDbSize.html(N);
            displayIterations.html(k);

            // Safety check: targetIndex must be less than N
            if (targetIndex >= N) {
                textAlign(CENTER, CENTER);
                fill(255, 0, 0);
                textSize(16);
                text("Error: Database size must be larger than target index (6).", width / 2, height / 2);
                probDisplay.html("Invalid Configuration");
                return;
            }

            // 2. Perform Grover's Algorithm calculations for each stage

            // Helper function for the diffusion operator (inversion about the mean)
            const applyDiffusion = (amplitudes) => {
                const mean = amplitudes.reduce((sum, val) => sum + val, 0) / amplitudes.length;
                return amplitudes.map(amp => 2 * mean - amp);
            };

            // Stage 1: Initial uniform superposition
            const amplitudes1 = Array(N).fill(1 / Math.sqrt(N));

            // Stage 2: After first oracle (inversion)
            let amplitudes2 = [...amplitudes1];
            amplitudes2[targetIndex] *= -1;

            // Stage 3: After first diffusion
            const amplitudes3 = applyDiffusion(amplitudes2);

            // Stage 4: After k iterations
            let amplitudes4 = [...amplitudes1];
            for (let i = 0; i < k; i++) {
                amplitudes4[targetIndex] *= -1; // Oracle
                amplitudes4 = applyDiffusion(amplitudes4); // Diffusion
            }

            // 3. Visualize the four stages

            const sectionHeight = height / 4;
            const titles = [
                "Stage 1: Original Amplitudes",
                "Stage 2: After First Inversion",
                "Stage 3: After First Diffusion",
                `Stage 4: After ${k} Iteration(s)`
            ];
            const allAmplitudes = [amplitudes1, amplitudes2, amplitudes3, amplitudes4];

            for (let i = 0; i < 4; i++) {
                drawPlot(titles[i], allAmplitudes[i], sectionHeight * i, i === 0);
            }

            // 4. Calculate and display success probability
            const successProb = Math.pow(Math.sin((2 * k + 1) * Math.asin(1 / Math.sqrt(N))), 2);
            probDisplay.html(`${(successProb * 100).toFixed(1)}% success probability`);
        }

        function drawPlot(title, amplitudes, yOffset, showParentheses) {
            const N = amplitudes.length;
            const sectionHeight = height / 4;
            const barHeightScale = 80;
            const margin = 40;

            const plotWidth = width - 2 * margin;
            const barSlotWidth = plotWidth / N;
            const barWidth = Math.min(20, barSlotWidth * 0.6);

            const baselineY = yOffset + sectionHeight / 2;

            // Draw title
            fill(0);
            noStroke();
            textAlign(CENTER, TOP);
            textSize(14);
            text(title, width / 2, yOffset + 10);

            // Draw baseline
            stroke(220);
            strokeWeight(1);
            line(margin, baselineY, width - margin, baselineY);

            // Draw bars for each amplitude
            noStroke();
            for (let i = 0; i < N; i++) {
                const amp = amplitudes[i];
                const barHeight = amp * barHeightScale;
                const x = margin + (i + 0.5) * barSlotWidth;

                fill(i === targetIndex ? targetBarColor : barColor);
                rectMode(CORNER);
                rect(x - barWidth / 2, baselineY, barWidth, -barHeight);
            }

            // Draw parentheses for the first plot
            if (showParentheses) {
                stroke(150);
                noFill();
                strokeWeight(2);
                const parenWidth = 20;
                const parenHeight = 60;
                // Left parenthesis
                arc(margin - parenWidth/2, baselineY, parenWidth, parenHeight, -HALF_PI, HALF_PI);
                // Right parenthesis
                arc(width - margin + parenWidth/2, baselineY, parenWidth, parenHeight, HALF_PI, -HALF_PI + TWO_PI);
            }
        }
    </script>
</body>
</html>
