            flex-direction: column;
            align-items: center;
        }
        #stages-count-display {
            font-size: 24px;
            font-weight: bold;
            color: #2c3e50;
            margin-bottom: 20px;
        }
        #canvas-container {
            width: 100%;
            height: 100%;
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 500px;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>

    <!-- Main container -->
    <div id="main-container">
        <!-- Left column: Control Panel -->
        <div id="control-panel">
            <h2>Controls</h2>
            <div class="control-group">
                <label for="slider-volatility">relative volatility</label>
                <div>
                    <input type="range" id="slider-volatility">
                    <button id="btn-volatility-plus">+</button>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-distillate">distillate specification</label>
                <div>
                    <input type="range" id="slider-distillate">
                    <button id="btn-distillate-plus">+</button>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-bottom">bottom specification</label>
                <div>
                    <input type="range" id="slider-bottom">
                    <button id="btn-bottom-plus">+</button>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-feed-comp">feed composition</label>
                <div>
                    <input type="range" id="slider-feed-comp">
                    <button id="btn-feed-comp-plus">+</button>
                </div>
            </div>
            <div class="control-group">
                <label for="slider-feed-quality">feed quality</label>
                <div>
                    <input type="range" id="slider-feed-quality">
                    <button id="btn-feed-quality-plus">+</button>
                </div>
            </div>
        </div>

        <!-- Right column: Visualization Area -->
        <div id="visualization-area">
            <p id="stages-count-display"></p>
            <div id="canvas-container"></div> <!-- p5.js canvas will be attached here -->
        </div>
    </div>

    <script>
        // DOM element references
        const sliderVolatility = document.getElementById('slider-volatility');
        const sliderDistillate = document.getElementById('slider-distillate');
        const sliderBottom = document.getElementById('slider-bottom');
        const sliderFeedComp = document.getElementById('slider-feed-comp');
        const sliderFeedQuality = document.getElementById('slider-feed-quality');

        const btnVolatility = document.getElementById('btn-volatility-plus');
        const btnDistillate = document.getElementById('btn-distillate-plus');
        const btnBottom = document.getElementById('btn-bottom-plus');
        const btnFeedComp = document.getElementById('btn-feed-comp-plus');
        const btnFeedQuality = document.getElementById('btn-feed-quality-plus');

        const stagesDisplay = document.getElementById('stages-count-display');

        // p5.js sketch
        const sketch = (p) => {
            let canvas;
            const margin = 60;
            let plotWidth, plotHeight;

            // Map logical coordinates (0-1) to pixel coordinates
            const mapX = (x) => p.map(x, 0, 1, margin, p.width - margin);
            const mapY = (y) => p.map(y, 0, 1, p.height - margin, margin); // y is inverted

            p.setup = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const size = Math.min(canvasContainer.offsetWidth, window.innerHeight) * 0.8;
                canvas = p.createCanvas(size, size);
                canvas.parent('canvas-container');
                plotWidth = p.width - 2 * margin;
                plotHeight = p.height - 2 * margin;
                p.noLoop(); // We will redraw manually

                setupControls();
                addEventListeners();
                drawDiagram(); // Initial draw
            };

            p.windowResized = () => {
                const canvasContainer = document.getElementById('canvas-container');
                const size = Math.min(canvasContainer.offsetWidth, window.innerHeight) * 0.8;
                p.resizeCanvas(size, size);
                plotWidth = p.width - 2 * margin;
                plotHeight = p.height - 2 * margin;
                drawDiagram();
            };

            function setupControls() {
                // Set slider properties based on the plan
                Object.assign(sliderVolatility, { min: 1.1, max: 10, step: 0.1, value: 2.5 });
                Object.assign(sliderDistillate, { min: 0.51, max: 0.99, step: 0.01, value: 0.95 });
                Object.assign(sliderBottom, { min: 0.01, max: 0.49, step: 0.01, value: 0.05 });
                Object.assign(sliderFeedComp, { min: 0.02, max: 0.98, step: 0.01, value: 0.50 });
                Object.assign(sliderFeedQuality, { min: 0.0, max: 2.0, step: 0.05, value: 1.0 });

                updateFeedCompConstraints();
            }

            function addEventListeners() {
                const sliders = [sliderVolatility, sliderDistillate, sliderBottom, sliderFeedComp, sliderFeedQuality];
                sliders.forEach(slider => slider.addEventListener('input', () => {
                    handleConstraints(slider);
                    drawDiagram();
                }));

                const buttons = [
                    { btn: btnVolatility, slider: sliderVolatility },
                    { btn: btnDistillate, slider: sliderDistillate },
                    { btn: btnBottom, slider: sliderBottom },
                    { btn: btnFeedComp, slider: sliderFeedComp },
                    { btn: btnFeedQuality, slider: sliderFeedQuality },
                ];
                buttons.forEach(({btn, slider}) => {
                    btn.addEventListener('click', () => {
                        let val = parseFloat(slider.value);
                        let step = parseFloat(slider.step);
                        let max = parseFloat(slider.max);
                        if (val + step <= max) {
                           slider.value = (val + step).toFixed(slider.step.includes('.') ? slider.step.split('.')[1].length : 0);
                           // Manually trigger input event
                           slider.dispatchEvent(new Event('input'));
                        }
                    });
                });
            }

            function updateFeedCompConstraints() {
                const bottomVal = parseFloat(sliderBottom.value);
                const distillateVal = parseFloat(sliderDistillate.value);
                const step = parseFloat(sliderFeedComp.step);

                sliderFeedComp.min = bottomVal + step;
                sliderFeedComp.max = distillateVal - step;
            }

            function handleConstraints(triggeredSlider) {
                const bottomVal = parseFloat(sliderBottom.value);
                const distillateVal = parseFloat(sliderDistillate.value);
                let feedVal = parseFloat(sliderFeedComp.value);
                const step = parseFloat(sliderFeedComp.step);

                if (triggeredSlider === sliderBottom || triggeredSlider === sliderDistillate) {
                    updateFeedCompConstraints();
                    // Re-check feed composition validity
                    if (feedVal <= bottomVal) {
                        sliderFeedComp.value = bottomVal + step;
                    }
                    if (feedVal >= distillateVal) {
                        sliderFeedComp.value = distillateVal - step;
                    }
                }
            }

            window.drawDiagram = function() {
                p.background(255);
                drawAxes();

                // 1. Read Inputs
                const alpha = parseFloat(sliderVolatility.value);
                const xD = parseFloat(sliderDistillate.value);
                const xB = parseFloat(sliderBottom.value);
                const xF = parseFloat(sliderFeedComp.value);
                const q = parseFloat(sliderFeedQuality.value);

                // 3. Draw Equilibrium Curve (Red)
                drawEquilibriumCurve(alpha);

                // 4. Draw Diagonal Line (Green)
                drawDiagonalLine();

                // 5. Calculate Intersection Point
                const { x_int, y_int } = calculateIntersection(alpha, xF, q);

                // 6. Draw Operating & Feed Lines
                drawOperatingLines(xD, xB, x_int, y_int);
                drawFeedLine(xF, x_int, y_int);

                // 7. Calculate and Draw Stages
                const stageCount = drawStages(alpha, xD, xB, x_int, y_int);

                // 8. Update Stage Count Display
                stagesDisplay.textContent = `The number of stages is ${stageCount}.`;
            }

            function drawAxes() {
                p.stroke(128); // Gray
                p.strokeWeight(1);
                p.fill(128);
                p.textAlign(p.CENTER, p.CENTER);
                p.textSize(12);

                // X-axis and Y-axis
                p.line(mapX(0), mapY(0), mapX(1), mapY(0));
                p.line(mapX(0), mapY(0), mapX(0), mapY(1));

                // Ticks and labels
                for (let i = 0; i <= 10; i++) {
                    let val = i / 10.0;
                    // X-axis ticks
                    p.line(mapX(val), mapY(0), mapX(val), mapY(0) + 5);
                    p.text(val.toFixed(1), mapX(val), mapY(0) + 15);
                    // Y-axis ticks
                    p.line(mapX(0), mapY(val), mapX(0) - 5, mapY(val));
                    p.text(val.toFixed(1), mapX(0) - 20, mapY(val));
                }

                p.text("x (mole fraction)", p.width / 2, p.height - margin / 2 + 10);
                p.push();
                p.translate(margin / 2 - 10, p.height / 2);
                p.rotate(-p.HALF_PI);
                p.text("y (mole fraction)", 0, 0);
                p.pop();
            }

            function drawEquilibriumCurve(alpha) {
                p.stroke("#FF0000"); // Red
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let x = 0; x <= 1.0; x += 0.01) {
                    const y = (alpha * x) / (1 + (alpha - 1) * x);
                    p.vertex(mapX(x), mapY(y));
                }
                p.endShape();
            }

            function drawDiagonalLine() {
                p.stroke("#228B22"); // Green
                p.strokeWeight(2);
                p.line(mapX(0), mapY(0), mapX(1), mapY(1));
            }

            function calculateIntersection(alpha, xF, q) {
                let x_int, y_int;

                if (Math.abs(q - 1.0) < 1e-9) { // Vertical q-line (saturated liquid)
                    x_int = xF;
                    y_int = (alpha * x_int) / (1 + (alpha - 1) * x_int);
                } else if (Math.abs(q) < 1e-9) { // Horizontal q-line (saturated vapor)
                    y_int = xF;
                    // Solve y = (a*x)/(1+(a-1)x) for x
                    // y * (1 + (a-1)x) = a*x => y = x * (a - y(a-1))
                    x_int = y_int / (alpha - y_int * (alpha - 1));
                } else { // Sloped q-line
                    const m = q / (q - 1);
                    const c = xF - m * xF; // y = mx + c where y-intercept is not c but passes through (xF, xF)

                    // Find intersection of y = mx + (xF - m*xF) and y = (ax)/(1+(a-1)x)
                    // This leads to a quadratic equation: Ax^2 + Bx + C = 0
                    const A = m * (alpha - 1);
                    const B = m + (xF - m * xF) * (alpha - 1) - alpha;
                    const C = xF - m * xF;

                    // Quadratic formula: x = (-B +/- sqrt(B^2 - 4AC)) / 2A
                    const discriminant = B * B - 4 * A * C;
                    if (discriminant >= 0) {
                        const root1 = (-B + Math.sqrt(discriminant)) / (2 * A);
                        const root2 = (-B - Math.sqrt(discriminant)) / (2 * A);
                        // Choose the physically meaningful root (between 0 and 1)
                        x_int = (root1 >= 0 && root1 <= 1) ? root1 : root2;
                    } else {
                        // Fallback if no real intersection (should not happen in normal range)
                        x_int = xF; 
                    }
                    y_int = m * x_int + (xF - m * xF);
                }
                return { x_int, y_int };
            }

            function drawOperatingLines(xD, xB, x_int, y_int) {
                // Rectifying Line (Cyan)
                p.stroke("#00FFFF");
                p.strokeWeight(1.5);
                p.line(mapX(xD), mapY(xD), mapX(x_int), mapY(y_int));

                // Stripping Line (Magenta)
                p.stroke("#FF00FF");
                p.strokeWeight(1.5);
                p.line(mapX(xB), mapY(xB), mapX(x_int), mapY(y_int));
            }

            function drawFeedLine(xF, x_int, y_int) {
                p.stroke("#0000CD"); // Dark Blue
                p.strokeWeight(1.5);
                p.line(mapX(xF), mapY(xF), mapX(x_int), mapY(y_int));
            }

            function drawStages(alpha, xD, xB, x_int, y_int) {
                let stage_count = 0;
                let x = xD;
                let y = xD;

                p.stroke("#000000"); // Black
                p.strokeWeight(1);

                // Slopes for operating lines
                const m_rect = (y_int - xD) / (x_int - xD);
                const m_strip = (y_int - xB) / (x_int - xB);

                while (x > xB && stage_count < 100) { // Safety break at 100 stages
                    // Horizontal line to equilibrium curve
                    const y_curr = y;
                    const x_new = y_curr / (alpha - y_curr * (alpha - 1));
                    p.line(mapX(x), mapY(y_curr), mapX(x_new), mapY(y_curr));
                    x = x_new;

                    stage_count++;
                    if (x <= xB) break;

                    // Vertical line to operating line
                    let y_new;
                    if (x > x_int) { // Above feed, use rectifying line
                        y_new = xD + m_rect * (x - xD);
                    } else { // Below feed, use stripping line
                        y_new = xB + m_strip * (x - xB);
                    }
                    p.line(mapX(x), mapY(y_curr), mapX(x), mapY(y_new));
                    y = y_new;
                }
                return stage_count;
            }
        };

        // Initialize p5.js
        new p5(sketch);

    </script>
</body>
</html>
