<body>

    <!-- Main container for the demo -->
    <div id="demo-container">

        <!-- Section for user controls -->
        <div id="control-panel">
            <div>
                <label for="slider-month">month</label>
                <input type="range" id="slider-month" min="0" max="11" step="1">
            </div>

            <div>
                <label for="perspective-buttons">perspective</label>
                <div id="perspective-buttons">
                    <button id="btn-2d" class="active">2D</button>
                    <button id="btn-3d">3D</button>
                </div>
            </div>
        </div>

        <!-- Section for the visualization canvas -->
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>

    </div>

    <!-- CDN Includes -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // --- GLOBAL STATE AND SETUP ---
        let currentMonth = (new Date()).getMonth();
        let currentMode = '2d';

        let p5Instance = null;
        let threeApp = {
            renderer: null,
            scene: null,
            camera: null,
            controls: null,
            animationFrameId: null,
            earthGroup: null,
            magnetosphere: null,
            sun: null
        };

        const sliderMonth = document.getElementById('slider-month');
        const btn2d = document.getElementById('btn-2d');
        const btn3d = document.getElementById('btn-3d');
        const canvas = document.getElementById('main-canvas');
        const visArea = document.getElementById('visualization-area');

        // --- INITIALIZATION ---
        window.addEventListener('load', () => {
            // Setup slider
            sliderMonth.value = currentMonth;

            // Setup event listeners
            sliderMonth.addEventListener('input', handleSliderChange);
            btn2d.addEventListener('click', switchTo2D);
            btn3d.addEventListener('click', switchTo3D);

            // Initial render
            start2D();
        });

        // --- INTERACTION HANDLERS ---
        function handleSliderChange(event) {
            currentMonth = parseInt(event.target.value, 10);
            updateVisualization();
        }

        function switchTo2D() {
            if (currentMode === '2d') return;
            currentMode = '2d';
            btn2d.classList.add('active');
            btn3d.classList.remove('active');

            cleanup3D();
            start2D();
        }

        function switchTo3D() {
            if (currentMode === '3d') return;
            currentMode = '3d';
            btn3d.classList.add('active');
            btn2d.classList.remove('active');

            cleanup2D();
            start3D();
        }

        function updateVisualization() {
            if (currentMode === '2d' && p5Instance) {
                // p5's draw loop will handle the update
            } else if (currentMode === '3d') {
                update3DScene();
            }
        }

        // --- CLEANUP LOGIC ---
        function cleanup2D() {
            if (p5Instance) {
                p5Instance.remove();
                p5Instance = null;
            }
        }

        function cleanup3D() {
            if (threeApp.animationFrameId) {
                cancelAnimationFrame(threeApp.animationFrameId);
            }
            if (threeApp.renderer) {
                // Dispose of scene objects
                if (threeApp.scene) {
                    threeApp.scene.traverse(object => {
                        if (object.isMesh || object.isLine) {
                            if (object.geometry) object.geometry.dispose();
                            if (object.material) {
                                if (Array.isArray(object.material)) {
                                    object.material.forEach(mat => mat.dispose());
                                } else {
                                    object.material.dispose();
                                }
                            }
                        }
                    });
                }
                threeApp.renderer.dispose();
                threeApp.renderer.domElement.remove(); // p5 will create its own canvas
            }
            // Reset three.js state
            threeApp = { renderer: null, scene: null, camera: null, controls: null, animationFrameId: null, earthGroup: null, magnetosphere: null, sun: null };
            // Ensure the original canvas element is back for p5 to use
            if (!document.getElementById('main-canvas')) {
                visArea.appendChild(canvas);
            }
        }

        // --- 2D VISUALIZATION (p5.js) ---
        function start2D() {
            const sketch2D = (p) => {
                const earthRadius = 40;

                p.setup = () => {
                    const canvasSize = Math.min(visArea.clientWidth, visArea.clientHeight) * 0.9;
                    p.createCanvas(canvasSize, canvasSize).parent('main-canvas');
                    p.angleMode(p.RADIANS);
                };

                p.draw = () => {
                    p.background(0);

                    // The whole system rotates based on the month, except for the solar wind.
                    const tilt = 23.5 * p.cos(2 * p.PI * currentMonth / 12);
                    const tiltRad = p.radians(tilt);

                    // --- 1. Draw Static Elements (Solar Wind) ---
                    p.push();
                    drawSolarWind(p);
                    p.pop();

                    // --- 2. Draw Rotated System ---
                    p.push();
                    p.translate(p.width / 2, p.height / 2);
                    p.rotate(tiltRad);

                    // Magnetotail (drawn first, as background)
                    drawMagnetotail(p);
                    // Magnetic Field Lines
                    drawFieldLines(p);
                    // Bow Shock
                    drawBowShock(p);
                    // Magnetopause
                    drawMagnetopause(p);
                    // Van Allen Belts
                    drawVanAllenBelts(p, earthRadius);
                    // Earth
                    drawEarth(p, earthRadius);
                    // Polar Cusps
                    drawPolarCusps(p, earthRadius);

                    p.pop();
                };

                function drawSolarWind(p) {
                    p.stroke('#990000');
                    p.strokeWeight(1);
                    for (let i = 0; i < 15; i++) {
                        let y = p.map(i, 0, 14, p.height * 0.1, p.height * 0.9);
                        p.line(0, y, p.width * 0.2, y);
                        p.line(p.width * 0.2, y, p.width * 0.2 - 5, y - 3); // Arrowhead
                        p.line(p.width * 0.2, y, p.width * 0.2 - 5, y + 3);
                    }
                }

                function drawBowShock(p) {
                    p.stroke('#FFA500');
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    for (let angle = -p.PI / 1.5; angle <= p.PI / 1.5; angle += 0.1) {
                        let r = p.width * 0.6 / (1 + 0.8 * p.cos(angle)); // Hyperbola-like shape
                        let x = -r * p.cos(angle);
                        let y = r * p.sin(angle);
                        p.vertex(x, y);
                    }
                    p.endShape();
                }

                function drawMagnetopause(p) {
                    p.stroke('#00FFFF');
                    p.strokeWeight(2);
                    p.noFill();
                    p.beginShape();
                    for (let angle = -p.PI / 2; angle <= p.PI / 2; angle += 0.05) {
                        let x = -p.width * 0.2 * p.cos(angle);
                        let y = p.width * 0.3 * p.sin(angle);
                        p.vertex(x, y);
                    }
                    p.endShape();
                }

                function drawFieldLines(p) {
                    p.stroke('#808080');
                    p.strokeWeight(1);
                    p.noFill();
                    // Closed field lines
                    for (let i = 1; i < 4; i++) {
                        p.beginShape();
                        p.curveVertex(0, -earthRadius * i * 1.5);
                        p.curveVertex(0, -earthRadius * i * 1.5);
                        p.curveVertex(-earthRadius * i * 0.8, 0);
                        p.curveVertex(0, earthRadius * i * 1.5);
                        p.curveVertex(0, earthRadius * i * 1.5);
                        p.endShape();
                    }
                    // Open field lines (tail)
                    p.line(0, earthRadius * 1.2, p.width / 2, earthRadius * 2);
                    p.line(0, -earthRadius * 1.2, p.width / 2, -earthRadius * 2);
                    p.bezier(0, earthRadius * 1.5, p.width * 0.1, earthRadius * 2, p.width * 0.3, earthRadius * 2.5, p.width / 2, earthRadius * 2.5);
                    p.bezier(0, -earthRadius * 1.5, p.width * 0.1, -earthRadius * 2, p.width * 0.3, -earthRadius * 2.5, p.width / 2, -earthRadius * 2.5);
                }

                function drawEarth(p, r) {
                    p.fill('#4169E1'); // Blue for ocean
                    p.stroke(255);
                    p.strokeWeight(1);
                    p.ellipse(0, 0, r * 2, r * 2);
                    // Green for landmass
                    p.fill('#228B22');
                    p.noStroke();
                    p.beginShape();
                    p.vertex(-r * 0.5, -r * 0.2);
                    p.vertex(r * 0.1, -r * 0.6);
                    p.vertex(r * 0.6, -r * 0.3);
                    p.vertex(r * 0.5, r * 0.4);
                    p.vertex(-r * 0.2, r * 0.7);
                    p.vertex(-r * 0.7, r * 0.3);
                    p.endShape(p.CLOSE);
                }

                function drawVanAllenBelts(p, r) {
                    p.noStroke();
                    // Outer belt
                    p.fill(50, 205, 50, 150); // #32CD32 with alpha
                    p.beginShape();
                    p.arc(0, 0, r * 5, r * 5, -p.PI / 2.5, p.PI / 2.5, p.CHORD);
                    p.endShape(p.CLOSE);
                    // Inner belt
                    p.fill(0, 100, 0, 200); // #006400 with alpha
                    p.beginShape();
                    p.arc(0, 0, r * 3, r * 3, -p.PI / 3, p.PI / 3, p.CHORD);
                    p.endShape(p.CLOSE);
                }

                function drawPolarCusps(p, r) {
                    p.fill(240, 230, 140, 180); // #F0E68C with alpha
                    p.noStroke();
                    // North cusp
                    p.triangle(-r*1.5, 0, -r*0.9, -r*0.5, -r*0.9, r*0.5);
                    // South cusp is conceptually on the other side of Earth, but in 2D we show it here for clarity
                }

                function drawMagnetotail(p) {
                    p.fill(0, 0, 139, 80); // #00008B with alpha
                    p.noStroke();
                    p.beginShape();
                    p.vertex(earthRadius * 1.5, -earthRadius * 2.5);
                    p.vertex(p.width/2, -p.height/2 * 0.8);
                    p.vertex(p.width/2, p.height/2 * 0.8);
                    p.vertex(earthRadius * 1.5, earthRadius * 2.5);
                    p.endShape(p.CLOSE);
                }
            };
            p5Instance = new p5(sketch2D, 'visualization-area');
        }

        // --- 3D VISUALIZATION (three.js) ---
        function start3D() {
            const width = visArea.clientWidth;
            const height = visArea.clientHeight;

            // 1. Scene
            threeApp.scene = new THREE.Scene();
            threeApp.scene.background = new THREE.Color(0x000000);

            // 2. Camera
            threeApp.camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 2000);
            threeApp.camera.position.set(0, 150, 300);

            // 3. Renderer
            threeApp.renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            threeApp.renderer.setSize(width, height);

            // 4. Controls
            threeApp.controls = new THREE.OrbitControls(threeApp.camera, threeApp.renderer.domElement);
            threeApp.controls.enableDamping = true;

            // 5. Lights
            threeApp.scene.add(new THREE.AmbientLight(0x404040, 0.5));
            const pointLight = new THREE.PointLight(0xffffff, 1.5, 2000);

            // 6. Objects
            // Sun
            const sunGeometry = new THREE.SphereGeometry(20, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xFFFF00 });
            threeApp.sun = new THREE.Mesh(sunGeometry, sunMaterial);
            threeApp.sun.add(pointLight); // Light originates from the Sun
            threeApp.scene.add(threeApp.sun);

            // Earth's Orbit
            const orbitCurve = new THREE.EllipseCurve(0, 0, 200, 180, 0, 2 * Math.PI, false, 0);
            const orbitPoints = orbitCurve.getPoints(100);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(orbitPoints);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xFFFFFF });
            const orbit = new THREE.Line(orbitGeometry, orbitMaterial);
            orbit.rotation.x = Math.PI / 2;
            threeApp.scene.add(orbit);

            // Earth Group (for tilt)
            threeApp.earthGroup = new THREE.Group();
            const earthTilt = 23.5 * (Math.PI / 180);
            threeApp.earthGroup.rotation.x = earthTilt;
            threeApp.scene.add(threeApp.earthGroup);

            // Earth Sphere
            const earthGeometry = new THREE.SphereGeometry(10, 32, 32);
            const earthMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1, roughness: 0.8 });
            const earth = new THREE.Mesh(earthGeometry, earthMaterial);
            threeApp.earthGroup.add(earth);

            // Earth's Axis
            const axisLength = 20;
            const earthAxis = new THREE.ArrowHelper(
                new THREE.Vector3(0, 1, 0), // direction
                new THREE.Vector3(0, 0, 0), // origin
                axisLength, // length
                0x00FF00 // color
            );
            threeApp.earthGroup.add(earthAxis);

            // Solar Wind
            for (let i = 0; i < 8; i++) {
                const angle = i / 8 * 2 * Math.PI;
                const dir = new THREE.Vector3(Math.cos(angle), 0, Math.sin(angle));
                const origin = dir.clone().multiplyScalar(30);
                const solarWindArrow = new THREE.ArrowHelper(dir, origin, 100, 0xFF0000, 10, 5);
                threeApp.scene.add(solarWindArrow);
            }

            // Magnetosphere
            const magnetoProfile = [];
            const profileLength = 80;
            const profileWidth = 40;
            for(let i=0; i<=20; i++) {
                const t = i / 20; // 0 to 1
                const x = -profileWidth * Math.cos(t * Math.PI/2);
                const y = profileLength * Math.pow(t, 2.5); // Elongated tail
                magnetoProfile.push(new THREE.Vector2(x, -y));
            }
            const magnetoGeom = new THREE.LatheGeometry(magnetoProfile, 32);
            const magnetoMat = new THREE.MeshStandardMaterial({
                color: 0x0000FF,
                transparent: true,
                opacity: 0.3,
                side: THREE.DoubleSide
            });
            threeApp.magnetosphere = new THREE.Mesh(magnetoGeom, magnetoMat);
            threeApp.magnetosphere.rotation.z = Math.PI/2; // Align with Z-axis before orienting
            threeApp.earthGroup.add(threeApp.magnetosphere);

            // Initial position and animation loop
            update3DScene();
            animate3D();
        }

        function animate3D() {
            threeApp.animationFrameId = requestAnimationFrame(animate3D);
            threeApp.controls.update();
            threeApp.renderer.render(threeApp.scene, threeApp.camera);
        }

        function update3DScene() {
            if (!threeApp.scene || !threeApp.earthGroup) return;

            // Update Earth's position in its orbit
            const orbitAngle = (currentMonth / 12) * 2 * Math.PI;
            const orbitRadiusX = 200;
            const orbitRadiusZ = 180;
            const earthX = orbitRadiusX * Math.cos(orbitAngle);
            const earthZ = orbitRadiusZ * Math.sin(orbitAngle);
            threeApp.earthGroup.position.set(earthX, 0, earthZ);

            // Update Magnetosphere orientation
            // It should always point away from the sun.
            // The magnetosphere is a child of the Earth group, so its transform is local.
            // We need to point its local Z-axis away from the sun's world position.
            const sunPosition = new THREE.Vector3();
            threeApp.sun.getWorldPosition(sunPosition);

            const earthPosition = new THREE.Vector3();
            threeApp.earthGroup.getWorldPosition(earthPosition);

            // Vector from sun to earth
            const sunToEarth = earthPosition.clone().sub(sunPosition);

            // We need to align the magnetosphere's 'tail' axis (local +X after rotation) with this vector
            const localTailAxis = new THREE.Vector3(1, 0, 0); 

            // Transform local axis to world space to create the quaternion
            const worldTailAxis = localTailAxis.clone().transformDirection(threeApp.magnetosphere.matrixWorld);

            const quaternion = new THREE.Quaternion().setFromUnitVectors(worldTailAxis, sunToEarth.normalize());
            threeApp.magnetosphere.quaternion.premultiply(quaternion);
        }

    </script>

</body>
</html>
